  assign button_text_height = section.settings.button_text_height
  assign button_text_color = section.settings.button_text_color
  assign button_text_hover_color = section.settings.button_text_hover_color
  assign button_radius_modal = section.settings.button_radius_modal
  assign button_border_thickness = section.settings.button_border_thickness
  assign button_border_color = section.settings.button_border_color
  assign button_bg_color = section.settings.button_bg_color
  assign button_bg_hover_color = section.settings.button_bg_hover_color
  assign button_padding_vertical = section.settings.button_padding_vertical
  assign button_padding_vertical_mobile = section.settings.button_padding_vertical_mobile
  assign button_padding_horizontal = section.settings.button_padding_horizontal
  assign button_padding_horizontal_mobile = section.settings.button_padding_horizontal_mobile

  assign video = section.settings.video
  assign video_url = section.settings.video_url
  assign video_width = section.settings.video_width
  assign video_width_mobile = section.settings.video_width_mobile

  assign close_button_size = section.settings.close_button_size
  assign close_button_size_mobile = section.settings.close_button_size_mobile
  assign close_button_bg_color = section.settings.close_button_bg_color
  assign close_button_bg_hover_color = section.settings.close_button_bg_hover_color
  assign close_button_color = section.settings.close_button_color
  assign close_button_hover_color = section.settings.close_button_hover_color
  assign close_button_radius = section.settings.close_button_radius
  assign close_button_border_thickness = section.settings.close_button_border_thickness
  assign close_button_border_color = section.settings.close_button_border_color
  assign close_button_border_hover_color = section.settings.close_button_border_hover_color

  assign modal_radius = section.settings.modal_radius
  assign modal_border_thickness = section.settings.modal_border_thickness
  assign modal_border_color = section.settings.modal_border_color
  assign modal_bg_color = section.settings.modal_bg_color
  assign modal_padding_vertical = section.settings.modal_padding_vertical
  assign modal_padding_horizontal = section.settings.modal_padding_horizontal

  assign modal_close_size = section.settings.modal_close_size
  assign modal_close_color = section.settings.modal_close_color

  assign modal_text = section.settings.modal_text
  assign modal_text_custom = section.settings.modal_text_custom
  assign modal_text_font = section.settings.modal_text_font
  assign modal_text_size = section.settings.modal_text_size
  assign modal_text_height = section.settings.modal_text_height
  assign modal_text_color = section.settings.modal_text_color
  assign modal_text_align = section.settings.modal_text_align

  assign modal_form_gap_mobile = section.settings.modal_form_gap_mobile
  assign modal_form_mt_mobile = section.settings.modal_form_mt_mobile
-%}

{%- style -%}
  {{  input_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}
  {{  modal_text_font | font_face: font_display: 'swap' }}  

  .newsletter-{{ section.id }} {
    display: block;
    position: fixed;
    z-index: 4;
  }

  .newsletter-wrapper-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr {{ video_width_mobile }}%;    
    width: 100%;
    max-width: {{ body_width_mobile }}px;
    height: 100%;
    border: {{ body_border_thickness }}px solid {{ body_border_color }};
    background-color: {{ button_bg_color }};
    border-radius: {{ body_border_radius }}px;
    overflow: hidden;
  }

  .newsletter-{{ section.id }}.active {
    display: none;
  }

  .newsletter-form-{{ section.id }} form {
    height: 100%;
  }

  .newsletter-row-{{ section.id }} {
    display: flex;
    flex-direction: column;
    height: 100%;
  }

  .newsletter-input-{{ section.id }},
  .newsletter-submit-{{ section.id }} {
    border: none;
    flex: 1 0;
    display: block;
    width: 100%;
    text-align: center;
  }

  .newsletter-input-{{ section.id }} {
    display: none;
    border-bottom: {{ body_border_thickness }}px solid {{ body_border_color }};
    padding: {{ input_padding_vertical_mobile }}px {{ input_padding_horizontal_mobile }}px;
    background-color: #FFFFFF;
  }

  .newsletter-input-{{ section.id }}::placeholder {
    color: {{ input_placeholder_color }};
  }

  .newsletter-submit-{{ section.id }} {
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    word-wrap: break-word;
    margin: 0;
    font-size: {{ button_text_size_mobile }}px;
    color: {{ button_text_color }};
    line-height: {{ button_text_height }}%;
    text-transform: unset;
    background-color: {{ button_bg_color }};
    transition: all 0.25s ease 0s;
  }

  .newsletter-submit-{{ section.id }} svg {
    width: 10px;
    height: 10px;
    transition: all 0.25s ease 0s;
  }  

  .newsletter-submit-{{ section.id }} svg path {
    fill: {{ button_text_color }};
    transition: all 0.25s ease 0s;
  }

  .newsletter-submit-{{ section.id }}:hover svg path {
    fill: {{ button_text_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .newsletter-submit-{{ section.id }}:hover {
    transition: all 0.25s ease 0s;
    color: {{ button_text_hover_color }};
    background-color: {{ button_bg_hover_color }};
  }

  .newsletter-video-{{ section.id }} {
    position: relative;
    text-decoration: none;
    overflow: hidden;
    transition: all 0.25s ease 0s;
    box-sizing: border-box !important;
    border-left: {{ body_border_thickness }}px solid {{ body_border_color }};
    aspect-ratio: 1 / 1;
  }

  .newsletter-video-{{ section.id }}:after {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    width: 100%;
    height: 100%;
    z-index: 2;
  }

  .newsletter-video-{{ section.id }} iframe,
  .newsletter-video-{{ section.id }} video {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    border: 0px;
    box-shadow: none;
    position: relative;
  }

  .newsletter-close-{{ section.id }} {
    cursor: pointer;
    position: absolute;
    right: 0;
    top: 0;
    z-index: 3;
    width: {{ close_button_size_mobile }}px;
    height: {{ close_button_size_mobile }}px;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: {{ close_button_bg_color }};
    transition: all 0.25s ease 0s;
    border-top-right-radius: {{ close_button_radius }}px;
    border-bottom-left-radius: {{ close_button_radius }}px;
    border: {{ close_button_border_thickness }}px solid {{ close_button_border_color }};
  }

  .newsletter-close-{{ section.id }} svg {
    display: block;
    width: 60%;
    height: 60%;
  }

  .newsletter-close-{{ section.id }} svg polygon {
    fill: {{ close_button_color }};
    transition: all 0.25s ease 0s;
  }

   .newsletter-close-{{ section.id }}:hover {
     background-color: {{ close_button_bg_hover_color }};
     border-color: {{ close_button_border_hover_color }};
     transition: all 0.25s ease 0s;
   }

  .newsletter-close-{{ section.id }}:hover svg polygon {
    transition: all 0.25s ease 0s;
    fill: {{ close_button_hover_color }};
  } 

  .newsletter-modal-{{ section.id }} {
    pointer-events: none;
    position: fixed;
    left: 0;
    top: 0;
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    display: none;
    align-items: center;
    height: 100%;
    width: 100%;
    z-index: 0;
    padding: 20px;
  }

  .newsletter-modal-{{ section.id }}.active {
    display: flex;
    pointer-events: all;
    z-index: 4;
  }

  .newsletter-content-{{ section.id }} {
    height: auto;
    padding: {{ modal_padding_vertical }}px {{ modal_padding_horizontal }}px;
    border-radius: {{ modal_radius }}px;
    position: relative;
    width: 100%;
    border: {{ modal_border_thickness }}px solid {{ modal_border_color }};
    background-color: {{ modal_bg_color }};
  }

  .newsletter-button-{{ section.id }} {
    position: absolute;
    top: 15px;
    right: 15px;
    cursor: pointer;
    width: {{ modal_close_size }}px;
    height: {{ modal_close_size }}px;
    transition: all 0.25s ease 0s;
  }

  .newsletter-button-{{ section.id }} svg {
    width: 100%;
    height: 100%;    
  }

  .newsletter-button-{{ section.id }} svg polygon {
    transition: all 0.25s ease 0s;
    fill: {{ modal_close_color }};
  }

  .newsletter-text-{{ section.id }} {
    word-wrap: break-word;
    margin: 0;
    font-size: {{ modal_text_size }}px;
    color: {{ modal_text_color }};
    line-height: {{ modal_text_height }}%;
    text-transform: unset;
    text-align: {{ modal_text_align }};
  }

  .newsletter-modal-{{ section.id }} .newsletter-row-{{ section.id }} {
    gap: {{ modal_form_gap_mobile }}px;
    margin-top: {{ modal_form_mt_mobile }}px;    
  }

  .newsletter-modal-{{ section.id }} .newsletter-submit-{{ section.id }} {        
    border: {{ button_border_thickness }}px solid {{ button_border_color }};
  }

  .newsletter-modal-{{ section.id }} .newsletter-input-{{ section.id }} {
    border: {{ input_border_thickness }}px solid {{ input_border_color }};
  }

  .newsletter-input-{{ section.id }}:focus {
    box-shadow: none;
    outline: none;
  }

  .newsletter-modal-{{ section.id }} .newsletter-input-{{ section.id }} {
    font-size: {{ input_modal_size }}px;
    padding: {{ input_padding_vertical_mobile }}px {{ input_padding_horizontal_mobile }}px;
    display: block;
    border-radius: {{ input_radius_modal }}px;
    color: {{ input_color }};
  }

  .newsletter-modal-{{ section.id }} .newsletter-submit-{{ section.id }} {
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius_modal }}px;
  }

  @media(min-width: 1024px) {
    .newsletter-input-{{ section.id }} {
      word-wrap: break-word;
      margin: 0;
      font-size: {{ input_size }}px;
      color: {{ input_color }};
      line-height: {{ input_height }}%;
      text-transform: unset;
      display: block;
      border-bottom: {{ body_border_thickness }}px solid {{ body_border_color }};
      padding: {{ input_padding_vertical }}px {{ input_padding_horizontal }}px;
    }

    .newsletter-wrapper-{{ section.id }} {
      grid-template-columns: 1fr {{ video_width }}%;    
      max-width: {{ body_width }}px;
    }

    .newsletter-close-{{ section.id }} {
      width: {{ close_button_size }}px;
      height: {{ close_button_size }}px;
    }

    .newsletter-submit-{{ section.id }} {
      font-size: {{ button_text_size }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
    }
  }

{%- endstyle -%}

{% if position == "top_left" %}
  <style>
    .newsletter-{{ section.id }} {
      left: {{ horizontal_dst_to_edge | times: 0.75 | round: 0 }}px;
      top: {{ vertical_dst_to_edge | times: 0.75 | round: 0 }}px;
    }

    @media(min-width: 1024px) {
      .newsletter-{{ section.id }} {
        left: {{ horizontal_dst_to_edge }}px;
        top: {{ vertical_dst_to_edge }}px;
      }
    }
  </style>
{% elsif position == "top_right" %}
  <style>
    .newsletter-{{ section.id }} {
      right: {{ horizontal_dst_to_edge | times: 0.75 | round: 0 }}px;
      top: {{ vertical_dst_to_edge | times: 0.75 | round: 0 }}px;
    }

    @media(min-width: 1024px) {
      .newsletter-{{ section.id }} {
        right: {{ horizontal_dst_to_edge }}px;
        top: {{ vertical_dst_to_edge }}px;
      }
    }
  </style>
{% elsif position == "bottom_left" %}
  <style>
    .newsletter-{{ section.id }} {
      left: {{ horizontal_dst_to_edge | times: 0.75 | round: 0 }}px;
      bottom: {{ vertical_dst_to_edge | times: 0.75 | round: 0 }}px;
    }

    @media(min-width: 1024px) {
      .newsletter-{{ section.id }} {
        left: {{ horizontal_dst_to_edge }}px;
        bottom: {{ vertical_dst_to_edge }}px;
      }
    }
  </style>
{% elsif position == "bottom_right" %}
  <style>
    .newsletter-{{ section.id }} {
      right: {{ horizontal_dst_to_edge | times: 0.75 | round: 0 }}px;
      bottom: {{ vertical_dst_to_edge | times: 0.75 | round: 0 }}px;
    }

    @media(min-width: 1024px) {
      .newsletter-{{ section.id }} {
        right: {{ horizontal_dst_to_edge }}px;
        bottom: {{ vertical_dst_to_edge }}px;
      }
    }
  </style>
{% endif %}

{% if input_custom %}
  <style>
    .newsletter-input-{{ section.id }} {
      font-family: {{ input_font.family }}, {{ input_font.fallback_families }};
      font-weight: {{ input_font.weight }};
      font-style: {{ input_font.style }};
    }
  </style>
{% endif %}  

{% if button_custom %}
  <style>
    .newsletter-submit-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}  

{% if modal_text_custom %}
  <style>
    .newsletter-text-{{ section.id }} {
      font-family: {{ modal_text_font.family }}, {{ modal_text_font.fallback_families }};
      font-weight: {{ modal_text_font.weight }};
      font-style: {{ modal_text_font.style }};
    }
  </style>
{% endif %} 

<div class="newsletter-{{ section.id }}">
    <div class="newsletter-wrapper-{{ section.id }}">
      <div class="newsletter-close-{{ section.id }}">
        <svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" version="1.1" id="Layer_1" x="0px" y="0px" viewBox="0 0 371.23 371.23" style="enable-background:new 0 0 371.23 371.23;" xml:space="preserve">
          <polygon points="371.23,21.213 350.018,0 185.615,164.402 21.213,0 0,21.213 164.402,185.615 0,350.018 21.213,371.23   185.615,206.828 350.018,371.23 371.23,350.018 206.828,185.615 "/>
        </svg>
      </div>
      <div class="newsletter-form-{{ section.id }}">
        {% form 'customer' %}
          <input type="hidden" name="contact[tags]" value="newsletter">
          <div class="newsletter-row-{{ section.id }}">
            <input
              id="NewsletterForm--{{ section.id }}"
              type="email"
              name="contact[email]"
              class="newsletter-input-{{ section.id }}"
              value="{{ form.email }}"
              aria-required="true"
              autocorrect="off"
              autocapitalize="off"
              autocomplete="email"
              placeholder="{{ input_placeholder }}"
              {% if form.errors %}
                autofocus
                aria-invalid="true"
                aria-describedby="Newsletter-error--{{ section.id }}"
              {% elsif form.posted_successfully? %}
                aria-describedby="Newsletter-success--{{ section.id }}"
              {% endif %}
              required
            >
            {% if button %}
              <button type="submit" class="newsletter-submit-{{ section.id }}" name="commit">
