      </div>
      <div class="newsletter-video-{{ section.id }}">
        {% assign video_id = video.id | default: video_url.id %}
        {% assign loop = '&loop=1&playlist=' | append: video_id %}
        {%- if video == null and video_url != null -%}
        {%- if video_url.type == 'youtube' -%}
          <iframe
            data-src="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1&mute=1&muted=1&autoplay=1&controls=0&showinfo=0{{ loop }}"
            data-src1="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1&mute=1&muted=1&autoplay=1&controls=0&showinfo=0{{ loop }}"
            data-src2="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1&mute=1&muted=1&autoplay=0&controls=0&showinfo=0{{ loop }}"
            allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
            allowfullscreen
            class="lazy"
          ></iframe>
          {%- else -%}
          <iframe
            data-src="https://player.vimeo.com/video/{{ video_id }}?enablejsapi=1&mute=1&muted=1&autoplay=1&controls=0&showinfo=0{{ loop }}"
            data-src1="https://player.vimeo.com/video/{{ video_id }}?enablejsapi=1&mute=1&muted=1&autoplay=1&controls=0&showinfo=0{{ loop }}"
            data-src2="https://player.vimeo.com/video/{{ video_id }}?enablejsapi=1&mute=1&muted=1&autoplay=0&controls=0&showinfo=0{{ loop }}"
            allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
            allowfullscreen
            class="lazy"
          ></iframe>
          {% endif %}
          {% else %}
            <video data-src="{{ video.sources[1].url }}" muted autoplay playsinline preload="none" loop class="lazy"></video>
          {% endif %}
      </div>
    </div>
</div>

<div class="newsletter-modal-{{ section.id }}">
  <div class="newsletter-content-{{ section.id }}">
    <div class="newsletter-button-{{ section.id }}">
      <svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" version="1.1" id="Layer_1" x="0px" y="0px" viewBox="0 0 371.23 371.23" style="enable-background:new 0 0 371.23 371.23;" xml:space="preserve">
        <polygon points="371.23,21.213 350.018,0 185.615,164.402 21.213,0 0,21.213 164.402,185.615 0,350.018 21.213,371.23   185.615,206.828 350.018,371.23 371.23,350.018 206.828,185.615 "/>
      </svg>
    </div>
    {% if modal_text != blank %}
      <div class="newsletter-text-{{ section.id }}">
        {{ modal_text }}
