<script>
  function initNewsletter2() {
    let lazyLoadInstance = new LazyLoad();
    window.addEventListener(
      "LazyLoad::Initialized",
      function (event) {
        window.lazyLoadInstance = event.detail.instance;
      },
      false
    );

    let newsletterContent = document.querySelector('.newsletter-{{ section.id }}');
    let modal = document.querySelector('.newsletter-modal-{{ section.id }}');
    let submit = document.querySelector('.newsletter-submit-{{ section.id }}');
    let closeModal = document.querySelector('.newsletter-button-{{ section.id }}');
    let close = document.querySelector('.newsletter-close-{{ section.id }}');

    close.addEventListener('click', function() {
      newsletterContent.classList.add('active')
    })

    submit.addEventListener('click', function(event) {        
      if (window.innerWidth < 1024) {
        event.preventDefault(); 
        modal.classList.add('active');
        document.body.style.overflow = 'hidden';
      }
    });

    closeModal.addEventListener('click', function() {
      modal.classList.remove('active');
      document.body.style.overflow = '';
    });

    function formToJSON(elements) {
    	return [].reduce.call(elements, function (data, element) {
    		data[element.name] = element.value;
    		return data;
    	}, {});
    }

    function getUrlString(data) {
    	var urlParameters = Object.entries(data).map(function (e) {
    		return e.join('=');
    	}).join('&');

    	return urlParameters;
    }

    const form = document.querySelector('.newsletter-form-{{ section.id }} form');

    if(form) {
      let inputs = form.querySelectorAll("[name]");

      form.addEventListener('submit', function(event) {
        event.preventDefault();

        var action = form.getAttribute("action");

        fetch(action, {
          method: 'POST',
          body: getUrlString(formToJSON(inputs))
        })
        .then(response => {
          console.log(response);
          console.log(response.status);
          if (response.status === 200) {
            alert(response);
          } else {
            throw new Error('Form submission failed.');
          }
        })
        .catch(error => {
          console.error('Form submission error:', error);
          alert(error);
        })
        .finally(() => {
          alert('finally');
        });

      });
    }
  }

  document.addEventListener('DOMContentLoaded', initNewsletter2);

  if (Shopify.designMode) {
    document.addEventListener('shopify:section:unload', initNewsletter2);
    document.addEventListener('shopify:section:load', initNewsletter2);
  }
</script>
