{%- style -%}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  sub_heading_font | font_face: font_display: 'swap' }}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  price_font | font_face: font_display: 'swap' }}
  {{  elements_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}
  {{  budges_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .pricing-table-heading-{{ section.id }} > * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
    text-align: {{ heading_align }};
  }

  .pricing-table-heading-{{ section.id }} strong {
     color: {{ heading_other_color }};
  }

  .pricing-table-subheading-{{ section.id }} {
    margin-top: {{ sub_heading_mt | times: 0.75 | round: 0 }}px;
    max-width: fit-content;
    width: 100%;
  }

  .pricing-table-subheading-{{ section.id }} > * {
    margin: 0;
    font-size: {{ sub_heading_size_mobile }}px;
    color: {{ sub_heading_color }};
    line-height: {{ sub_heading_height }}%;
    text-transform: unset;
    text-align: {{ sub_heading_align }};
  }

  .pricing-table-items-{{ section.id }} {
    margin-top: {{ table_mt | times: 0.75 | round: 0 }}px;
    width: 100%;
    max-width: {{ table_width_mobile }}%;
    margin-left: auto;
    margin-right: auto;
  }

  .pricing-table-item-{{ section.id }} {
    height: fit-content;
    padding: {{ card_padding_vertical | times: 0.75 | round: 0 }}px {{ card_padding_horizontal | times: 0.75 | round: 0 }}px;
    border: {{ card_border_thickness }}px solid {{ card_border_color }};
    background-color: {{ card_bg_color }};
    transition: all 0.25s ease 0s;
    border-radius: {{ card_radius }}px; 
  }

  .pricing-table-item-{{ section.id }}.active-item-{{ section.id }} {
    background-color: {{ card_bg_active_color }};
    border: {{ card_border_thickness }}px solid {{ card_border_active_color }};
  }

  .pricing-table-item-{{ section.id }}:not(:first-child) {
    margin-top: {{ table_gap_mobile  }}px;
  }

  .pricing-table-title-{{ section.id }} {
    margin: 0px;
    margin-top: {{ title_mt | times: 0.75 | round: 0 }}px;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: none;
    text-align: {{ title_align }};
    font-weight: 700;
    position: relative;
  }

  .pricing-table-item-{{ section.id }}.active-item-{{ section.id }} .pricing-table-title-{{ section.id }} {
    color: {{ title_active_color }}; 
  }

  .pricing-table-price-{{ section.id }} {
    margin: 0px;
    margin-top: {{ price_mt | times: 0.75 | round: 0 }}px;
    font-size: {{ price_size_mobile }}px;
    color: {{ price_color }};
    line-height: {{ price_height }}%;
    text-transform: none;
    font-weight: 700;
    display: flex;
    align-items: center;
    justify-content: {{ price_align }};
    gap: 5px;
    {% if price_color_gradient != blank %}
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-image: {{ price_color_gradient }};
    {% endif %}
  }

  .pricing-table-item-{{ section.id }}.active-item-{{ section.id }} .pricing-table-price-{{ section.id }} {
    color: {{ price_active_color }};
    {% if price_active_color_gradient != blank  %}
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-image: {{ price_active_color_gradient }};
    {% endif %}
  }

  .pricing-table-price-{{ section.id }} span {
    max-width: 70px;
    line-height: 100%;
    font-size: {{ price_size_mobile | times: 0.35 | round: 0 }}px;
    opacity: 0.6;
    font-weight: 400;
    {% if price_color_gradient != blank  %}
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-image: {{ price_color_gradient }};
    {% endif %}
  }

  .pricing-table-item-{{ section.id }}.active-item-{{ section.id }} .pricing-table-price-{{ section.id }} span {
    {% if price_active_color_gradient != blank  %}
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-image: {{ price_active_color_gradient }};
    {% endif %}
  }

  .pricing-table-price-sale-{{ section.id }} {
    opacity: 0.6;
    text-decoration: line-through;
    font-size: {{ price_sale_size_mobile }}px;
    font-weight: 700;
  }

  .pricing-table-list-{{ section.id }} {
    margin-top: {{ elements_mt | times: 0.75 | round: 0 }}px;
  }

  .pricing-table-list-{{ section.id }} ul {
    margin: 0px;
    padding: 0px;
    list-style: none;
  }

  .pricing-table-list-{{ section.id }} p,
  .pricing-table-list-{{ section.id }} li {
    margin: 0px;
    font-size: {{ elements_size_mobile }}px;
    color: {{ elements_color }};
    line-height: {{ elements_height }}%;
    text-transform: unset;
    text-align: {{ elements_align }};
    list-style: none;
  }

  .pricing-table-item-{{ section.id }}.active-item-{{ section.id }} .pricing-table-list-{{ section.id }} p,
  .pricing-table-item-{{ section.id }}.active-item-{{ section.id }} .pricing-table-list-{{ section.id }} li {
    color: {{ elements_active_color }};
  }

  .pricing-table-list-{{ section.id }} p:not(:first-child),
  .pricing-table-list-{{ section.id }} li {
    margin-top: {{ elements_gap | times: 0.75 | round: 0 }}px;
  }

  .pricing-table-btn-{{ section.id }} {
    margin: 0px;
    margin-top: {{ button_mt | times: 0.75 | round: 0 }}px;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 5px;
    width: 100%;
    max-width: 100%;
    font-size: {{ button_size_mobile }}px;
    font-weight: 700;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical | times: 0.75 | round: 0 }}px {{ button_padding_horizontal | times: 0.75 | round: 0 }}px;
    border-radius: {{ button_radius }}px;
    border: {{ button_border_thickness }}px solid {{ button_border_color }};
    background-color: {{ button_bg_color }};
    transition: all 0.25s ease 0s;
    {% if button_bg_color_gradient != blank %}
      background-image: {{ button_bg_color_gradient }};
    {% endif %}
  }

  .pricing-table-btn-{{ section.id }}:hover {
    transition: all 0.25s ease 0s;
    color: {{ button_hover_color }};
    border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    background-color: {{ button_bg_hover_color }};
    {% if button_bg_hover_color_gradient != blank %}
      background-image: {{ button_bg_hover_color_gradient }};
    {% endif %}
  }

  .pricing-table-item-{{ section.id }}.active-item-{{ section.id }} .pricing-table-btn-{{ section.id }} {
    color: {{ button_active_color }};
    border: {{ button_border_thickness }}px solid {{ button_border_active_color }};
    background-color: {{ button_bg_active_color }};
    {% if button_bg_active_color_gradient != blank %}
      background-image: {{ button_bg_active_color_gradient }};
    {% endif %}
  }

  .pricing-table-item-{{ section.id }}.active-item-{{ section.id }} .pricing-table-btn-{{ section.id }}:hover {
    transition: all 0.25s ease 0s;
    color: {{ button_active_hover_color }};
    border: {{ button_border_thickness }}px solid {{ button_border_active_hover_color }};
    background-color: {{ button_bg_active_hover_color }};
    {% if button_bg_active_hover_color_gradient != blank %}
      background-image: {{ button_bg_active_hover_color_gradient }};
    {% endif %}
  }

  .pricing-table-btn-{{ section.id }} img {
    display: block;
    width: 16px;
    height: 16px;
    object-fit: cover;
  }

  .pricing-table-image-{{ section.id }} {
    margin-top: {{ image_mt | times: 0.75 | round: 0 }}px;
    margin:auto;
    width: 100%;
    max-width: {{ image_width_mobile }}%;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    border-radius: {{ image_radius }}px;
    background-color: {{ image_bg_color }};
    aspect-ratio: 1 / 1;
  }

  .pricing-table-item-{{ section.id }}.active-item-{{ section.id }} .pricing-table-image-{{ section.id }} {
    background-color: {{ image_bg_active_color }};
    border: {{ image_border_thickness }}px solid {{ image_border_active_color }};
  }

  .pricing-table-image-{{ section.id }} img {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: {{ image_radius }}px;
  }

  .pricing-table-budges-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 10px;
  }

  .pricing-table-budge-{{ section.id }} {
    width: 100%;
    max-width: fit-content;
    font-size: {{ budges_size_mobile }}px;
    line-height: {{ budges_height }}%;
    text-transform: unset;
    padding: {{ budges_padding_vertical | times: 0.75 | round: 0 }}px {{ budges_padding_horizontal | times: 0.75 | round: 0 }}px;
    border-radius: {{ budges_radius }}px;
    border: {{ budges_border_thickness }}px solid {{ budges_border_color }};
    background-color: {{ budges_bg_color }};
    color: {{ budges_color }};
  }

  .pricing-table-item-{{ section.id }}.active-item-{{ section.id }} .pricing-table-budge-{{ section.id }} {
    color: {{ budges_active_color }};
    border: {{ budges_border_thickness }}px solid {{ budges_border_active_color }};
    background-color: {{ budges_bg_active_color }};
  }

  .pricing-table-budge-{{ section.id }}:last-child {
    border: {{ budges_border_thickness }}px solid {{ budges_border_second_color }};
    background-color: {{ budges_bg_second_color }};
    color: {{ budges_second_color }};
  }

  .pricing-table-item-{{ section.id }}.active-item-{{ section.id }} .pricing-table-budge-{{ section.id }}:last-child {
    color: {{ budges_second_active_color }};
    border: {{ budges_border_thickness }}px solid {{ budges_border_second_active_color }};
    background-color: {{ budges_bg_second_active_color }};
  }

  .pricing-table-text-{{ section.id }} {
    margin: 0px;
    margin-top: {{ text_mt | times: 0.75 | round: 0 }}px;
    font-size: {{ text_size_mobile }}px;
    line-height: {{ text_height }}%;
    color: {{ text_color }};
  }

  @media(min-width: 992px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .pricing-table-heading-{{ section.id }} > * {
      font-size: {{ heading_size }}px; 
    }

    .pricing-table-subheading-{{ section.id }} {
      margin-top: {{ sub_heading_mt }}px;
    }

    .pricing-table-subheading-{{ section.id }} > * {
      font-size: {{ sub_heading_size }}px; 
    }

    .pricing-table-items-{{ section.id }} {
      margin-top: {{ table_mt  }}px;
      max-width: {{ table_width }}%;
      display: grid;
      grid-template-columns: repeat({{ section.blocks.size }}, 1fr);
      gap: {{ table_gap }}px;
    }

    .pricing-table-item-{{ section.id }} {
      padding: {{ card_padding_vertical }}px {{ card_padding_horizontal }}px;
    }

    .pricing-table-item-{{ section.id }}:not(:first-child) {
      margin-top: 0px;
    }

    .pricing-table-title-{{ section.id }} {
      margin-top: {{ title_mt }}px;
      font-size: {{ title_size }}px;
    }

    .pricing-table-price-{{ section.id }} {
      margin-top: {{ price_mt }}px;
      font-size: {{ price_size }}px;
    }

    .pricing-table-price-sale-{{ section.id }} {
      font-size: {{ price_sale_size }}px;
    }

    .pricing-table-price-{{ section.id }} span {
      font-size: {{ price_size | times: 0.35 | round: 0 }}px;
    }

    .pricing-table-list-{{ section.id }} {
      margin-top: {{ elements_mt }}px;
    }

    .pricing-table-list-{{ section.id }} p,
    .pricing-table-list-{{ section.id }} li {
      font-size: {{ elements_size }}px;
    }

    .pricing-table-list-{{ section.id }} p:not(:first-child),
    .pricing-table-list-{{ section.id }} li {
      margin-top: {{ elements_gap }}px;
    }

    .pricing-table-btn-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      font-size: {{ button_size }}px;
      max-width: {{ button_width }}%;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
    }

    .pricing-table-image-{{ section.id }} {
      margin-top: {{ image_mt }}px;
      max-width: {{ image_width }}%;
    }

    .pricing-table-budge-{{ section.id }} {
      font-size: {{ budges_size }}px;
    }

    .pricing-table-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      font-size: {{ text_size}}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .pricing-table-heading-{{ section.id }} > * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if sub_heading_custom %}
  <style>
    .pricing-table-subheading-{{ section.id }} > * {
      font-family: {{ sub_heading_font.family }}, {{ sub_heading_font.fallback_families }};
      font-weight: {{ sub_heading_font.weight }};
      font-style: {{ sub_heading_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .pricing-table-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if elements_custom %}
  <style>
    .pricing-table-list-{{ section.id }} p,
    .pricing-table-list-{{ section.id }} li {
      font-family: {{ elements_font.family }}, {{ elements_font.fallback_families }};
      font-weight: {{ elements_font.weight }};
      font-style: {{ elements_font.style }};
    }
  </style>
