{% if button_custom %}
  <style>
    .pricing-table-btn-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if budges_custom %}
  <style>
    .pricing-table-budge-{{ section.id }} {
      font-family: {{ budges_font.family }}, {{ budges_font.fallback_families }};
      font-weight: {{ budges_font.weight }};
      font-style: {{ budges_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .pricing-table-text-{{ section.id }} {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if sub_heading_align == "center" %}
  <style>
    .pricing-table-subheading-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    }
  </style>
{% elsif sub_heading_align == "right" %}
  <style>
    .pricing-table-subheading-{{ section.id }} {
      margin-left: auto;
    }
  </style>
{% endif %}

{% if sub_heading_border %}
  <style>
    .pricing-table-subheading-{{ section.id }} {
      position: relative;
    }

    .pricing-table-subheading-{{ section.id }}::after {
      content: '';
      position: absolute;
      left: 50%;
      transform: translateX(-50%);
      bottom: -15px;
      width: 50px;
      height: 2px;
      background-color: {{ sub_heading_border_color }};
    }
  </style>
{% endif %}

{% if card_shadow %}
  <style>
    .pricing-table-item-{{ section.id }} {
      moz-box-shadow: 0 0 10px #cccccc;
      -webkit-box-shadow: 0 0 10px #cccccc;
      -o-box-shadow: 0 0 10px #cccccc;
      box-shadow: 0 0 10px #cccccc;
    }
  </style>
{% endif %}

{% if button_align == "center" %}
  <style>
    .pricing-table-btn-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    }
  </style>
{% elsif button_align == "right" %}
  <style>
    .pricing-table-btn-{{ section.id }} {
      margin-left: auto;
    }
  </style>
{% endif %}

{% if elements_icon != blank %}
  <style>
    .pricing-table-list-{{ section.id }} li {
      position: relative;
      padding-left: {{ section.settings.icon_size | times: 1.5 }}px;
    }
    .pricing-table-list-{{ section.id }} li:before {
      content: '';
      position: absolute;
      left: 0;
      top: 50%;
      transform: translateY(-50%);
      width: {{ section.settings.icon_size }}px;
      height: {{ section.settings.icon_size }}px;
      background-image: url('{{ elements_icon | image_url }}');
      background-size: cover;
      background-position: center;
      background-repeat: no-repeat;
    }
  </style>
{% endif %}

{% if elements_active_icon != blank %}
  <style>
    .pricing-table-item-{{ section.id }}.active-item-{{ section.id }} .pricing-table-list-{{ section.id }} li {
      position: relative;
      padding-left: {{ section.settings.icon_size | times: 1.5 }}px;
    }
    .pricing-table-item-{{ section.id }}.active-item-{{ section.id }} .pricing-table-list-{{ section.id }} li:before {
      content: '';
      position: absolute;
      left: 0;
      top: 50%;
      transform: translateY(-50%);
      width: {{ section.settings.icon_size }}px;
      height: {{ section.settings.icon_size }}px;
      background-image: url('{{ elements_active_icon | image_url }}');
      background-size: cover;
      background-position: center;
      background-repeat: no-repeat;
    }
  </style>
{% endif %}

{% if budges_top %}
  <style>
    .pricing-table-item-{{ section.id }} {
      position: relative;
    }

    .pricing-table-budges-{{ section.id }} {
      position: absolute;
      left: 0px;
      width: 100%;
      top: -{{ budges_size_mobile | times: 0.8 | round: 0 | plus: budges_padding_vertical | times: 0.75 }}px;
      justify-content: center;
    }

    @media(min-width: 992px) {
      .pricing-table-budges-{{ section.id }} {
        top: -{{ budges_size | times: 0.6 | round: 0 | plus: budges_padding_vertical | times: 0.75 }}px;
      }
    }
  </style>
{% endif %}

{% if price_time_under %}
  <style>
    .pricing-table-price-{{ section.id }} {
      flex-direction: column;
    }
    .pricing-table-price-{{ section.id }} span {
      max-width: fit-content;
    }
  </style>
{% endif %}

{% if budges_weight == "bold" %}
  <style>
    .pricing-table-budge-{{ section.id }} {
      font-weight: 700;
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} pricing-table-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings ">
      {% if heading != blank %}
        <div class="pricing-table-heading-{{ section.id }}">{{ heading }}</div>
      {% endif %}

      {% if sub_heading != blank %}
        <div class="pricing-table-subheading-{{ section.id }}">{{ sub_heading }}</div>
      {% endif %}

      <div class="pricing-table-items-{{ section.id }}">
        {% for block in section.blocks %}
          <div class="pricing-table-item-{{ section.id }} {% if block.settings.active %}active-item-{{ section.id }}{% endif %}">
            {% if block.settings.budges != blank %}
              {% assign budges = block.settings.budges | split: ',' %}
              <div class="pricing-table-budges-{{ section.id }}">
                {% for budge in budges %}
                  <div class="pricing-table-budge-{{ section.id }}">{{ budge }}</div>
                {% endfor %}
              </div>
            {% endif %}
            {% if block.settings.image != blank %}
              <div class="pricing-table-image-{{ section.id }}">
                <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
              </div>
            {% endif %}
            {% if block.settings.heading != blank %}
              <p class="pricing-table-title-{{ section.id }}">{{ block.settings.heading }}</p>
            {% endif %}
            <p class="pricing-table-price-{{ section.id }}">{% if block.settings.price_sale != blank %}<span class="pricing-table-price-sale-{{ section.id }}">{% if block.settings.currency != blank %}{{ block.settings.currency }}{% endif %}{{ block.settings.price_sale }}</span>{% endif %}{% if block.settings.currency != blank %}{{ block.settings.currency }}{% endif %}{{ block.settings.price }}{% if block.settings.time != blank %}<span>{{ block.settings.time }}</span>{% endif %}</p>
            {% if elements_position == "before" %}
              <div class="pricing-table-list-{{ section.id }}">
                {{ block.settings.elements }}
              </div>
            {% endif %}
            {% if block.settings.button_text != blank %}
              <a href="{{ block.settings.button_url }}" class="pricing-table-btn-{{ section.id }}">
                {% if button_icon != blank %}
                  <img src="{{ button_icon | image_url }}" alt="{{ button_icon.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                {% endif %}
                {{ block.settings.button_text }}
              </a>
            {% endif %}
            {% if block.settings.text != blank %}
              <p class="pricing-table-text-{{ section.id }}">
                {{ block.settings.text }}
              </p>
            {% endif %}
            {% if elements_position == "after" %}
              <div class="pricing-table-list-{{ section.id }}">
                {{ block.settings.elements }}
              </div>
            {% endif %}
          </div>
        {% endfor %}
      </div>
    </div>
</div>
