  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy

  assign heading = section.settings.heading
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_color = section.settings.heading_color
  assign heading_other_color = section.settings.heading_other_color
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_height = section.settings.heading_height
  assign heading_align = section.settings.heading_align

  assign sub_heading = section.settings.sub_heading
  assign sub_heading_size = section.settings.sub_heading_size
  assign sub_heading_size_mobile = section.settings.sub_heading_size_mobile
  assign sub_heading_color = section.settings.sub_heading_color
  assign sub_heading_custom = section.settings.sub_heading_custom
  assign sub_heading_font = section.settings.sub_heading_font
  assign sub_heading_height = section.settings.sub_heading_height
  assign sub_heading_align = section.settings.sub_heading_align
  assign sub_heading_mt = section.settings.sub_heading_mt
  assign sub_heading_border = section.settings.sub_heading_border
  assign sub_heading_border_color = section.settings.sub_heading_border_color

  assign table_mt = section.settings.table_mt
  assign table_width_mobile = section.settings.table_width_mobile
  assign table_width = section.settings.table_width
  assign table_gap = section.settings.table_gap
  assign table_gap_mobile = section.settings.table_gap_mobile

  assign card_padding_vertical = section.settings.card_padding_vertical
  assign card_padding_horizontal = section.settings.card_padding_horizontal
  assign card_radius = section.settings.card_radius
  assign card_border_thickness = section.settings.card_border_thickness
  assign card_bg_color = section.settings.card_bg_color
  assign card_border_color = section.settings.card_border_color
  assign card_active_color = section.settings.card_active_color
  assign card_shadow = section.settings.card_shadow
  assign use_hover = section.settings.use_hover

  assign title_size = section.settings.title_size
  assign title_size_mobile = section.settings.title_size_mobile
  assign title_color = section.settings.title_color
  assign title_active_color = section.settings.title_active_color
  assign title_custom = section.settings.title_custom
  assign title_font = section.settings.title_font
  assign title_height = section.settings.title_height
  assign title_align = section.settings.title_align

  assign price_size = section.settings.price_size
  assign price_size_mobile = section.settings.price_size_mobile
  assign price_color = section.settings.price_color
  assign price_active_color = section.settings.price_active_color
  assign price_custom = section.settings.price_custom
  assign price_font = section.settings.price_font
  assign price_height = section.settings.price_height
  assign price_mt = section.settings.price_mt
  assign price_align = section.settings.price_align

  assign elements_size = section.settings.elements_size
  assign elements_size_mobile = section.settings.elements_size_mobile
  assign elements_color = section.settings.elements_color
  assign elements_custom = section.settings.elements_custom
  assign elements_font = section.settings.elements_font
  assign elements_height = section.settings.elements_height
  assign elements_mt = section.settings.elements_mt
  assign elements_gap = section.settings.elements_gap
  assign elements_align = section.settings.elements_align
  assign elements_icon = section.settings.elements_icon

  assign button_size = section.settings.button_size
  assign button_size_mobile = section.settings.button_size_mobile
  assign button_color = section.settings.button_color
  assign button_hover_color = section.settings.button_hover_color
  assign button_custom = section.settings.button_custom
  assign button_font = section.settings.button_font
  assign button_height = section.settings.button_height
  assign button_mt = section.settings.button_mt
  assign button_padding_vertical = section.settings.button_padding_vertical
  assign button_padding_horizontal = section.settings.button_padding_horizontal
  assign button_radius = section.settings.button_radius
  assign button_border_thickness = section.settings.button_border_thickness
  assign button_border_color = section.settings.button_border_color
  assign button_border_hover_color = section.settings.button_border_hover_color
  assign button_bg_color = section.settings.button_bg_color
  assign button_bg_hover_color = section.settings.button_bg_hover_color
  assign button_align = section.settings.button_align
-%}

{%- style -%}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  sub_heading_font | font_face: font_display: 'swap' }}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  price_font | font_face: font_display: 'swap' }}
  {{  elements_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .pricing-table-heading-{{ section.id }} > * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
    text-align: {{ heading_align }};
  }

  .pricing-table-heading-{{ section.id }} strong {
     color: {{ heading_other_color }};
  }

  .pricing-table-subheading-{{ section.id }} {
    margin-top: {{ sub_heading_mt | times: 0.75 | round: 0 }}px;
    max-width: fit-content;
    width: 100%;
  }

  .pricing-table-subheading-{{ section.id }} > * {
    margin: 0;
    font-size: {{ sub_heading_size_mobile }}px;
    color: {{ sub_heading_color }};
    line-height: {{ sub_heading_height }}%;
    text-transform: unset;
    text-align: {{ sub_heading_align }};
  }

  .pricing-table-items-{{ section.id }} {
    margin-top: {{ table_mt | times: 0.75 | round: 0 }}px;
    width: 100%;
    max-width: {{ table_width_mobile }}%;
    margin-left: auto;
    margin-right: auto;
  }

  .pricing-table-item-{{ section.id }} {
    border: {{ card_border_thickness }}px solid {{ card_border_color }};
    background-color: {{ card_bg_color }};
    cursor: pointer;
    overflow: hidden;
    height: 100%;
    transition: all 0.25s ease 0s;
    border-radius: {{ card_radius }}px; 
  }

  .pricing-table-item-{{ section.id }}.active-item-{{ section.id }} {
    height: 105%;
    margin-top: -10px;
    border-radius: {{ card_radius }}px;
  }

  .pricing-table-item-{{ section.id }}:not(:first-child) {
    margin-top: {{ table_gap_mobile  }}px;
  }

  .pricing-table-bottom-{{ section.id }} {
    padding-bottom: {{ card_padding_vertical | times: 0.75 | round: 0 }}px;
  }

  .pricing-table-title-{{ section.id }} {
    padding: {{ card_padding_vertical | times: 0.75 | round: 0 }}px {{ card_padding_horizontal | times: 0.75 | round: 0 }}px 0px;
    background-color: rgba(0, 0, 0, 0.5);
    margin: 0px;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: uppercase;
    text-align: {{ title_align }};
    letter-spacing: 4px;
    font-weight: 700;
    position: relative;
  }

  .pricing-table-title-{{ section.id }}:after {
    position: absolute;
    content: '';
    top: 100%;
    left: 0;
    width: 0;
    height: 0;
    border-style: solid;
    border-width: 25px 400px 0 0;
    border-color: rgba(0, 0, 0, 0.5) transparent transparent;
  }

  .pricing-table-price-{{ section.id }} {
    margin: 0px;
    margin-top: {{ price_mt | times: 0.75 | round: 0 }}px;
    padding: 0px {{ card_padding_horizontal | times: 0.75 | round: 0 }}px;
    font-size: {{ price_size_mobile }}px;
    color: {{ price_color }};
    line-height: {{ price_height }}%;
    text-transform: uppercase;
    text-align: {{ price_align }};
    font-weight: 700;
  }

  .pricing-table-price-{{ section.id }} span {
    font-size: {{ price_size_mobile | times: 0.4 | round: 0 }}px;
    font-weight: 400;
  }

  .pricing-table-list-{{ section.id }} {
    padding: 0px {{ card_padding_horizontal | times: 0.75 | round: 0 }}px {{ card_padding_vertical | times: 0.75 | round: 0 }}px;
    margin-top: {{ elements_mt | times: 0.75 | round: 0 }}px;
    border-bottom: 1px solid rgba(0, 0, 0, 0.2);
  }

  .pricing-table-list-{{ section.id }} ul {
    margin: 0px;
    padding: 0px;
    list-style: none;
  }

  .pricing-table-list-{{ section.id }} p,
  .pricing-table-list-{{ section.id }} li {
    display: block;
    width: 100%;
    max-width: fit-content;
    margin: 0px;
    font-size: {{ elements_size_mobile }}px;
    color: {{ elements_color }};
    line-height: {{ elements_height }}%;
    text-transform: unset;
    text-align: {{ elements_align }};
    list-style: none;
  }

  .pricing-table-list-{{ section.id }} p:not(:first-child),
  .pricing-table-list-{{ section.id }} li:not(:first-child) {
    margin-top: {{ elements_gap | times: 0.75 | round: 0 }}px;
  }

  .pricing-table-btn-{{ section.id }} {
    margin: 0px;
    margin-top: {{ button_mt | times: 0.75 | round: 0 }}px;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 100%;
    max-width: fit-content;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-decoration: none;
    padding: {{ button_padding_vertical | times: 0.75 | round: 0 }}px {{ button_padding_horizontal | times: 0.75 | round: 0 }}px;
    border-radius: {{ button_radius }}px;
    border: {{ button_border_thickness }}px solid {{ button_border_color }};
    background-color: {{ button_bg_color }};
    transition: all 0.25s ease 0s;
    text-transform: uppercase;
  }

  .pricing-table-btn-{{ section.id }}:hover {
    transition: all 0.25s ease 0s;
    color: {{ button_hover_color }};
    border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    background-color: {{ button_bg_hover_color }};
  }

  @media(min-width: 992px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .pricing-table-heading-{{ section.id }} > * {
      font-size: {{ heading_size }}px; 
    }

    .pricing-table-subheading-{{ section.id }} {
      margin-top: {{ sub_heading_mt }}px;
    }

    .pricing-table-subheading-{{ section.id }} > * {
      font-size: {{ sub_heading_size }}px; 
    }

    .pricing-table-items-{{ section.id }} {
      margin-top: {{ table_mt  }}px;
      max-width: {{ table_width }}%;
      display: grid;
      grid-template-columns: repeat({{ section.blocks.size }}, 1fr);
      gap: {{ table_gap }}px;
    }

    .pricing-table-item-{{ section.id }} {
      border-radius: 0px;
    }

    .pricing-table-item-{{ section.id }}:first-of-type {
      border-radius: {{ card_radius }}px 0 0 {{ card_radius }}px;
    }

    .pricing-table-item-{{ section.id }}:last-of-type {
      border-radius: 0 {{ card_radius }}px {{ card_radius }}px 0;
    }

    .pricing-table-item-{{ section.id }}:not(:first-child) {
      margin-top: 0px;
    }

    .pricing-table-item-{{ section.id }}:not(:first-child).active-item-{{ section.id }} {
      margin-top: -10px;
    }

    {% if use_hover %}

      .pricing-table-item-{{ section.id }}:not(:first-child):hover {
        margin-top: -10px;
      }

      .pricing-table-item-{{ section.id }}:hover {
        transition: all 0.25s ease 0s;
        margin-top: -10px;
        border-radius: {{ card_radius }}px; 
      }

    {% endif %}

    .pricing-table-bottom-{{ section.id }} {
      padding-bottom: {{ card_padding_vertical }}px;
    }

    .pricing-table-title-{{ section.id }} {
      padding: {{ card_padding_vertical }}px {{ card_padding_horizontal }}px 0px;
      font-size: {{ title_size }}px;
    }

    .pricing-table-price-{{ section.id }} {
      padding: 0px {{ card_padding_horizontal }}px;
      margin-top: {{ price_mt }}px;
      font-size: {{ price_size }}px;
    }

    .pricing-table-price-{{ section.id }} span {
      font-size: {{ price_size | times: 0.4 | round: 0 }}px;
    }

    .pricing-table-list-{{ section.id }} {
      padding: 0px {{ card_padding_horizontal }}px {{ card_padding_vertical }}px;
      margin-top: {{ elements_mt }}px;
    }

    .pricing-table-list-{{ section.id }} p,
    .pricing-table-list-{{ section.id }} li {
      font-size: {{ elements_size }}px;
    }

    .pricing-table-list-{{ section.id }} p:not(:first-child),
    .pricing-table-list-{{ section.id }} li:not(:first-child) {
      margin-top: {{ elements_gap }}px;
    }

    .pricing-table-btn-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      font-size: {{ button_size }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .pricing-table-heading-{{ section.id }} > * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if sub_heading_custom %}
  <style>
    .pricing-table-subheading-{{ section.id }} > * {
      font-family: {{ sub_heading_font.family }}, {{ sub_heading_font.fallback_families }};
      font-weight: {{ sub_heading_font.weight }};
      font-style: {{ sub_heading_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .pricing-table-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if elements_custom %}
  <style>
    .pricing-table-list-{{ section.id }} p,
    .pricing-table-list-{{ section.id }} li {
      font-family: {{ elements_font.family }}, {{ elements_font.fallback_families }};
      font-weight: {{ elements_font.weight }};
      font-style: {{ elements_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .pricing-table-btn-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if sub_heading_align == "center" %}
  <style>
    .pricing-table-subheading-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    }
  </style>
{% elsif sub_heading_align == "right" %}
  <style>
    .pricing-table-subheading-{{ section.id }} {
      margin-left: auto;
    }
  </style>
{% endif %}

{% if sub_heading_border %}
  <style>
    .pricing-table-subheading-{{ section.id }} {
      position: relative;
    }

    .pricing-table-subheading-{{ section.id }}::after {
      content: '';
      position: absolute;
      left: 50%;
      transform: translateX(-50%);
      bottom: -15px;
      width: 50px;
      height: 2px;
      background-color: {{ sub_heading_border_color }};
    }
  </style>
{% endif %}

{% if card_shadow %}
  <style>
    .pricing-table-item-{{ section.id }} {
      box-shadow: 0 0 5px rgba(0, 0, 0, 0.3);
    }

    {% if use_hover %}

      .pricing-table-item-{{ section.id }}:hover {
        box-shadow: 0 0 25px rgba(0, 0, 0, 0.4);
      }

      .pricing-table-item-{{ section.id }}:first-of-type:hover {
        border-radius: {{ card_radius }}px;
      }

      .pricing-table-item-{{ section.id }}:last-of-type:hover {
        border-radius: {{ card_radius }}px;
      }

    {% endif %}
  </style>
{% endif %}

{% if button_align == "center" %}
  <style>
    .pricing-table-btn-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    }
  </style>
{% elsif button_align == "right" %}
  <style>
    .pricing-table-btn-{{ section.id }} {
      margin-left: auto;
    }
  </style>
{% endif %}

{% if elements_icon != blank %}
  <style>
    .pricing-table-list-{{ section.id }} li,
    .pricing-table-list-{{ section.id }} p {
      position: relative;
      padding-left: 20px;
    }
    .pricing-table-list-{{ section.id }} li:before,
    .pricing-table-list-{{ section.id }} p:before {
      content: '';
      position: absolute;
      left: 0;
      top: 50%;
      transform: translateY(-50%);
      width: 12px;
      height: 12px;
      background-image: url('{{ elements_icon | image_url }}');
      background-size: cover;
      background-position: center;
      background-repeat: no-repeat;
    }
  </style>
{% endif %}

{% if elements_align == "center" %}
  <style>
    .pricing-table-list-{{ section.id }} li {
      margin-left: auto;
      margin-right: auto;
    }
  </style>
{% elsif elements_align == "right" %}
  <style>
    .pricing-table-list-{{ section.id }} li {
      margin-left: auto;
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} pricing-table-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings ">
      {% if heading != blank %}
        <div class="pricing-table-heading-{{ section.id }}">{{ heading }}</div>
      {% endif %}

      {% if sub_heading != blank %}
        <div class="pricing-table-subheading-{{ section.id }}">{{ sub_heading }}</div>
      {% endif %}

      <div class="pricing-table-items-{{ section.id }}">
        {% for block in section.blocks %}
          <div class="pricing-table-item-{{ section.id }} {% if block.settings.active %}active-item-{{ section.id }}{% endif %}">
            <div class="pricing-table-top-{{ section.id }}">
              {% if block.settings.heading != blank %}
                <p class="pricing-table-title-{{ section.id }}">{{ block.settings.heading }}</p>
              {% endif %}
              <p class="pricing-table-price-{{ section.id }}">{% if block.settings.currency != blank %}{{ block.settings.currency }}{% endif %}{{ block.settings.price }}{% if block.settings.time != blank %}<span>/{{ block.settings.time }}</span>{% endif %}</p>
            </div>
            <div class="pricing-table-bottom-{{ section.id }}">
              <div class="pricing-table-list-{{ section.id }}">
                {{ block.settings.elements }}
              </div>
              {% if block.settings.button_text != blank %}
                <a href="{{ block.settings.button_url }}" class="pricing-table-btn-{{ section.id }}">
                  {{ block.settings.button_text }}
                </a>
              {% endif %}
            </div>
          </div>
        {% endfor %}
      </div>
    </div>
</div>
