  assign table_border_thickness = section.settings.table_border_thickness
  assign table_border_color = section.settings.table_border_color
  assign table_radius = section.settings.table_radius

  assign table_column_width_mobile = section.settings.table_column_width_mobile
  assign table_column_width = section.settings.table_column_width
  assign table_column_height_mobile = section.settings.table_column_height_mobile
  assign table_column_height = section.settings.table_column_height

  assign table_column_active = section.settings.table_column_active
  assign table_column_active_bg_color = section.settings.table_column_active_bg_color
  assign table_column_active_radius = section.settings.table_column_active_radius
  assign table_column_active_border_thickness = section.settings.table_column_active_border_thickness
  assign table_column_active_border_color = section.settings.table_column_active_border_color

  assign first_heading = section.settings.first_heading
  assign second_heading = section.settings.second_heading
