  assign table_heading_custom = section.settings.table_heading_custom
  assign table_heading_font = section.settings.table_heading_font
  assign table_heading_height = section.settings.table_heading_height
  assign table_heading_align = section.settings.table_heading_align
  assign table_heading_padding_horizontal = section.settings.table_heading_padding_horizontal
  assign table_heading_padding_horizontal_mobile = section.settings.table_heading_padding_horizontal_mobile
  assign table_heading_padding_vertical = section.settings.table_heading_padding_vertical
  assign table_heading_padding_vertical_mobile = section.settings.table_heading_padding_vertical_mobile

  assign table_row_padding_horizontal = section.settings.table_row_padding_horizontal
  assign table_row_padding_horizontal_mobile = section.settings.table_row_padding_horizontal_mobile
  assign table_row_padding_vertical = section.settings.table_row_padding_vertical
  assign table_row_padding_vertical_mobile = section.settings.table_row_padding_vertical_mobile

  assign table_row_heading_size = section.settings.table_row_heading_size
  assign table_row_heading_size_mobile = section.settings.table_row_heading_size_mobile
  assign table_row_heading_color = section.settings.table_row_heading_color
  assign table_row_heading_custom = section.settings.table_row_heading_custom
  assign table_row_heading_font = section.settings.table_row_heading_font
  assign table_row_heading_height = section.settings.table_row_heading_height
  assign table_row_heading_align = section.settings.table_row_heading_align

  assign table_row_text_size = section.settings.table_row_text_size
  assign table_row_text_size_mobile = section.settings.table_row_text_size_mobile
  assign table_row_text_color = section.settings.table_row_text_color
  assign table_row_active_text_color = section.settings.table_row_active_text_color
  assign table_row_text_custom = section.settings.table_row_text_custom
  assign table_row_text_font = section.settings.table_row_text_font
  assign table_row_text_height = section.settings.table_row_text_height
  assign table_row_text_align = section.settings.table_row_text_align

  assign stars_count = section.settings.stars_count
  assign stars_color = section.settings.stars_color

  assign review = section.settings.review
  assign review_size = section.settings.review_size
  assign review_size_mobile = section.settings.review_size_mobile
  assign review_color = section.settings.review_color
  assign review_custom = section.settings.review_custom
  assign review_font = section.settings.review_font
  assign review_height = section.settings.review_height
  assign review_align = section.settings.review_align

  assign badge = section.settings.badge
  assign badge_mobile = section.settings.badge_mobile
  assign badge_size = section.settings.badge_size
  assign badge_size_mobile = section.settings.badge_size_mobile
  assign badge_color = section.settings.badge_color
  assign badge_custom = section.settings.badge_custom
  assign badge_font = section.settings.badge_font
  assign badge_height = section.settings.badge_height
  assign badge_padding_vertical = section.settings.badge_padding_vertical
  assign badge_padding_horizontal = section.settings.badge_padding_horizontal
  assign badge_radius = section.settings.badge_radius
  assign badge_border_thickness = section.settings.badge_border_thickness
  assign badge_border_color = section.settings.badge_border_color
  assign badge_bg_color = section.settings.badge_bg_color

-%}

{%- style -%}

  {{ heading_font | font_face: font_display: 'swap' }}
  {{ table_heading_font | font_face: font_display: 'swap' }}
  {{ table_row_heading_font | font_face: font_display: 'swap' }}
  {{ table_row_text_font | font_face: font_display: 'swap' }}
  {{ review_font | font_face: font_display: 'swap' }}
  {{ badge_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .comparison-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .comparison-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
    display: block;
    width: 100%;
  }

  .comparison-table-{{ section.id }} {
    position: relative;
    margin-top: {{ table_mt | times: 0.75 | round: 0 }}px;
    padding-top: 16px;
    overflow: auto;
  }

  .comparison-grid-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr {% if first_heading != blank %}{% if table_column_active == "1" %}1.5fr {% else %}1fr{% endif %}{% endif %} {% if second_heading != blank %}{% if table_column_active == "2" %}1.5fr {% else %}1fr{% endif %}{% endif %} {% if third_heading != blank %}{% if table_column_active == "3" %}1.5fr {% else %}1fr{% endif %}{% endif %} {% if four_heading != blank %}{% if table_column_active == "4" %}1.5fr {% else %}1fr{% endif %}{% endif %};
  }

  .comparison-item-{{ section.id }} {
    min-width: {{ table_column_width_mobile }}px;
    min-height: {{ table_column_height_mobile }}px;
    display: flex !important;
    flex-direction: column;
    align-items: center;
    justify-content: center;
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }}:not(:first-child):not(.active):not(:last-child) {
    border-top: {{ table_border_thickness }}px solid {{ table_border_color }};
    border-bottom: {{ table_border_thickness }}px solid {{ table_border_color }};
    border-left: {{ table_border_thickness }}px solid {{ table_border_color }};
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }}:not(.active):last-child {
    border: {{ table_border_thickness }}px solid {{ table_border_color }};
    border-top-right-radius: {{ table_radius }}px;
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }}:not(.active):nth-child(2) {
    border-top-left-radius: {{ table_radius }}px;
  }

  .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }}:not(.active) {
    border-bottom: {{ table_border_thickness }}px solid {{ table_border_color }};
    border-left: {{ table_border_thickness }}px solid {{ table_border_color }};
  }

  .comparison-grid-rows-first-{{ section.id }} .comparison-item-{{ section.id }}:first-child {
    border-top: {{ table_border_thickness }}px solid {{ table_border_color }};
    border-top-left-radius: {{ table_radius }}px;
  }

  .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }}:last-child {
    border-right: {{ table_border_thickness }}px solid {{ table_border_color }};
  }

  .comparison-grid-rows-last-{{ section.id }} .comparison-item-{{ section.id }}:first-child {
    border-bottom-left-radius: {{ table_radius }}px;
  }

  .comparison-grid-rows-last-{{ section.id }} .comparison-item-{{ section.id }}:not(.active):last-child {
    border-bottom-right-radius: {{ table_radius }}px;
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }}.active,
  .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }}.active{
    border-right: {{ table_column_active_border_thickness }}px solid {{ table_column_active_border_color }};
    border-left: {{ table_column_active_border_thickness }}px solid {{ table_column_active_border_color }};
  }

  .comparison-grid-header-top-{{ section.id }} .comparison-item-{{ section.id }}.active {
    height: 30px;
    position: relative;
    border: {{ table_column_active_border_thickness }}px solid {{ table_column_active_border_color }};
    border-bottom: 0px;
    border-top-left-radius: {{ table_column_active_radius }}px;
    border-top-right-radius: {{ table_column_active_radius }}px;
    background-color: {{ table_column_active_bg_color }};
  }

  .comparison-grid-header-top-{{ section.id }} .comparison-item-{{ section.id }},
  .comparison-grid-rows-bottom-{{ section.id }} .comparison-item-{{ section.id }} {
    min-height: fit-content;
  }

  .comparison-badge-{{ section.id }} {
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    top: -55%;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 90%;
    margin: 0;
    font-size: {{ badge_size_mobile }}px;
    font-weight: 700;
    color: {{ badge_color }};
    line-height: {{ badge_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ badge_padding_vertical | times: 0.75 | round: 0 }}px {{ badge_padding_horizontal | times: 0.75 | round: 0 }}px;
    border-radius: {{ badge_radius }}px;
    border: {{ badge_border_thickness }}px solid {{ badge_border_color }};
    background-color: {{ badge_bg_color }};
  }

  .comparison-badge-{{ section.id }} * {
    margin: 0px;
  }

  .comparison-badge-desktop-{{ section.id }} {
    display: none !important;
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }} {
    padding: {{ table_heading_padding_vertical_mobile }}px {{ table_heading_padding_horizontal_mobile }}px;
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }} h3 {
    margin: 0;
    font-size: {{ table_heading_size_mobile }}px;
    color: {{ table_heading_color }};
    line-height: {{ table_heading_height }}%;
    text-transform: unset;
    text-align: {{ table_heading_align }};
    display: block;
    width: 100%;
    font-weight: 700;
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }}.active h3 {
    color: {{ table_heading_active_color }};
  }

  .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }} {
     padding: {{ table_row_padding_vertical_mobile }}px {{ table_row_padding_horizontal_mobile }}px;
  }

  .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }}:first-child h3 {
    margin: 0;
    font-size: {{ table_row_heading_size_mobile }}px;
    color: {{ table_row_heading_color }};
    line-height: {{ table_row_heading_height }}%;
    text-transform: unset;
    text-align: {{ table_row_heading_align }};
    display: block;
    width: 100%;
    font-weight: 700;
  }

  .comparison-item-text-{{ section.id }} {
    margin: 0;
    font-size: {{ table_row_text_size_mobile }}px;
    color: {{ table_row_text_color }};
    line-height: {{ table_row_text_height }}%;
    text-transform: unset;
    text-align: {{ table_row_text_align }};
    display: block;
    width: 100%;
    font-weight: 700;
  }

  .comparison-item-{{ section.id }}.active .comparison-item-text-{{ section.id }} {
    color: {{ table_row_active_text_color }};
  }

  .comparison-review-{{ section.id }} {
    margin: 0;
    margin-top: 5px;
    font-size: {{ review_size_mobile }}px;
    color: {{ review_color }};
    line-height: {{ review_height }}%;
    text-transform: unset;
    text-align: {{ review_align }};
    display: block;
    width: 100%;
  }

  .comparison-stars-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 3px;
  }

  .comparison-stars-{{ section.id }} svg {
    width: 12px;
    height: 12px;
    display: block;
    object-fit: contain;
  }

  .comparison-stars-{{ section.id }} svg path {
    fill: {{ stars_color }};
  }

  .comparison-grid-rows-bottom-{{ section.id }} .comparison-item-{{ section.id }}:not(.active) {
    border: 0px;
  }

  .comparison-grid-rows-bottom-{{ section.id }} .comparison-item-{{ section.id }}.active {
    border-bottom: {{ table_column_active_border_thickness }}px solid {{ table_column_active_border_color }};
    border-bottom-left-radius: {{ table_column_active_radius }}px;
    border-bottom-right-radius: {{ table_column_active_radius }}px;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .comparison-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .comparison-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .comparison-table-{{ section.id }} {
      margin-top: {{ table_mt }}px;
    }

    .comparison-item-{{ section.id }} {
      min-width: {{ table_column_width }}px;
      min-height: {{ table_column_height }}px;
    }

    .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }} {
      padding: {{ table_heading_padding_vertical }}px {{ table_heading_padding_horizontal }}px;
    }

    .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }} h3 {
      font-size: {{ table_heading_size }}px;
    }

    .comparison-badge-{{ section.id }} {
      font-size: {{ badge_size }}px;
      padding: {{ badge_padding_vertical }}px {{ badge_padding_horizontal }}px;
    }

    .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }} {
      padding: {{ table_row_padding_vertical }}px {{ table_row_padding_horizontal }}px;
    }

    .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }}:first-child h3 {
      font-size: {{ table_row_heading_size }}px;
    }

    .comparison-item-text-{{ section.id }} {
      font-size: {{ table_row_text_size }}px;
    }

    .comparison-review-{{ section.id }} {
      font-size: {{ review_size }}px;
    }

    .comparison-badge-desktop-{{ section.id }} {
      display: inline !important;
    }

    .comparison-badge-mobile-{{ section.id }} {
      display: none !important;
    }

  }

{%- endstyle -%}

{% if heading_custom %}
  <style>
    .comparison-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if table_heading_custom %}
  <style>
    .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }} h3 {
      font-family: {{ table_heading_font.family }}, {{ table_heading_font.fallback_families }};
      font-weight: {{ table_heading_font.weight }};
      font-style: {{ table_heading_font.style }};
    }
  </style>
{% endif %}

{% if table_row_heading_custom %}
  <style>
    .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }}:first-child h3 {
      font-family: {{ table_row_heading_font.family }}, {{ table_row_heading_font.fallback_families }};
      font-weight: {{ table_row_heading_font.weight }};
      font-style: {{ table_row_heading_font.style }};
    }
  </style>
{% endif %}

{% if table_row_text_custom %}
  <style>
    .comparison-item-text-{{ section.id }} {
      font-family: {{ table_row_text_font.family }}, {{ table_row_text_font.fallback_families }};
      font-weight: {{ table_row_text_font.weight }};
      font-style: {{ table_row_text_font.style }};
    }
  </style>
{% endif %}

{% if review_custom %}
  <style>
    .comparison-review-{{ section.id }} {
      font-family: {{ review_font.family }}, {{ review_font.fallback_families }};
      font-weight: {{ review_font.weight }};
      font-style: {{ review_font.style }};
    }
  </style>
{% endif %}

{% if badge_custom %}
  <style>
    .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }}.active:after {
      font-family: {{ badge_font.family }}, {{ badge_font.fallback_families }};
      font-weight: {{ badge_font.weight }};
      font-style: {{ badge_font.style }};
    }
  </style>
{% endif %}

<script>
  (function () {
    function moreLoadProductForm() {
      var productForm = document.querySelector('#custom-comparison-table');
      if (!productForm){
        var productForm = document.querySelector('{% if table_position == "below_title" or table_position == "above_title" %}.product__title{% elsif table_position == "below_description" or table_position == "above_description" %}.product__description{% else %}.button--add-to-cart, .product .product-form, .product-single__add-to-cart, .shopify-payment-button, .shopify-payment-button__button, .ProductForm__BuyButtons, .add-to-cart{% endif %}');
        if (!productForm) return;
        }
        const templateContent = document.querySelector(
          '#custom-comparison-table-{{ section.id }}'
        ).content;

        productForm.insertAdjacentElement(
          '{% if table_position == "below_buy" or table_position == "below_title" or table_position == "below_description" %}afterend{% else %}beforebegin{% endif %}',
          templateContent.firstElementChild
        );
    }
    window.addEventListener('DOMContentLoaded', moreLoadProductForm);
  })();
</script>

<template id="custom-comparison-table-{{ section.id }}">
    <div class="section-{{ section.id }} comparison-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
      <div class="section-{{ section.id }}-settings">

        {% if heading != blank %}
          <div class="comparison-heading-{{ section.id }}">{{ heading }}</div>
        {% endif %}

        <div class="comparison-table-{{ section.id }}">
          <div class="comparison-grid-{{ section.id }} comparison-grid-header-top-{{ section.id }}">
            <div class="comparison-item-{{ section.id }}"></div>
            {% if first_heading != blank %}
              <div class="comparison-item-{{ section.id }} {% if table_column_active == "1" %}active{% endif %}">
                {% if first_heading != blank %}
                  <div class="comparison-badge-{{ section.id }}">
                    <p class="comparison-badge-desktop-{{ section.id }}">{{ badge }}</p>
                    <p class="comparison-badge-mobile-{{ section.id }}">{{ badge_mobile }}</p>
                  </div>
                {% endif %}
              </div>
            {% endif %}
            {% if second_heading != blank %}
              <div class="comparison-item-{{ section.id }} {% if table_column_active == "2" %}active{% endif %}">
                {% if second_heading != blank %}
                  <div class="comparison-badge-{{ section.id }}">
                    <p class="comparison-badge-desktop-{{ section.id }}">{{ badge }}</p>
                    <p class="comparison-badge-mobile-{{ section.id }}">{{ badge_mobile }}</p>
                  </div>
                {% endif %}
              </div>
            {% endif %}
            {% if third_heading != blank %}
              <div class="comparison-item-{{ section.id }} {% if table_column_active == "3" %}active{% endif %}">
                {% if third_heading != blank %}
                  <div class="comparison-badge-{{ section.id }}">
                    <p class="comparison-badge-desktop-{{ section.id }}">{{ badge }}</p>
                    <p class="comparison-badge-mobile-{{ section.id }}">{{ badge_mobile }}</p>
                  </div>
                {% endif %}
              </div>
            {% endif %}
            {% if four_heading != blank %}
              <div class="comparison-item-{{ section.id }} {% if table_column_active == "4" %}active{% endif %}">
                {% if four_heading != blank %}
                  <div class="comparison-badge-{{ section.id }}">
                    <p class="comparison-badge-desktop-{{ section.id }}">{{ badge }}</p>
                    <p class="comparison-badge-mobile-{{ section.id }}">{{ badge_mobile }}</p>
                  </div>
                {% endif %}
              </div>
            {% endif %}
          </div>

          <div class="comparison-grid-{{ section.id }} comparison-grid-header-{{ section.id }}">
            <div class="comparison-item-{{ section.id }}"></div>
            {% if first_heading != blank %}
              <div class="comparison-item-{{ section.id }} {% if table_column_active == "1" %}active{% endif %}">
                <h3>{{ first_heading }}</h3>
              </div>
            {% endif %}
            {% if second_heading != blank %}
              <div class="comparison-item-{{ section.id }} {% if table_column_active == "2" %}active{% endif %}">
                <h3>{{ second_heading }}</h3>
              </div>
            {% endif %}
            {% if third_heading != blank %}
              <div class="comparison-item-{{ section.id }} {% if table_column_active == "3" %}active{% endif %}">
                <h3>{{ third_heading }}</h3>
              </div>
            {% endif %}
            {% if four_heading != blank %}
              <div class="comparison-item-{{ section.id }} {% if table_column_active == "4" %}active{% endif %}">
                <h3>{{ four_heading }}</h3>
              </div>
            {% endif %}
          </div>
          {% for block in section.blocks %}
            <div class="comparison-grid-rows-{{ section.id }} comparison-grid-{{ section.id }} {% if forloop.first %} comparison-grid-rows-first-{{ section.id }}{% endif %} {% if forloop.last %} comparison-grid-rows-last-{{ section.id }}{% endif %}">
              <div class="comparison-item-{{ section.id }}">
                <h3>{{ block.settings.row_heading }}</h3>
              </div>
              {% if first_heading != blank %}
                <div class="comparison-item-{{ section.id }} {% if table_column_active == "1" %}active{% endif %}">
                  {% if block.settings.first_column != blank %}
                    <p class="comparison-item-text-{{ section.id }}">{{ block.settings.first_column }}</p>
                  {% endif %}
                </div>
              {% endif %}
              {% if second_heading != blank %}
                <div class="comparison-item-{{ section.id }} {% if table_column_active == "2" %}active{% endif %}">
                  {% if block.settings.second_column != blank %}
                    <p class="comparison-item-text-{{ section.id }}">{{ block.settings.second_column }}</p>
                  {% endif %}
                </div>
              {% endif %}
              {% if third_heading != blank %}
                <div class="comparison-item-{{ section.id }} {% if table_column_active == "3" %}active{% endif %}">
                  {% if block.settings.third_column != blank %}
                    <p class="comparison-item-text-{{ section.id }}">{{ block.settings.third_column }}</p>
                  {% endif %}
