{%- style -%}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  tag_font | font_face: font_display: 'swap' }}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}
  {{  card_tag_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .product-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-align: {{ heading_align_mobile }};
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
  }

  .product-tags-{{ section.id }} {
    display: flex;
    align-items: center;
    flex-wrap: wrap;
    margin-top: {{ tags_mt_mobile }}px;
    justify-content: {{ tags_horizontal_align_mobile }};
    gap: {{ tags_gap_mobile }}px;
    width: 100%;    
  }

  .product-tag-{{ section.id }} {
    margin: 0;
    font-size: {{ tag_size_mobile }}px;
    color: {{ tag_color }};
    line-height: {{ tag_height }}%;
    padding: {{ tag_padding_vertical_mobile }}px {{ tag_padding_horizontal_mobile }}px;
    border: {{ tag_border_thickness }}px solid {{ tag_border_color }};
    border-radius: {{ tag_radius }}px;
    background-color: {{ tag_bg_color }};
    text-transform: unset;
    text-decoration: none;
    cursor: pointer;
    transition: all 0.25s ease 0s;
  }

  .product-tag-{{ section.id }}.active {
    color: {{ tag_active_color }};
    border: {{ tag_border_thickness }}px solid {{ tag_border_active_color }};
    background-color: {{ tag_bg_active_color }};
    transition: all 0.25s ease 0s;
  }

  .product-cards-{{ section.id }} {
    display: grid;
    grid-template-columns: repeat({{ cards_per_view_mobile }}, 1fr);
    gap: {{ cards_gap_mobile }}px;
    margin-top: {{ cards_mt_mobile }}px;
  }

  .product-card-{{ section.id }} {
    display: none;
    flex-direction: row;
    align-items: {{ card_horizontal_align_mobile }};
    gap: 5px;
    padding: {{ card_padding_vertical_mobile }}px {{ card_padding_horizontal_mobile }}px;
    border-radius: {{ card_radius }}px;
    border: {{ card_border_thickness }}px solid {{ card_border_color }};
    background-color: {{ card_bg_color }};    
  }

  .product-card-{{ section.id }}.active {
    display: flex;
  }

  .product-title-{{ section.id }} {
    margin: 0;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-align: {{ card_align_mobile }};
    word-break: break-word;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
  }

  .product-text-{{ section.id }} {
    margin-top: {{ text_mt_mobile }}px;
    text-align: {{ card_align_mobile }};
  }

  .product-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;  
    word-break: break-word;
    text-transform: unset;
    text-decoration: none;
  }

  .product-card-tags-{{ section.id }} {
    display: grid;
    grid-template-columns: repeat({{ card_tags_per_view_mobile }}, 1fr);
    margin-top: {{ card_tags_mt_mobile }}px;
    justify-content: {{ card_horizontal_align_mobile }};
    gap: {{ card_tags_gap_mobile }}px;    
  }

  .product-card-tag-{{ section.id }} {
    margin: 0;
    width: 100%;
    font-size: {{ card_tag_size_mobile }}px;
    color: {{ card_tag_color }};
    line-height: {{ card_tag_height }}%;
    padding: {{ card_tag_padding_vertical_mobile }}px {{ card_tag_padding_horizontal_mobile }}px;
    border: {{ card_tag_border_thickness }}px solid {{ card_tag_border_color }};
    border-radius: {{ card_tag_radius }}px;
    background-color: {{ card_tag_bg_color }};
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .product-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
      text-align: {{ heading_align }};
    }

    .product-tags-{{ section.id }} {
      margin-top: {{ tags_mt }}px;
      justify-content: {{ tags_horizontal_align }};
      gap: {{ tags_gap }}px;
      width: {{ tags_max_width }}%;
    }

    .product-tag-{{ section.id }} {
      font-size: {{ tag_size }}px;
      padding: {{ tag_padding_vertical }}px {{ tag_padding_horizontal }}px;
    }

    .product-tag-{{ section.id }}:hover {
      color: {{ tag_hover_color }};
      border: {{ tag_border_thickness }}px solid {{ tag_border_hover_color }};
      background-color: {{ tag_bg_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .product-cards-{{ section.id }} {
      grid-template-columns: repeat({{ cards_per_view }}, 1fr);
      gap: {{ cards_gap }}px;
      margin-top: {{ cards_mt }}px;
    }

    .product-card-{{ section.id }} {
      align-items: {{ card_horizontal_align }};
      padding: {{ card_padding_vertical }}px {{ card_padding_horizontal }}px;
    }

    .product-card-content-{{ section.id }} {
      width: 100%;
    }

    .product-title-{{ section.id }} {   
      font-size: {{ title_size }}px;
      text-align: {{ card_align }};
    }

    .product-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ card_align }};
    }

    .product-text-{{ section.id }} * {
      font-size: {{ text_size }}px; 
    }

    .product-card-tags-{{ section.id }} {
      display: flex;   
      flex-wrap: wrap;
      margin-top: {{ card_tags_mt }}px;
      justify-content: {{ card_horizontal_align }};
      align-items: start;
      gap: {{ card_tags_gap }}px;
    }

    .product-card-tag-{{ section.id }} {     
      width: auto;
      font-size: {{ card_tag_size }}px;   
      padding: {{ card_tag_padding_vertical }}px {{ card_tag_padding_horizontal }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .product-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

