{% endif %}

{% if card_tag_custom %}
  <style>
    .product-card-tag-{{ section.id }} {
      font-family: {{ card_tag_font.family }}, {{ card_tag_font.fallback_families }};
      font-weight: {{ card_tag_font.weight }};
      font-style: {{ card_tag_font.style }};
    }
  </style>
{% endif %}


{% if tags_align_mobile == 'center' %}
  <style>
    .product-tags-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    }
  </style>
{% elsif tags_align_mobile == 'right' %}
  <style> 
    .product-tags-{{ section.id }} {
      margin-left: auto;    
    }   
  </style>
{% endif %}

{% if tags_align == 'left' %}
  <style>
    @media(min-width: 1024px) {
      .product-tags-{{ section.id }} {
        margin-right: 0;    
        margin-left: 0;
      }
    }
  </style>
{% elsif tags_align == 'center' %}
  <style>
    @media(min-width: 1024px) {
      .product-tags-{{ section.id }} {
        margin-left: auto;
        margin-right: auto;
      }
    }   
  </style>
{% elsif tags_align == 'right' %}
  <style>
    @media(min-width: 1024px) {
      .product-tags-{{ section.id }} {
        margin-left: auto;    
        margin-right: 0;
      }
    }   
  </style>
{% endif %}

{% if card_tag_placement_mobile == 'bottom' %}
  <style>
    .product-card-{{ section.id }} {
      flex-direction: column !important;
    }
  </style>
{% else %}  
  <style>
    .product-card-{{ section.id }} {
      flex-direction: row !important;
    }

    .product-card-tags-{{ section.id }} { 
      margin-top: 0;
    }

    @media(min-width: 1024px) {
      .product-card-tags-{{ section.id }} {
        margin-top: {{ card_tags_mt }}px;
      }
    }
  </style>
{% endif %}


{% if card_tag_placement == 'bottom' %}
  <style>
    @media(min-width: 1024px) {
      .product-card-{{ section.id }} {
        flex-direction: column !important;
      }
    }

  </style>
{% else %}  
  <style>
    @media(min-width: 1025px) {
      .product-card-{{ section.id }} {
        flex-direction: row !important;
      }
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} product-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      {% if heading != blank %}
        <div class="product-heading-{{ section.id }}">{{ heading }}</div>
      {% endif %}
      <div class="product-body-{{ section.id }}">
        <div class="product-tags-{{ section.id }}">
          {% assign unique_tags = '' %}

          {% for block in section.blocks %}
            {% assign tags = block.settings.tags | strip | split: ',' %}
            {% for tag in tags %}
              {% assign tag = tag | strip %}
              {% unless unique_tags contains tag %}
                <p class="product-tag-{{ section.id }}" data-option="{{- tag | escape | replace: ' ', '' -}}">{{ tag }}</p>
                {% assign unique_tags = unique_tags | append: tag | append: ',' %}
              {% endunless %}
            {% endfor %}
          {% endfor %}
        </div>
        <div class="product-cards-{{ section.id }}">
          {% for block in section.blocks %}
            <div class="product-card-{{ section.id }} active">
              <div class="product-card-content-{{ section.id }}">
                {% if block.settings.title != blank %}
                  <p class="product-title-{{ section.id }}">{{ block.settings.title }}</p>
                {% endif %}
                {% if block.settings.text != blank %}
                  <div class="product-text-{{ section.id }}">{{ block.settings.text }}</div>
                {% endif %}
              </div>              
              {% if block.settings.tags != blank %}
                <div class="product-card-tags-{{ section.id }}">
                  {% assign tags = block.settings.tags | strip | split: ',' %}              
                  {% for tag in tags %}
                    <p class="product-card-tag-{{ section.id }}" data-option="{{- tag | escape | replace: ' ', '' -}}">{{ tag }}</p>
                  {% endfor %}
                </div>                
              {% endif %}              
            </div>
          {% endfor %}
        </div>
      </div>
    </div>
</div>

<script>
  function initProductIngredients3() {
    const ingredients = document.querySelectorAll('.product-tag-{{ section.id }}');
    const cards = document.querySelectorAll('.product-card-{{ section.id }}');

    ingredients.forEach(ingredient => {
      ingredient.addEventListener('click', () => {
        const selectedOption = ingredient.dataset.option;

        ingredient.classList.toggle('active');
        const hasActiveIngredients = Array.from(ingredients).some(ing => ing.classList.contains('active'));

        if (!hasActiveIngredients) {
          cards.forEach(card => card.classList.add('active'));
          return;
        }

        cards.forEach(card => {
          const cardIngredients = card.querySelectorAll('.product-card-tag-{{ section.id }}');
          const hasActiveIngredient = Array.from(cardIngredients).some(cardIngredient => {
            const option = cardIngredient.dataset.option;
            return Array.from(ingredients).some(ing => ing.dataset.option === option && ing.classList.contains('active'));
          });

          if (hasActiveIngredient) {
            card.classList.add('active');
          } else {
            card.classList.remove('active');
          }
        });
      });
    });
  }

  document.addEventListener('DOMContentLoaded', initProductIngredients3);

  if (Shopify.designMode) {
    document.addEventListener('shopify:section:unload', initProductIngredients3);
    document.addEventListener('shopify:section:load', initProductIngredients3);
  }
</script>
