{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign content_horizontal_align_mobile = section.settings.content_horizontal_align_mobile
  assign content_horizontal_align = section.settings.content_horizontal_align

  assign text_align = ""
  if content_horizontal_align == "start"
    assign text_align = "left"
  elsif content_horizontal_align == "center"
    assign text_align = "center"
  else
    assign text_align = "right"
  endif

  assign text_align_mobile = ""
  if content_horizontal_align_mobile == "start"
    assign text_align_mobile = "left"
  elsif content_horizontal_align_mobile == "center"
    assign text_align_mobile = "center"
  else
    assign text_align_mobile = "right"
  endif

  assign heading = section.settings.heading
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_height = section.settings.heading_height
  assign heading_color = section.settings.heading_color
  assign heading_width = section.settings.heading_width

  assign text = section.settings.text
  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_height = section.settings.text_height
  assign text_color = section.settings.text_color
  assign text_width = section.settings.text_width
  assign text_mt = section.settings.text_mt
  assign text_mt_mobile = section.settings.text_mt_mobile

  assign layout_width = section.settings.layout_width
  assign layout_radius = section.settings.layout_radius
  assign layout_border_thickness = section.settings.layout_border_thickness
  assign layout_border_color = section.settings.layout_border_color
  assign layout_gap = section.settings.layout_gap
  assign layout_gap_mobile = section.settings.layout_gap_mobile
  assign layout_mt = section.settings.layout_mt
  assign layout_mt_mobile = section.settings.layout_mt_mobile

  assign image = section.settings.image
  assign hide_image = section.settings.hide_image
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color
  assign image_radius = section.settings.image_radius

  assign item_content_align = section.settings.item_content_align
  assign item_content_align_mobile = section.settings.item_content_align_mobile

  assign item_content_horizontal_align = ""
  if item_content_align == "center"
    assign item_content_horizontal_align = "center"
  elsif item_content_align == "right"
    assign item_content_horizontal_align = "end"
  else
    assign item_content_horizontal_align = "start"
  endif

  assign item_content_horizontal_align_mobile = ""
  if item_content_align_mobile == "center"
    assign item_content_horizontal_align_mobile = "center"
  elsif item_content_align_mobile == "right"
    assign item_content_horizontal_align_mobile = "end"
  else
    assign item_content_horizontal_align_mobile = "start"
  endif

  assign item_image_size = section.settings.item_image_size
  assign item_image_size_mobile = section.settings.item_image_size_mobile
  assign item_image_border_thickness = section.settings.item_image_border_thickness
  assign item_image_border_color = section.settings.item_image_border_color
  assign item_image_radius = section.settings.item_image_radius

  assign item_title_custom = section.settings.item_title_custom
  assign item_title_font = section.settings.item_title_font
  assign item_title_size = section.settings.item_title_size
  assign item_title_size_mobile = section.settings.item_title_size_mobile
  assign item_title_height = section.settings.item_title_height
  assign item_title_color = section.settings.item_title_color
  assign item_title_mt = section.settings.item_title_mt
  assign item_title_mt_mobile = section.settings.item_title_mt_mobile

  assign item_text_custom = section.settings.item_text_custom
  assign item_text_font = section.settings.item_text_font
  assign item_text_size = section.settings.item_text_size
  assign item_text_size_mobile = section.settings.item_text_size_mobile
  assign item_text_height = section.settings.item_text_height
  assign item_text_color = section.settings.item_text_color
  assign item_text_mt = section.settings.item_text_mt
  assign item_text_mt_mobile = section.settings.item_text_mt_mobile

-%}

{%- style -%}

  {{ heading_font | font_face: font_display: 'swap' }}
  {{ text_font | font_face: font_display: 'swap' }}
  {{ item_title_font | font_face: font_display: 'swap' }}
  {{ item_text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .ingredients-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: {{ content_horizontal_align_mobile }};
  }

  .ingredients-heading-{{ section.id }} {
    text-align: {{ text_align_mobile }};
    width: 100%;
  }

  .ingredients-heading-{{ section.id }} * {
    margin: 0;  
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
    font-weight: 700;
  }

  .ingredients-text-{{ section.id }} {
    margin-top: {{ text_mt_mobile }}px;
    text-align: {{ text_align_mobile }};
    width: 100%;
  }

  .ingredients-text-{{ section.id }} * {
    margin: 0;  
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
  }

  .ingredients-layout-{{ section.id }} {
    margin-top: {{ layout_mt_mobile }}px;
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-wrap: wrap;
    gap: {{ layout_gap_mobile }}px;
  }

  .ingredients-image-{{ section.id }} {
    display: none;
    border-radius: {{ image_radius }}%;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    overflow: hidden;
    aspect-ratio: 1/1;
  }

  .ingredients-image-{{ section.id }} img,
  .ingredients-image-{{ section.id }} svg {
    display:  block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .ingredients-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .ingredients-column-{{ section.id }} {
    display: flex;
    flex-direction: row;
    gap: {{ layout_gap_mobile }}px;
    flex-wrap: wrap;
    justify-content: center;
  }

  .ingredients-item-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: {{ item_content_horizontal_align_mobile }};
    width: 100%;
    max-width: 140px;
    flex-shrink: 0;
  }

  .ingredients-item-image-{{ section.id }} {
    width: {{ item_image_size_mobile }}px;
    height: {{ item_image_size_mobile }}px;
    border-radius: {{ item_image_radius }}px;
    border: {{ item_image_border_thickness }}px solid {{ item_image_border_color }};
    overflow: hidden;
  }

  .ingredients-item-image-{{ section.id }} img,
  .ingredients-item-image-{{ section.id }} svg {
    display:  block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .ingredients-item-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .ingredients-item-title-{{ section.id }} {
    margin: 0;  
    margin-top: {{ item_title_mt_mobile }}px;
    text-align: {{ item_content_align_mobile }};
    font-size: {{ item_title_size_mobile }}px;
    color: {{ item_title_color }};
    line-height: {{ item_title_height }}%;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
    font-weight: 700;
  }

  .ingredients-item-text-{{ section.id }} {
    margin: 0;  
    margin-top: {{ item_text_mt_mobile }}px;
    text-align: {{ item_content_align_mobile }};
    font-size: {{ item_text_size_mobile }}px;
    color: {{ item_text_color }};
    line-height: {{ item_text_height }}%;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .ingredients-content-{{ section.id }} {
      align-items: {{ content_horizontal_align }};
    }

    .ingredients-heading-{{ section.id }} {
      text-align: {{ text_align }};
      max-width: {{ heading_width }}%;
    }

    .ingredients-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .ingredients-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ text_align }};
      max-width: {{ text_width }}%;
    }

    .ingredients-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .ingredients-image-{{ section.id }} {
      display: block;
    }

    .ingredients-layout-{{ section.id }} {
      position: relative;
      margin-bottom: {{ item_image_size | times: 0.5 | round: 0 }}px;
      margin-top: calc({{ layout_mt }}px + {{ item_image_size | times: 0.5 | round: 0 }}px);
      margin-left: auto;
      margin-right: auto;
      max-width: {{ layout_width }}%;
      aspect-ratio: 1/1;
      flex-wrap: nowrap;
      justify-content: space-between;
      border-radius: {{ layout_radius }}%;
      border: {{ layout_border_thickness }}px solid {{ layout_border_color }};
      gap: {{ layout_gap }}px;
    }

    .ingredients-item-{{ section.id }} {
      max-width: 200px;
      align-items: {{ item_content_horizontal_align }};
    }

    .ingredients-item-image-{{ section.id }} {
      width: {{ item_image_size }}px;
      height: {{ item_image_size }}px;
    }

    .ingredients-column-{{ section.id }} {
      gap: {{ layout_gap }}px;
      flex-direction: column;
      max-width: 200px;
      flex-shrink: 0;
      flex-wrap: nowrap;
      width: 100%;
    }

    .ingredients-column-top-{{ section.id }} {
      flex-direction: row;
      position: absolute;
      top: -{{ item_image_size | times: 0.5 | round: 0 }}px;
      left: 50%;
      transform: translateX(-50%);
      z-index: 2;
      align-items: center;
      max-width: fit-content;
    }

    .ingredients-column-bottom-{{ section.id }} {
      flex-direction: row;
      position: absolute;
      bottom: -{{ item_image_size | times: 0.5 | round: 0 }}px;
      left: 50%;
      transform: translateX(-50%);
      z-index: 2;
      align-items: center;
      max-width: fit-content;
    }

    .ingredients-column-left-{{ section.id }} {
      position: relative;
      left: -{{ item_image_size | times: 0.5 | round: 0 }}px;
    }

    .ingredients-column-right-{{ section.id }} {
      position: relative;
      right: -{{ item_image_size | times: 0.5 | round: 0 }}px;
    }

    .ingredients-item-title-{{ section.id }} {
      margin-top: {{ item_title_mt }}px;
      text-align: {{ item_content_align }};
      font-size: {{ item_title_size }}px;
    }

    .ingredients-item-text-{{ section.id }} {
      margin-top: {{ item_text_mt }}px;
      text-align: {{ item_content_align }};
      font-size: {{ item_text_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .ingredients-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .ingredients-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if item_title_custom %}
  <style>
    .ingredients-item-title-{{ section.id }} {
      font-family: {{ item_title_font.family }}, {{ item_title_font.fallback_families }};
      font-weight: {{ item_title_font.weight }};
      font-style: {{ item_title_font.style }};
    }
  </style>
{% endif %}


{% if item_text_custom %}
  <style>
    .ingredients-item-text-{{ section.id }} {
      font-family: {{ item_text_font.family }}, {{ item_text_font.fallback_families }};
      font-weight: {{ item_text_font.weight }};
      font-style: {{ item_text_font.style }};
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} ingredients-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">

      {% if heading != blank or text != blank %}
        <div class="ingredients-content-{{ section.id }}">

          {% if heading != blank %}
            <div class="ingredients-heading-{{ section.id }}">
                {{ heading }}
            </div>
          {% endif %}

          {% if text != blank %}
              <div class="ingredients-text-{{ section.id }}">
                  {{ text }}
              </div>
          {% endif %}

        </div>
      {% endif %}

      <div class="ingredients-layout-{{ section.id }}">
        {% if section.blocks.size > 4 %}
          <div class="ingredients-column-{{ section.id }} ingredients-column-top-{{ section.id }}">
            {% for block in section.blocks %}
              {% if forloop.index == 5 or forloop.index == 7 %}
                <div class="ingredients-item-{{ section.id }}">

                  <div class="ingredients-item-image-{{ section.id }}">
                    {% if block.settings.image != blank %}
                      <img src="{{ block.settings.image | img_url: "700x700" }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                    {% else %}
                      {{ 'image' | placeholder_svg_tag }}
                    {% endif %}
                  </div>

                  {% if block.settings.title != blank %}
                    <p class="ingredients-item-title-{{ section.id }}">{{ block.settings.title }}</p>
                  {% endif %}

                  {% if block.settings.text != blank %}
                    <p class="ingredients-item-text-{{ section.id }}">{{ block.settings.text }}</p>
                  {% endif %}
                </div>
              {% endif %}
            {% endfor %}
          </div>
        {% endif %}
        {% if section.blocks.size > 5 %}
          <div class="ingredients-column-{{ section.id }} ingredients-column-bottom-{{ section.id }}">
            {% for block in section.blocks %}
              {% if forloop.index == 6 or forloop.index == 8 %}
                <div class="ingredients-item-{{ section.id }}">

                  <div class="ingredients-item-image-{{ section.id }}">
                    {% if block.settings.image != blank %}
                      <img src="{{ block.settings.image | img_url: "700x700" }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                    {% else %}
                      {{ 'image' | placeholder_svg_tag }}
                    {% endif %}
                  </div>

                  {% if block.settings.title != blank %}
                    <p class="ingredients-item-title-{{ section.id }}">{{ block.settings.title }}</p>
                  {% endif %}

                  {% if block.settings.text != blank %}
                    <p class="ingredients-item-text-{{ section.id }}">{{ block.settings.text }}</p>
                  {% endif %}
                </div>
              {% endif %}
            {% endfor %}
          </div>
        {% endif %}
        <div class="ingredients-column-{{ section.id }} ingredients-column-left-{{ section.id }}">
          {% for block in section.blocks %}
            {% if forloop.index < 3 %}
              <div class="ingredients-item-{{ section.id }}">

                <div class="ingredients-item-image-{{ section.id }}">
                  {% if block.settings.image != blank %}
                    <img src="{{ block.settings.image | img_url: "700x700" }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                  {% else %}
                    {{ 'image' | placeholder_svg_tag }}
                  {% endif %}
                </div>

                {% if block.settings.title != blank %}
                  <p class="ingredients-item-title-{{ section.id }}">{{ block.settings.title }}</p>
                {% endif %}

                {% if block.settings.text != blank %}
                  <p class="ingredients-item-text-{{ section.id }}">{{ block.settings.text }}</p>
                {% endif %}
              </div>
            {% endif %}
          {% endfor %}
        </div>

        <div class="ingredients-image-{{ section.id }}">
          {% unless hide_image %}
            {% if image != blank %}
              <img src="{{ image | image_url }}" alt="{{ image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
            {% else %}
              {{ 'image' | placeholder_svg_tag }}
            {% endif %}
          {% endunless %}
        </div>

        <div class="ingredients-column-{{ section.id }} ingredients-column-right-{{ section.id }}">
          {% for block in section.blocks %}
            {% if forloop.index > 2 and forloop.index < 5 %}
              <div class="ingredients-item-{{ section.id }}">

                <div class="ingredients-item-image-{{ section.id }}">
                  {% if block.settings.image != blank %}
                    <img src="{{ block.settings.image | img_url: "700x700" }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                  {% else %}
                    {{ 'image' | placeholder_svg_tag }}
                  {% endif %}
                </div>

                {% if block.settings.title != blank %}
                  <p class="ingredients-item-title-{{ section.id }}">{{ block.settings.title }}</p>
                {% endif %}

                {% if block.settings.text != blank %}
                  <p class="ingredients-item-text-{{ section.id }}">{{ block.settings.text }}</p>
                {% endif %}
              </div>
            {% endif %}
          {% endfor %}
        </div>
      </div>
    </div>
</div>
