{%- style -%}
  {{ heading_font | font_face: font_display: 'swap' }}
  {{ results_font | font_face: font_display: 'swap' }}
  {{ title_font | font_face: font_display: 'swap' }}
  {{ details_font | font_face: font_display: 'swap' }}
  {{ button_font | font_face: font_display: 'swap' }}
  {{ price_font | font_face: font_display: 'swap' }}
  {{ top_text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }  

  .quiz-top-wrapper-{{ section.id }} {
    padding: {{ top_padding_vertical_mobile }}px {{ padding_horizontal_mobile }}rem;
    background-color: {{ top_bg_color }};
  }

  .quiz-heading-{{ section.id }} {
    text-align: {{ top_content_align_mobile }};
  }

  .quiz-heading-{{ section.id }} * {
    margin: 0;    
    font-size: {{ heading_size_mobile }}px;
    line-height: {{ heading_height }}%;
    color: {{ heading_color }};  
    text-transform: unset;  
    word-break: break-word;
    font-weight: 700;
  }

  .quiz-results-{{ section.id }} {
    margin: 0;    
    margin-top: {{ results_mt_mobile }}px;
    text-align: {{ top_content_align_mobile }};
    font-size: {{ results_size_mobile }}px;
    line-height: {{ results_height }}%;
    color: {{ results_color }};  
    text-transform: unset;  
    word-break: break-word;
    font-weight: 700;
  }

  .quiz-top-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 15px;
    justify-content: {{ top_content_horizontal_align_mobile }};
    flex-wrap: wrap;
    margin-top: {{ top_filter_mt_mobile }}px;
  }

  .quiz-option-list-{{ section.id }} {
    border: 0px !important;
    background: transparent !important;  
    border-bottom: 1px solid {{ top_text_color }} !important; 
  }

  .quiz-option-list-{{ section.id }}:focus-visible {
    border: 0px !important;
    box-shadow: none !important;
    border-bottom: 1px solid {{ top_text_color }} !important; 
    outline: none !important;
  }

  .quiz-top-text-{{ section.id }} *,
  .quiz-option-list-{{ section.id }},
  .quiz-option-list-{{ section.id }} option {
    margin: 0;    
    font-size: {{ top_text_size_mobile }}px;
    line-height: {{ top_text_height }}%;
    color: {{ top_text_color }};  
    text-transform: unset;  
    word-break: break-word;
  }

  .quiz-grid-{{ section.id }} {
    display: grid;
    grid-template-columns: repeat({{ cards_row_mobile }}, 1fr);
    gap: {{ cards_gap_mobile }}px;
    margin-top: {{ cards_mt_mobile }}px;
  }

  .quiz-card-{{ section.id }} {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
  }

  .quiz-card-{{ section.id }}.hide {
    display: none !important;
  }

  .quiz-image-{{ section.id }} {
    border: {{ product_image_border_thickness }}px solid {{ product_image_border_color }};
    border-radius: {{ product_image_radius }}px;
    display: block;
    box-sizing: border-box !important;
    overflow: hidden;
  }

  .quiz-image-{{ section.id }} img,
  .quiz-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: all 0.25s ease 0s;
  }

  .quiz-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .quiz-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    flex: 1 1 0;
    justify-content: space-between;
    align-items: {{ product_content_horizontal_align_mobile }};
  }

  .quiz-title-{{ section.id }} {
    margin: 0;   
    display: block;
    text-decoration: none;
    text-align: {{ product_content_align_mobile }};
    margin-top: {{ title_mt_mobile }}px;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
    word-break: break-word;
    transition: all 0.25s ease 0s;
  }   

  .quiz-text-{{ section.id }} {
    text-align: {{ product_content_align_mobile }};
    margin-top: {{ details_mt_mobile }}px;    
  }

  .quiz-text-{{ section.id }} * {
    margin: 0;    
    font-size: {{ details_size_mobile }}px;
    line-height: {{ details_height }}%;
    color: {{ details_color }};  
    text-transform: unset;  
    word-break: break-word;
  }

  .quiz-price-{{ section.id }} {
    margin-top: {{ price_mt_mobile }}px;  
    width: 100%;
    display: flex;
    align-items: center;
    gap: 5px;
    justify-content: {{ product_content_horizontal_align_mobile }};
  }

  .quiz-price-{{ section.id }} * {
    margin: 0;        
    text-align: {{ product_content_align_mobile }};
    font-size: {{ price_size_mobile }}px;
    color: {{ price_color }};
    line-height: {{ price_height }}%;
    text-transform: unset;
    word-break: break-word;
    transition: all 0.25s ease 0s;
    font-weight: 700;
  }   

  .quiz-compare-price-{{ section.id }} {
    text-decoration: line-through;
    color: {{ price_original_color }};
  }

  .quiz-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    width: 100%;
    margin: 0;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    background-color: transparent;
    border: 0px;
    cursor: pointer;
    margin-top: {{ button_mt_mobile }}px;
    font-weight: 700;
  }

  .quiz-button-inner-{{ section.id }} {
    margin: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
  }

  .quiz-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .quiz-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
    border: none;
    background-color: transparent;
  }

  .quiz-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .quiz-button-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .quiz-top-wrapper-{{ section.id }} {
      padding: {{ top_padding_vertical }}px {{ padding_horizontal }}rem;    
    }

    .quiz-heading-{{ section.id }} {
      text-align: {{ top_content_align }};
    }

    .quiz-heading-{{ section.id }} * {       
      font-size: {{ heading_size }}px;     
    }

    .quiz-results-{{ section.id }} {
      margin-top: {{ results_mt }}px;
      text-align: {{ top_content_align }};
      font-size: {{ results_size }}px;
    }

    .quiz-top-{{ section.id }} {    
      margin-top: {{ top_filter_mt }}px;
      justify-content: {{ top_content_horizontal_align }};
    }

    .quiz-top-text-{{ section.id }} *,
    .quiz-option-list-{{ section.id }},
    .quiz-option-list-{{ section.id }} option {     
      font-size: {{ top_text_size }}px;     
    }

    .quiz-grid-{{ section.id }} {
      margin-top: {{ cards_mt }}px;
      grid-template-columns: repeat({{ cards_row }}, 1fr);
      gap: {{ cards_gap }}px;
    }

    .quiz-content-{{ section.id }} {
      align-items: {{ product_content_horizontal_align }};
    }

    .quiz-title-{{ section.id }} {
      text-align: {{ product_content_align }};
      margin-top: {{ title_mt }}px;
      font-size: {{ title_size }}px;    
    }   

    .quiz-text-{{ section.id }} {
      text-align: {{ product_content_align }};
      margin-top: {{ details_mt }}px;    
    }

    .quiz-text-{{ section.id }} * {    
      font-size: {{ details_size }}px;
    }

    .quiz-price-{{ section.id }} {    
      margin-top: {{ price_mt }}px;       
      justify-content: {{ product_content_horizontal_align }};
    }

    .quiz-price-{{ section.id }} * {        
      text-align: {{ product_content_align }};
      font-size: {{ price_size }}px;    
    }   

    .quiz-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .quiz-heading-{{ section.id }} *  {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if results_custom %}
  <style>
    .quiz-results-{{ section.id }} {
      font-family: {{ results_font.family }}, {{ results_font.fallback_families }};
      font-weight: {{ results_font.weight }};
      font-style: {{ results_font.style }};
    }
  </style>
{% endif %}

{% if top_text_custom %}
  <style>
    .quiz-top-text-{{ section.id }} *,
    .quiz-option-list-{{ section.id }},
    .quiz-option-list-{{ section.id }} option {
      font-family: {{ top_text_font.family }}, {{ top_text_font.fallback_families }};
      font-weight: {{ top_text_font.weight }};
      font-style: {{ top_text_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .quiz-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if details_custom %}
  <style>
    .quiz-text-{{ section.id }} * {
      font-family: {{ details_font.family }}, {{ details_font.fallback_families }};
      font-weight: {{ details_font.weight }};
      font-style: {{ details_font.style }};
    }
  </style>
{% endif %}

{% if price_custom %}
  <style>
    .quiz-price-{{ section.id }} * {
      font-family: {{ price_font.family }}, {{ price_font.fallback_families }};
      font-weight: {{ price_font.weight }};
      font-style: {{ price_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .quiz-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if product_image_ratio_mobile == "portrait" %}
  <style>
    .quiz-image-{{ section.id }} {
      aspect-ratio: 9.6/13;
    }
  </style>
{% elsif product_image_ratio_mobile == "landscape" %}
  <style>
  .quiz-image-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif product_image_ratio_mobile == "square" %}
  <style>
    .quiz-image-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .quiz-image-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}

{% if product_image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
      .quiz-image-{{ section.id }} {
        aspect-ratio: 9.6/13;
      }
    }

  </style>
{% elsif product_image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .quiz-image-{{ section.id }} {
        aspect-ratio: 12/8;
      }
    }  
  </style>
{% elsif product_image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .quiz-image-{{ section.id }} {
        aspect-ratio: 12/12;
      }
    }    
  </style>
{% else %}
  <style>
     @media(min-width: 1024px) {
      .quiz-image-{{ section.id }} {
        aspect-ratio: auto;
      }
    }
  </style>
{% endif %}

{% if product_image_hover_type == "second_image" %}
  <style>      
    .quiz-second-image-{{ section.id }} {
      display: none !important;  
    }

    @media(min-width: 1024px) {
      .quiz-card-{{ section.id }}:hover .quiz-first-image-{{ section.id }} {
        display: none !important;  
      }

      .quiz-card-{{ section.id }}:hover .quiz-second-image-{{ section.id }} {
        display: block !important;  
      } 
    }
  </style>
{% elsif product_image_hover_type == "zoom" %}
  <style>
    @media(min-width: 1024px) {
      .quiz-card-{{ section.id }}:hover .quiz-image-{{ section.id }} img,
      .quiz-card-{{ section.id }}:hover .quiz-image-{{ section.id }} svg {
        transform: scale(1.05);
        transition: all 0.25s ease 0s;
      } 
    }
  </style>
{% endif %}

{% if button_style == "non_outline" or button_style == "non_outline_arrow" %}
  <style>
    .quiz-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
    }

    .quiz-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
    }
  </style>
{% elsif button_style == "outline" or button_style == "outline_arrow" %}
  <style>
    .quiz-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_color }};
    }

    .quiz-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    }
  </style>
{% elsif button_style == "link" %}
  <style>
    .quiz-button-{{ section.id }} {
      justify-content: {{ product_content_horizontal_align_mobile }};
      padding: 0px !important;
    }

    @media(min-width: 1024px) {
      .quiz-button-{{ section.id }} {
        justify-content: {{ product_content_horizontal_align }};      
      }
    }
  </style>
