<script>
  function initProductQuiz() {
    const firstSelect = document.getElementById(`first-option-list-{{ section.id }}`);
    const secondSelect = document.getElementById(`second-option-list-{{ section.id }}`);
    const quizCardsContainer = document.querySelector(`.quiz-grid-{{ section.id }}`);
    const quizCountWrapper = document.querySelector(`.quiz-visible-count-wrapper-{{ section.id }}`);
    const quizCountSpan = document.getElementById(`quiz-visible-count-{{ section.id }}`);
    const emptyResultsMessage = document.querySelector(`.quiz-empty-results-{{ section.id }}`);

    if (!firstSelect || !secondSelect || !quizCardsContainer || !quizCountWrapper || !quizCountSpan || !emptyResultsMessage) {
      return;
    }

    const allSecondOptions = Array.from(secondSelect.options);
    const quizCards = Array.from(quizCardsContainer.querySelectorAll(`.quiz-card-{{ section.id }}`));

    function updateProductCount() {
      const visibleCardsCount = quizCards.filter(card => !card.classList.contains('hide')).length;

      if (visibleCardsCount > 0) {
        quizCountSpan.textContent = `(${visibleCardsCount})`;
        quizCountWrapper.style.display = '';
        emptyResultsMessage.style.display = 'none';
      } else {
        quizCountWrapper.style.display = 'none';
        emptyResultsMessage.style.display = '';
      }
    }

    function filterQuizCards() {
      const selectedMainTag = firstSelect.value;
      const selectedInternalTag = secondSelect.value;

      quizCards.forEach(card => {
        const dataTags = card.dataset.tags;
        const productTags = dataTags ? dataTags.split(',').map(tag => tag.trim().toLowerCase()) : [];

        let isVisible = true;

        if (selectedMainTag !== '') {
          if (!productTags.includes(selectedMainTag.toLowerCase())) {
            isVisible = false;
          }
        }

        if (selectedInternalTag !== '') {
          if (!productTags.includes(selectedInternalTag.toLowerCase())) {
            isVisible = false;
          }
        }

        if (isVisible) {
          card.classList.remove('hide');
        } else {
          card.classList.add('hide');
        }
      });

      updateProductCount();
    }

    function updateSecondDropdownOptions() {
      const selectedBlockId = firstSelect.options[firstSelect.selectedIndex].dataset.id;
      const currentSelectedSecondValue = secondSelect.value;

      secondSelect.innerHTML = '<option value="">All</option>';

      let foundCurrentSelection = false;

      allSecondOptions.forEach(option => {
        if (option.value === "" && option.textContent.toLowerCase() === "all" && option.dataset.id === undefined) {
          return;
        }

        if (!selectedBlockId) {
          secondSelect.add(option.cloneNode(true));
          if (option.value === currentSelectedSecondValue) {
            foundCurrentSelection = true;
          }
        } else {
          if (option.dataset.id === selectedBlockId) {
            secondSelect.add(option.cloneNode(true));
            if (option.value === currentSelectedSecondValue) {
              foundCurrentSelection = true;
            }
          }
        }
      });

      if (foundCurrentSelection) {
        secondSelect.value = currentSelectedSecondValue;
      } else {
        secondSelect.value = "";
      }

      filterQuizCards();
    }

    firstSelect.addEventListener('change', updateSecondDropdownOptions);
    secondSelect.addEventListener('change', filterQuizCards);

    updateSecondDropdownOptions();
    filterQuizCards();
  }

  document.addEventListener('DOMContentLoaded', initProductQuiz);

  if (Shopify.designMode) {
    document.addEventListener('shopify:section:unload', initProductQuiz);
    document.addEventListener('shopify:section:load', initProductQuiz);
  }
</script>
