{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign body_gap = section.settings.body_gap
  assign body_gap_mobile = section.settings.body_gap_mobile

  assign image_width = section.settings.image_width
  assign image_width_mobile = section.settings.image_width_mobile
  assign image_ratio = section.settings.image_ratio
  assign image_ratio_mobile = section.settings.image_ratio_mobile
  assign image_radius = section.settings.image_radius
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color
-%}

{%- style -%}
  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
    position: relative;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

 .scrolling-scroll-body-{{ section.id }} {
    overflow: hidden;
    white-space: nowrap;
    scrollbar-width: none;
    width: 100%;
    margin-left: 0;
  }

  .scrolling-scroll-body-{{ section.id }}::-webkit-scrollbar {
    display: none;
  }

  .scrolling-body-{{ section.id }} {
    display: flex;
    gap: {{ body_gap_mobile }}px;
    padding-left: {{ body_gap_mobile }}px;
    transform: translateX(0);
    transition: transform 0.1s ease-out;
    position: relative;
    left: 0;
  }

  .scrolling-image-{{ section.id }} {
    overflow: hidden;
    border-radius: {{ image_radius }}px;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    width: {{ image_width_mobile }}px;
    flex-shrink: 0;
  }

  .scrolling-image-{{ section.id }} img,
  .scrolling-image-{{ section.id }} svg {
    display: inline-block;
    width: 100%;
    height: 100%;
    object-fit: cover
  }

  .scrolling-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  @media(min-width: 1024px) {
    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .scrolling-body-{{ section.id }} {
      gap: {{ body_gap }}px;
    }

    .scrolling-scroll-body-{{ section.id }} {
      padding-left: {{ body_gap }}px; /* Desktop padding equal to desktop gap */
    }

    .scrolling-image-{{ section.id }} {
      width: {{ image_width }}px;
    }
  }
{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .scrolling-image-{{ section.id }} {
      aspect-ratio: 9.6/13;
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
  .scrolling-image-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .scrolling-image-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .scrolling-image-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
      .scrolling-image-{{ section.id }} {
        aspect-ratio: 9.6/13;
      }
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .scrolling-image-{{ section.id }} {
        aspect-ratio: 12/8;
      }
    }  
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .scrolling-image-{{ section.id }} {
        aspect-ratio: 12/12;
      }
    }    
  </style>
{% else %}
  <style>
     @media(min-width: 1024px) {
      .scrolling-image-{{ section.id }} {
        aspect-ratio: auto;
      }
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} scrolling-{{ section.id }}" 
     style="background-color:{{ background_color }}; background-image: {{ background_gradient }};"
     data-scroll-speed="{{ section.settings.scroll_speed }}">
    <div class="section-{{ section.id }}-settings">
      <div class="scrolling-scroll-body-{{ section.id }}">
        <div class="scrolling-body-{{ section.id }}">
          {% for block in section.blocks %}
            <div class="scrolling-image-{{ section.id }}">
              {% if block.settings.image != blank %}
                <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>          
              {% else %}    
                {{ 'image' | placeholder_svg_tag }}
              {% endif %}
            </div>
          {% endfor %}
        </div>
      </div>      
    </div>
</div>

<script>
  class ScrollingImages {
    constructor(sectionId) {
      this.sectionId = sectionId;
      this.scrollBody = document.querySelector(`.scrolling-body-${sectionId}`);
      this.section = document.querySelector(`.section-${sectionId}`);
      this.baseSpeedMultiplier = parseFloat(this.section.getAttribute('data-scroll-speed') || 0.3);
      this.isMobile = window.innerWidth <= 768;
      // Halve the speed on mobile
      this.speedMultiplier = this.isMobile ? this.baseSpeedMultiplier * 0.3 : this.baseSpeedMultiplier;
      this.ticking = false;
      this.initialized = false;

      setTimeout(() => this.init(), 50);
    }

    init() {
      if (!this.scrollBody || !this.section || this.initialized) return;

      // Reset transform
      this.scrollBody.style.transform = 'translateX(0)';

      // Calculate initial dimensions
      this.recalculateDimensions();

      // Bind methods
      this.onScroll = this.onScroll.bind(this);
      this.updateScrollPosition = this.updateScrollPosition.bind(this);
      this.handleResize = this.handleResize.bind(this);

      // Add event listeners
      window.addEventListener('scroll', this.onScroll, { passive: true });
      window.addEventListener('resize', this.handleResize);

      // Mark as initialized
      this.initialized = true;

      // Initial update
      this.updateScrollPosition();
    }

    recalculateDimensions() {
      const rect = this.section.getBoundingClientRect();
      this.sectionWidth = rect.width;
      this.totalWidth = this.scrollBody.scrollWidth;
      this.maxScroll = Math.max(0, this.totalWidth - this.sectionWidth);
    }

    updateScrollPosition() {
      const rect = this.section.getBoundingClientRect();
      const viewportHeight = window.innerHeight;

      let progress = 0;

      // Only start if section is approaching view and continue until completely out of view
      if (rect.bottom >= 0) { // Changed condition to continue until section is gone
        const totalDistance = rect.height + viewportHeight; // Add viewport height to total distance
        const startTrigger = viewportHeight - 500; // Start 500px before section reaches top

        if (rect.top <= startTrigger) {
          const traveled = startTrigger - rect.top;
          progress = Math.max(0, Math.min(1, traveled / totalDistance));
        }
      } else {
        // Section has scrolled completely out of view
        progress = 1;
      }

      // Use the speed multiplier here
      const scrollOffset = Math.max(0, progress * this.maxScroll * this.speedMultiplier);

      requestAnimationFrame(() => {
        this.scrollBody.style.transform = `translateX(-${scrollOffset}px)`;
      });
    }

    onScroll() {
      if (!this.ticking) {
        requestAnimationFrame(() => {
          this.updateScrollPosition();
          this.ticking = false;
        });
        this.ticking = true;
      }
    }

    handleResize() {
      clearTimeout(this.resizeTimeout);
        this.resizeTimeout = setTimeout(() => {
          this.isMobile = window.innerWidth <= 768;
          // Update speed multiplier when screen size changes
          this.speedMultiplier = this.isMobile ? this.baseSpeedMultiplier * 0.3 : this.baseSpeedMultiplier;
          this.recalculateDimensions();
          this.scrollBody.style.transform = 'translateX(0)';
          this.updateScrollPosition();
        }, 100);
    }

    destroy() {
      if (this.initialized) {
        window.removeEventListener('scroll', this.onScroll);
        window.removeEventListener('resize', this.handleResize);
        clearTimeout(this.resizeTimeout);
      }
    }
  }

  // Store all section instances
  window.scrollingSections = {};

  // Initialize all sections
  function initAllScrollingSections() {
    // Clean up existing instances
    Object.values(window.scrollingSections).forEach(instance => instance.destroy());
    window.scrollingSections = {};

    // Initialize new instances
    document.querySelectorAll('[class*="section-"]').forEach(section => {
      const sectionId = section.className.match(/section-([^\s]+)/)?.[1];
      if (sectionId && section.querySelector(`.scrolling-body-${sectionId}`)) {
        window.scrollingSections[sectionId] = new ScrollingImages(sectionId);
      }
    });
  }

  // Initialize on load and after DOM content loaded
  initAllScrollingSections();
  document.addEventListener('DOMContentLoaded', initAllScrollingSections);

  // Handle Shopify theme editor
  if (Shopify.designMode) {
    document.addEventListener('shopify:section:unload', (event) => {
      const sectionId = event.detail.sectionId;
      if (window.scrollingSections[sectionId]) {
        window.scrollingSections[sectionId].destroy();
        delete window.scrollingSections[sectionId];
      }
    });

    document.addEventListener('shopify:section:load', () => {
      setTimeout(initAllScrollingSections, 50);
    });

    document.addEventListener('shopify:section:reorder', () => {
      setTimeout(initAllScrollingSections, 50);
    });
  }
</script>
