{%- liquid

  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign background_color = section.settings.background_color

  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width

  assign border_thickness = section.settings.border_thickness
  assign border_color = section.settings.border_color

  assign ss-title = section.settings.ss-title
  assign header_size =  section.settings.header_size 
  assign header_color =  section.settings.header_color
  assign header_align = section.settings.header_align
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font

  assign sliders_mt = section.settings.sliders_mt
  assign logo_padding_top = section.settings.logo_padding_top
  assign logo_padding_bottom = section.settings.logo_padding_bottom
  assign shadow = section.settings.shadow
  assign scrolling_velocity = section.settings.scrolling_velocity
  assign pause_hover = section.settings.pause_hover
  assign slider_background_color = section.settings.slider_background_color
  assign slider_gap_mobile = section.settings.slider_gap_mobile
  assign slider_gap = section.settings.slider_gap
  assign slider_direction = section.settings.slider_direction
  assign slider_rotate = section.settings.slider_rotate
  assign slider_mb = section.settings.slider_mb
  assign slider_rotate_mobile = section.settings.slider_rotate_mobile
  assign slider_mb_mobile = section.settings.slider_mb_mobile

  assign second_slider_direction = section.settings.second_slider_direction
  assign second_slider = section.settings.second_slider
  assign second_slider_background_color = section.settings.second_slider_background_color
  assign second_slider_rotate = section.settings.second_slider_rotate
  assign second_slider_mt = section.settings.second_slider_mt
  assign second_slider_rotate_mobile = section.settings.second_slider_rotate_mobile
  assign second_slider_mt_mobile = section.settings.second_slider_mt_mobile

  assign icon_width =  section.settings.icon_width

-%}
<style>

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    margin-top: {{ margin_top | times: 0.85 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.85 | round: 0 }}px;
    padding-top: var(--pt);
    padding-bottom: var(--pb);
    background: {{ background_color }};
    border: {{ border_thickness }}px solid {{ border_color }};
    {% unless full_width %}
      max-width: {{ content_width }}rem;
    {% endunless %}
    overflow: hidden;
  }

  @media screen and (min-width: 768px) {
    .section-{{ section.id }}-padding {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      padding-top: var(--pt-desktop);
      padding-bottom: var(--pb-desktop);
    }
  }
</style>

<style>

  {{ heading_font | font_face: font_display: 'swap' }}

  .scrolling-logo-cloud_{{ section.id }} {
    margin-top: {{ sliders_mt | times: 0.85 | round: 0 }}px;
  }

  .scrolling-logo-cloud-title-{{ section.id }} {
    width: 100%;
    margin: 0px;
    text-align: {{ header_align }};
    padding: 0 15px;
    font-size:{{ header_size | times: 0.75 | round: 0 }}px;
    color: {{ header_color }};
  }

  .scrolling-logo-cloud_placeholder {
    width:100%;
  }

  .scrolling-logo-cloud_{{ section.id }} .slide-{{ section.id }} {
    display: block;
    flex: 0 0 {{ icon_width | times: 0.85 | round: 0 }}px;
    width: 100%;
  }

  .scrolling-logo-cloud_{{ section.id }} .slide-{{ section.id }} img,
  .scrolling-logo-cloud_{{ section.id }} .slide-{{ section.id }} svg {
    width: 100%;
    height: 100%;
    display: block;
    object-fit: cover;
  }

  .scrolling-logo-cloud_{{ section.id }} .slide-{{ section.id }} svg {
    max-height: 80px;
  }

  .marquee-horizontal-{{ section.id }} {
      position: relative;
      z-index: 1;
      display: inline-flex;
      overflow: hidden;
      width: 100%;
      -webkit-box-pack: start;
      -webkit-justify-content: flex-start;
      -ms-flex-pack: start;
      justify-content: flex-start;
      -webkit-box-align: center;
      -webkit-align-items: center;
      -ms-flex-align: center;
      align-items: center;
      width: 200%;
      left: -50%;
  }

  .marquee-horizontal-{{ section.id }}.reverse {
    flex-direction: row-reverse;
  }

  .track-horizontal-{{section.id}} {
      padding-left: {{ slider_gap_mobile }}px;
      display: -webkit-box;
      display: -webkit-flex;
      display: -ms-flexbox;
      display: flex;
      flex-shrink: 0;
      width: calc(({{ icon_width | times: 0.85 | round: 0 }}px * {{ section.blocks.size }}) + ({{ slider_gap_mobile }}px * {{ section.blocks.size }}));
      gap: {{ slider_gap_mobile }}px;
      -webkit-box-pack: start;
      -webkit-justify-content: flex-start;
      -ms-flex-pack: start;
      justify-content: flex-start;
      -webkit-box-align: center;
      -webkit-align-items: center;
      -ms-flex-align: center;
      align-items: center;
      white-space: nowrap;
      will-change: transform;
  }

  @keyframes marquee-horizontal-left {
    0% {
        transform: translateZ(0);
        visibility: visible;
    }

    100% {
        transform: translate3d(-100%,0,0);
    }
  }

  @keyframes marquee-horizontal-right {
    0% {
        transform: translate3d(-100%,0,0);
        visibility: visible;
    }

    100% {
        transform: translateZ(0);
    }
  }

  .marquee-horizontal-{{ section.id }}.first-marquee-horizontal-{{ section.id }} {
    background-color: {{ slider_background_color }};
    transform: rotate({{ slider_rotate_mobile }}deg);
    transform-origin: center;
    bottom: {{ slider_mb_mobile }}px;
  }

  .marquee-horizontal-{{ section.id }}.first-marquee-horizontal-{{ section.id }} .track-horizontal-{{section.id}} {
    animation: marquee-horizontal-{{ slider_direction }} {{ scrolling_velocity | times: 1000 | round: 0 }}ms linear infinite;
  }

  .marquee-horizontal-{{ section.id }}.second-marquee-horizontal-{{ section.id }} {
    background-color: {{ second_slider_background_color }};
    transform: rotate({{ second_slider_rotate_mobile }}deg);
    transform-origin: center;
    top: {{ second_slider_mt_mobile }}px;
  }

  .marquee-horizontal-{{ section.id }}.second-marquee-horizontal-{{ section.id }} .track-horizontal-{{section.id}} {
    animation: marquee-horizontal-{{ second_slider_direction }} {{ scrolling_velocity | times: 1000 | round: 0 }}ms linear infinite;
  }

  @media screen and (min-width: 768px) {

    .scrolling-logo-cloud-title-{{ section.id }} {
      font-size:{{ header_size }}px;
      color: {{ header_color }};
    }

    .scrolling-logo-cloud_{{ section.id }} .slide-{{ section.id }} {
      flex: 0 0 {{ icon_width }}px;
      width: 100%;
    }

    .track-horizontal-{{section.id}} {
      padding-left: {{ slider_gap }}px;
      width: calc(({{ icon_width }}px * {{ section.blocks.size }}) + ({{ slider_gap }}px * {{ section.blocks.size }}));
      gap: {{ slider_gap }}px; 
    }

    .marquee-horizontal-{{ section.id }}.first-marquee-horizontal-{{ section.id }} {
      transform: rotate({{ slider_rotate }}deg);
      bottom: {{ slider_mb }}px;
    }

    .marquee-horizontal-{{ section.id }}.second-marquee-horizontal-{{ section.id }} {
      transform: rotate({{ second_slider_rotate }}deg);
      top: {{ second_slider_mt }}px;
    }
  }

  @media screen and (max-width: 767px) {
    .scrolling-logo-cloud_{{ section.id }} {
      padding: 0 15px;
    }    
  }

  {% if pause_hover %}
    .marquee-horizontal-{{ section.id }}:hover .track-horizontal-{{section.id}} {
       animation-play-state: paused 
    }
  {% endif %}
</style>

{% if heading_custom %}
  <style>
    .scrolling-logo-cloud-title-{{ section.id }} {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% capture loop %}
  {%- for block in section.blocks -%}
    {%- case block.type -%}
      {%- when 'slide' -%}
          <div class="slide-{{ section.id }}">
            {% if block.settings.link != blank %}<a href="{{ block.settings.link }}" >{% endif %}
              {%- if block.settings.image != blank -%}
                <img class="image-content__image lazyload"
                                  src="{{ block.settings.image | img_url: '300x300' }}"
                                  data-src="{{ img_url }}"
                                  data-widths="[180, 360, 540, 720, 900, 1080, 1296, 1512, 1728, 1944, 2048]"
                                  data-aspectratio="{{ block.settings.image.aspect_ratio }}"
                                  data-sizes="auto"
                                  alt="{{ block.settings.image.alt | escape }}">
              {%- else -%}
                {{ 'product-2' | placeholder_svg_tag: 'scrolling-logo-cloud_placeholder' }}
              {%- endif -%}
            {% if block.settings.link != blank %}</a>{% endif %}
          </div>
    {%- endcase -%}
  {%- endfor -%}
{% endcapture %}

<section
  class="section-{{ section.id }}-settings"
  data-section-id="{{ section.id }}"
  style="
    --pt: {{ padding_top | times: 0.75 | round: 0 }}px;
    --pt-desktop: {{ padding_top }}px;
    --pb: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    --pb-desktop: {{ padding_bottom }}px;
  "
>
  <h2
    class="scrolling-logo-cloud-title-{{ section.id }}"
    id="scrolling-logo-cloud"
    style="font-weight: 700;"
  >
    {{ ss-title }}
  </h2>
  <div 
    class="scrolling-logo-cloud_{{ section.id }}" id="custom-scrolling-logo-cloud"
    >
    <div class="marquee-horizontal-{{ section.id }} first-marquee-horizontal-{{ section.id }}" style="padding:0;
    padding-top:{{ logo_padding_top }}px;padding-bottom:{{ logo_padding_bottom }}px; {% if shadow %} box-shadow: 4px 4px 20px rgba(191, 191, 191, 0.5);{% endif %};">
      <div class="track-horizontal track-horizontal-{{section.id}} outer_flex">
        {{ loop }}
      </div>
      <div class="track-horizontal track-horizontal-{{section.id}} outer_flex">
        {{ loop }}
      </div>
      <div class="track-horizontal track-horizontal-{{section.id}} outer_flex">
        {{ loop }}
      </div>
      <div class="track-horizontal track-horizontal-{{section.id}} outer_flex">
        {{ loop }}
      </div>
      <div class="track-horizontal track-horizontal-{{section.id}} outer_flex">
        {{ loop }}
      </div>
      <div class="track-horizontal track-horizontal-{{section.id}} outer_flex">
        {{ loop }}
      </div>
      <div class="track-horizontal track-horizontal-{{section.id}} outer_flex">
        {{ loop }}
      </div>
    </div>
    {% if second_slider %}
      <div class="marquee-horizontal-{{ section.id }} second-marquee-horizontal-{{ section.id }}" style="padding:0;
    padding-top:{{ logo_padding_top }}px;padding-bottom:{{ logo_padding_bottom }}px; {% if shadow %} box-shadow: 4px 4px 20px rgba(191, 191, 191, 0.5);{% endif %};">
        <div class="track-horizontal track-horizontal-{{section.id}} outer_flex">
          {{ loop }}
        </div>
        <div class="track-horizontal track-horizontal-{{section.id}} outer_flex">
          {{ loop }}
        </div>
        <div class="track-horizontal track-horizontal-{{section.id}} outer_flex">
          {{ loop }}
        </div>
        <div class="track-horizontal track-horizontal-{{section.id}} outer_flex">
          {{ loop }}
        </div>
        <div class="track-horizontal track-horizontal-{{section.id}} outer_flex">
          {{ loop }}
        </div>
        <div class="track-horizontal track-horizontal-{{section.id}} outer_flex">
          {{ loop }}
        </div>
        <div class="track-horizontal track-horizontal-{{section.id}} outer_flex">
          {{ loop }}
        </div>
      </div>
    {% endif %}
  </div>
</section>

<script>
  function initScrollingLogoCloudPro() {
    var htmlElement = document.documentElement;

    if (window.getComputedStyle(htmlElement).getPropertyValue('direction') === 'rtl') {
        document.querySelector('.marquee-horizontal-{{ section.id }}').classList.add('reverse');
    }
  }

  document.addEventListener('DOMContentLoaded', initScrollingLogoCloudPro);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initScrollingLogoCloudPro);
     document.addEventListener('shopify:section:load', initScrollingLogoCloudPro);
  }
</script>
