liquid_code:
{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign scrolling_velocity = section.settings.scrolling_velocity
  assign scrolling_velocity_mobile = section.settings.scrolling_velocity_mobile
  assign scrolling_direction = section.settings.scrolling_direction
  assign scrolling_gap_mobile = section.settings.scrolling_gap_mobile
  assign scrolling_gap = section.settings.scrolling_gap
  assign pause_hover = section.settings.pause_hover

  assign card_width_desktop = section.settings.card_width_desktop
  assign card_width_mobile = section.settings.card_width_mobile
  assign card_padding_horizontal_mobile = section.settings.card_padding_horizontal_mobile
  assign card_padding_horizontal = section.settings.card_padding_horizontal
  assign card_padding_vertical_mobile = section.settings.card_padding_vertical_mobile
  assign card_padding_vertical = section.settings.card_padding_vertical
  assign card_bg_color = section.settings.card_bg_color
  assign card_radius = section.settings.card_radius
  assign card_border_color = section.settings.card_border_color
  assign card_border_thickness = section.settings.card_border_thickness
  assign card_shadow = section.settings.card_shadow

  assign edge_blur_width = section.settings.edge_blur_width
  assign edge_blur_intensity = section.settings.edge_blur_intensity

-%}

{%- style -%}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .scrolling-logo-slider-{{ section.id }} {
    display: flex;
    position: relative;
    overflow: hidden;
    padding: 36px 0px;
  }

  .scrolling-logo-slider-{{ section.id }}::before,
  .scrolling-logo-slider-{{ section.id }}::after {
    content: "";
    position: absolute;
    width: {{ edge_blur_width }}%;
    height: 100%;
    top: 0;
    z-index: 2;
    pointer-events: none;
  }

  .scrolling-logo-slider-{{ section.id }}::before {
    background: linear-gradient(90deg, {{ background_color }} {{ edge_blur_intensity }}%, rgba(0,0,0,0));
    left: 0;
  }

  .scrolling-logo-slider-{{ section.id }}::after {
    background: linear-gradient(270deg, {{ background_color }} {{ edge_blur_intensity }}%, rgba(0,0,0,0));
    right: 0;
  }

  .scrolling-logo-slide-{{ section.id }} {
    width: 100%;
    flex: 0 0 {{ card_width_mobile }}px;
    overflow: hidden;
    position: relative;
    border-radius: {{ card_radius }}px;
    padding: {{ card_padding_vertical_mobile }}px {{ card_padding_horizontal_mobile }}px;
    border: solid {{ card_border_color }} {{ card_border_thickness }}px;
    background: {{ card_bg_color }};
  }

  .scrolling-logo-slide-{{ section.id }}.card-shadow {
    box-shadow: 0 .8rem 4rem rgba(0,0,0,.08)
  }

  .scrolling-logo-slide-{{ section.id }} img,
  .scrolling-logo-slide-{{ section.id }} svg {
    width: 100%;
    height: 100%;
    display: block;
    object-fit: contain;
  }

  .scrolling-logo-slide-{{ section.id }} > svg {
    background-color: #a0a0a0;
  }

  .marquee-horizontal-{{ section.id }} {
    position: relative;
    z-index: 1;
    display: inline-flex;
    /* overflow: hidden; */
    width: 100%;
    -webkit-box-pack: start;
    -webkit-justify-content: flex-start;
    -ms-flex-pack: start;
    justify-content: flex-start;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -ms-flex-align: center;
    align-items: center;
}

  html[dir="rtl"] .marquee-horizontal-{{ section.id }} {
    flex-direction: row-reverse;
  }

  .track-horizontal-{{section.id}} {
    padding-left: {{ scrolling_gap_mobile }}px;
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    flex-shrink: 0;
    width: calc(({{ card_width_mobile }}px * {{ section.blocks.size }}) + ({{ scrolling_gap_mobile }}px * {{ section.blocks.size }}));
    gap: {{ scrolling_gap_mobile }}px;
    -webkit-box-pack: start;
    -webkit-justify-content: flex-start;
    -ms-flex-pack: start;
    justify-content: flex-start;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -ms-flex-align: center;
    align-items: stretch;
    white-space: nowrap;
    will-change: transform;
    animation: marquee-horizontal-{{ scrolling_direction }} {{ scrolling_velocity_mobile }}s linear infinite;
  }

  @keyframes marquee-horizontal-left {
    0% {
        transform: translateZ(0);
        visibility: visible;
    }

    100% {
        transform: translate3d(-100%,0,0);
    }
  }

  @keyframes marquee-horizontal-right {
    0% {
        transform: translate3d(-100%,0,0);
        visibility: visible;
    }

    100% {
        transform: translateZ(0);
    }
  }

  .clip-{{ section.id }} {
    width: 100%;
    height: 100%;
    pointer-events: none;
    position: absolute;
    top: 0%;
    bottom: 0%;
    left: 0%;
    right: 0%;
    background-color: transparent !important;
  }

  .scrolling-hover-{{ section.id }} {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: #1b1b1b80;
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0;
    transition: all 0.25s ease 0s;
  }

  .scrolling-logo-slide-{{ section.id }}:hover .scrolling-hover-{{ section.id }} {
    opacity: 1;
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .scrolling-logo-slide-{{ section.id }} {
      flex: 0 0 {{ card_width_desktop }}px;
      padding: {{ card_padding_vertical }}px {{ card_padding_horizontal }}px;
    }

    .track-horizontal-{{section.id}} {
      padding-left: {{ scrolling_gap }}px;
      width: calc(({{ card_width_desktop }}px * {{ section.blocks.size }}) + ({{ scrolling_gap }}px * {{ section.blocks.size }}));
      gap: {{ scrolling_gap }}px;
      animation-duration: {{ scrolling_velocity }}s;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if pause_hover %}
  <style>
    .marquee-horizontal-{{ section.id }}:hover .track-horizontal-{{section.id}} {
      animation-play-state: paused 
    }
  </style>
{% endif %}

{% capture blocks %}
  <div class="track-horizontal-{{section.id}}">
    {% for block in section.blocks %}
      <{% if block.settings.url != blank %}a href="{{ block.settings.url }}"{% else %}div{% endif %} class="scrolling-logo-slide-{{ section.id }} {% if card_shadow %}card-shadow{% endif %}">
        {% if block.settings.image != blank %}
          <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
          {% else %}
            {{ 'image' | placeholder_svg_tag }}
          {% endif %}

        </{% if block.settings.url != blank %}a{% else %}div{% endif %}>
    {% endfor %}
  </div>
{% endcapture %}

<div class="section-{{ section.id }} scrolling-{{ section.id }}" style="background-color:{{ background_color }};">
    <div class="section-{{ section.id }}-settings">
      <div class="scrolling-logo-slider-{{ section.id }}">
        <div class="marquee-horizontal-{{ section.id }}">
          {% for item in (1..4) %}
            {{ blocks }}
          {% endfor %}
        </div>
      </div>
    </div>
</div>
