{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy

  assign heading = section.settings.heading
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_color = section.settings.heading_color
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_height = section.settings.heading_height
  assign heading_align = section.settings.heading_align
  assign heading_align_mobile = section.settings.heading_align_mobile

  assign products = section.settings.products
  assign collection = section.settings.collection
  assign collection_show_all = section.settings.collection_show_all
  assign collection_show = section.settings.collection_show

  assign scrolling_direction = section.settings.scrolling_direction
  assign scrolling_velocity = section.settings.scrolling_velocity
  assign scrolling_gap = section.settings.scrolling_gap
  assign scrolling_gap_mobile = section.settings.scrolling_gap_mobile
  assign pause_hover = section.settings.pause_hover

  assign card_width_mobile = section.settings.card_width_mobile
  assign card_width = section.settings.card_width
  assign card_radius = section.settings.card_radius
  assign card_border_thickness = section.settings.card_border_thickness
  assign card_border_color = section.settings.card_border_color
  assign card_bg_color = section.settings.card_bg_color
  assign card_shadow = section.settings.card_shadow
  assign card_shadow_color = section.settings.card_shadow_color

  assign content_padding_vertical = section.settings.content_padding_vertical
  assign content_padding_vertical_mobile = section.settings.content_padding_vertical_mobile
  assign content_padding_horizontal = section.settings.content_padding_horizontal
  assign content_padding_horizontal_mobile = section.settings.content_padding_horizontal_mobile

  assign title_size = section.settings.title_size
  assign title_size_mobile = section.settings.title_size_mobile
  assign title_color = section.settings.title_color
  assign title_custom = section.settings.title_custom
  assign title_font = section.settings.title_font
  assign title_height = section.settings.title_height
  assign title_uppercase = section.settings.title_uppercase

  assign price_size = section.settings.price_size
  assign price_size_mobile = section.settings.price_size_mobile
  assign price_color = section.settings.price_color
  assign price_custom = section.settings.price_custom
  assign price_font = section.settings.price_font
  assign price_height = section.settings.price_height
  assign price_mt = section.settings.price_mt

  assign button = section.settings.button
  assign button_size = section.settings.button_size
  assign button_size_mobile = section.settings.button_size_mobile
  assign button_color = section.settings.button_color
  assign button_hover_color = section.settings.button_hover_color
  assign button_custom = section.settings.button_custom
  assign button_font = section.settings.button_font
  assign button_height = section.settings.button_height
  assign button_mt = section.settings.button_mt
  assign button_padding_vertical = section.settings.button_padding_vertical
  assign button_padding_horizontal = section.settings.button_padding_horizontal
  assign button_padding_vertical_mobile = section.settings.button_padding_vertical_mobile
  assign button_padding_horizontal_mobile = section.settings.button_padding_horizontal_mobile
  assign button_radius = section.settings.button_radius
  assign button_border_thickness = section.settings.button_border_thickness
  assign button_border_color = section.settings.button_border_color
  assign button_border_hover_color = section.settings.button_border_hover_color
  assign button_bg_color = section.settings.button_bg_color
  assign button_bg_hover_color = section.settings.button_bg_hover_color
  assign button_arrow = section.settings.button_arrow

  assign badge = section.settings.badge
  assign badge_size = section.settings.badge_size
  assign badge_size_mobile = section.settings.badge_size_mobile
  assign badge_color = section.settings.badge_color
  assign badge_custom = section.settings.badge_custom
  assign badge_font = section.settings.badge_font
  assign badge_height = section.settings.badge_height
  assign badge_bg_color = section.settings.badge_bg_color
  assign badge_padding_vertical = section.settings.badge_padding_vertical
  assign badge_padding_horizontal = section.settings.badge_padding_horizontal
  assign badge_padding_vertical_mobile = section.settings.badge_padding_vertical_mobile
  assign badge_padding_horizontal_mobile = section.settings.badge_padding_horizontal_mobile

  assign count = ''

  if collection != blank
    if collection_show_all
      for item in collections[collection].products
        assign count = count | plus: 1
      endfor
    else
      for item in (1..collection_show)
        assign count = count | plus: 1
      endfor
    endif
  elsif products != blank
    for item in products
      assign count = count | plus: 1
    endfor
  endif

-%}

{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}

  {{ title_font | font_face: font_display: 'swap' }}
  {{ price_font | font_face: font_display: 'swap' }}
  {{ button_font | font_face: font_display: 'swap' }}
  {{ badge_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .products-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .products-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .products-card-{{ section.id }} {
    width: 100%;
    max-width: {{ card_width_mobile }}px;
    border-radius: {{ card_radius }}px;
    border: {{ card_border_thickness }}px solid {{ card_border_color }};
    background-color: {{ card_bg_color }};
    position: relative;
    overflow: hidden;
    text-decoration: none;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    box-sizing: border-box;
  }

  .products-image-{{ section.id }} {
    aspect-ratio: 1/1;
  }

  .products-image-{{ section.id }} img,
  .products-image-{{ section.id }} svg {
    display: block;
    object-fit: cover;
    width: 100%;
    aspect-ratio: 1/1;
  }

  .products-image-{{ section.id }}.products-sale-image-{{ section.id }} img,
  .products-image-{{ section.id }}.products-sale-image-{{ section.id }} svg {
    aspect-ratio: 1/0.81;
  }

  .products-image-{{ section.id }} svg {
    background-color: #DEDEDE;
  }

  .products-content-{{ section.id }} {
    flex: 1 1 auto;
    display: flex;
    flex-direction: column;
    box-sizing: border-box;
    padding: {{ content_padding_vertical_mobile }}px {{ content_padding_horizontal_mobile }}px;
  }

  .products-content-inner-{{ section.id }} {
    flex: 1 1 auto;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
  }

  .products-title-{{ section.id }} {
    display: block;
    width: 100%;
    max-width: 100%;
    margin: 0px;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
    font-weight: 700;
    text-decoration: none;
    text-overflow: ellipsis;
    white-space: normal;
  }

  .products-price-{{ section.id }} {
    display: flex;
    flex-wrap: wrap;
    gap: 5px;
    margin-top: {{ price_mt | times: 0.75 | round: 0 }}px;
  }

  .products-main-price-{{ section.id }} {
    margin: 0px;
    font-size: {{ price_size_mobile }}px;
    color: {{ price_color }};
    line-height: {{ price_height }}%;
    text-transform: unset;
    font-weight: 700;
    text-decoration: none;
  }

  .products-crossed-price-{{ section.id }} {
    margin: 0px;
    color: #999999;
    text-decoration: line-through;
    font-size: {{ price_size_mobile | times: 0.75 | round: 0 }}px;
  }

  .products-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: 100%;
    widht: 100%;
    margin: 0;
    margin-top: {{ button_mt | times: 0.75 | round: 0 }}px;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    border: {{ button_border_thickness }}px solid {{ button_border_color }};
    background-color: {{ button_bg_color }};
    transition: all 0.25s ease 0s;
    position: relative;
    font-weight: 700;
    z-index: 2;
  }

  .products-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    background-color: {{ button_bg_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .products-button-{{ section.id }} svg {
    width: 20px;
    height: 20px;
    stroke: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .products-button-{{ section.id }}:hover svg {
    stroke: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .products-badge-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: 100%;
    widht: 100%;
    margin: 0;
    font-size: {{ badge_size_mobile }}px;
    color: {{ badge_color }};
    line-height: {{ badge_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
    padding: {{ badge_padding_vertical_mobile }}px {{ badge_padding_horizontal_mobile }}px;
    background-color: {{ badge_bg_color }};
  }

  .marquee-horizontal-{{ section.id }} {
    position: relative;
    z-index: 1;
    display: inline-flex;
    overflow: hidden;
    width: 100%;
    -webkit-box-pack: start;
    -webkit-justify-content: flex-start;
    -ms-flex-pack: start;
    justify-content: flex-start;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -ms-flex-align: center;
    align-items: center;
  }

  .track-horizontal-{{section.id}} {
      padding-left: {{ scrolling_gap_mobile }}px;
      display: -webkit-box;
      display: -webkit-flex;
      display: -ms-flexbox;
      display: flex;
      flex-shrink: 0;
      width: calc(({{ card_width_mobile }}px * {{ count }}) + ({{ scrolling_gap_mobile }}px * {{ count }}));
      gap: {{ scrolling_gap_mobile }}px;
      -webkit-box-pack: start;
      -webkit-justify-content: flex-start;
      -ms-flex-pack: start;
      justify-content: flex-start;
      -webkit-box-align: center;
      -webkit-align-items: center;
      -ms-flex-align: center;
      align-items: stretch;
      white-space: nowrap;
      will-change: transform;
      animation: marquee-horizontal-{{ scrolling_direction }} {{ scrolling_velocity | times: 1000 | round: 0 }}ms linear infinite;
  }

  @keyframes marquee-horizontal-left {
    0% {
        transform: translateZ(0);
        visibility: visible;
    }

    100% {
        transform: translate3d(-100%,0,0);
    }
  }

  @keyframes marquee-horizontal-right {
    0% {
        transform: translate3d(-100%,0,0);
        visibility: visible;
    }

    100% {
        transform: translateZ(0);
    }
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .products-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .products-heading-{{ section.id }} * {
      font-size: {{ heading_size | times: 0.75 }}px;
    }

    .products-card-{{ section.id }} {
      max-width: {{ card_width }}px;
    }

    .products-content-{{ section.id }} {
      padding: {{ content_padding_vertical }}px {{ content_padding_horizontal }}px;
    }

    .products-title-{{ section.id }} {
      font-size: {{ title_size }}px;
    }

    .products-price-{{ section.id }} {
      margin-top: {{ price_mt }}px;
    }

    .products-main-price-{{ section.id }} {
      font-size: {{ price_size }}px;
    }

    .products-crossed-price-{{ section.id }} {
      font-size: {{ price_size | times: 0.75 | round: 0 }}px;
    }

    .products-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }

    .products-badge-{{ section.id }} {
      font-size: {{ badge_size }}px;
      padding: {{ badge_padding_vertical }}px {{ badge_padding_horizontal }}px;
    }

    .track-horizontal-{{section.id}} {
      padding-left: {{ scrolling_gap }}px;
      width: calc(({{ card_width }}px * {{ count }}) + ({{ scrolling_gap }}px * {{ count }}));
      gap: {{ scrolling_gap }}px; 
    }

  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .products-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .products-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if price_custom %}
  <style>
    .products-main-price-{{ section.id }},
    .products-crossed-price-{{ section.id }} {
      font-family: {{ price_font.family }}, {{ price_font.fallback_families }};
      font-weight: {{ price_font.weight }};
      font-style: {{ price_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .products-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if badge_custom %}
  <style>
    .products-badge-{{ section.id }} {
      font-family: {{ badge_font.family }}, {{ badge_font.fallback_families }};
      font-weight: {{ badge_font.weight }};
      font-style: {{ badge_font.style }};
    }
  </style>
{% endif %}

{% if card_shadow %}
  <style>
    .track-horizontal-{{section.id}} {
      padding-bottom: 12px;
      padding-top: 12px;
    }
    .products-card-{{ section.id }} {
      box-shadow: {{ card_shadow_color | hex_to_rgba: 0.15 }} 0px 2px 8px;
    }
  </style>
{% endif %}

{% if title_uppercase %}
  <style>
    .products-title-{{ section.id }} {
      text-transform: uppercase;
    }
  </style>
{% endif %}

{% if pause_hover %}
  <style>
    .marquee-horizontal-{{ section.id }}:hover .track-horizontal-{{section.id}} {
       animation-play-state: paused 
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} products-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      {% if heading != blank %}
        <div class="products-heading-{{ section.id }}">{{ heading }}</div>
      {% endif %}
      <div class="marquee-horizontal-{{ section.id }}">
        {% for item in (1..4) %}
          <div class="track-horizontal-{{ section.id }}">
            {% if collection != blank %}
              {% if collection_show_all %}
                {% for product in collections[collection].products %}
                  <a href="{{ product.url }}" class="products-card-{{ section.id }}">
                    <div class="products-image-{{ section.id }} {% if product.compare_at_price > product.price and badge != blank %}products-sale-image-{{ section.id }}{% endif %}">
                      {% if product.compare_at_price > product.price and badge != blank %}
                        <div class="products-badge-{{ section.id }}">
                          {{ badge }}
                        </div>
                      {% endif %}
                      {% if product.featured_image != blank %}
                        <img src="{{ product.featured_image | image_url }}" alt="{{ product.featured_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                      {% else %}
                        {{ 'image' | placeholder_svg_tag }}
                      {% endif %}
                    </div>
                    <div class="products-content-{{ section.id }}">
                      <div class="products-content-inner-{{ section.id }}">
                        <p class="products-title-{{ section.id }}">{{ product.title }}</p>
                        <div class="products-price-{{ section.id }}">
                          {% if product.compare_at_price > product.price %}
                            <p class="products-crossed-price-{{ section.id }} ">{{ product.compare_at_price | money }}</p>
                            <p class="products-main-price-{{ section.id }}">{{ product.price | money }}</p>
                          {% else %}
                            <p class="products-main-price-{{ section.id }}">{{ product.price | money }}</p>
                          {% endif %}
                        </div>
                      </div>
                      <div class="products-button-{{ section.id }}">
                        {{ button }}
                        {% if button_arrow %}
                          <svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg" fill="none" stroke="#000000" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="feather feather-arrow-up-right"><line x1="7" y1="17" x2="17" y2="7"></line><polyline points="7 7 17 7 17 17"></polyline></svg>
                        {% endif %}
                      </div>
                    </div>
                  </a>
                {% endfor %}
              {% else %}
                {% for product in collections[collection].products limit:collection_show %}
                  <a href="{{ product.url }}" class="products-card-{{ section.id }} swiper-slide">
                    <div class="products-image-{{ section.id }} {% if product.compare_at_price > product.price and badge != blank %}products-sale-image-{{ section.id }}{% endif %}">
                      {% if product.compare_at_price > product.price and badge != blank %}
                        <div class="products-badge-{{ section.id }}">
                          {{ badge }}
                        </div>
                      {% endif %}
                      {% if product.featured_image != blank %}
                        <img src="{{ product.featured_image | image_url }}" alt="{{ product.featured_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                      {% else %}
                        {{ 'image' | placeholder_svg_tag }}
                      {% endif %}
                    </div>
                    <div class="products-content-{{ section.id }}">
                      <div class="products-content-inner-{{ section.id }}">
                        <p class="products-title-{{ section.id }}">{{ product.title }}</p>
                        <div class="products-price-{{ section.id }}">
                          {% if product.compare_at_price > product.price %}
                            <p class="products-crossed-price-{{ section.id }} ">{{ product.compare_at_price | money }}</p>
                            <p class="products-main-price-{{ section.id }}">{{ product.price | money }}</p>
                          {% else %}
                            <p class="products-main-price-{{ section.id }}">{{ product.price | money }}</p>
                          {% endif %}
                        </div>
                      </div>
                      <div class="products-button-{{ section.id }}">
                        {{ button }}
                        {% if button_arrow %}
                          <svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg" fill="none" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="feather feather-arrow-up-right"><line x1="7" y1="17" x2="17" y2="7"></line><polyline points="7 7 17 7 17 17"></polyline></svg>
                        {% endif %}
                      </div>
                    </div>
                  </a>
                {% endfor %}
              {% endif %}
            {% elsif products != blank %}
              {% for product in products %}
                <a href="{{ product.url }}" class="products-card-{{ section.id }} swiper-slide">
                  <div class="products-image-{{ section.id }} {% if product.compare_at_price > product.price and badge != blank %}products-sale-image-{{ section.id }}{% endif %}">
                    {% if product.compare_at_price > product.price and badge != blank %}
                      <div class="products-badge-{{ section.id }}">
                        {{ badge }}
                      </div>
                    {% endif %}
                    {% if product.featured_image != blank %}
                      <img src="{{ product.featured_image | image_url }}" alt="{{ product.featured_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                    {% else %}
                      {{ 'image' | placeholder_svg_tag }}
                    {% endif %}
                  </div>
                  <div class="products-content-{{ section.id }}">
                    <div class="products-content-inner-{{ section.id }}">
                      <p class="products-title-{{ section.id }}">{{ product.title }}</p>
                      <div class="products-price-{{ section.id }}">
                        {% if product.compare_at_price > product.price %}
                          <p class="products-crossed-price-{{ section.id }} ">{{ product.compare_at_price | money }}</p>
                          <p class="products-main-price-{{ section.id }}">{{ product.price | money }}</p>
                        {% else %}
                          <p class="products-main-price-{{ section.id }}">{{ product.price | money }}</p>
                        {% endif %}
                      </div>
                    </div>
                    <div class="products-button-{{ section.id }}">
                      {{ button }}
                      {% if button_arrow %}
                        <svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg" fill="none" stroke="#000000" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="feather feather-arrow-up-right"><line x1="7" y1="17" x2="17" y2="7"></line><polyline points="7 7 17 7 17 17"></polyline></svg>
                      {% endif %}
                    </div>
                  </div>
                </a>
              {% endfor %}
            {% else %}
              {% for item in (1..8) %}
                <div class="products-card-{{ section.id }} swiper-slide">
                  <div class="products-image-{{ section.id }} {% if forloop.index == 5 or forloop.index == 6 and badge != blank %}products-sale-image-{{ section.id }}{% endif %}">
                    {% if forloop.index == 5 or forloop.index == 6 %}
                      <div class="products-badge-{{ section.id }}">
                        {{ badge }}
                      </div>
                    {% endif %}
                    {{ 'image' | placeholder_svg_tag }}
                  </div>
                  <div class="products-content-{{ section.id }}">
                    <div class="products-content-inner-{{ section.id }}">
                      <p class="products-title-{{ section.id }}">{{ product.title }}</p>
                      <div class="products-price-{{ section.id }}">
                        {% if forloop.index == 5 or forloop.index == 6 %}
                          <p class="products-crossed-price-{{ section.id }} ">$175.00</p>
                          <p class="products-main-price-{{ section.id }}">$125.00</p>
                        {% else %}
                          <p class="products-main-price-{{ section.id }}">$175.00</p>
                        {% endif %}
                      </div>
                    </div>
                    <div class="products-button-{{ section.id }}">
                      {{ button }}
                      {% if button_arrow %}
                        <svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg" fill="none" stroke="#000000" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="feather feather-arrow-up-right"><line x1="7" y1="17" x2="17" y2="7"></line><polyline points="7 7 17 7 17 17"></polyline></svg>
