{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign scrolling_velocity = section.settings.scrolling_velocity
  assign scrolling_velocity_mobile = section.settings.scrolling_velocity_mobile

  assign reverse_direction = section.settings.reverse_direction
  assign pause_hover = section.settings.pause_hover

-%}

{%- style -%}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .scrolling-scrolling-wrap-{{ section.id }} {
    position: relative;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-wrap: nowrap;
    flex-wrap: nowrap;
    background-attachment: scroll !important;
    transform-origin: center;
    overflow: hidden;
  }

  .scrolling-scrolling-list-{{ section.id }} {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    align-items: center;
    white-space: nowrap;
    background-attachment: scroll !important;
    -webkit-animation: ticker{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
    animation: ticker{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
    flex-shrink: 0;
  }

  .scrolling-scrolling-item-{{ section.id }} {
    background-attachment: scroll !important;
    flex-shrink: 0;
    text-decoration: none;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .scrolling-scrolling-list-{{ section.id }} {
      animation-duration: {{ scrolling_velocity }}s;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if reverse_direction %}
  <style>
    @keyframes ticker{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(-100%);
      }
      100% {
        transform: translateX(0%);
      }
    }
  </style>
{% else %}
  <style>
    @keyframes ticker{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(0%);
      }
      100% {
        transform: translateX(-100%);
      }
    }
  </style>
{% endif %}

{% if pause_hover %}
  <style>
    .scrolling-scrolling-wrap-{{ section.id }}:hover .scrolling-scrolling-list-{{ section.id }} {
       animation-play-state: paused 
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  {% if block.type == "text" %}
    <style>
      {{ block.settings.text_font | font_face: font_display: 'swap' }}

      .scrolling-scrolling-item-{{ block.id }} {
        text-decoration: none;
      }

      .scrolling-scrolling-text-{{ block.id }} {
        margin: 0px;
        margin-right: {{ block.settings.text_mr_mobile }}px;
        font-size: {{ block.settings.text_size_mobile }}px;
        color: {{ block.settings.text_color }};
        line-height: {{ block.settings.text_height }}%;
        text-transform: unset;
        letter-spacing: 0px;
        text-decoration: none;
      }

      .scrolling-scrolling-item-{{ block.id }} .scrolling-scrolling-stencill {
        -webkit-text-stroke: .01em {{ block.settings.text_color }};
        -webkit-text-fill-color: transparent;
      }

      @media(min-width: 1024px) {
        .scrolling-scrolling-text-{{ block.id }} {
          margin-right: {{ block.settings.text_mr }}px;
          font-size: {{ block.settings.text_size }}px;
        }
      }
    </style>

    {% if block.settings.text_custom %}
      <style>
        .scrolling-scrolling-text-{{ block.id }} {
          font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
          font-weight: {{ block.settings.text_font.weight }};
          font-style: {{ block.settings.text_font.style }};
        }
      </style>
    {% endif %}

  {% elsif block.type == "button" %}

    <style>

      {{  block.settings.button_font | font_face: font_display: 'swap' }}

      .scrolling-scrolling-item-{{ block.id }}{
        margin-right: {{ block.settings.button_mr_mobile }}px;
      }

      .scrolling-button-{{ block.id }} {
        display: flex;
        align-items: center;
        justify-content: center;
        gap: 10px;
        width: 100%;
        max-width: fit-content;
        margin: 0;
        font-size: {{ block.settings.button_size_mobile }}px;
        color: {{ block.settings.button_color }};
        line-height: {{ block.settings.button_height }}%;
        text-align: center;
        text-transform: unset;
        text-decoration: none;
        padding: {{ block.settings.button_padding_vertical_mobile }}px {{ block.settings.button_padding_horizontal_mobile }}px;
        border-radius: {{ block.settings.button_radius }}px;
        transition: all 0.25s ease 0s;
        position: relative;
        font-weight: 700;
        z-index: 2;
      }

      .scrolling-button-{{ block.id }}:hover {
        color: {{ block.settings.button_hover_color }};
        transition: all 0.25s ease 0s;
      }

      .scrolling-button-{{ block.id }} svg {
        width: 14px;
        height: 14px;
      }

      .scrolling-button-{{ block.id }} svg path {
        fill: {{ block.settings.button_color }};
        transition: all 0.25s ease 0s;
      }

      .scrolling-button-{{ block.id }}:hover svg path {
        fill: {{ block.settings.button_hover_color }};
        transition: all 0.25s ease 0s;
      }

      @media(min-width: 1024px) {

        .scrolling-scrolling-item-{{ block.id }}{
          margin-right: {{ block.settings.button_mr }}px;
        }

        .scrolling-button-{{ block.id }} {
            padding: {{ block.settings.button_padding_vertical }}px {{ block.settings.button_padding_horizontal }}px;
            font-size: {{ block.settings.button_size }}px;
        }
      }
    </style>

    {% if block.settings.button_custom %}
        <style>
          .scrolling-button-{{ block.id }} {
              font-family: {{ block.settings.button_font.family }}, {{ block.settings.button_font.fallback_families }};
              font-weight: {{ block.settings.button_font.weight }};
              font-style: {{ block.settings.button_font.style }};
          }
        </style>
    {% endif %}

    {% if block.settings.button_style == "non_outline" or block.settings.button_style == "non_outline_arrow" %}
        <style>
          .scrolling-button-{{ block.id }} {
              background-color: {{ block.settings.button_bg_color }};
          }

          .scrolling-button-{{ block.id }}:hover {
              background-color: {{ block.settings.button_bg_hover_color }};
          }
        </style>
    {% elsif block.settings.button_style == "outline" or block.settings.button_style == "outline_arrow" %}
        <style>
          .scrolling-button-{{ block.id }} {
              background-color: {{ block.settings.button_bg_color }};
              border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_color }};
          }

          .scrolling-button-{{ block.id }}:hover {
              background-color: {{ block.settings.button_bg_hover_color }};
              border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_hover_color }};
          }
        </style>
    {% elsif block.settings.button_style == "link" %}
        <style>
            .scrolling-button-{{ block.id }} {
                max-width: fit-content;
                padding: 0px;
            }
        </style>
    {% endif %}

  {% elsif block.type == "image" %}

    <style>
      .scrolling-image-{{ block.id }} {
        margin-right: {{ block.settings.image_mr_mobile }}px;
        width: {{ block.settings.image_width_mobile }}px;
        overflow: hidden;
        border-radius: {{  block.settings.image_radius }}px;
        border: {{ block.settings.image_border_thickness }}px solid {{ block.settings.image_border_color }};
      }

      .scrolling-image-{{ block.id }} img,
      .scrolling-image-{{ block.id }} svg {
        display: block;
        width: 100%;
        height: 100%;
        object-fit: cover;
      }

      .scrolling-image-{{ block.id }} svg {
        background-color: #F7F7F7;
      }

      @media(min-width: 1024px) {
        .scrolling-image-{{ block.id }} {
          margin-right: {{ block.settings.image_mr }}px;
          width: {{ block.settings.image_width }}px;
        }
      }
    </style>

  {% elsif block.type == "timer" %}

    <style>
      {{  block.settings.timer_text_font | font_face: font_display: 'swap' }}
      {{  block.settings.timer_numbers_font | font_face: font_display: 'swap' }}

      .countdown-timer-{{ block.id }} {
        display: flex;
        align-items: center;
        gap: 16px;
        margin-right: {{ block.settings.timer_mr_mobile }}px;
      }

      .countdown-timer-{{ block.id }} .time-row {
        display: flex;
        align-items: center;
        justify-content: center;
        position: relative;
      }

      .countdown-timer-{{ block.id }} .time-block__num {
        min-width: {{  block.settings.timer_numbers_size_mobile | times: 1.35 }}px;
        text-align: center;
      }

      .countdown-timer-{{ block.id }} .separator {
        position: absolute;
        top: 50%;
        transform: translateY(-50%);
        right: -{{ block.settings.timer_numbers_size_mobile | times: 0.3 }}px;
      }

      .countdown-timer-{{ block.id }} .time-block__num,
      .countdown-timer-{{ block.id }} .separator{
        margin: 0px;
        font-size: {{ block.settings.timer_numbers_size_mobile }}px;
        color: {{ block.settings.timer_numbers_color }};
        line-height: {{ block.settings.timer_numbers_height }}%;
        text-transform: unset;
      }

      .countdown-timer-{{ block.id }} .time-block__unit {
        display: block;
        width: 100%;
        text-align: center;
        margin: 0px;
        margin-top: {{ block.settings.timer_text_mt_mobile }}px;
        font-size: {{ block.settings.timer_text_size_mobile }}px;
        color: {{ block.settings.timer_text_color }};
        line-height: {{ block.settings.timer_text_height }}%;
        text-transform: unset;
      }

      @media(min-width: 1024px) {
        .countdown-timer-{{ block.id }} {
          margin-right: {{ block.settings.timer_mr }}px;
        }

        .countdown-timer-{{ block.id }} .time-block__unit {
          margin-top: {{ block.settings.timer_text_mt }}px;
          font-size: {{ block.settings.timer_text_size }}px;
        }

        .countdown-timer-{{ block.id }} .time-block__num,
        .countdown-timer-{{ block.id }} .separator {
          font-size: {{ block.settings.timer_numbers_size }}px;
        }

        .countdown-timer-{{ block.id }} .separator {
          right: -{{ block.settings.timer_numbers_size | times: 0.3 }}px;
        }

        .countdown-timer-{{ block.id }} .time-block__num {
          min-width: {{  block.settings.timer_numbers_size | times: 1.35 }}px;
        }
      }
    </style>

    {% if block.settings.timer_text_custom %}
      <style>
        .countdown-timer-{{ block.id }} .time-block__unit {
          font-family: {{ block.settings.timer_text_font.family }}, {{ block.settings.timer_text_font.fallback_families }};
          font-weight: {{ block.settings.timer_text_font.weight }};
          font-style: {{ block.settings.timer_text_font.style }};
        }
      </style>
    {% endif %}

    {% if block.settings.timer_numbers_custom %}
      <style>
        .countdown-timer-{{ block.id }} .time-block__num,
        .countdown-timer-{{ block.id }} .separator {
          font-family: {{ block.settings.timer_numbers_font.family }}, {{ block.settings.timer_numbers_font.fallback_families }};
          font-weight: {{ block.settings.timer_numbers_font.weight }};
          font-style: {{ block.settings.timer_numbers_font.style }};
        }
      </style>
    {% endif %}

  {% endif %}
{% endfor %}

<div class="section-{{ section.id }} scrolling-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="scrolling-scrolling-wrap-{{ section.id }}">
        <div class="scrolling-scrolling-list-{{ section.id }}">
          {% for item in (1..5) %}
            {% for block in section.blocks %}
              <div class="scrolling-scrolling-item-{{ section.id }} scrolling-scrolling-item-{{ block.id }}">
                {% if block.type == "text" %}

                  {% if block.settings.text != blank %}
                    <{% if block.settings.url != blank %}a href="{{ block.settings.url }}"{% else %}p{% endif %} class="scrolling-scrolling-text-{{ block.id }}">
                      {% if block.settings.stencil_all %}
                      <span class="scrolling-scrolling-stencill">{{ block.settings.text }}</span>
                      {% else %}
                        {% if block.settings.stencil_text != blank %}
                          {% assign stencil_parts = block.settings.stencil_text | split: ',' %}
                          {% assign return_text = block.settings.text %}
                          {% for item in stencil_parts %}
                            {% if return_text contains item %}
                              {% assign span_tag = '<span class="scrolling-scrolling-stencill">' | append: item  %}
                              {% assign span_tag = span_tag | append: '</span>'  %}
                              {% assign return_text = return_text | replace:  item, span_tag %}
                            {% endif %}
                          {% endfor %}
                          {{ return_text }}
                        {% else %}
                        {{ block.settings.text }}
                        {% endif %}
                      {% endif %}
                    </{% if block.settings.url != blank %}a{% else %}p{% endif %}>
                  {% endif %}

                {% elsif block.type == "button" %}

                  {% if block.settings.button != blank %}
                    <a href="{{ block.settings.button_url }}" class="scrolling-button-{{ block.id }}">
                        {{ block.settings.button }}
                        {% if block.settings.button_style == "non_outline_arrow" or block.settings.button_style == "outline_arrow" %}
