                            </svg>
                        {% endif %}
                    </a>
                  {% endif %} 

                {% elsif block.type == "image" %}

                  <div class="scrolling-image-{{ block.id }}">
                    {% if block.settings.image != blank %}
                      <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}">
                    {% else %}
                      {{ 'image' | placeholder_svg_tag }}
                    {% endif %}
                  </div>

                {% elsif block.type == "timer" %}

                  <div class="countdown-timer-{{ block.id }}">

                    <div class="time-block">
                      <div class="time-row">
                        <span class="time-block__num js-timer-days-{{ block.id }}">00</span>
                        <span class="separator">:</span>
                      </div>
                      <span class="time-block__unit">{{ block.settings.days_text }}</span>
                    </div>
                    <div class="time-block">
                      <div class="time-row">
                        <span class="time-block__num js-timer-hours-{{ block.id }}">00</span>
                        <span class="separator">:</span>
                      </div>
                      <span class="time-block__unit">{{ block.settings.hours_text }}</span>
                    </div>
                    <div class="time-block">
                      <div class="time-row">
                        <span class="time-block__num js-timer-minutes-{{ block.id }}">00</span>
                        <span class="separator">:</span>
                      </div>
                      <span class="time-block__unit">{{ block.settings.minutes_text }}</span>
                    </div>
                    <div class="time-block">
                      <div class="time-row">
                        <span class="time-block__num js-timer-seconds-{{ block.id }}">00</span>
                      </div>
                      <span class="time-block__unit">{{ block.settings.seconds_text }}</span>
                    </div>
                  </div>

                  <style>
                    .countdown-timer-completed-text-{{ block.id }} {
                      display: none;
                    }
                  </style>
                  <div
                    class="countdown-timer-completed-text-{{ block.id }}"
                  >
                    {{ block.settings.timer_completed_text }}
                  </div>

                {% endif %}
              </div>
            {% endfor %}
          {% endfor %}
        </div>
        <div class="scrolling-scrolling-list-{{ section.id }}">
          {% for item in (1..5) %}
            {% for block in section.blocks %}
              <div class="scrolling-scrolling-item-{{ section.id }} scrolling-scrolling-item-{{ block.id }}">
                {% if block.type == "text" %}

                  {% if block.settings.text != blank %}
                    <{% if block.settings.url != blank %}a href="{{ block.settings.url }}"{% else %}p{% endif %} class="scrolling-scrolling-text-{{ block.id }}">
                      {% if block.settings.stencil_all %}
                      <span class="scrolling-scrolling-stencill">{{ block.settings.text }}</span>
                      {% else %}
                        {% if block.settings.stencil_text != blank %}
                          {% assign stencil_parts = block.settings.stencil_text | split: ',' %}
                          {% assign return_text = block.settings.text %}
                          {% for item in stencil_parts %}
                            {% if return_text contains item %}
                              {% assign span_tag = '<span class="scrolling-scrolling-stencill">' | append: item  %}
                              {% assign span_tag = span_tag | append: '</span>'  %}
                              {% assign return_text = return_text | replace:  item, span_tag %}
                            {% endif %}
                          {% endfor %}
                          {{ return_text }}
                        {% else %}
                        {{ block.settings.text }}
                        {% endif %}
                      {% endif %}
                    </{% if block.settings.url != blank %}a{% else %}p{% endif %}>
                  {% endif %}

                {% elsif block.type == "button" %}

                  {% if block.settings.button != blank %}
                    <a href="{{ block.settings.button_url }}" class="scrolling-button-{{ block.id }}">
                        {{ block.settings.button }}
