<script>
  function initScrollingText8() {
    {% for block in section.blocks %}
      let timers{{ block.id }} = document.querySelectorAll('.countdown-timer-{{ block.id }}');
      timers{{ block.id }}.forEach(el => {
        const second = 1000,
          minute = second * 60,
          hour = minute * 60,
          day = hour * 24,
          wrapper = el;

        const dateStr = `{{ block.settings.year }}/{{ block.settings.month }}/{{ block.settings.day }} {{ block.settings.hour }}:{{ block.settings.minute }}:00`;
        const countDown = new Date(dateStr).getTime();

        const interval = setInterval(function() {
          const now = new Date().getTime();
          const distance = countDown - now;

          const days = Math.max(0, Math.floor(distance / day));
          const hours = Math.max(0, Math.floor((distance % day) / hour));
          const minutes = Math.max(0, Math.floor((distance % hour) / minute));
          const seconds = Math.max(0, Math.floor((distance % minute) / second));

          wrapper.querySelector('.js-timer-days-{{ block.id }}').innerText = String(days).padStart(2, '0');
          wrapper.querySelector('.js-timer-hours-{{ block.id }}').innerText = String(hours).padStart(2, '0');
          wrapper.querySelector('.js-timer-minutes-{{ block.id }}').innerText = String(minutes).padStart(2, '0');
          wrapper.querySelector('.js-timer-seconds-{{ block.id }}').innerText = String(seconds).padStart(2, '0');

          if (distance < 0) {
            wrapper.querySelector('.js-timer-days-{{ block.id }}').innerText = '00';
            wrapper.querySelector('.js-timer-hours-{{ block.id }}').innerText = '00';
            wrapper.querySelector('.js-timer-minutes-{{ block.id }}').innerText = '00';
            wrapper.querySelector('.js-timer-seconds-{{ block.id }}').innerText = '00';

            {% if block.settings.hide_timer_on_completed %}
              wrapper.style.display = 'none';
            {% endif %}

            document.querySelector('.countdown-timer-completed-text-{{ block.id }}').style.display = 'block';
            clearInterval(interval);
          }
        }, second);

        document.addEventListener('shopify:section:unload', function(event) {
          if (event.detail.sectionId === '{{ section.id }}') clearInterval(interval);
        });
      });
    {% endfor %}
}

  document.addEventListener('DOMContentLoaded', initScrollingText8);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initScrollingText8);
     document.addEventListener('shopify:section:load', initScrollingText8);
  }
</script>
