{%- style -%}
  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }
  
  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    position: relative;
  }

  .slider-title-{{ section.id }} {    
    margin: 0;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
    font-weight: 700;
    transition: all 0.4s ease 0s;
  }

  .slider-buttons-{{ section.id }} {
    position: absolute;
    z-index: 3;
    display: flex;
    align-items: center;
    gap: 15px;
    width: 100%;
    justify-content: center;
    left: 0;
    bottom: 20px;
  }

  .slider-btn-prev-{{ section.id }},
  .slider-btn-next-{{ section.id }} {
    z-index: 2;
    display: flex;
    flex-shrink: 0;
    width: {{ arrow_size_mobile }}px;
    height: {{ arrow_size_mobile }}px;
    border: 0px;
    border: {{ arrow_border_thickness }}px solid {{ arrow_border_color }};
    border-radius: {{ arrow_radius }}px;
    background: transparent;
    background: {{ arrow_bg_color }};
    align-items: center;
    justify-content: center;
    transition: all 0.3s ease 0s;
    cursor: pointer;
    -webkit-appearance: none;
    box-shadow: 0px 0px 10px 0px rgba(0,0,0,.15);
  }

  .slider-btn-prev-{{ section.id }} .animate-icon,
  .slider-btn-next-{{ section.id }} .animate-icon {
    pointer-events: none;
    place-items: center;
    display: grid;
  }

  .slider-btn-prev-{{ section.id }} .animate-icon:before,
  .slider-btn-next-{{ section.id }} .animate-icon:before {
    content: "";
    grid-area: 1 / -1;
    transition: all .2s ease;
    width: {{ arrow_icon_size_mobile }}px;
    height: {{ arrow_icon_size_mobile }}px;
    transform: rotate(calc(1 * 45deg));
    box-sizing: border-box;
    border-style: solid;
    border: 0px;
    border-right: 2px solid currentColor;
    border-top: 2px solid currentColor;
    position: relative;
    left: -1px;
  }

  .slider-btn-prev-{{ section.id }} .animate-icon:before {
    left: unset;
    right: -1px;
    border-right: 0px;
    border-top: 0px;
    border-bottom: 2px solid currentColor;
    border-left: 2px solid currentColor;
  }

  .slider-btn-prev-{{ section.id }} .animate-icon:after,
  .slider-btn-next-{{ section.id }} .animate-icon:after {
    content: "";
    grid-area: 1 / -1;
    transition: all .2s ease;
    width: {{ arrow_icon_size_mobile | minus: 0.3 }}px;
    height: 1.6px;
    transform-origin: center;
    opacity: 0;
    background: currentColor;
    transform: scaleX(.5);
  }

  .slider-btn-prev-{{ section.id }} span,
  .slider-btn-next-{{ section.id }} span {
    color: {{ arrow_color }};
  }

  .slider-btn-prev-{{ section.id }}.swiper-button-disabled,
  .slider-btn-next-{{ section.id }}.swiper-button-disabled {
    opacity: 0.5;
  }

  .slider-btn-prev-{{ section.id }}.swiper-button-lock,
  .slider-btn-next-{{ section.id }}.swiper-button-lock {
    opacity: 0;
    pointer-events: none;
  }

   .slider-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: fit-content;
    gap: 10px;
    width: 100%;
    margin: 0;
    margin-top: {{ button_mt_mobile }}px;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    background-color: transparent;
    border: 0px;
    cursor: pointer;
    font-weight: 700;
    min-width: max-content;
  }

  .slider-button-inner-{{ section.id }} {
    margin: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
  }

  .slider-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .slider-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .slider-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .slider-button-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .slider-container-{{ section.id }} {
    position: relative;
    width: 100%;
    height: {{ content_height_mobile }}px;
    border-radius: {{ image_radius }}px;
    overflow: visible;
  }

  .slider-slide-{{ section.id }} {
    position: relative;
    width: 100%;
    height: 100%;
    border-radius: {{ image_radius }}px;
  }

  .slider-item-{{ section.id }} {
    width: 140px;
    height: 170px;
    position: absolute;
    top: 70%;
    transform: translate(0, -50%);
    border-radius: {{ image_radius }}px;
    box-shadow: 0 30px 50px #dbdbdb;
    display: inline-block;
    transition: all 0.5s;
    background-color: {{ background_color }};
  }

  .slider-image-{{ section.id }} {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 1;
    border-radius: {{ image_radius }}px;
    overflow: hidden;
  }

  .slider-image-{{ section.id }}::after {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: {{ overlay_color }};
    opacity: {{ overlay_opacity | times: 0.01 }};
    pointer-events: none;
    border-radius: inherit;
    z-index: 2;
  }

  .slider-image-{{ section.id }} img,
  .slider-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .slider-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .slider-item-{{ section.id }}:nth-child(3) {
    left: 30%;
  }

  .slider-item-{{ section.id }}:nth-child(4) {
    left: calc(30% + 150px);
  }

  .slider-item-{{ section.id }}:nth-child(5) {
    left: calc(30% + 290px);
  }

  .slider-item-{{ section.id }}:nth-child(n + 6) {
    left: calc(30% + 520px);
    opacity: 0;
  }

  .slider-item-{{ section.id }}:nth-child(1),
  .slider-item-{{ section.id }}:nth-child(1) .slider-image-{{ section.id }},
  .slider-item-{{ section.id }}:nth-child(2),
  .slider-item-{{ section.id }}:nth-child(2) .slider-image-{{ section.id }} {
    top: 0;
    left: 0;
    transform: translate(0, 0);
    border-radius: {{ image_radius }}px;
    width: 100%;
    height: 100%;
    transition: all 0.5s;
  }

  .slider-content-{{ section.id }} {
    position: absolute;
    top: 15%;
    padding: 20px;
    width: 100%;
    text-align: left;
    transform: translate(0, -15%);
    display: none;
    z-index: 2;
  }

  .slider-item-{{ section.id }}:nth-child(2) .slider-content-{{ section.id }} {
    display: block;
  }

  .slider-item-{{ section.id }}:nth-child(2) .slider-title-{{ section.id }} {
    opacity: 0;
    animation: slideAnimate 0.5s ease-in-out 0s 1 forwards;
  }

  .slider-item-{{ section.id }}:nth-child(2) .slider-text-{{ section.id }} {
    opacity: 0;
    animation: slideAnimate 0.5s ease-in-out 0.3s 1 forwards;
  }

  .slider-item-{{ section.id }}:nth-child(2) .slider-button-{{ section.id }} {
    opacity: 0;
    animation: slideAnimate 0.5s ease-in-out 0.6s 1 forwards;
  }

  @keyframes slideAnimate {
    from {
      opacity: 0;
      transform: translate(0, 100px);
      filter: blur(33px);
    }
    to {
      opacity: 1;
      transform: translate(0);
      filter: blur(0);
    }
  }

  .slider-text-{{ section.id }} * {
    margin-top: {{ text_mt_mobile }}px;
    color: {{ text_color }};
    font-size: {{ text_size_mobile }}px;
    line-height: {{ text_height }}%;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }
    
    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .slider-container-{{ section.id }} {
      height: {{ content_height }}px;
    }

    .slider-text-{{ section.id }} * {
      margin-top: {{ text_mt }}px;
      font-size: {{ text_size }}px;
    }

    .slider-title-{{ section.id }} {  
      font-size: {{ title_size }}px;
    }

    .slider-content-{{ section.id }} {
      top: 50%;
      left: 30px;
      width: 45%;
      transform: translate(0, -50%);
    }

    .slider-content-{{ section.id }} .slider-button-{{ section.id }} {
      opacity: 0;
      transition: all 0.3s ease 0s;
    }

    .slider-btn-prev-{{ section.id }},
    .slider-btn-next-{{ section.id }} {
      width: {{ arrow_size }}px;
      height: {{ arrow_size }}px;
      transition: all 0.25s ease 0s;
    }

    .slider-btn-prev-{{ section.id }} .animate-icon:before,
    .slider-btn-next-{{ section.id }} .animate-icon:before {
      width: {{ arrow_icon_size }}px;
      height: {{ arrow_icon_size }}px;
    }

    .slider-btn-prev-{{ section.id }} .animate-icon:after,
    .slider-btn-next-{{ section.id }} .animate-icon:after {
      width: {{ arrow_icon_size | minus: 0.3 }}px;
    }

    .slider-buttons-{{ section.id }} {
      bottom: 30px;
    }

    .slider-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }    

    .slider-item-{{ section.id }} {
      width: 200px;
      height: 300px;
      top: 50%;
    }
    
    .slider-item-{{ section.id }}:nth-child(3) {
      left: 55%;
    }

    .slider-item-{{ section.id }}:nth-child(4) {
      left: calc(55% + 220px);
    }

    .slider-item-{{ section.id }}:nth-child(5) {
      left: calc(55% + 440px);
    }

    .slider-item-{{ section.id }}:nth-child(n + 6) {
      left: calc(55% + 660px);
      opacity: 0;
    }
  }

  {% if section.blocks.size == 1 %}
    .slider-item-{{ section.id }} .slider-content-{{ section.id }},
    .slider-item-{{ section.id }} .slider-button-{{ section.id }} {
      display: block !important;
      opacity: 1 !important;
      animation: none !important;
      filter: none !important;
    }
  {% endif %}
    
{%- endstyle -%}

{% if width == 'custom' %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% elsif width == 'page' %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: var(--page-width);
    }
  </style>
{% endif %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }
    
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    {{ title_font | font_face: font_display: 'swap' }}
    .slider-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    {{ text_font | font_face: font_display: 'swap' }}

    .slider-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    {{  button_font | font_face: font_display: 'swap' }}
    
    .slider-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if button_style == "non_outline" or button_style == "non_outline_arrow" %}
  <style>
    .slider-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
    }

    .slider-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
    }
  </style>
{% elsif button_style == "outline" or button_style == "outline_arrow" %}
  <style>
    .slider-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_color }};
    }

    .slider-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    }
  </style>
{% elsif button_style == "link" %}
  <style>
    .slider-button-{{ section.id }} {
      padding: 0px !important;
    }
  </style>
{% endif %}

{% if arrow_hover_effect == "color" %}
  <style>
    .slider-btn-prev-{{ section.id }}:hover,
    .slider-btn-next-{{ section.id }}:hover {
      transition: all 0.3s ease 0s;
      border: {{ arrow_border_thickness }}px solid {{ arrow_border_hover_color }};
      background: {{ arrow_bg_hover_color }};
    }
  
    .slider-btn-prev-{{ section.id }}:hover .animate-icon,
    .slider-btn-next-{{ section.id }}:hover .animate-icon {
      color: {{ arrow_hover_color }};
    }
  </style>
{% elsif arrow_hover_effect == "scale" %}
  <style>
    .slider-btn-prev-{{ section.id }}:hover,
    .slider-btn-next-{{ section.id }}:hover {
      transform: scale(0.9);
    }

    @media(min-width: 1024px) {
      .slider-btn-prev-{{ section.id }}:hover,
      .slider-btn-next-{{ section.id }}:hover {
        transform: scale(0.9);
      }
    }
  </style>
{% elsif arrow_hover_effect == "arrow" %}
  <style>
    .slider-btn-next-{{ section.id }}:hover .animate-icon:before {
      transform: translate(calc(1 * 0.125rem)) rotate(calc(1 * 45deg));
    }
  
    .slider-btn-prev-{{ section.id }}:hover .animate-icon:before {
      transform: translate(calc(-1 * 0.125rem)) rotate(calc(1 * 45deg));
    }
    
    .slider-btn-prev-{{ section.id }}:hover .animate-icon:after,
    .slider-btn-next-{{ section.id }}:hover .animate-icon:after {
      opacity: 1;
      transform: scaleX(1);
    }
  </style>
{% endif %}



