<script>
function initSlider16() {
  let container = document.querySelector(".slider-slide-{{ section.id }}");

  function fixDuplicates() {
    let items = Array.from(container.querySelectorAll(".slider-item-{{ section.id }}"));

    for (let i = 0; i < items.length - 1; i++) {
      let current = items[i];
      let next = items[i + 1];

      if (current.dataset.id === next.dataset.id) {
        let ids = items.map(e => e.dataset.id);
        let unique = [...new Set(ids)];

        let currentIndex = unique.indexOf(current.dataset.id);

        let nextId = unique[(currentIndex + 1) % unique.length];

        let original = items.find(el => el.dataset.id == nextId);
        if (original) {
          let clone = original.cloneNode(true);
          next.replaceWith(clone);
        }
      }
    }
  }

  if (container && !container.dataset.cloneAdded) {

    let items = container.querySelectorAll(".slider-item-{{ section.id }}");

    if (items.length < 5) {
      let prevIndex = 0;
      let clone = items[prevIndex].cloneNode(true);
      container.appendChild(clone);
    }

    container.dataset.cloneAdded = "true";
  }

  let next = document.querySelector(".slider-btn-next-{{ section.id }}");
  let prev = document.querySelector(".slider-btn-prev-{{ section.id }}");

  if (next) {
    next.addEventListener("click", function () {
      let items = container.querySelectorAll(".slider-item-{{ section.id }}");
      container.appendChild(items[0]);
      fixDuplicates();
    });
  }

  prev.addEventListener("click", function () {
  let items = Array.from(container.querySelectorAll(".slider-item-{{ section.id }}"));

  let first = items[0];
  let firstId = first.dataset.id;

  let unique = [...new Set(items.map(i => i.dataset.id))];

  let index = unique.indexOf(firstId);

  let prevId = unique[(index - 1 + unique.length) % unique.length];

  let target = items.find(i => i.dataset.id === prevId);

  let clone = target.cloneNode(true);

  container.prepend(clone);

  items[items.length - 1].remove();

  fixDuplicates();
});
}

document.addEventListener("DOMContentLoaded", initSlider16);

if (Shopify.designMode) {
  document.addEventListener("shopify:section:load", initSlider16);
  document.addEventListener("shopify:section:unload", () => {});
}
</script>
