{%- liquid 
  assign vertical_position = section.settings.vertical_position
  assign horizontal_position = section.settings.horizontal_position
  assign horizontal_dst_to_edge = section.settings.horizontal_dst_to_edge
  assign horizontal_dst_to_edge_mobile = section.settings.horizontal_dst_to_edge_mobile
  assign vertical_dst_to_edge = section.settings.vertical_dst_to_edge
  assign vertical_dst_to_edge_mobile = section.settings.vertical_dst_to_edge_mobile

  assign hide_on_mobile = section.settings.hide_on_mobile

  assign body_padding_horizontal = section.settings.body_padding_horizontal
  assign body_padding_horizontal_mobile = section.settings.body_padding_horizontal_mobile
  assign body_padding_vertical = section.settings.body_padding_vertical
  assign body_padding_vertical_mobile = section.settings.body_padding_vertical_mobile
  assign body_gap = section.settings.body_gap
  assign body_gap_mobile = section.settings.body_gap_mobile
  assign body_radius = section.settings.body_radius
  assign body_border_thickness = section.settings.body_border_thickness
  assign body_border_color = section.settings.body_border_color
  assign body_bg_color = section.settings.body_bg_color
  assign use_shadow = section.settings.use_shadow

  assign socials_gap = section.settings.socials_gap
  assign socials_gap_mobile = section.settings.socials_gap_mobile

  assign facebook_url = section.settings.facebook_url
  assign twitter_url = section.settings.twitter_url
  assign instagram_url = section.settings.instagram_url
  assign youtube_url = section.settings.youtube_url
  assign linkedin_url = section.settings.linkedin_url
  assign tiktok_url = section.settings.tiktok_url
  assign whatsapp_url = section.settings.whatsapp_url

  assign icon_size = section.settings.icon_size
  assign icon_size_mobile = section.settings.icon_size_mobile
  assign icon_color = section.settings.icon_color

  assign button = section.settings.button
  assign button_url = section.settings.button_url
  assign button_size = section.settings.button_size
  assign button_size_mobile = section.settings.button_size_mobile
  assign button_color = section.settings.button_color
  assign button_hover_color = section.settings.button_hover_color
  assign button_custom = section.settings.button_custom
  assign button_font = section.settings.button_font
  assign button_height = section.settings.button_height
  assign button_padding_vertical = section.settings.button_padding_vertical
  assign button_padding_vertical_mobile = section.settings.button_padding_vertical_mobile
  assign button_padding_horizontal = section.settings.button_padding_horizontal
  assign button_padding_horizontal_mobile = section.settings.button_padding_horizontal_mobile
  assign button_radius = section.settings.button_radius
  assign button_border_thickness = section.settings.button_border_thickness
  assign button_border_color = section.settings.button_border_color
  assign button_border_hover_color = section.settings.button_border_hover_color
  assign button_bg_color = section.settings.button_bg_color
  assign button_bg_hover_color = section.settings.button_bg_hover_color
  assign button_style = section.settings.button_style
-%}

{%- style -%}
  {{  button_font | font_face: font_display: 'swap' }}

  .bar-{{ section.id }} {
    position: fixed;
    z-index: 3;
  }

  .bar-body-{{ section.id }} {
    display: flex;
    align-items: center;
    flex-direction: column;
    max-width: fit-content;
    gap: {{  body_gap_mobile }}px;
    background-color: {{ body_bg_color | hex_to_rgba: 0.8 }};
    border: {{  body_border_thickness }}px solid {{ body_border_color }};
    padding: {{ body_padding_vertical_mobile }}px {{ body_padding_horizontal_mobile }}px;
    border-radius: {{ body_radius }}px;
    -webkit-backdrop-filter: blur(6px);
    backdrop-filter: blur(6px);
  }

  .bar-socials-{{ section.id }} {
    display: flex;
    align-items: center;
    flex-direction: column;
    gap: {{ socials_gap_mobile }}px;
  }

  .bar-social-icon-{{ section.id }} {
    width: {{ icon_size_mobile }}px;
    height: {{ icon_size_mobile }}px;
    margin: 10px;
  }

  .bar-social-icon-{{ section.id }} img,
  .bar-social-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .bar-social-icon-{{ section.id }} svg {
    fill: {{ icon_color }}
  }

  .bar-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    max-width: fit-content;
    width: 100%;
    margin: 0;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    background-color: transparent;
    border: 0px;
    cursor: pointer;
    writing-mode: vertical-rl;
    transform: rotate(180deg);
  }

  .bar-button-inner-{{ section.id }} {
    margin: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
  }

  .bar-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .bar-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .bar-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .bar-button-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .bar-body-{{ section.id }} {
      gap: {{  body_gap }}px;
      padding: {{ body_padding_vertical }}px {{ body_padding_horizontal }}px;
    }

    .bar-socials-{{ section.id }} {
      gap: {{ socials_gap }}px;
    }

    .bar-social-icon-{{ section.id }} {
      width: {{ icon_size }}px;
      height: {{ icon_size }}px;
    }

    .bar-button-{{ section.id }} {
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .bar-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if button_style == "non_outline" or button_style == "non_outline_arrow" %}
  <style>
    .bar-button-{{ section.id }} {
      background-color: {{ button_bg_color | hex_to_rgba: 0.5 }};
    }

    .bar-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color | hex_to_rgba: 0.5 }};
    }
  </style>
{% elsif button_style == "outline" or button_style == "outline_arrow" %}
  <style>
    .bar-button-{{ section.id }} {
      background-color: {{ button_bg_color | hex_to_rgba: 0.5 }};
      border: {{ button_border_thickness }}px solid {{ button_border_color }};
    }

    .bar-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color | hex_to_rgba: 0.5 }};
      border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    }
  </style>
{% elsif button_style == "link" %}
  <style>
    .bar-button-{{ section.id }} {
      padding: 0px !important;
    }
  </style>
{% endif %}

{% if vertical_position == "top" %}
  <style>
    .bar-{{ section.id }} {
      top: {{ vertical_dst_to_edge_mobile }}px;
    }    

    @media(min-width: 1024px) {
      .bar-{{ section.id }} {
        top: {{ vertical_dst_to_edge }}px;
      }
    }
  </style>
{% elsif vertical_position == "center" %}
  <style>
    .bar-{{ section.id }} {
      top: 50%;
      transform: translateY(-50%);
    }

    @media(min-width: 1024px) {
      .bar-{{ section.id }} {
        top: 50%;
      }
    }
  </style>
{% elsif vertical_position == "bottom" %}
  <style>
    .bar-{{ section.id }} {
      bottom: {{ vertical_dst_to_edge_mobile }}px;
    }

    @media(min-width: 1024px) {
      .bar-{{ section.id }} {
        bottom: {{ vertical_dst_to_edge }}px;
      }
    }
  </style>
{% endif %}  

{% if horizontal_position == "left" %}
  <style>
    .bar-{{ section.id }} {
      left: {{ horizontal_dst_to_edge_mobile }}px;
    }

    @media(min-width: 1024px) {
      .bar-{{ section.id }} {
        left: {{ horizontal_dst_to_edge }}px;
      }
    }
  </style>
{% elsif horizontal_position == "right" %}
  <style>
    .bar-{{ section.id }} {
      right: {{ horizontal_dst_to_edge_mobile }}px;
    }

    @media(min-width: 1024px) {
      .bar-{{ section.id }} {
        right: {{ horizontal_dst_to_edge }}px;
      }
    }
  </style>
{% endif %}

{% if use_shadow %}
  <style>
    .bar-body-{{ section.id }} {
      box-shadow: 0 0 1px 1px #dbdbdb;
    }
  </style>
{% endif %}

{% if hide_on_mobile %}
  <style>
    .bar-{{ section.id }} {
      display: none;      
    }

    @media(min-width: 1024px) {
      .bar-{{ section.id }} {
        display: block;      
      }
    }
  </style>
