liquid_code:
{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign layout = section.settings.layout
  assign layout_mobile = section.settings.layout_mobile
  assign layout_gap = section.settings.layout_gap
  assign layout_gap_mobile = section.settings.layout_gap_mobile

  assign image = section.settings.image
  assign image_ratio = section.settings.image_ratio
  assign image_ratio_mobile = section.settings.image_ratio_mobile
  assign image_radius = section.settings.image_radius
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color

  assign steps_size = section.settings.steps_size
  assign steps_size_mobile = section.settings.steps_size_mobile
  assign steps_custom = section.settings.steps_custom
  assign steps_font = section.settings.steps_font
  assign steps_height = section.settings.steps_height
  assign steps_color = section.settings.steps_color
  assign steps_radius = section.settings.steps_radius
  assign steps_border_thickness = section.settings.steps_border_thickness
  assign steps_border_color = section.settings.steps_border_color
  assign steps_bg_color = section.settings.steps_bg_color
  assign steps_padding_horizontal = section.settings.steps_padding_horizontal
  assign steps_padding_horizontal_mobile = section.settings.steps_padding_horizontal_mobile
  assign steps_padding_vertical = section.settings.steps_padding_vertical
  assign steps_padding_vertical_mobile = section.settings.steps_padding_vertical_mobile

  assign steps_content_align = section.settings.steps_content_align
  assign steps_content_align_mobile = section.settings.steps_content_align_mobile

  assign title_size = section.settings.title_size
  assign title_size_mobile = section.settings.title_size_mobile
  assign title_custom = section.settings.title_custom
  assign title_font = section.settings.title_font
  assign title_height = section.settings.title_height
  assign title_color = section.settings.title_color

  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_height = section.settings.text_height
  assign text_color = section.settings.text_color
  assign text_mt = section.settings.text_mt
  assign text_mt_mobile = section.settings.text_mt_mobile

  assign lazy_load_setting = 'lazy'
  unless lazy
    assign lazy_load_setting = 'eiger'
  endunless
-%}

{%- style -%}
  {{  steps_font | font_face: font_display: 'swap' }}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}
  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .steps-body-{{ section.id }} {
    display: grid;
    align-items: center;
    grid-template-columns: 1fr;
    gap: {{ layout_gap_mobile }}px;
  }

  .steps-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    justify-content: center;
    gap: 40px;
    position: relative;
  }

  .steps-image-{{ section.id }} {
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    border-radius: {{ image_radius }}px;
    overflow: hidden;
  }

  .steps-image-{{ section.id }} img,
  .steps-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .steps-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .steps-item-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 30px;
  }

  .steps-step-{{ section.id }} {
    width: 80px;
    text-align: center;
    position: relative;
    z-index: 2;
    padding: {{ steps_padding_vertical_mobile }}px {{ steps_padding_horizontal_mobile }}px;
    border: {{ steps_border_thickness }}px solid {{ steps_border_color }};
    border-radius: {{ steps_radius }}px;
    background-color: {{ steps_bg_color }};
    margin: 0;  
    font-size: {{ steps_size_mobile }}px;
    color: {{ steps_color }};
    line-height: {{ steps_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .steps-item-content-{{ section.id }} {
    flex: 1 1 0;
  }

  .steps-title-{{ section.id }} {
    margin: 0;  
    text-align: {{ steps_content_align_mobile }};
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    font-weight: 700;
    text-transform: unset;
    text-decoration: none;
  }

  .steps-text-{{ section.id }} {
    margin-top: {{ text_mt_mobile }}px;  
    text-align: {{ steps_content_align_mobile }};
  }

  .steps-text-{{ section.id }} * {
    margin: 0; 
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .steps-border-{{ section.id }} {
    left: 40px;
    display: block !important;
    position: absolute;
    background-image: repeating-linear-gradient({{ steps_border_color }}, {{ steps_border_color }} 6px, transparent 6px, transparent calc(9px));
    background-size: 1px 100%;
    background-repeat: no-repeat;
    width: {{ steps_border_thickness }}px;
    height: 100%;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .steps-body-{{ section.id }} {
      grid-template-columns: 1fr 1fr;
      gap: {{ layout_gap }}px;
    }

    .steps-step-{{ section.id }} {
      padding: {{ steps_padding_vertical }}px {{ steps_padding_horizontal }}px;  
      font-size: {{ steps_size }}px;
    }

    .steps-title-{{ section.id }} {
      font-size: {{ title_size }}px;
      text-align: {{ steps_content_align }};
    }

    .steps-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ steps_content_align }};
    }

    .steps-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if steps_custom %}
  <style>
    .steps-step-{{ section.id }} {
      font-family: {{ steps_font.family }}, {{ steps_font.fallback_families }};
      font-weight: {{ steps_font.weight }};
      font-style: {{ steps_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .steps-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .steps-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}


{% if image_ratio_mobile == "portrait" %}
  <style>
    .steps-image-{{ section.id }} {
      aspect-ratio: 9.6/13;
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
  .steps-image-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .steps-image-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .steps-image-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
      .steps-image-{{ section.id }} {
        aspect-ratio: 9.6/13;
      }
    }

  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .steps-image-{{ section.id }} {
        aspect-ratio: 12/8;
      }
    }  
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .steps-image-{{ section.id }} {
        aspect-ratio: 12/12;
      }
    }    
  </style>
{% else %}
  <style>
     @media(min-width: 1024px) {
      .steps-image-{{ section.id }} {
        aspect-ratio: auto;
      }
    }
  </style>
{% endif %}


{% if layout_mobile == "image_content" %}
  <style>
    .steps-image-{{ section.id }} {
      order: 1;
    }

    .steps-content-{{ section.id }} {
      order: 2;
    }
  </style>
{% else %} 
  {% if layout_mobile == "content_image" %}
    <style>
      .steps-image-{{ section.id }} {
        order: 2;
      }

      .steps-content-{{ section.id }} {
        order: 1;
      }
    </style>
  {% endif %}  
{% endif %}

{% if layout == "image_content" %}
  <style>
    @media (min-width: 1024px) {
      .steps-image-{{ section.id }} {
        order: 1;
      }

      .steps-content-{{ section.id }} {
        order: 2;
      }
    }
  </style>
{% else %}  
  {% if layout == "content_image" %}
    <style>
      @media (min-width: 1024px) {
        .steps-image-{{ section.id }} {
          order: 2;
        }

        .steps-content-{{ section.id }} {
          order: 1;
        }
      }
    </style>
  {% endif %}  
{% endif %}


<div class="section-{{ section.id }} steps-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="steps-body-{{ section.id }}">
        <div class="steps-image-{{ section.id }}">
          {% if image != blank %}
           {%- capture sizes -%}(min-width: {{ content_width }}px) {{ content_width | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
            {{ image | image_url: width: 1200 | image_tag:
              loading: lazy_load_setting,
              width: image.width,
              height: image.height,
              sizes: sizes,
              widths: '360, 535, 750, 1070, 1500'
            }}
          {% else %}  
            {{ 'image' | placeholder_svg_tag }}
          {% endif %}
        </div>
        <div class="steps-content-{{ section.id }}">
          <div class="steps-border-{{ section.id }}"></div>
          {% for block in section.blocks %}
            <div class="steps-item-{{ section.id }}">
              {% if block.settings.step != blank %}
                <p class="steps-step-{{ section.id }}">{{ block.settings.step }}</p>
              {% endif %}
              <div class="steps-item-content-{{ section.id }}">
                {% if block.settings.title != blank %}
                  <p class="steps-title-{{ section.id }}">{{ block.settings.title }}</p>
                {% endif %}
                {% if block.settings.text != blank %}
                  <div class="steps-text-{{ section.id }}">{{ block.settings.text }}</div>
                {% endif %}
              </div>
            </div>
          {% endfor %}
        </div>
      </div>
    </div>
</div>
