liquid_code:
{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width

  assign heading = section.settings.heading
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_color = section.settings.heading_color
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_height = section.settings.heading_height
  assign heading_align = section.settings.heading_align
  assign heading_align_mobile = section.settings.heading_align_mobile

  assign body_gap_mobile = section.settings.body_gap_mobile
  assign body_gap = section.settings.body_gap
  assign body_mt_mobile = section.settings.body_mt_mobile
  assign body_mt = section.settings.body_mt

  assign steps_border_color = section.settings.steps_border_color

  assign content_align = section.settings.content_align
  assign content_maxwidth = section.settings.content_maxwidth

  assign number_size = section.settings.number_size
  assign number_size_mobile = section.settings.number_size_mobile
  assign number_width = section.settings.number_width
  assign number_width_mobile = section.settings.number_width_mobile
  assign number_color = section.settings.number_color
  assign number_custom = section.settings.number_custom
  assign number_font = section.settings.number_font
  assign number_height = section.settings.number_height
  assign number_border_thickness = section.settings.number_border_thickness
  assign number_border_color = section.settings.number_border_color
  assign number_bg_color = section.settings.number_bg_color

  assign title_size = section.settings.title_size
  assign title_size_mobile = section.settings.title_size_mobile
  assign title_color = section.settings.title_color
  assign title_custom = section.settings.title_custom
  assign title_font = section.settings.title_font
  assign title_height = section.settings.title_height
  assign title_mt = section.settings.title_mt
  assign title_mt_mobile = section.settings.title_mt_mobile

  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_color = section.settings.text_color
  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_height = section.settings.text_height
  assign text_mt = section.settings.text_mt
  assign text_mt_mobile = section.settings.text_mt_mobile

  assign text_align = ""
  if content_align == "center"
    assign text_align = "center"
  elsif content_align == "end"
    assign text_align = "right"
  elsif content_align == "start"
    assign text_align = "left"
  endif
-%}

{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  number_font | font_face: font_display: 'swap' }}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .steps-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .steps-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .steps-body-{{ section.id }} {
    display: grid;
    margin-top: {{ body_mt_mobile }}px;
    position: relative;
  }

  .steps-item-{{ section.id }} {
    position: relative;
    display: flex;
    flex-direction: row;
    align-items: center;
    gap: 24px;
    overflow: hidden;
    padding: {{ body_gap_mobile }}px 0px;
  }

  .steps-item-{{ section.id }}:after {
    content: '';
    position: absolute;
    background-color: {{ steps_border_color }};
    z-index: 1;
    height: 100%;
    width: 2px;
    top: 0;
    left: {{ number_width_mobile | times: 0.5 | minus: 1 }}px;
  }

  .steps-item-{{ section.id }}:first-child:after {
    top: 50%;
  }

  .steps-item-{{ section.id }}:last-child:after {
    height: 50%;
  }

  .steps-row-{{ section.id }} {
    display: flex;
    flex-direction: column;
  }

  .steps-number-{{ section.id }} {
    flex: 0 0 {{ number_width_mobile }}px;
    height: {{ number_width_mobile }}px;
    display: flex;
    align-items: center;
    justify-content: center;
    text-align: center;
    border-radius: 50%;
    border: {{ number_border_thickness }}px solid {{ number_border_color }};
    margin: 0;
    font-weight: 700;
    font-size: {{ number_size_mobile }}px;
    color: {{ number_color }};
    line-height: {{ number_height }}%;
    text-transform: unset;
    background-color: {{ number_bg_color }};
    position: relative;
    z-index: 2;
  }

  .steps-title-{{ section.id }} {
    margin: 0;
    margin-top: {{ title_mt_mobile }}px;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .steps-text-{{ section.id }} {
    margin: 0;
    margin-top: {{ text_mt_mobile }}px;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .steps-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .steps-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .steps-body-{{ section.id }} {
      grid-template-columns: repeat({{ section.blocks.size }}, 1fr);
      margin-top: {{ body_mt }}px;
    }

    .steps-item-{{ section.id }} {
      padding: 0px {{ body_gap }}px;
      gap: 0px;
      flex-direction: column;
      align-items: {{ content_align }};
    }

    .steps-item-{{ section.id }}:first-child {
      padding-left: 0px;
    }

    .steps-item-{{ section.id }}:last-child {
      padding-right: 0px;
    }

    .steps-row-{{ section.id }} {
      align-items: {{ content_align }};
    }

    .steps-number-{{ section.id }} {
      width: {{ number_width }}px;
      flex: 0 0 {{ number_width }}px;
      font-size: {{ number_size }}px;
    }

    .steps-title-{{ section.id }} {
      max-width: {{ content_maxwidth }}px;
      margin-top: {{ title_mt }}px;
      font-size: {{ title_size }}px;
      text-align: {{ text_align }};
    }

    .steps-text-{{ section.id }} {
      max-width: {{ content_maxwidth }}px;
      margin-top: {{ text_mt }}px;
      text-align: {{ text_align }};
      font-size: {{ text_size }}px;
    }

  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .steps-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if number_custom %}
  <style>
    .steps-number-{{ section.id }} {
      font-family: {{ number_font.family }}, {{ number_font.fallback_families }};
      font-weight: {{ number_font.weight }};
      font-style: {{ number_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .steps-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .steps-text-{{ section.id }} {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if content_align == "start" %}
  <style>

    @media(min-width: 1024px) {
      .steps-item-{{ section.id }}:after {
        left: 0 !important;
        top: {{ number_width | times: 0.5 | minus: 1 }}px !important;
        width: 100% !important;
        height: 2px !important;
      }

      .steps-item-{{ section.id }}:last-child:after {
        width: {{ body_gap }}px !important;
      }
    }

  </style>
{% elsif content_align == "center" %}
  <style>

    @media(min-width: 1024px) {
      .steps-item-{{ section.id }}:after {
        left: 0 !important;
        top: {{ number_width | times: 0.5 | minus: 1 }}px !important;
        width: 100% !important;
        height: 2px !important;
      }

      .steps-item-{{ section.id }}:first-child:after {
        left: 50% !important;
        top: {{ number_width | times: 0.5 | minus: 1 }}px !important;
      }

      .steps-item-{{ section.id }}:last-child:after {
        width: 50% !important;
        height: 2px !important;
      }
    }

  </style>
{% else %}
  <style>

    @media(min-width: 1024px) {
      .steps-item-{{ section.id }}:after {
        left: 0 !important;
        top: {{ number_width | times: 0.5 | minus: 1 }}px !important;
        width: 100% !important;
        height: 2px !important;
      }

      .steps-item-{{ section.id }}:first-child:after {
        left: calc( 100% - {{ body_gap }}px ) !important;
        width: {{ body_gap }}px !important;
      }
    }

  </style>
{% endif %}

<div class="section-{{ section.id }} steps-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      {% if heading != blank %}
        <div class="steps-heading-{{ section.id }}">
          {{ heading }}
        </div>
      {% endif %}
      <div class="steps-body-{{ section.id }}">
        {% for block in section.blocks %}
          <div class="steps-item-{{ section.id }}">

            <div class="steps-number-{{ section.id }}">
              {{ forloop.index }}
            </div>

            <div class="steps-row-{{ section.id }}">

              {% if block.settings.title != blank %}
                <p class="steps-title-{{ section.id }}">{{ block.settings.title }}</p>
              {% endif %}

              {% if block.settings.text != blank %}
                <p class="steps-text-{{ section.id }}">{{ block.settings.text }}</p>
              {% endif %}

            </div>

          </div>
        {% endfor %}
      </div>
    </div>
</div>
