liquid_code:
{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign layout = section.settings.layout
  assign layout_mobile = section.settings.layout_mobile
  assign layout_gap = section.settings.layout_gap
  assign layout_gap_mobile = section.settings.layout_gap_mobile

  assign image_ratio = section.settings.image_ratio
  assign image_ratio_mobile = section.settings.image_ratio_mobile
  assign image_radius = section.settings.image_radius
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color

  assign content_align = section.settings.content_align
  assign content_align_mobile = section.settings.content_align_mobile
  assign content_vertical_align  = section.settings.content_vertical_align

  assign content_horizontal_align = ""
  if content_align == "center"
    assign content_horizontal_align = "center"
  elsif content_align == "right"
    assign content_horizontal_align = "end"
  else
    assign content_horizontal_align = "start"
  endif

  assign content_horizontal_align_mobile = ""
  if content_align_mobile == "center"
    assign content_horizontal_align_mobile = "center"
  elsif content_align_mobile == "right"
    assign content_horizontal_align_mobile = "end"
  else
    assign content_horizontal_align_mobile = "start"
  endif

  assign number_size = section.settings.number_size
  assign number_size_mobile = section.settings.number_size_mobile
  assign number_custom = section.settings.number_custom
  assign number_font = section.settings.number_font
  assign number_height = section.settings.number_height
  assign number_color = section.settings.number_color
  assign number_padding_horizontal = section.settings.number_padding_horizontal
  assign number_padding_horizontal_mobile = section.settings.number_padding_horizontal_mobile
  assign number_padding_vertical = section.settings.number_padding_vertical
  assign number_padding_vertical_mobile = section.settings.number_padding_vertical_mobile
  assign number_radius = section.settings.number_radius
  assign number_bg_color = section.settings.number_bg_color
  assign number_border_thickness = section.settings.number_border_thickness
  assign number_border_color = section.settings.number_border_color

  assign title_size = section.settings.title_size
  assign title_size_mobile = section.settings.title_size_mobile
  assign title_custom = section.settings.title_custom
  assign title_font = section.settings.title_font
  assign title_height = section.settings.title_height
  assign title_color = section.settings.title_color
  assign title_mt = section.settings.title_mt
  assign title_mt_mobile = section.settings.title_mt_mobile

  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_height = section.settings.text_height
  assign text_color = section.settings.text_color
  assign text_mt = section.settings.text_mt
  assign text_mt_mobile = section.settings.text_mt_mobile

  assign nav_mt = section.settings.nav_mt
  assign nav_mt_mobile = section.settings.nav_mt_mobile

  assign step_size = section.settings.step_size
  assign step_size_mobile = section.settings.step_size_mobile
  assign step_custom = section.settings.step_custom
  assign step_font = section.settings.step_font
  assign step_height = section.settings.step_height
  assign step_color = section.settings.step_color
  assign step_mt = section.settings.step_mt
  assign step_mt_mobile = section.settings.step_mt_mobile

  assign arrow_size = section.settings.arrow_size
  assign arrow_size_mobile = section.settings.arrow_size_mobile
  assign arrow_icon_size = section.settings.arrow_icon_size
  assign arrow_icon_size_mobile = section.settings.arrow_icon_size_mobile
  assign arrow_color = section.settings.arrow_color
  assign arrow_hover_color = section.settings.arrow_hover_color
  assign arrow_bg_color = section.settings.arrow_bg_color
  assign arrow_bg_hover_color = section.settings.arrow_bg_hover_color
  assign arrow_radius = section.settings.arrow_radius
  assign arrow_border_thickness = section.settings.arrow_border_thickness
  assign arrow_border_color = section.settings.arrow_border_color
  assign arrow_border_hover_color = section.settings.arrow_border_hover_color
  assign arrow_show_desktop = section.settings.arrow_show_desktop
  assign arrow_show_mobile = section.settings.arrow_show_mobile
  assign arrow_hover_effect = section.settings.arrow_hover_effect
-%}

{%- style -%}
  {{  number_font | font_face: font_display: 'swap' }}  
  {{  title_font | font_face: font_display: 'swap' }}  
  {{  text_font | font_face: font_display: 'swap' }}  
  {{  step_font | font_face: font_display: 'swap' }}  

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .steps-item-{{ section.id }} {
    display: none;
    flex-direction: column;
    gap: {{ layout_gap_mobile }}px;
  }

  .steps-item-{{ section.id }}.active {
    display: flex;
    -webkit-animation: opacity{{ section.id | replace: '-', '' }} 0.5s linear;
    animation: opacity{{ section.id | replace: '-', '' }} 0.5s linear;    
  }

  @keyframes opacity{{ section.id | replace: '-', '' }} {
    0% {
      opacity: 0;
    }
    100% {
      opacity: 1;
    }
  }

  .steps-image-{{ section.id }} {
    overflow: hidden;
    flex: auto;
    border-radius: {{ image_radius }}px;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
  }

  .steps-image-{{ section.id }} img,
  .steps-image-{{ section.id }} svg {
    display: inline-block;
    width: 100%;
    height: 100%;
    object-fit: cover
  }

  .steps-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .steps-content-{{ section.id }} {
    flex: 1 1 0;
    display: flex;
    flex-direction: column;
    justify-content: {{ content_vertical_align }};
    align-items: {{ content_horizontal_align_mobile }};
  }

  .steps-number-{{ section.id }} {
    margin: 0px;
    max-width: fit-content;
    text-align: {{ content_align_mobile }};
    font-size: {{ number_size_mobile }}px;
    line-height: {{ number_height }}%;
    color: {{ number_color }};
    text-transform: unset;
    word-break: break-word;
    padding: {{ number_padding_vertical_mobile }}px {{ number_padding_horizontal_mobile }}px;
    border: {{ number_border_thickness }}px solid {{ number_border_color }};
    border-radius: {{ number_radius }}px;
    background-color: {{ number_bg_color }};
    font-weight: 700;
  }

  .steps-title-{{ section.id }} {
    margin: 0px;
    font-size: {{ title_size_mobile }}px;
    line-height: {{ title_height }}%;
    color: {{ title_color }};
    text-transform: unset;
    word-break: break-word;
    text-align: {{ content_align_mobile }};
    margin-top: {{ title_mt_mobile }}px;
    font-weight: 700;
  }

  .steps-text-{{ section.id }} {
    text-align: {{ content_align_mobile }};
    margin-top: {{ text_mt_mobile }}px;
  }

  .steps-text-{{ section.id }} * {
    margin: 0px;
    font-size: {{ text_size_mobile }}px;
    line-height: {{ text_height }}%;
    color: {{ text_color }};
    text-transform: unset;
    word-break: break-word;
  }

  .steps-nav-body-{{ section.id }} {
    display: flex;
    align-items: center;
    flex-direction: column;
    gap: 20px;
    justify-content: space-between;
    margin-top: {{ nav_mt_mobile | plus: 17.5 }}px;
    margin-left: -{{ padding_horizontal_mobile | plus: 2 }}rem !important;
    margin-right: -{{ padding_horizontal_mobile | plus: 2 }}rem !important;
    padding-left: {{ padding_horizontal_mobile }}rem !important;
    padding-right: {{ padding_horizontal_mobile }}rem !important;
  }

  .steps-nav-wrapper-{{ section.id }} {
    padding-top: calc({{ nav_mt_mobile | divided_by: 2 }}px + 17.5px);
    overflow-x: scroll;
    scrollbar-width: none;
    width: 100%;
  }

  .steps-nav-wrapper-{{ section.id }}::-webkit-scrollbar {
    display: none;
  }

  .steps-nav-{{ section.id }} {
    display: flex;
    position: relative;
    justify-content: space-between;
  }

  .steps-nav-{{ section.id }}:after {
    content: '';
    position: absolute;
    top: 0;
    left: 80px;
    width: 550px;
    height: 1px;
    background-color: {{ step_color }};
  }

  .steps-step-{{ section.id }} {
    display: flex;
    justify-content: center;
    align-items: center;
    position: relative;
    cursor: pointer;
  }

  .steps-step-{{ section.id }} * {   
    max-width: 70%;
    width: 180px;
    margin: 0px;
    margin-top: {{ step_mt_mobile }}px;
    margin-bottom:5px;
    font-size: {{ step_size_mobile | minus: 2 }}px;
    line-height: {{ step_height }}%;
    color: {{ step_color }};
    text-transform: unset;
    word-break: break-word;
    text-align: center;
    opacity: 0.5;
    font-weight: 700;
    transition: all 0.25s ease 0s;
  }

  .steps-step-{{ section.id }}:after {
    content: '';
    position: absolute;
    width: 20px;
    height: 20px;
    top: -10px;
    left: 50%;
    transform: translate(-50%);
    background-color: {{ step_color }};
    border-radius: 100%;
    transition: all 0.25s ease 0s;
  }

  .steps-step-{{ section.id }}.active * {
    transform: scale(1.3);
    opacity: 1;
    transition: all 0.25s ease 0s;
  }

  .steps-step-{{ section.id }}.active:after {
    width: 35px;
    height: 35px;
    transition: all 0.25s ease 0s;
    top: -17.5px
  }

  .steps-buttons-{{ section.id }} {
    align-items: center;
    gap: 10px;
  }

  .steps-btn-prev-{{ section.id }},
  .steps-btn-next-{{ section.id }} {
    display: flex;
    width: {{ arrow_size_mobile }}px;
    height: {{ arrow_size_mobile }}px;
    border: 0px;
    border: {{ arrow_border_thickness }}px solid {{ arrow_border_color }};
    border-radius: {{ arrow_radius }}px;
    background: transparent;
    background: {{ arrow_bg_color }};
    align-items: center;
    justify-content: center;
    transition: all 0.3s ease 0s;
    cursor: pointer;
    -webkit-appearance: none;
    /* box-shadow: 0px 0px 10px 0px rgba(0,0,0,.15); */
  }

  .steps-btn-prev-{{ section.id }} .animate-icon,
  .steps-btn-next-{{ section.id }} .animate-icon {
    pointer-events: none;
    place-items: center;
    display: grid;
  }

  .steps-btn-prev-{{ section.id }} .animate-icon:before,
  .steps-btn-next-{{ section.id }} .animate-icon:before {
    content: "";
    grid-area: 1 / -1;
    transition: all .2s ease;
    width: {{ arrow_icon_size_mobile }}px;
    height: {{ arrow_icon_size_mobile }}px;
    transform: rotate(calc(1 * 45deg));
    box-sizing: border-box;
    border-style: solid;
    border: 0px;
    border-right: 2px solid currentColor;
    border-top: 2px solid currentColor;
    position: relative;
    left: -1px;
  }

  .steps-btn-prev-{{ section.id }} .animate-icon:before {
    left: unset;
    right: -1px;
    border-right: 0px;
    border-top: 0px;
    border-bottom: 2px solid currentColor;
    border-left: 2px solid currentColor;
  }

  .steps-btn-prev-{{ section.id }} .animate-icon:after,
  .steps-btn-next-{{ section.id }} .animate-icon:after {
    content: "";
    grid-area: 1 / -1;
    transition: all .2s ease;
    width: {{ arrow_icon_size_mobile | minus: 0.3 }}px;
    height: 1.6px;
    transform-origin: center;
    opacity: 0;
    background: currentColor;
    transform: scaleX(.5);
  }

  .steps-btn-prev-{{ section.id }} span,
  .steps-btn-next-{{ section.id }} span {
    color: {{ arrow_color }};
  }

  .steps-btn-prev-{{ section.id }}.swiper-button-disabled,
  .steps-btn-next-{{ section.id }}.swiper-button-disabled {
    opacity: 0.5;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .steps-image-{{ section.id }} {
      flex: 1 1 0;
    }

    .steps-item-{{ section.id }} {
      flex-direction: row;
      gap: {{ layout_gap }}px;
    }

    .steps-number-{{ section.id }} {
      text-align: {{ content_align }};
      font-size: {{ number_size }}px;
      padding: {{ number_padding_vertical }}px {{ number_padding_horizontal }}px;
    }

    .steps-title-{{ section.id }} {
      text-align: {{ content_align }};
      margin-top: {{ title_mt }}px;
    }

    .steps-title-{{ section.id }} {
      font-size: {{ title_size }}px;
    }

    .steps-text-{{ section.id }} {
      text-align: {{ content_align }};
      margin-top: {{ text_mt }}px;
    }

    .steps-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .steps-nav-body-{{ section.id }} {
      flex-direction: row;
    }

    .steps-step-{{ section.id }} * {
      font-size: {{ step_size | minus: 2 }}px;
    }

    .steps-nav-body-{{ section.id }} {
      margin-top: {{ nav_mt }}px;
      margin-left: 0rem !important;
      margin-right: 0rem !important;
      padding-left: 0rem !important;
      padding-right: 0rem !important;
    }

    .steps-nav-wrapper-{{ section.id }} {
      padding-top: {{ nav_mt | divided_by: 2 }}px;
      overflow-x: visible;
    }

    .steps-content-{{ section.id }} {
      align-items: {{ content_horizontal_align }};
    }

    .steps-step-{{ section.id }} * {
      margin-top: {{ step_mt }}px;
    }

    .steps-step-{{ section.id }}.active * {
      transform: scale(1.3);
      opacity: 1;
    }

    .steps-nav-{{ section.id }}:after {
      width: calc(100% - 180px);
    }

    .steps-btn-prev-{{ section.id }},
    .steps-btn-next-{{ section.id }} {
      width: {{ arrow_size }}px;
      height: {{ arrow_size }}px;
      transition: all 0.25s ease 0s;
    }

    .steps-btn-prev-{{ section.id }} .animate-icon:before,
    .steps-btn-next-{{ section.id }} .animate-icon:before {
      width: {{ arrow_icon_size }}px;
      height: {{ arrow_icon_size }}px;
    }

    .steps-btn-prev-{{ section.id }} .animate-icon:after,
    .steps-btn-next-{{ section.id }} .animate-icon:after {
      width: {{ arrow_icon_size | minus: 0.3 }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if number_custom %}
  <style>
    .steps-number-{{ section.id }} {
      font-family: {{ number_font.family }}, {{ number_font.fallback_families }};
      font-weight: {{ number_font.weight }};
      font-style: {{ number_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .steps-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .steps-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if step_custom %}
  <style>
    .steps-step-{{ section.id }} * {
      font-family: {{ step_font.family }}, {{ step_font.fallback_families }};
      font-weight: {{ step_font.weight }};
      font-style: {{ step_font.style }};
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .steps-image-{{ section.id }} {
      aspect-ratio: 9.6/13;
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
  .steps-image-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .steps-image-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .steps-image-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
      .steps-image-{{ section.id }} {
        aspect-ratio: 9.6/13;
      }
    }

  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .steps-image-{{ section.id }} {
        aspect-ratio: 12/8;
      }
    }  
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .steps-image-{{ section.id }} {
        aspect-ratio: 12/12;
      }
    }    
  </style>
{% else %}
  <style>
     @media(min-width: 1024px) {
      .steps-image-{{ section.id }} {
        aspect-ratio: auto;
      }
    }
  </style>
{% endif %}


{% if arrow_hover_effect == "color" %}
  <style>
    .steps-btn-prev-{{ section.id }}:hover,
    .steps-btn-next-{{ section.id }}:hover {
      transition: all 0.3s ease 0s;
      border: {{ arrow_border_thickness }}px solid {{ arrow_border_hover_color }};
      background: {{ arrow_bg_hover_color }};
    }

    .steps-btn-prev-{{ section.id }}:hover .animate-icon,
    .steps-btn-next-{{ section.id }}:hover .animate-icon {
      color: {{ arrow_hover_color }};
    }
  </style>
{% elsif arrow_hover_effect == "scale" %}
  <style>
    .steps-btn-prev-{{ section.id }}:hover,
    .steps-btn-next-{{ section.id }}:hover {
      transform: scale(0.9);
    }

    @media(min-width: 1024px) {
      .steps-btn-prev-{{ section.id }}:hover,
      .steps-btn-next-{{ section.id }}:hover {
        transform: scale(0.9);
      }
    }
  </style>
{% elsif arrow_hover_effect == "arrow" %}
  <style>
    .steps-btn-next-{{ section.id }}:hover .animate-icon:before {
      transform: translate(calc(1 * 0.125rem)) rotate(calc(1 * 45deg));
    }

    .steps-btn-prev-{{ section.id }}:hover .animate-icon:before {
      transform: translate(calc(-1 * 0.125rem)) rotate(calc(1 * 45deg));
    }

    .steps-btn-prev-{{ section.id }}:hover .animate-icon:after,
    .steps-btn-next-{{ section.id }}:hover .animate-icon:after {
      opacity: 1;
      transform: scaleX(1);
    }
  </style>
{% endif %}

{% if arrow_show_mobile %}
  <style>
    .steps-buttons-{{ section.id }} {
      display: flex;
    }
  </style>
{% else %}
  <style>
    .steps-buttons-{{ section.id }} {
      display: none;
    }
  </style>
{% endif %}

{% if arrow_show_desktop %}
  <style>
    @media(min-width: 1024px) {
      .steps-buttons-{{ section.id }} {
        display: flex;
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .steps-buttons-{{ section.id }} {
        display: none;
      }
    }
  </style>
{% endif %}

{% if layout_mobile == "image_content" %}
  <style>
    .steps-image-{{ section.id }} {
      order: 1;
    }

    .steps-content-{{ section.id }} {
      order: 2;
    }
  </style>
{% else %} 
  {% if layout_mobile == "content_image" %}
    <style>
      .steps-image-{{ section.id }} {
        order: 2;
      }

      .steps-content-{{ section.id }} {
        order: 1;
      }
    </style>
  {% endif %}  
{% endif %}

{% if layout == "image_content" %}
  <style>
    @media (min-width: 1024px) {
      .steps-image-{{ section.id }} {
        order: 1;
      }

      .steps-content-{{ section.id }} {
        order: 2;
      }
    }
  </style>
{% else %}  
  {% if layout == "content_image" %}
    <style>
      @media (min-width: 1024px) {
        .steps-image-{{ section.id }} {
          order: 2;
        }

        .steps-content-{{ section.id }} {
          order: 1;
        }
      }
    </style>
  {% endif %}  
{% endif %}

<div class="section-{{ section.id }} steps-{{ section.id }}" data-section-id="{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="steps-body-{{ section.id }}">
        {% for block in section.blocks %}
          <div class="steps-item-{{ section.id }} {% if forloop.first %}active{% endif %}" data-id="{{ block.id }}">
           <div class="steps-image-{{ section.id }}">
            {% if block.settings.image != blank %}
              {%- assign loading_attribute = 'eager' -%}
              {%- if section.settings.lazy -%}
                {%- assign loading_attribute = 'lazy' -%}
              {%- endif -%}
              {{ block.settings.image | image_url: width: 1200 | image_tag:
                loading: loading_attribute,
                sizes: "(min-width: 1024px) 50vw, 100vw",
                widths: '375, 550, 750, 1100, 1500',
                alt: block.settings.image.alt | escape
              }}
            {% else %}  
              {{ 'image' | placeholder_svg_tag }}
            {% endif %}
          </div>
            <div class="steps-content-{{ section.id }}">
              <p class="steps-number-{{ section.id }}">{{ forloop.index }}</p>
              {% if block.settings.title %}
                <h3 class="steps-title-{{ section.id }}">{{ block.settings.title }}</h3>
              {% endif %}
              {% if block.settings.text %}
                <div class="steps-text-{{ section.id }}">{{ block.settings.text }}</div>
              {% endif %}
            </div>
          </div>
        {% endfor %}
        <div class="steps-nav-body-{{ section.id }}">
          <div class="steps-nav-wrapper-{{ section.id }}">
            <div class="steps-nav-{{ section.id }}">
              {% for block in section.blocks %}
                {% if block.settings.step != blank %}
                  <div class="steps-step-{{ section.id }} {% if forloop.first %}active{% endif %}" id="{{ block.id }}"><p>{{ block.settings.step }}</p></div>
                {% endif %}             
              {% endfor %}
            </div>
          </div>         
          <div class="steps-buttons-{{ section.id }}">
            <button class="steps-btn-prev-{{ section.id }}">
              <span class="animate-icon"></span>
            </button>
            <button class="steps-btn-next-{{ section.id }}">
              <span class="animate-icon"></span>
            </button>   
          </div>
        </div>
      </div>
    </div>
</div>

<script>
  function initSteps8() {
    const steps = document.querySelectorAll(`.steps-step-{{ section.id }}`);
    const items = document.querySelectorAll(`.steps-item-{{ section.id }}`);
    const prevBtn = document.querySelector(`.steps-btn-prev-{{ section.id }}`);
    const nextBtn = document.querySelector(`.steps-btn-next-{{ section.id }}`);
    const scrollContainer = document.querySelector(`.steps-nav-wrapper-{{ section.id }}`);
    let currentIndex = 0;

    function updateActiveStep(index) {
      steps.forEach(step => step.classList.remove('active'));
      items.forEach(item => item.classList.remove('active'));

      steps[index].classList.add('active');
      items[index].classList.add('active');

      if (window.innerWidth < 1024 && scrollContainer) {
        const step = steps[index];
        const stepOffset = step.offsetLeft - scrollContainer.offsetLeft;
        scrollContainer.scrollTo({
          left: stepOffset - scrollContainer.clientWidth / 2 + step.clientWidth / 2,
          behavior: 'smooth',
        });
      }
    }

    steps.forEach((step, index) => {
      step.addEventListener('click', () => {
        currentIndex = index;
        updateActiveStep(currentIndex);
      });
    });

    nextBtn.addEventListener('click', () => {
      currentIndex = (currentIndex + 1) % steps.length;
      updateActiveStep(currentIndex);
    });

    prevBtn.addEventListener('click', () => {
      currentIndex = (currentIndex - 1 + steps.length) % steps.length;
      updateActiveStep(currentIndex);
    });
  }

  document.addEventListener('DOMContentLoaded', initSteps8);

  if (Shopify.designMode) {
    document.addEventListener('shopify:section:unload', initSteps8);
    document.addEventListener('shopify:section:load', initSteps8);
  }
</script>
