{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}
  {{  number_font | font_face: font_display: 'swap' }}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  item_text_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .steps-body-{{ section.id }} {
    display: flex;
    flex-direction: column;
    gap: {{ layout_gap_mobile }}px;
  }

  .steps-info-{{ section.id }} {
    order: {{ info_position_mobile }};
  }

  .steps-heading-{{ section.id }} {
    width: 100%;
    text-align: {{ heading_align_mobile }};
  }

  .steps-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .steps-text-{{ section.id }} {
    width: 100%;
    margin-top: {{ text_mt | times: 0.75 | round: 0 }}px;
    text-align: {{ text_align_mobile }};
  }

  .steps-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
  }

  .steps-image-{{ section.id }} {
    margin-top: {{ image_mt | times: 0.75 | round: 0 }}px;
    width: 100%;
    max-width: {{ content_maxwidth }}px;
    border-radius: {{ image_radius }}px;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    overflow: hidden;
    position: relative;
  }

  .steps-image-desktop-{{ section.id }} {
    display: none;
  }

  .steps-image-{{ section.id }} img {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .steps-items-{{ section.id }} {
    order: {{ steps_position_mobile }};
  }

  .steps-items-wrapper-{{ section.id }} {
    display: flex;
    flex-direction: column;
    gap: {{ steps_space_mobile }}px;
  }

  .steps-item-{{ section.id }} {
    position: relative;
  }

  .steps-top-{{ section.id }} {
    display: flex;
    align-items: flex-start;
    gap: 16px;
    cursor: pointer;
  }

  .steps-item-{{ section.id }}:not(:last-child):after {
    content: '';
    position: absolute;
    left: 0;
    bottom: -{{ steps_space_mobile | times: 0.5 | round: 0 }}px;
    width: 100%;
    height: {{ steps_border_thickness }}px;
    background-color: {{ steps_border_color }};
  }

  .steps-number-{{ section.id }} {
    flex: 0 0 {{ number_width_mobile }}px;
    height: {{ number_width_mobile }}px;
    display: flex;
    align-items: center;
    justify-content: center;
    text-align: center;
    border-radius: 50%;
    border: {{ number_border_thickness }}px solid {{ number_border_color }};
    margin: 0;
    font-size: {{ number_size_mobile }}px;
    color: {{ number_color }};
    line-height: {{ number_height }}%;
    text-transform: unset;
  }

  .steps-title-{{ section.id }} {
    margin: 0;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
  }

  .steps-item-text-{{ section.id }} {
    margin-top: {{ item_text_mt | times: 0.75 | round: 0 }}px;
  }

  .steps-item-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ item_text_size_mobile }}px;
    color: {{ item_text_color }};
    line-height: {{ item_text_height }}%;
    text-transform: unset;
  }

  .steps-bottom-{{ section.id }} {
    display: none;
  }

  .steps-item-{{ section.id }}.active .steps-bottom-{{ section.id }} {
    display: block;
  }

  .steps-arrow-{{ section.id }} {
    margin-left: auto;
    flex: 0 0 19px;
    height: 19px;
    transition: all 0.2s ease;
  }

  .steps-arrow-{{ section.id }} path {
    fill: {{ arrow_color }};
  }

  .steps-item-{{ section.id }}.active .steps-arrow-{{ section.id }} {
    transition: all 0.2s ease;
    transform: rotate(90deg);
  }

  .steps-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    width: 100%;
    max-width: {{ button_width_mobile }}px;
    margin: 0;
    margin-top: {{ button_mt_mobile }}px;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    position: relative;
    font-weight: 700;
    z-index: 2;
  }

  .steps-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .steps-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .steps-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .steps-button-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .steps-body-{{ section.id }} {
      display: grid;
      grid-template-columns: repeat(2, 1fr);
      gap: {{ layout_gap }}px;
    }

    .steps-info-{{ section.id }} {
      order: {{ info_position }};
    }

    .steps-heading-{{ section.id }} {
      max-width: {{ content_maxwidth }}px;
      text-align: {{ heading_align }};
    }

    .steps-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .steps-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ text_align }};
      max-width: {{ content_maxwidth }}px;
    }

    .steps-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .steps-image-{{ section.id }} {
      margin-top: {{ image_mt }}px;
    }

    .steps-image-desktop-{{ section.id }} {
      display: block;
    }

    .steps-items-{{ section.id }} {
      margin-top: 0px;
      order: {{ steps_position }};
    }

    .steps-items-wrapper-{{ section.id }} {
      gap: {{ steps_space }}px;
    }

    .steps-top-{{ section.id }} {
      align-items: flex-start !important;
      cursor: unset;
    }

    .steps-item-{{ section.id }}:not(:last-child):after {
      width: calc(100% - {{ number_width | plus: 16 }}px);
      left: {{ number_width | plus: 16 }}px;
      bottom: -{{ steps_space | times: 0.5 | round: 0 }}px;
    }

    .steps-number-{{ section.id }} {
      flex: 0 0 {{ number_width }}px;
      height: {{ number_width }}px;
      font-size: {{ number_size }}px;
    }

    .steps-title-{{ section.id }} {
      font-size: {{ title_size }}px;
    }

    .steps-item-text-{{ section.id }} {
      margin-top: {{ item_text_mt }}px;
    }

    .steps-item-text-{{ section.id }} * {
      font-size: {{ item_text_size }}px;
    }

    .steps-bottom-{{ section.id }} {
      display: none !important;
    }

    .steps-arrow-{{ section.id }} {
      display: none !important;
    }

    .steps-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      max-width: {{ button_width }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .steps-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .steps-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if number_custom %}
  <style>
    .steps-number-{{ section.id }} {
      font-family: {{ number_font.family }}, {{ number_font.fallback_families }};
      font-weight: {{ number_font.weight }};
