    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .steps-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if item_text_custom %}
  <style>
    .steps-item-text-{{ section.id }} * {
      font-family: {{ item_text_font.family }}, {{ item_text_font.fallback_families }};
      font-weight: {{ item_text_font.weight }};
      font-style: {{ item_text_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .steps-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if heading_align == "center" %}
  <style>
    @media(min-width: 1024px) {
      .steps-heading-{{ section.id }} {
        margin-left: auto;
        margin-right: auto;
      }
    }
  </style>
{% elsif heading_align == "right" %}
   <style>
    @media(min-width: 1024px) {
      .steps-heading-{{ section.id }} {
        margin-left: auto;
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .steps-heading-{{ section.id }} {
        margin: 0px;
      }
    }
  </style>
{% endif %}

{% if text_align == "center" %}
  <style>
    @media(min-width: 1024px) {
      .steps-text-{{ section.id }} {
        margin-left: auto;
        margin-right: auto;
      }
    }
  </style>
{% elsif text_align == "right" %}
   <style>
    @media(min-width: 1024px) {
      .steps-text-{{ section.id }} {
        margin-left: auto;
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .steps-text-{{ section.id }} {
        margin: 0px;
        margin-top: {{ text_mt }}px;
      }
    }
  </style>
{% endif %}

{% if button_align_mobile == "center" %}
  <style>
   .steps-button-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    } 
  </style>
{% elsif button_align_mobile == "right" %}
  <style>
   .steps-button-{{ section.id }} {
      margin-left: auto;
    } 
  </style>
{% endif %}

{% if button_align == "center" %}
  <style>
    @media(min-width: 1024px) {
     .steps-button-{{ section.id }} {
        margin-left: auto !important;
        margin-right: auto !important;
      } 
    }
  </style>
{% elsif button_align == "right" %}
  <style>
    @media(min-width: 1024px) {
     .steps-button-{{ section.id }} {
        margin-left: auto !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .steps-button-{{ section.id }} {
        margin-left: {{ number_width | plus: 16 }}px !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% endif %}

{% if image_align == "center" %}
  <style>
    @media(min-width: 1024px) {
     .steps-image-{{ section.id }} {
        margin-left: auto !important;
        margin-right: auto !important;
      } 
    }
  </style>
{% elsif image_align == "right" %}
  <style>
    @media(min-width: 1024px) {
     .steps-image-{{ section.id }} {
        margin-left: auto !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .steps-image-{{ section.id }} {
        margin-left: 0px !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% endif %}

{% if steps_accordion_mobile %}
  <style>
    .steps-top-{{ section.id }} {
      align-items: center;
    }

    .steps-item-text-desktop-{{ section.id }} {
      display: none;
    }

    @media(min-width: 1024px) {
      .steps-item-text-desktop-{{ section.id }} {
        display: block;
      }
    }
  </style>
{% endif %}

{% if button_style == "non_outline" or button_style == "non_outline_arrow" %}
  <style>
    .steps-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
    }

    .steps-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
    }
  </style>
{% elsif button_style == "outline" or button_style == "outline_arrow" %}
  <style>
    .steps-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_color }};
    }

    .steps-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .steps-image-{{ section.id }} {
     aspect-ratio: 9.6/14; 
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
    .steps-image-{{ section.id }}{
     aspect-ratio: 12/9; 
    }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .steps-image-{{ section.id }} {
     aspect-ratio: 12/12; 
    }
  </style>
{% else%}
  <style>
    .steps-image-{{ section.id }} {
     aspect-ratio: auto; 
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .steps-image-{{ section.id }} {
       aspect-ratio: 9.6/14; 
      } 
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .steps-image-{{ section.id }}{
       aspect-ratio: 12/9; 
      }
    }
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .steps-image-{{ section.id }} {
       aspect-ratio: 12/12; 
      }
    }
  </style>
{% else%}
  <style>
    @media(min-width: 1024px) {
      .steps-image-{{ section.id }} {
       aspect-ratio: auto; 
      }
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} steps-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="steps-body-{{ section.id }}">
        <div class="steps-info-{{ section.id }}">
          {% if heading != blank %}
            <div class="steps-heading-{{ section.id }}">
              {{ heading }}
            </div>
          {% endif %}

          {% if text != blank %}
            <div class="steps-text-{{ section.id }}">
              {{ text }}
            </div>
          {% endif %}
          {% if image != blank %}
            <div class="steps-image-{{ section.id }} {% if hide_image_mobile %}steps-image-desktop-{{ section.id }}{% endif %}">
              <img src="{{ image | image_url }}" alt="{{ image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
            </div>
          {% endif %}
        </div>
        <div class="steps-items-{{ section.id }}">
          <div class="steps-items-wrapper-{{ section.id }}">
            {% for block in section.blocks %}
              <div class="steps-item-{{ section.id }}">
                <div class="steps-top-{{ section.id }}">
                  <div class="steps-number-{{ section.id }}">
                    {{ forloop.index }}
                  </div>
                  <div class="steps-content-{{ section.id }}">
                    {% if block.settings.title != blank %}
                      <p class="steps-title-{{ section.id }}">{{ block.settings.title }}</p>
                    {% endif %}

                    {% if block.settings.text != blank %}
                      <div class="steps-item-text-{{ section.id }} steps-item-text-desktop-{{ section.id }}">{{ block.settings.text }}</div>
                    {% endif %}
                  </div>
