<script>
  function initSteps() {
    if(document.querySelectorAll('.steps-item-{{ section.id }}')) {
      document.querySelectorAll('.steps-item-{{ section.id }}').forEach(el => {
        let toggle = el.querySelector('.steps-top-{{ section.id }}');
        if(toggle) {
          toggle.addEventListener('click', () => {
            document.querySelectorAll('.steps-item-{{ section.id }}').forEach(element => {
              element.classList.remove('active');
            })
            el.classList.toggle('active');
          })
        }
      })
    }
  }

  document.addEventListener('DOMContentLoaded', initSteps);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initSteps);
     document.addEventListener('shopify:section:load', initSteps);
  }
</script>
