{%- style -%}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  tab_title_font | font_face: font_display: 'swap' }}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}
  {{  sub_text_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    transition: background-color 0.25s ease 0s; 
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .tabs-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .tabs-heading-{{ section.id }} * {
    margin: 0px;
    font-size: {{ heading_size_mobile }}px;
    line-height: {{ heading_height }}%;
    color: {{ heading_color }};
    text-transform: unset;
    word-break: break-word;
  }

  .tabs-wrapper-{{ section.id }} {
    margin-top: {{ tabs_mt_mobile }}px;
  }

  .tabs-tab-{{ section.id }} {
    display: flex;
    align-items: center;
    width: 100%;
    cursor: pointer;
    padding: {{ tab_padding_vertical_mobile }}px {{ tab_padding_horizontal_mobile }}px;
    border-bottom: {{ tab_border_thickness }}px solid {{ tab_border_color }};  
  }

  .tabs-tab-title-{{ section.id }} {
    margin: 0px;
    flex: 1 1 0;
    text-align: {{ tab_title_align_mobile }};
    font-size: {{ tab_title_size_mobile }}px;
    line-height: {{ tab_title_height }}%;
    color: {{ tab_title_color }};
    text-transform: unset;
    word-break: break-word;
    transform: skew(0deg);
    transition: all 0.25s ease 0s;
  }  

  .tabs-arrow-{{ section.id }} {
    display: flex;
    position: relative;
    flex-shrink: 0;
    width: {{ arrow_size_mobile }}px;
    height: {{ arrow_size_mobile }}px;
    border: 0px;
    border: {{ arrow_border_thickness }}px solid {{ arrow_border_color }};
    border-radius: {{ arrow_radius }}px;
    background: transparent;
    background: {{ arrow_bg_color }};
    align-items: center;
    justify-content: center;
    transition: all 0.3s ease 0s;
    cursor: pointer;
    -webkit-appearance: none;
    overflow: hidden;
    /* box-shadow: 0px 0px 10px 0px rgba(0,0,0,.15); */
  }

  .tabs-arrow-{{ section.id }} .animate-bg-color {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 0%;
    z-index: 0;
    background-color: {{ arrow_bg_hover_color }};    
    transition: all 0.25s ease 0s;
  }

  .tabs-arrow-{{ section.id }} .animate-icon {
    position: relative;
    transform: translateY(0%);
    z-index: 1;
    pointer-events: none;
    place-items: center;
    display: grid;
    transition: all 0.25s ease 0s;
  }

  .tabs-arrow-{{ section.id }} .animate-icon:before {
    content: "";
    grid-area: 1 / -1;
    transition: all .2s ease;
    width: {{ arrow_icon_size_mobile }}px;
    height: {{ arrow_icon_size_mobile }}px;
    transform: rotate(calc(1 * 45deg));
    box-sizing: border-box;
    border-style: solid;
    border: 0px;
    border-right: 2px solid currentColor;
    border-top: 2px solid currentColor;
    position: relative;
    left: -1px;
  }

  .tabs-arrow-{{ section.id }} .animate-icon:after {
    content: "";
    grid-area: 1 / -1;
    transition: all .2s ease;
    width: {{ arrow_icon_size_mobile | minus: 0.3 }}px;
    height: 1.6px;
    transform-origin: center;
    opacity: 0;
    background: currentColor;
    transform: scaleX(.5);
  }

  .tabs-arrow-{{ section.id }} .animate-second-icon {
    position: absolute;
    top: 150%;
    left: 50%;
    transform: translate(-50%, 150%);
    z-index: 1;
    pointer-events: none;
    place-items: center;
    display: grid;    
    transition: all 0.25s ease 0s;
  }

  .tabs-arrow-{{ section.id }} .animate-second-icon:before {
    content: "";
    grid-area: 1 / -1;
    transition: all .2s ease;
    width: {{ arrow_icon_size_mobile }}px;
    height: {{ arrow_icon_size_mobile }}px;
    transform: rotate(calc(1 * 45deg));
    box-sizing: border-box;
    border-style: solid;
    border: 0px;
    border-right: 2px solid currentColor;
    border-top: 2px solid currentColor;
    position: relative;
    left: -1px;
  }

  .tabs-arrow-{{ section.id }} .animate-second-icon:after {
    content: "";
    grid-area: 1 / -1;
    transition: all .2s ease;
    width: {{ arrow_icon_size_mobile | minus: 0.3 }}px;
    height: 1.6px;
    transform-origin: center;
    opacity: 0;
    background: currentColor;
    transform: scaleX(.5);
  }

  .tabs-arrow-{{ section.id }} .animate-icon {
    color: {{ arrow_color }};
  }

  .tabs-arrow-{{ section.id }} .animate-second-icon {
    color: {{ arrow_hover_color }};
  }

  .tabs-modal-{{ section.id }} {
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    z-index: -1;
    display: flex;
    z-index: -1;
    opacity: 0;
    pointer-events: none;
    align-items: center;
    justify-content: right;
    -webkit-backdrop-filter: blur(5px);
    backdrop-filter: blur(5px);
    background-color: {{ modal_overlay_color |  hex_to_rgba: modal_overlay }};   
    transition: all 0.25s ease 0s;
  }

  .tabs-modal-{{ section.id }}.active {
    opacity: 1;
    z-index: 9999;
    pointer-events: all;
    transition: all 0.25s ease 0s;
  }

  .tabs-modal-inner-{{ section.id }} {
    width: 100%;
    height: 100%;
    position: relative;
    overflow-y: auto;
    scrollbar-width: none;
    -ms-overflow-style: none; 
    transform: translateX(100%);
    padding: {{ modal_inner_padding_vertical_mobile }}px {{ modal_inner_padding_horizontal_mobile }}px;
    background-color: {{ modal_inner_bg_color }};
    transition: all 0.25s ease 0s;
  }

  .tabs-modal-inner-{{ section.id }}.active {
    transition: all 0.25s ease 0s;
    transform: translateX(0%);
  }

  .tabs-modal-inner-{{ section.id }}::-webkit-scrollbar {
    display: none;
  }

  .tabs-modal-title-{{ section.id }} {
    text-align: {{ modal_content_align_mobile }};
  }

  .tabs-modal-title-{{ section.id }} * {
    margin: 0px;
    font-size: {{ title_size_mobile }}px;
    line-height: {{ title_height }}%;
    color: {{ title_color }};
    text-transform: unset;
    word-break: break-word;
  }

  .tabs-modal-text-{{ section.id }} {
    margin-top: {{ text_mt_mobile }}px;
    text-align: {{ modal_content_align_mobile }};
  }

  .tabs-modal-text-{{ section.id }} * {
    margin: 0px;
    font-size: {{ text_size_mobile }}px;
    line-height: {{ text_height }}%;
    color: {{ text_color }};
    text-transform: unset;
    word-break: break-word;
  }

  .tabs-modal-sub-text-{{ section.id }} {
    margin-top: {{ sub_text_mt_mobile }}px;
    text-align: {{ modal_content_align_mobile }};
  }

  .tabs-modal-sub-text-{{ section.id }} * {
    margin: 0px;
    font-size: {{ sub_text_size_mobile }}px;
    line-height: {{ sub_text_height }}%;
    color: {{ sub_text_color }};
    text-transform: unset;
    word-break: break-word;
  }

  .tabs-modal-content-{{ section.id }} {
    margin-top: {{ modal_close_size_mobile }}px;
    display: none;
    padding-bottom: 80px;
  }

  .tabs-modal-content-{{ section.id }}.active {
    display: block;
  }

  .tabs-close-modal-{{ section.id }} {
    padding: 0;
    position: absolute;
    top: 15px;
    right: 15px;
    box-shadow: none;
    border: 0px;
    cursor: pointer;
    z-index: 2;
    border-radius: {{ modal_close_roundness }}px;
    background-color: {{ modal_close_color | hex_to_rgba: 0.7 }};
    overflow: hidden;
  }

  .tabs-close-modal-wrap-{{ section.id }} {
    position: relative;
    width: 100%; 
    height: 100%;
  }

  .tabs-close-modal-wrap-{{ section.id }} .tabs-close-bg-color-{{ section.id }} {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 0%;
    z-index: 0;
    background-color: {{ modal_close_color_hover }};    
    transition: all 0.25s ease 0s;
  }

  .tabs-close-modal-{{ section.id }} .tabs-close-modal-wrap-{{ section.id }} svg {
    position: relative;
    z-index: 2;
    padding: 15px;
    display: block;
    width: {{ modal_close_size_mobile }}px;
    height: {{ modal_close_size_mobile }}px;
    transition: all 0.25s ease 0s;
  }

  .tabs-close-modal-{{ section.id }} .tabs-close-modal-wrap-{{ section.id }} svg path {
    fill: {{ modal_close_icon_color }};   
  }

  .tabs-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: fit-content;
    gap: 10px;
    width: 100%;
    margin: 0 auto;
    margin-top: {{ button_mt_mobile }}px;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    background-color: transparent;
    border: 0px;
    cursor: pointer;
    font-weight: 700;
  }

  .tabs-button-inner-{{ section.id }} {
    margin: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
  }

  .tabs-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .tabs-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .tabs-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .tabs-button-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .tabs-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .tabs-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .tabs-wrapper-{{ section.id }} {
      margin-top: {{ tabs_mt }}px;
    }

    .tabs-tab-{{ section.id }} {
      padding: {{ tab_padding_vertical }}px {{ tab_padding_horizontal }}px    
    }

    .tabs-tab-title-{{ section.id }} {
      text-align: {{ tab_title_align }};
      font-size: {{ tab_title_size }}px;
    }

    .tabs-modal-content-{{ section.id }} {
      margin-top: {{ modal_close_size }}px;
    }

    .tabs-modal-title-{{ section.id }} {
      text-align: {{ modal_content_align }};
    }

    .tabs-modal-title-{{ section.id }} * {      
      font-size: {{ title_size }}px;
    }

    .tabs-modal-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ modal_content_align }};
    }

    .tabs-modal-text-{{ section.id }} * {      

      font-size: {{ text_size }}px;
    }

    .tabs-modal-sub-text-{{ section.id }} {
      margin-top: {{ sub_text_mt }}px; 
      text-align: {{ modal_content_align }};  
    }

    .tabs-modal-sub-text-{{ section.id }} * {     
      font-size: {{ sub_text_size }}px;
    }

    .tabs-arrow-{{ section.id }} {
      width: {{ arrow_size }}px;
      height: {{ arrow_size }}px;
      transition: all 0.25s ease 0s;
    }

    .tabs-arrow-{{ section.id }} .animate-icon:before {
      width: {{ arrow_icon_size }}px;
      height: {{ arrow_icon_size }}px;
    }

    .tabs-arrow-{{ section.id }} .animate-icon:after {
      width: {{ arrow_icon_size | minus: 0.3 }}px;
    }

    .tabs-arrow-{{ section.id }} .animate-second-icon:before {
      width: {{ arrow_icon_size }}px;
      height: {{ arrow_icon_size }}px;
    }

    .tabs-arrow-{{ section.id }} .animate-second-icon:after {
      width: {{ arrow_icon_size | minus: 0.3 }}px;
    }

    .tabs-modal-inner-{{ section.id }} {
      width: {{ modal_inner_width }}px;
      padding: {{ modal_inner_padding_vertical }}px {{ modal_inner_padding_horizontal }}px;
    }

    .tabs-close-modal-{{ section.id }} {
      transform: none;
    }

    .tabs-close-modal-{{ section.id }} svg {
      width: {{ modal_close_size }}px;
      height: {{ modal_close_size }}px;
    }

    .tabs-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }

  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .tabs-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if tab_title_custom %}
  <style>
    .tabs-tab-title-{{ section.id }} {
      font-family: {{ tab_title_font.family }}, {{ tab_title_font.fallback_families }};
      font-weight: {{ tab_title_font.weight }};
      font-style: {{ tab_title_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .tabs-modal-title-{{ section.id }} * {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .tabs-modal-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if sub_text_custom %}
