    .tabs-modal-sub-text-{{ section.id }} * {
      font-family: {{ sub_text_font.family }}, {{ sub_text_font.fallback_families }};
      font-weight: {{ sub_text_font.weight }};
      font-style: {{ sub_text_font.style }};
    }
  </style>
{% endif %}

