    .tabs-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if tab_title_use_hover %}
  <style>
    .tabs-tab-{{ section.id }}:hover .tabs-tab-title-{{ section.id }} {
      transform: skew(-15deg);
      transition: all 0.25s ease 0s;
    }
  </style>  
{% endif %}

{% if arrow_hover_effect == "fill" %}
