      height: 100%;
    }

    .tabs-tab-{{ section.id }}:hover .tabs-arrow-{{ section.id }} .animate-icon {
      transition: all 0.25s ease 0s;
      transform: translateY(-350%);
    }

    .tabs-tab-{{ section.id }}:hover .tabs-arrow-{{ section.id }} .animate-second-icon {
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%); 
      color: {{ arrow_hover_color }};
      transition: all 0.25s ease 0s;
    }
  </style>
{% elsif arrow_hover_effect == "color" %}
  <style>
    .tabs-tab-{{ section.id }}:hover .tabs-arrow-{{ section.id }} {
      transition: all 0.3s ease 0s;
      border: {{ arrow_border_thickness }}px solid {{ arrow_border_hover_color }};
      background: {{ arrow_bg_hover_color }};
    }    

