          <path fill-rule="evenodd" clip-rule="evenodd"
          d="M12.0002 10.586L4.70718 3.29297L3.29297 4.70718L10.586 12.0002L3.29297 19.2933L4.70718 20.7075L12.0002 13.4145L19.2933 20.7075L20.7075 19.2933L13.4145 12.0002L20.7075 4.70723L19.2933 3.29302L12.0002 10.586Z"
          fill="white"></path>
        </svg>
        <span class="tabs-close-bg-color-{{ section.id }}"></span>
      </div>      
    </button>
  </div>
</div>

<script>
  function initTabs() {
    const tabs = document.querySelectorAll('.tabs-tab-{{ section.id }}');
    const modal = document.querySelector('.tabs-modal-{{ section.id }}');
    const modalInner = document.querySelector('.tabs-modal-inner-{{ section.id }}');
    const modalText = document.querySelectorAll('.tabs-modal-content-{{ section.id }}');
    const closeModal = document.querySelector('.tabs-close-modal-{{ section.id }}');
    const section = document.querySelector('.section-{{ section.id }}');


    if (tabs.length > 0 && section) {
      const defaultBgColor = tabs[0].getAttribute('data-color');
      const defaultBgImage = tabs[0].getAttribute('data-image');

      if (defaultBgImage) {
          section.style.backgroundImage = defaultBgImage;
      } else if (defaultBgColor) {
          section.style.backgroundColor = defaultBgColor;
      }

      tabs.forEach(tab => {
        tab.addEventListener('mouseenter', () => {
          const bgColor = tab.getAttribute('data-color');
          const bgImage = tab.getAttribute('data-image');

          if (bgImage) {
            section.style.backgroundImage = bgImage;
            section.style.backgroundColor = ''; 
          } else if (bgColor) {
            section.style.backgroundColor = bgColor;
            section.style.backgroundImage = '';
          }
        });

        tab.addEventListener('mouseleave', () => {
          if (defaultBgImage) {
            section.style.backgroundImage = defaultBgImage;
            section.style.backgroundColor = '';
          } else if (defaultBgColor) {
            section.style.backgroundColor = defaultBgColor;
            section.style.backgroundImage = '';
          }
        });
      });
    }

    if(tabs) {
      tabs.forEach(tab => {
        tab.addEventListener('click', () => {              
          if(modalText) {
            document.body.style.overflow = 'hidden';     
            if(modal) {
              modal.classList.add('active');
              setTimeout(() => {
                modalInner.classList.add('active');
              }, 200)
            } else {              
              modalInner.classList.remove('active');             
              setTimeout(() => {
                modal.classList.remove('active');      
              }, 200)        
            }
            modalText.forEach(e => {
              if(tab.id === e.getAttribute('data-id')) {
                e.classList.add('active');
              } else {
                setTimeout(() => {
                  e.classList.remove('active')  
                }, 200)               
              }
            })
          }
        })
      })
    }

    if(closeModal) {
      closeModal.addEventListener("click", () => {
        document.body.style.overflow = '';   
        if(modal) {     
          modalInner.classList.remove('active');             
          setTimeout(() => {
            modal.classList.remove('active');      
          }, 200)            
        }
        modalText.forEach(e => {          
          setTimeout(() => {
            e.classList.remove('active')  
          }, 200)      
        })
      })
    }

    document.addEventListener('click', function(e) {
      if (e.target == modal) {
        document.body.style.overflow = '';   
        modalInner.classList.remove('active');             
        setTimeout(() => {
          modal.classList.remove('active');      
        }, 200)
      }
    });
  }

  document.addEventListener('DOMContentLoaded', initTabs);

  if (Shopify.designMode) {
    document.addEventListener('shopify:section:unload', initTabs);
    document.addEventListener('shopify:section:load', initTabs);
  }
</script>
