liquid_code:
{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy

  assign scrolling_velocity = section.settings.scrolling_velocity
  assign scrolling_velocity_mobile = section.settings.scrolling_velocity_mobile
  assign scrolling_gap_mobile = section.settings.scrolling_gap_mobile
  assign scrolling_gap = section.settings.scrolling_gap
  assign scrolling_in_row = section.settings.scrolling_in_row

  assign reverse_direction = section.settings.reverse_direction

  assign slide_width_mobile = section.settings.slide_width_mobile
  assign slide_width = section.settings.slide_width
  assign slide_padding_vertical = section.settings.slide_padding_vertical
  assign slide_padding_vertical_mobile = section.settings.slide_padding_vertical_mobile
  assign slide_padding_horizontal = section.settings.slide_padding_horizontal
  assign slide_padding_horizontal_mobile = section.settings.slide_padding_horizontal_mobile
  assign slide_border_thickness = section.settings.slide_border_thickness
  assign slide_border_color = section.settings.slide_border_color
  assign slide_bg_color = section.settings.slide_bg_color
  assign slide_radius = section.settings.slide_radius
  assign slide_space = section.settings.slide_space
  assign slide_space_mobile = section.settings.slide_space_mobile
  assign slide_gap_mobile = section.settings.slide_gap_mobile
  assign slide_gap = section.settings.slide_gap
  assign slide_shadow_color = section.settings.slide_shadow_color
  assign slide_shadow = section.settings.slide_shadow

  assign show_stars = section.settings.show_stars
  assign stars_icon = section.settings.stars_icon
  assign stars_size = section.settings.stars_size
  assign stars_color = section.settings.stars_color
  assign stars_align_mobile = section.settings.stars_align_mobile
  assign stars_align = section.settings.stars_align

  assign content_vertical_align_mobile = section.settings.content_vertical_align_mobile
  assign content_vertical_align = section.settings.content_vertical_align

  assign show_image = section.settings.show_image
  assign image_radius = section.settings.image_radius
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color
  assign image_ratio = section.settings.image_ratio
  assign image_ratio_mobile = section.settings.image_ratio_mobile
  assign image_width = section.settings.image_width
  assign image_width_mobile = section.settings.image_width_mobile

  assign show_heading = section.settings.show_heading
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_color = section.settings.heading_color
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_height = section.settings.heading_height
  assign heading_align = section.settings.heading_align
  assign heading_align_mobile = section.settings.heading_align_mobile
  assign heading_mt = section.settings.heading_mt

  assign use_tag = section.settings.use_tag
  assign tag = section.settings.tag
  assign tag_width = section.settings.tag_width
  assign tag_width_mobile = section.settings.tag_width_mobile
  assign tag_align_mobile = section.settings.tag_align_mobile
  assign tag_align = section.settings.tag_align
  assign tag_mt = section.settings.tag_mt

  assign tag_icon_color = section.settings.tag_icon_color

  assign tag_text = section.settings.tag_text
  assign tag_text_size = section.settings.tag_text_size
  assign tag_text_size_mobile = section.settings.tag_text_size_mobile
  assign tag_text_color = section.settings.tag_text_color
  assign tag_text_custom = section.settings.tag_text_custom
  assign tag_text_font = section.settings.tag_text_font
  assign tag_text_height = section.settings.tag_text_height

  assign show_text = section.settings.show_text
  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_color = section.settings.text_color
  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_height = section.settings.text_height
  assign text_align = section.settings.text_align
  assign text_align_mobile = section.settings.text_align_mobile
  assign text_mt = section.settings.text_mt

-%}

{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}
  {{  tag_text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .testimonials-scrolling-item-{{ section.id }} {
    justify-content: flex-start;
    width: 100%;
    display: flex;
    overflow: hidden;
  }

  .testimonials-scrolling-{{ section.id }} {
    flex: none;
    display: flex;
    transform: translate(0);
    -webkit-animation: reviewloop{{ section.id }} {{ scrolling_velocity_mobile }}s linear infinite;
    -moz-animation: reviewloop{{ section.id }} {{ scrolling_velocity_mobile }}s linear infinite;
    -o-animation: reviewloop{{ section.id }} {{ scrolling_velocity_mobile }}s linear infinite;
  }

  .testimonials-scrolling-reverse-item-{{ section.id }} {
    margin-top: {{ scrolling_gap_mobile }}px;
  }

  .testimonials-scrolling-reverse-item-{{ section.id }} .testimonials-scrolling-{{ section.id }} {
    -webkit-animation: reviewlooprotate{{ section.id }} {{ scrolling_velocity_mobile }}s linear infinite;
    -moz-animation: reviewlooprotate{{ section.id }} {{ scrolling_velocity_mobile }}s linear infinite;
    -o-animation: reviewlooprotate{{ section.id }} {{ scrolling_velocity_mobile }}s linear infinite;
  }

  .testimonials-scrolling-inner-{{ section.id }} {
    flex: none;
    width: auto;
    transform: translate(0%);
    display: flex;
  }

  .testimonials-scrolling-inner-flex-{{ section.id }} {
    display: flex;
  }

  .testimonials-slide-{{ section.id }} {
    flex: none;
    align-items: {{ content_vertical_align_mobile }};
    max-width: {{ slide_width_mobile }}px;
    display: flex;
    margin-right: {{ slide_space_mobile }}px;
    padding: {{ slide_padding_vertical_mobile }}px {{ slide_padding_horizontal_mobile }}px;
    border-radius: {{ slide_radius }}px;
    border: {{ slide_border_thickness }}px solid {{ slide_border_color }};
    background-color: {{ slide_bg_color }};
    gap: {{ slide_gap_mobile }}px;
  }

  .testimonials-image-{{ section.id }} {
    flex: 0 0 {{ image_width_mobile }}px;
    overflow: hidden;
    border-radius: {{ image_radius }}px;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
  }

  .testimonials-image-{{ section.id }} img,
  .testimonials-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .testimonials-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .testimonials-stars-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: {{ stars_align_mobile }};
    gap: 3px;
  }

  .testimonials-stars-{{ section.id }} svg,
  .testimonials-stars-{{ section.id }} img {
    display: block;
    width: {{ stars_size }}px;
    height: {{ stars_size }}px;
    object-fit: cover;
  }

  .testimonials-heading-{{ section.id }} {
    margin-top: {{ heading_mt | times: 0.75 | round: 0 }}px;
    text-align: {{ heading_align_mobile }};
  }

  .testimonials-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .testimonials-text-{{ section.id }} {
    margin-top: {{ text_mt | times: 0.75 | round: 0 }}px;
    text-align: {{ text_align_mobile }};
  }

  .testimonials-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
  }

  .testimonials-tag-wrapper-{{ section.id }} {
    margin-top: {{ tag_mt | times: 0.75 | round: 0 }}px;
    display: flex;
    align-items: center;
    gap: 5px;
  }

  .testimonials-tag-{{ section.id }} {
    flex: 0 0 {{ tag_width_mobile }}px;
  }

  .testimonials-tag-{{ section.id }} img,
  .testimonials-tag-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .testimonials-tag-text-{{ section.id }} {
    margin: 0;
    font-size: {{ tag_text_size_mobile }}px;
    color: {{ tag_text_color }};
    line-height: {{ tag_text_height }}%;
    text-transform: unset;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .testimonials-scrolling-reverse-item-{{ section.id }} {
      margin-top: {{ scrolling_gap }}px;
    }

    .testimonials-scrolling-{{ section.id }} {
      animation-duration: {{ scrolling_velocity }}s;
    }

    .testimonials-scrolling-reverse-item-{{ section.id }} .testimonials-scrolling-{{ section.id }} {
      animation-duration: {{ scrolling_velocity }}s;
    }

    .testimonials-slide-{{ section.id }} {
      margin-right: {{ slide_space }}px;
      padding: {{ slide_padding_vertical }}px {{ slide_padding_horizontal }}px;
      align-items: {{ content_vertical_align }};
      gap: {{ slide_gap }}px;
      max-width: {{ slide_width }}px;
    }

    .testimonials-image-{{ section.id }} {
      flex: 0 0 {{ image_width }}px;
    }

    .testimonials-stars-{{ section.id }} {
      justify-content: {{ stars_align }};
    }

    .testimonials-heading-{{ section.id }} {
      margin-top: {{ heading_mt }}px;
      text-align: {{ heading_align }};
    }

    .testimonials-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .testimonials-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ text_align }};
    }

    .testimonials-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .testimonials-tag-wrapper-{{ section.id }} {
      margin-top: {{ tag_mt }}px;
    }

    .testimonials-tag-{{ section.id }} {
      flex: 0 0 {{ tag_width }}px;
    }

    .testimonials-tag-text-{{ section.id }} {
      font-size: {{ tag_text_size }}px;
    }

  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .testimonials-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .testimonials-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if tag_text_custom %}
  <style>
    .testimonials-tag-text-{{ section.id }} {
      font-family: {{ tag_text_font.family }}, {{ tag_text_font.fallback_families }};
      font-weight: {{ tag_text_font.weight }};
      font-style: {{ tag_text_font.style }};
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .testimonials-image-{{ section.id }} {
     aspect-ratio: 9.6/14; 
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
    .testimonials-image-{{ section.id }} {
     aspect-ratio: 12/9; 
    }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .testimonials-image-{{ section.id }} {
     aspect-ratio: 12/12; 
    }
  </style>
{% else%}
  <style>
    .testimonials-image-{{ section.id }} {
     aspect-ratio: auto; 
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .testimonials-image-{{ section.id }} {
       aspect-ratio: 9.6/14; 
      } 
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .testimonials-image-{{ section.id }} {
       aspect-ratio: 12/9; 
      }
    }
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .testimonials-image-{{ section.id }} {
       aspect-ratio: 12/12; 
      }
    }
  </style>
{% else%}
  <style>
    @media(min-width: 1024px) {
      .testimonials-image-{{ section.id }} {
       aspect-ratio: auto; 
      }
    }
  </style>
{% endif %}

{% if slide_shadow %}
  <style>
    .testimonials-slide-{{ section.id }} {
      box-shadow: {{ slide_shadow_color | hex_to_rgba: 0.15 }} 0px 2px 12px;

    }

    .testimonials-scrolling-{{ section.id }} {
      padding: 10px 0px;
    }
  </style>
{% endif %}

{% if tag_align_mobile == "center" %}
  <style>
   .testimonials-tag-wrapper-{{ section.id }} {
      justify-content: center;
    } 
  </style>
{% elsif tag_align_mobile == "right" %}
  <style>
   .testimonials-tag-wrapper-{{ section.id }} {
      justify-content: end;
    } 
  </style>
{% endif %}

{% if tag_align == "center" %}
  <style>
    @media(min-width: 1024px) {
      .testimonials-tag-wrapper-{{ section.id }} {
        justify-content: center;
      } 
    }
  </style>
{% elsif tag_align == "right" %}
  <style>
    @media(min-width: 1024px) {
     .testimonials-tag-wrapper-{{ section.id }} {
        justify-content: end;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .testimonials-tag-wrapper-{{ section.id }} {
        justify-content: start;
      }  
    }
  </style>
{% endif %}

{% if reverse_direction %}
  <style>
    @keyframes reviewloop{{ section.id }} {
      0% {
        transform: translateX(-50%);
      }
      100% {
        transform: translateX(0%);
      }
    }

    @keyframes reviewlooprotate{{ section.id }} {
      0% {
        transform: translateX(0%);
      }
      100% {
        transform: translateX(-50%);
      }
    }
  </style>
{% else %}
  <style>
    @keyframes reviewloop{{ section.id }} {
      0% {
        transform: translateX(0%);
      }
      100% {
        transform: translateX(-50%);
      }
    }

    @keyframes reviewlooprotate{{ section.id }} {
      0% {
        transform: translateX(-50%);
      }
      100% {
        transform: translateX(0%);
      }
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} testimonials-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="testimonials-scrolling-item-{{ section.id }}">
        <div class="testimonials-scrolling-{{ section.id }}">
          <div class="testimonials-scrolling-inner-{{ section.id }}">
            <div class="testimonials-scrolling-inner-flex-{{ section.id }}">
              {% assign blocks_count = section.blocks.size %}
              {% assign half_block_count = blocks_count | times: 0.5 | round: 0 %}
              {% for block in section.blocks %}
                {% if scrolling_in_row %}
                  <div class="testimonials-slide-{{ section.id }}">
                    {% if show_image %}
                      <div class="testimonials-image-{{ section.id }}">
                        {% if block.settings.image != blank %}
                          <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                        {% else %}
                          {{ 'image' | placeholder_svg_tag }}
                        {% endif %}
                      </div>
                    {% endif %}
                    <div class="testimonials-content-{{ section.id }}">
                      {% if block.settings.stars_count != 0 and show_stars %}
                        <div class="testimonials-stars-{{ section.id }}">
                          {% if stars_icon != blank %}
                            {% for item in (1..block.settings.stars_count) %}
                              <img src="{{ stars_icon | image_url }}" alt="{{ stars_icon.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                            {% endfor %}
                          {% else %}
                            <svg xmlns="http://www.w3.org/2000/svg" width="20" height="19" viewBox="0 0 20 19" fill="none">
                              {% if block.settings.stars_count > 0 and block.settings.stars_count < 1  %}
                                <defs>
                                  <linearGradient id="grad{{ section.id }}1">
                                    <stop offset="0%" stop-color="{{ stars_color }}" />
                                    <stop offset="50%" stop-color="{{ stars_color }}" />
                                    <stop offset="50%" stop-color="white" />
                                    <stop offset="100%" stop-color="white" />
                                  </linearGradient>
                                </defs>
                              {% endif %}
                              <path {% if block.settings.stars_count > 0 and block.settings.stars_count < 1 %}stroke="{{ stars_color }}"{% endif %} d="M10 0L12.9389 5.95492L19.5106 6.90983L14.7553 11.5451L15.8779 18.0902L10 15L4.12215 18.0902L5.24472 11.5451L0.489435 6.90983L7.06107 5.95492L10 0Z" fill="{% if block.settings.stars_count > 0 and block.settings.stars_count < 1 %}url(#grad{{ section.id }}1){% elsif block.settings.stars_count > 0.5 %}{{ stars_color }}{% endif %}"></path>
                            </svg>
                            <svg xmlns="http://www.w3.org/2000/svg" width="20" height="19" viewBox="0 0 20 19" fill="none">
                              {% if block.settings.stars_count > 1 and block.settings.stars_count < 2  %}
                                <defs>
                                  <linearGradient id="grad{{ section.id }}2">
                                    <stop offset="0%" stop-color="{{ stars_color }}" />
                                    <stop offset="50%" stop-color="{{ stars_color }}" />
                                    <stop offset="50%" stop-color="white" />
                                    <stop offset="100%" stop-color="white" />
                                  </linearGradient>
                                </defs>
                              {% endif %}
                              <path {% if block.settings.stars_count > 1 and block.settings.stars_count < 2 %}stroke="{{ stars_color }}"{% endif %} d="M10 0L12.9389 5.95492L19.5106 6.90983L14.7553 11.5451L15.8779 18.0902L10 15L4.12215 18.0902L5.24472 11.5451L0.489435 6.90983L7.06107 5.95492L10 0Z" fill="{% if block.settings.stars_count > 1 and block.settings.stars_count < 2 %}url(#grad{{ section.id }}2){% elsif block.settings.stars_count > 1.5 %}{{ stars_color }}{% endif %}"></path>
                            </svg>
                            <svg xmlns="http://www.w3.org/2000/svg" width="20" height="19" viewBox="0 0 20 19" fill="none">
                              {% if block.settings.stars_count > 2 and block.settings.stars_count < 3  %}
                                <defs>
                                  <linearGradient id="grad{{ section.id }}3">
                                    <stop offset="0%" stop-color="{{ stars_color }}" />
                                    <stop offset="50%" stop-color="{{ stars_color }}" />
                                    <stop offset="50%" stop-color="white" />
                                    <stop offset="100%" stop-color="white" />
                                  </linearGradient>
                                </defs>
                              {% endif %}
                              <path {% if block.settings.stars_count > 2 and block.settings.stars_count < 3 %}stroke="{{ stars_color }}"{% endif %} d="M10 0L12.9389 5.95492L19.5106 6.90983L14.7553 11.5451L15.8779 18.0902L10 15L4.12215 18.0902L5.24472 11.5451L0.489435 6.90983L7.06107 5.95492L10 0Z" fill="{% if block.settings.stars_count > 2 and block.settings.stars_count < 3 %}url(#grad{{ section.id }}3){% elsif block.settings.stars_count > 2.5 %}{{ stars_color }}{% endif %}"></path>
                            </svg>
                            <svg xmlns="http://www.w3.org/2000/svg" width="20" height="19" viewBox="0 0 20 19" fill="none">
                              {% if block.settings.stars_count > 3 and block.settings.stars_count < 4  %}
                                <defs>
                                  <linearGradient id="grad{{ section.id }}4">
                                    <stop offset="0%" stop-color="{{ stars_color }}" />
                                    <stop offset="50%" stop-color="{{ stars_color }}" />
                                    <stop offset="50%" stop-color="white" />
                                    <stop offset="100%" stop-color="white" />
                                  </linearGradient>
                                </defs>
                              {% endif %}
                              <path {% if block.settings.stars_count > 3 and block.settings.stars_count < 4  %}stroke="{{ stars_color }}"{% endif %} d="M10 0L12.9389 5.95492L19.5106 6.90983L14.7553 11.5451L15.8779 18.0902L10 15L4.12215 18.0902L5.24472 11.5451L0.489435 6.90983L7.06107 5.95492L10 0Z" fill="{% if block.settings.stars_count > 3 and block.settings.stars_count < 4  %}url(#grad{{ section.id }}4){% elsif block.settings.stars_count > 3.5 %}{{ stars_color }}{% endif %}"></path>
                            </svg>
                            <svg xmlns="http://www.w3.org/2000/svg" width="20" height="19" viewBox="0 0 20 19" fill="none">
                              {% if block.settings.stars_count > 4 and block.settings.stars_count < 5  %}
                                <defs>
                                  <linearGradient id="grad{{ section.id }}5">
                                    <stop offset="0%" stop-color="{{ stars_color }}" />
                                    <stop offset="50%" stop-color="{{ stars_color }}" />
                                    <stop offset="50%" stop-color="white" />
                                    <stop offset="100%" stop-color="white" />
                                  </linearGradient>
                                </defs>
                              {% endif %}
                              <path {% if block.settings.stars_count > 4 and block.settings.stars_count < 5  %}stroke="{{ stars_color }}"{% endif %} d="M10 0L12.9389 5.95492L19.5106 6.90983L14.7553 11.5451L15.8779 18.0902L10 15L4.12215 18.0902L5.24472 11.5451L0.489435 6.90983L7.06107 5.95492L10 0Z" fill="{% if block.settings.stars_count > 4 and block.settings.stars_count < 5  %}url(#grad{{ section.id }}5){% elsif block.settings.stars_count > 4.5 %}{{ stars_color }}{% endif %}"></path>
                            </svg>
                          {% endif %}
                        </div>
                      {% endif %}
                      {% if block.settings.heading != blank and show_heading %}
                        <div class="testimonials-heading-{{ section.id }}">
                          {{ block.settings.heading }}
                        </div>
                      {% endif %}

                      {% if use_tag %}
                        <div class="testimonials-tag-wrapper-{{ section.id }}">
                          <div class="testimonials-tag-{{ section.id }}">
                            {% if tag != blank %}
