                                <path fill="{{ tag_icon_color }}" d="M26.331,4.609l2.49,1.397l2.855,0.035c1.66,0.021,3.19,0.904,4.038,2.331l1.458,2.455 l2.455,1.458c1.428,0.848,2.311,2.378,2.331,4.038l0.035,2.855l1.397,2.49c0.812,1.448,0.812,3.215,0,4.663l-1.397,2.49 l-0.035,2.855c-0.021,1.66-0.904,3.19-2.331,4.038l-2.455,1.458l-1.458,2.455c-0.848,1.428-2.378,2.311-4.038,2.331l-2.855,0.035 l-2.49,1.397c-1.448,0.812-3.215,0.812-4.663,0l-2.49-1.397l-2.855-0.035c-1.66-0.021-3.19-0.904-4.038-2.331l-1.458-2.455 l-2.455-1.458c-1.428-0.848-2.311-2.378-2.331-4.038l-0.035-2.855l-1.397-2.49c-0.812-1.448-0.812-3.215,0-4.663l1.397-2.49 l0.035-2.855c0.021-1.66,0.904-3.19,2.331-4.038l2.455-1.458l1.458-2.455c0.848-1.428,2.378-2.311,4.038-2.331l2.855-0.035 l2.49-1.397C23.117,3.797,24.883,3.797,26.331,4.609z"></path><path d="M32.172,16.172L22,26.344l-5.172-5.172c-0.781-0.781-2.047-0.781-2.828,0l-1.414,1.414 c-0.781,0.781-0.781,2.047,0,2.828l8,8c0.781,0.781,2.047,0.781,2.828,0l13-13c0.781-0.781,0.781-2.047,0-2.828L35,16.172 C34.219,15.391,32.953,15.391,32.172,16.172z" opacity=".05"></path><path d="M20.939,33.061l-8-8c-0.586-0.586-0.586-1.536,0-2.121l1.414-1.414c0.586-0.586,1.536-0.586,2.121,0 L22,27.051l10.525-10.525c0.586-0.586,1.536-0.586,2.121,0l1.414,1.414c0.586,0.586,0.586,1.536,0,2.121l-13,13 C22.475,33.646,21.525,33.646,20.939,33.061z" opacity=".07"></path><path fill="#fff" d="M21.293,32.707l-8-8c-0.391-0.391-0.391-1.024,0-1.414l1.414-1.414c0.391-0.391,1.024-0.391,1.414,0 L22,27.758l10.879-10.879c0.391-0.391,1.024-0.391,1.414,0l1.414,1.414c0.391,0.391,0.391,1.024,0,1.414l-13,13 C22.317,33.098,21.683,33.098,21.293,32.707z"></path>
