{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign heading = section.settings.heading
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_height = section.settings.heading_height
  assign heading_color = section.settings.heading_color
  assign heading_active_color = section.settings.heading_active_color
  assign heading_align = section.settings.heading_align
  assign heading_align_mobile = section.settings.heading_align_mobile

  assign heading_horizontal_align = ""
  if heading_align == "center"
    assign heading_horizontal_align = "center"
  elsif heading_align == "right"
    assign heading_horizontal_align = "end"
  else
    assign heading_horizontal_align = "start"
  endif

  assign heading_horizontal_align_mobile = ""
  if heading_align_mobile == "center"
    assign heading_horizontal_align_mobile = "center"
  elsif heading_align_mobile == "right"
    assign heading_horizontal_align_mobile = "end"
  else
    assign heading_horizontal_align_mobile = "start"
  endif

  assign button = section.settings.button
  assign button_url  = section.settings.button_url
  assign button_size = section.settings.button_size
  assign button_size_mobile = section.settings.button_size_mobile
  assign button_color = section.settings.button_color
  assign button_hover_color = section.settings.button_hover_color
  assign button_custom = section.settings.button_custom
  assign button_font = section.settings.button_font
  assign button_height = section.settings.button_height
  assign button_padding_vertical = section.settings.button_padding_vertical
  assign button_padding_vertical_mobile = section.settings.button_padding_vertical_mobile
  assign button_padding_horizontal = section.settings.button_padding_horizontal
  assign button_padding_horizontal_mobile = section.settings.button_padding_horizontal_mobile
  assign button_radius = section.settings.button_radius
  assign button_border_thickness = section.settings.button_border_thickness
  assign button_border_color = section.settings.button_border_color
  assign button_border_hover_color = section.settings.button_border_hover_color
  assign button_bg_color = section.settings.button_bg_color
  assign button_bg_hover_color = section.settings.button_bg_hover_color
  assign button_style = section.settings.button_style
  assign button_align_mobile = section.settings.button_align_mobile
  assign button_mt_mobile = section.settings.button_mt_mobile

  assign button_horizontal_align_mobile = ""
  if button_align_mobile == "center"
    assign button_horizontal_align_mobile = "center"
  elsif button_align_mobile == "right"
    assign button_horizontal_align_mobile = "end"
  else
    assign button_horizontal_align_mobile = "start"
  endif

  assign testimonials_gap = section.settings.testimonials_gap
  assign testimonials_gap_mobile = section.settings.testimonials_gap_mobile
  assign testimonials_mt = section.settings.testimonials_mt
  assign testimonials_mt_mobile = section.settings.testimonials_mt_mobile
  assign testimonials_shadow = section.settings.testimonials_shadow

  assign card_radius = section.settings.card_radius
  assign card_border_thickness = section.settings.card_border_thickness
  assign card_border_color = section.settings.card_border_color

  assign review_padding_horizontal = section.settings.review_padding_horizontal
  assign review_padding_horizontal_mobile = section.settings.review_padding_horizontal_mobile
  assign review_padding_vertical = section.settings.review_padding_vertical
  assign review_padding_vertical_mobile = section.settings.review_padding_vertical_mobile 
  assign review_bg_color = section.settings.review_bg_color
  assign review_align = section.settings.review_align
  assign review_align_mobile = section.settings.review_align_mobile

  assign review_horizontal_align = ""
  if review_align == "center"
    assign review_horizontal_align = "center"
  elsif review_align == "right"
    assign review_horizontal_align = "end"
  else
    assign review_horizontal_align = "start"
  endif

  assign review_horizontal_align_mobile = ""
  if review_align_mobile == "center"
    assign review_horizontal_align_mobile = "center"
  elsif review_align_mobile == "right"
    assign review_horizontal_align_mobile = "end"
  else
    assign review_horizontal_align_mobile = "start"
  endif

  assign author_wrapper_gap = section.settings.author_wrapper_gap
  assign author_wrapper_gap_mobile = section.settings.author_wrapper_gap_mobile

  assign author_image_width = section.settings.author_image_width
  assign author_image_width_mobile = section.settings.author_image_width_mobile
  assign author_image_radius = section.settings.author_image_radius
  assign author_image_border_thickness = section.settings.author_image_border_thickness
  assign author_image_border_color = section.settings.author_image_border_color

  assign author_custom = section.settings.author_custom
  assign author_font = section.settings.author_font
  assign author_size = section.settings.author_size
  assign author_size_mobile = section.settings.author_size_mobile
  assign author_height = section.settings.author_height
  assign author_color = section.settings.author_color

  assign author_username_custom = section.settings.author_username_custom
  assign author_username_font = section.settings.author_username_font
  assign author_username_size = section.settings.author_username_size
  assign author_username_size_mobile = section.settings.author_username_size_mobile
  assign author_username_height = section.settings.author_username_height
  assign author_username_color = section.settings.author_username_color
  assign author_username_mt = section.settings.author_username_mt
  assign author_username_mt_mobile = section.settings.author_username_mt_mobile

  assign author_time_custom = section.settings.author_time_custom
  assign author_time_font = section.settings.author_time_font
  assign author_time_size = section.settings.author_time_size
  assign author_time_size_mobile = section.settings.author_time_size_mobile
  assign author_time_height = section.settings.author_time_height
  assign author_time_color = section.settings.author_time_color
  assign author_time_mt = section.settings.author_time_mt
  assign author_time_mt_mobile = section.settings.author_time_mt_mobile

  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_height = section.settings.text_height
  assign text_color = section.settings.text_color
  assign text_mt = section.settings.text_mt
  assign text_mt_mobile = section.settings.text_mt_mobile

  assign media_ratio = section.settings.media_ratio
  assign media_ratio_mobile = section.settings.media_ratio_mobile
  assign media_align = section.settings.media_align
  assign media_content_align_mobile = section.settings.media_content_align_mobile

  assign play_size = section.settings.play_size
  assign play_size_mobile = section.settings.play_size_mobile
  assign play_icon_color = section.settings.play_icon_color

  assign mute_size = section.settings.mute_size
  assign mute_size_mobile = section.settings.mute_size_mobile
  assign mute_bg_color = section.settings.mute_bg_color
  assign mute_icon_color = section.settings.mute_icon_color

  assign media_content_align = section.settings.media_content_align
  assign media_content_align_mobile = section.settings.media_content_align_mobile

  assign media_title_custom = section.settings.media_title_custom
  assign media_title_font = section.settings.media_title_font
  assign media_title_size = section.settings.media_title_size
  assign media_title_size_mobile = section.settings.media_title_size_mobile
  assign media_title_height = section.settings.media_title_height
  assign media_title_color = section.settings.media_title_color

  assign media_text_custom = section.settings.media_text_custom
  assign media_text_font = section.settings.media_text_font
  assign media_text_size = section.settings.media_text_size
  assign media_text_size_mobile = section.settings.media_text_size_mobile
  assign media_text_height = section.settings.media_text_height
  assign media_text_color = section.settings.media_text_color
  assign media_text_mt = section.settings.media_text_mt
  assign media_text_mt_mobile = section.settings.media_text_mt_mobile

  assign media_review_bg_color = section.settings.media_review_bg_color
  assign media_review_align = section.settings.media_review_align
  assign media_review_align_mobile = section.settings.media_review_align_mobile

  assign image_ratio = section.settings.image_ratio
  assign image_ratio_mobile = section.settings.image_ratio_mobile
  assign image_radius = section.settings.image_radius
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color

  assign media_review_tag_custom = section.settings.media_review_tag_custom
  assign media_review_tag_font = section.settings.media_review_tag_font
  assign media_review_tag_size = section.settings.media_review_tag_size
  assign media_review_tag_size_mobile = section.settings.media_review_tag_size_mobile
  assign media_review_tag_height = section.settings.media_review_tag_height
  assign media_review_tag_color = section.settings.media_review_tag_color
  assign media_review_tag_mt = section.settings.media_review_tag_mt
  assign media_review_tag_mt_mobile = section.settings.media_review_tag_mt_mobile  

  assign column_first_count = 0
  assign column_second_count = 0
  assign column_third_count = 0
  assign column_fourth_count = 0

  for block in section.blocks
    if block.settings.column == "1"
      assign column_first_count = column_first_count | plus: 1
    elsif block.settings.column == "2"
      assign column_second_count = column_second_count | plus: 1  
    elsif block.settings.column == "3"
      assign column_third_count = column_third_count | plus: 1  
    elsif block.settings.column == "4"
      assign column_fourth_count = column_fourth_count | plus: 1  
    endif
  endfor
-%}

{%- style -%}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}
  {{  author_font | font_face: font_display: 'swap' }}
  {{  author_username_font | font_face: font_display: 'swap' }}
  {{  author_time_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}
  {{  media_title_font | font_face: font_display: 'swap' }}
  {{  media_text_font | font_face: font_display: 'swap' }}
  {{  media_review_tag_font | font_face: font_display: 'swap' }}  

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
    position: relative;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }


  .section-{{ section.id }}::after {
    content: "";
    display: block;
    z-index: 1;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: -32px;
    pointer-events: none;
    background: linear-gradient(180deg, {{ background_color }}00 40%, {{ background_color }}F2 90%);
  }

  .testimonials-top-{{ section.id }} {
    display: flex;
    align-items: center;
    flex-direction: column;
    justify-content: {{ heading_horizontal_align_mobile }};
  }

  .testimonials-heading-{{ section.id }} {
    width: 100%;
  }

  .testimonials-heading-{{ section.id }} * {
    margin: 0px;
    font-size: {{ heading_size_mobile }}px;
    line-height: {{ heading_height }}%;
    color: {{ heading_color }};
    text-transform: unset;
    word-break: break-word;
  }

  .testimonials-heading-{{ section.id }} strong {
    color: {{ heading_active_color }};
  }

  .testimonials-top-{{ section.id }} {
    justify-content: {{ heading_horizontal_align_mobile }};
  }

  .testimonials-heading-{{ section.id }} * {
    text-align: {{ heading_align_mobile }};
  }

  .testimonials-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    width: 100%;
    max-width: fit-content;
    margin: 0;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    background-color: transparent;
    border: 0px;
    cursor: pointer;
    font-weight: 700;
    margin-top: {{ button_mt_mobile }}px;
  }

  .testimonials-button-wrapper-{{ section.id }} {
    display: flex;
    width: 100%;
    justify-content: {{ button_horizontal_align_mobile }};
  }

  .testimonials-button-mobile-{{ section.id }} {
    position: relative;
    z-index: 2;
    display: block;
  }

  .testimonials-button-desktop-{{ section.id }} {
    display: none;
  }

  .testimonials-button-inner-{{ section.id }} {
    margin: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
  }

  .testimonials-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .testimonials-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .testimonials-button-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .testimonials-reviews-{{ section.id }} {
    display: flex;
    gap: {{ testimonials_gap_mobile }}px;
    position: relative;
    margin-top: {{ testimonials_mt_mobile }}px;
  }

  .testimonials-reviews-column-{{ section.id }} {
    width: 50%;
    display: flex;
    flex-direction: column;
    gap: {{ testimonials_gap_mobile }}px;
  }

  .testimonials-column-{{ section.id }} {
    display: flex;
    flex-direction: column;
    gap: {{ testimonials_gap_mobile }}px;
  }

  .testimonials-column-{{ section.id }}:first-child {
    margin-top: 0px;
  }

  .testimonials-review-{{ section.id }} {
    max-height: fit-content;
    padding: {{ review_padding_vertical_mobile }}px {{ review_padding_horizontal_mobile }}px;
    border: {{ card_border_thickness }}px solid {{ card_border_color }};
    border-radius: {{ card_radius }}px;
    background-color: {{ review_bg_color }};
    transition: all 0.25s ease 0s;
  }

  .testimonials-author-{{ section.id }} {
    display: flex;
    align-items: center;
    flex-wrap: wrap;
    justify-content: {{ review_horizontal_align_mobile }};
    gap: {{ author_wrapper_gap_mobile }}px;
  }

  .testimonials-author-image-{{ section.id }} {
    flex-shrink: 0;
    border: {{ author_image_border_thickness }}px solid {{ author_image_border_color }};
    max-width: {{ author_image_width_mobile }}px;    
    width: 100%;
    border-radius: {{ author_image_radius }}px;
    overflow: hidden;
    aspect-ratio: 1/1;
  }

  .testimonials-author-image-{{ section.id }} img,
  .testimonials-author-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .testimonials-author-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .testimonials-author-text-{{ section.id }} {    
    margin: 0;
    font-weight: 700;
    font-size: {{ author_size_mobile }}px;
    color: {{ author_color }};
    line-height: {{ author_height }}%;
    text-transform: unset;
    word-wrap: break-word;
    text-align: {{ review_align_mobile }};
  }

  .testimonials-author-username-{{ section.id }} {
    margin: 0;
    margin-top: {{ author_username_mt_mobile }}px;
    font-size: {{ author_username_size_mobile }}px;
    color: {{ author_username_color }};
    line-height: {{ author_username_height }}%;
    text-transform: unset;
    word-wrap: break-word;
    text-align: {{ review_align_mobile }};
  }

  .testimonials-text-{{ section.id }} {
    margin-top: {{ text_mt_mobile }}px;
    text-align: {{ review_align_mobile }};
  }

  .testimonials-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .testimonials-author-time-{{ section.id }} {
    margin: 0;
    margin-top: {{ author_time_mt_mobile }}px;
    font-size: {{ author_time_size_mobile }}px;
    color: {{ author_time_color }};
    line-height: {{ author_time_height }}%;
    text-transform: unset;
    word-wrap: break-word;
    text-align: {{ review_align_mobile }};
  }

  .testimonials-media-{{ section.id }} {
    position: relative;
    border: {{ card_border_thickness }}px solid {{ card_border_color }};
    border-radius: {{ card_radius }}px;
    overflow: hidden;
    transition: all 0.25s ease 0s;
  }

  .testimonials-media-{{ section.id }} img,
  .testimonials-media-{{ section.id }} svg,
  .testimonials-media-{{ section.id }} video,
  .testimonials-media-{{ section.id }} iframe  {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    margin: 0;
    border: 0;
    box-shadow: 0;
  }

  .testimonials-media-{{ section.id }}::after {
    content: "";
    position: absolute;
    z-index: 1;
    width: 100%;
    height: 100%;
    left: 0;
    top: 0;
    background: linear-gradient(180deg, transparent 40.63%, #000 94%);
  }

  .testimonials-mute-{{ section.id }} {
    position: absolute;
    top: 10px;
    right: 10px;
    z-index: 2;
    width: {{ mute_size_mobile }}px;
    height: {{ mute_size_mobile }}px;
    opacity: 1;
    transition: all 0.25s ease 0s;
  }

  .testimonials-mute-{{ section.id }}.hidden {
    opacity: 0 !important;
    transition: all 0.25s ease 0s;
  }

  .testimonials-mute-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .testimonials-mute-{{ section.id }} svg path:nth-child(1) {
    fill: {{ mute_bg_color }};
  }

  .testimonials-mute-{{ section.id }} svg path:nth-child(2) {
    fill: {{ mute_icon_color }};
  }

  .testimonials-mute-{{ section.id }} svg:nth-child(1) {
    display: none;
  }

  .testimonials-mute-{{ section.id }}.active svg:nth-child(1) {
    display: block;
  }

  .testimonials-mute-{{ section.id }}.active svg:nth-child(2) {
    display: none;
  }

  .testimonials-play-{{ section.id }} {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    z-index: 2;
    opacity: 1;
    transition: all 0.25s ease 0s;
    width: {{ play_size_mobile }}px;
    height: {{ play_size_mobile }}px;
    pointer-events: all;
  }

  .testimonials-play-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .testimonials-play-{{ section.id }} svg path {
    fill: {{ play_icon_color }};
  }

  .testimonials-media-content-{{ section.id }} {
    position: absolute;
    bottom: 10px;
    left: 0;
    width: 100%;
    padding: {{ review_padding_vertical_mobile }}px {{ review_padding_horizontal_mobile }}px;
    z-index: 2;
  }

  .testimonials-media-title-{{ section.id }} {
    margin: 0;
    font-size: {{ media_title_size_mobile }}px;
    color: {{ media_title_color }};
    line-height: {{ media_title_height }}%;
    text-transform: unset;
    word-wrap: break-word;
    text-align: {{ media_content_align_mobile }};
  }

  .testimonials-media-text-{{ section.id }} {
    margin-top: {{ media_text_mt_mobile }}px;
    text-align: {{ media_content_align_mobile }};
  }

  .testimonials-media-text-{{ section.id }} * {    
    margin: 0;
    font-size: {{ media_text_size_mobile }}px;
    color: {{ media_text_color }};
    line-height: {{ media_text_height }}%;
    text-transform: unset;
    word-wrap: break-word; 
  }

  .testimonials-media-review-{{ section.id }} {
    background-color: {{ media_review_bg_color }};
    padding: {{ review_padding_vertical_mobile }}px {{ review_padding_horizontal_mobile }}px;
    border-radius: {{ card_radius }}px;
    border: {{ card_border_thickness }}px solid {{ card_border_color }};
    transition: all 0.25s ease 0s;
  }

  .testimonials-media-review-image-{{ section.id }} {
    width: 100%;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    border-radius: {{ image_radius }}px;
    overflow: hidden;
  }

  .testimonials-media-review-image-{{ section.id }} img,
  .testimonials-media-review-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .testimonials-media-review-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .testimonials-media-review-text-{{ section.id }} {
    text-align: {{ media_review_align_mobile }};
    margin-top: 12px;
  }

  .testimonials-media-review-author-text-{{ section.id }} {
    text-align: {{ media_review_align_mobile }};
    margin-top: 12px;
  }

  .testimonials-media-review-tag-{{ section.id }} {
    margin: 0;
    margin-top: {{ media_review_tag_mt_mobile }}px;
    font-size: {{ media_review_tag_size_mobile }}px;
    color: {{ media_review_tag_color }};
    line-height: {{ media_review_tag_height }}%;
    text-transform: unset;
    word-wrap: break-word;
    text-align: {{ media_review_align_mobile }};
  }  

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .testimonials-top-{{ section.id }} {
      flex-direction: row;
      justify-content: space-between;
    }   

    .testimonials-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
      text-align: left;
    }

    .testimonials-button-{{ section.id }} {
      margin-top: 0px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }

    .testimonials-button-mobile-{{ section.id }} {
      display: none;      
    }

    .testimonials-button-desktop-{{ section.id }} {
      display: block;
    }

    .testimonials-button-{{ section.id }}:hover {
      color: {{ button_hover_color }};
      transition: all 0.25s ease 0s;
