                        <div class="testimonials-media-content-{{ section.id }}">
                          {% if block.settings.title != blank %}
                            <div class="testimonials-media-title-{{ section.id }}">{{ block.settings.title }}</div>
                          {% endif %}
                          {% if block.settings.media_text != blank %}
                            <div class="testimonials-media-text-{{ section.id }}">{{ block.settings.media_text }}</div>
                          {% endif %}
                        </div>
                      </div>
                    {% endif %}                
                  {% when 'media_review' %}
                    {% if block.settings.column == '1' %}
                      <div class="testimonials-media-review-{{ section.id }} testimonials-media-review-{{ block.id }} testimonials-card-{{ section.id }}">
                        <div class="testimonials-media-review-image-{{ section.id }}">
                          {% if block.settings.image != blank %}
                            <img src="{{ block.settings.image | img_url: "400x400" }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                          {% else %}
                            {{ 'image' | placeholder_svg_tag }}
                          {% endif %}   
                        </div>
                        {% if block.settings.text %}
                          <div class="testimonials-text-{{ section.id }} testimonials-media-review-text-{{ section.id }}">{{ block.settings.text }}</div>
                        {% endif %}
                        {% if block.settings.author != blank %}
                          <p class="testimonials-author-text-{{ section.id }} testimonials-media-review-author-text-{{ section.id }}">{{ block.settings.author }}</p>
                        {% endif %}
                        {% if block.settings.review_tag %}
                          <p class="testimonials-media-review-tag-{{ section.id }}">{{ block.settings.review_tag }}</p>
                        {% endif %}  
                      </div>  
                    {% endif %}                
                {% endcase %}
              {% endfor %}
            </div>
          {% endif %}
          {% if column_second_count > 0 %}
            <div class="testimonials-column-{{ section.id }}">
              {% for block in section.blocks %}
                {% case block.type %}
                  {% when 'review' %}
                    {% if block.settings.column == '2' %}
                      <div class="testimonials-review-{{ section.id }} testimonials-review-{{ block.id }} testimonials-card-{{ section.id }}">
                        <div class="testimonials-author-{{ section.id }}">
                          <div class="testimonials-author-image-{{ section.id }}">
                            {% if block.settings.author_image != blank %}
                              <img src="{{ block.settings.author_image | image_url }}" alt="{{ block.settings.author_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>                 
                            {% else %}    
                              {{ 'image' | placeholder_svg_tag }}
                            {% endif %}
                          </div>
                          <div class="testimonials-author-content-{{ section.id }}">
                            {% if block.settings.author != blank %}
                              <p class="testimonials-author-text-{{ section.id }}">{{ block.settings.author }}</p>
                            {% endif %}
                            {% if block.settings.username %}
                              <p class="testimonials-author-username-{{ section.id }}">{{ block.settings.username }}</p>
                            {% endif %}                  
                          </div>
                        </div>
                        {% if block.settings.text %}
                          <div class="testimonials-text-{{ section.id }}">{{ block.settings.text }}</div>
                        {% endif %}
                        {% if block.settings.time %}
                          <p class="testimonials-author-time-{{ section.id }}">{{ block.settings.time }}</p>
                        {% endif %}
                      </div>
                    {% endif %}               
                  {% when 'media' %}  
                    {% if block.settings.column == '2' %}
                      <div class="testimonials-media-{{ section.id }} testimonials-media-{{ block.id }} testimonials-card-{{ section.id }}">
                        {% if block.settings.media_type == 'image' %}                   
                          {% if block.settings.image != blank %}
                            <img src="{{ block.settings.image | img_url: "400x400" }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                          {% else %}
                            {{ 'image' | placeholder_svg_tag }}
                          {% endif %}                   
                        {% elsif block.settings.media_type == 'video' %}            
                          {% assign video_id = block.settings.video.id | default: block.settings.video_url.id %}
                          {% assign loop = '&loop=1&playlist=' | append: video_id %}
                          {%- if block.settings.video == null and block.settings.video_url != null -%}
                          {%- if block.settings.video_url.type == 'youtube' -%}
                          <iframe
                            data-src="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1&mute=1&autoplay=0&controls=0&showinfo=0{{ loop }}"
                            data-src1="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1&mute=1&autoplay=1&controls=0&showinfo=0{{ loop }}"      
                            data-src2="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1&mute=0&autoplay=1&controls=0&showinfo=0{{ loop }}"                          
                            allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                            allowfullscreen
                            class="lazy"
                          ></iframe>
                          {%- else -%}
                          <iframe
                            data-src="https://player.vimeo.com/video/{{ video_id }}?enablejsapi=1&mute=1&muted=1&autoplay=0&controls=0&showinfo=0{{ loop }}"
                            data-src1="https://player.vimeo.com/video/{{ video_id }}?enablejsapi=1&mute=1&muted=1&autoplay=1&controls=0&showinfo=0{{ loop }}"
                            data-src2="https://player.vimeo.com/video/{{ video_id }}?enablejsapi=1&mute=0&&autoplay=1&controls=0&shinfo=0{{ loop }}"
                            allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                            allowfullscreen
                            class="lazy"
                          ></iframe>
                          {% endif %}
                          {% else %}
                          {% assign placeholder_video = 'https://cdn.shopify.com/videos/c/o/v/fbb140fd46e3429eba459e1549dbbe38.mp4' %}
                          <video data-src="{% if block.settings.video != blank %}{{ block.settings.video.sources[1].url }}{% else %}{{ placeholder_video }}{% endif %}" muted playsinline preload="none" preload="none" loop class="lazy"></video>
                          {% endif %}
