{%- style -%}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}
  {{  author_font | font_face: font_display: 'swap' }}
  {{  author_job_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    position: relative;
  }

  .testimonials-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
    padding: 0 {{ heading_padding_horizontal_mobile }}px;
  }

  .testimonials-heading-{{ section.id }} * {
    margin: 0px;
    font-size: {{ heading_size_mobile }}px;
    line-height: {{ heading_height }}%;
    color: {{ heading_color }};
    text-transform: unset;
    word-break: break-word;
    font-weight: 700;
  }

  .testimonials-scrolling-{{ section.id }} {
    margin-top: {{ scrolling_mt_mobile }}px;
    mask-image: linear-gradient(90deg, transparent, rgba(0, 0, 0, 1) 10%, rgba(0, 0, 0, 1) 90%, transparent);
    overflow: hidden;   
  }

  .testimonials-wrapper-{{ section.id }} {
    position: relative;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-wrap: nowrap;
    flex-wrap: nowrap;
    width: 200vw;
    position: relative;
    z-index: 0;
    left: -100vw;
    gap: {{ scrolling_gap_mobile }}px;
    background-attachment: scroll !important;    

  }

  .testimonials-wrapper-{{ section.id }}:first-child .testimonials-list-{{ section.id }} {
    -webkit-animation: tickerUp{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile | times: 3 }}s infinite linear;
    animation: tickerUp{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile | times: 3 }}s infinite linear;
  }

  .testimonials-wrapper-{{ section.id }}:last-child .testimonials-list-{{ section.id }} {
    -webkit-animation: tickerDown{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile | times: 3 }}s infinite linear;
    animation: tickerDown{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile | times: 3 }}s infinite linear;
    margin-top: {{ scrolling_gap_mobile }}px;
  }

  .testimonials-list-{{ section.id }} {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    white-space: nowrap;
    background-attachment: scroll !important;
    gap: {{ scrolling_gap_mobile }}px;   
    flex-shrink: 0;
    -webkit-animation: tickerUp{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
    animation: tickerUp{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;   
  }

  .testimonials-card-{{ section.id }} {
    width: {{ card_width_mobile }}px;
    padding: {{ card_padding_vertical_mobile }}px {{ card_padding_horizontal_mobile }}px;
    border: {{ card_border_thickness }}px solid {{ card_border_color }};
    border-radius: {{ card_radius }}px;
    background-color: {{ card_bg_color }};
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    text-decoration: none;
  }

  .testimonials-author-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: {{ card_content_horizontal_align_mobile }};
    gap: {{ author_wrapper_gap_mobile }}px;
  }

  .testimonials-author-image-{{ section.id }} {
    flex-shrink: 0;
    width: {{ author_image_width_mobile }}px;    
    border-radius: {{ author_image_radius }}px;
    overflow: hidden;
    aspect-ratio: 1/1;
  }

  .testimonials-author-image-{{ section.id }} img,
  .testimonials-author-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .testimonials-author-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .testimonials-author-text-{{ section.id }} {    
    margin: 0;
    font-size: {{ author_size_mobile }}px;
    color: {{ author_color }};
    line-height: {{ author_height }}%;
    text-transform: unset;
    word-wrap: break-word;
    text-align: {{ card_content_align_mobile }};
    font-weight: 700;
  }

  .testimonials-author-job-{{ section.id }} {
    margin: 0;
    margin-top: {{ author_job_mt_mobile }}px;
    font-size: {{ author_job_size_mobile }}px;
    color: {{ author_job_color }};
    line-height: {{ author_job_height }}%;
    text-transform: unset;
    word-wrap: break-word;
    text-align: {{ card_content_align_mobile }};
  }

  .testimonials-text-{{ section.id }} {
    margin-top: {{ text_mt_mobile }}px;
    text-align: {{ card_content_align_mobile }};
    text-wrap: wrap;
  }

  .testimonials-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .testimonials-bottom-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: space-between;
    flex-wrap: wrap;
    gap: 10px;
    margin-top: {{ stars_mt_mobile }}px;
  }

  .testimonials-stars-{{ section.id }} {
    display: flex;
    align-items: end;
    gap: {{ stars_gap }}px;
  }

  .testimonials-stars-{{ section.id }} svg,
  .testimonials-stars-{{ section.id }} img {
    display: block;
    width: {{ stars_size_mobile }}px;
    height: {{ stars_size_mobile }}px;
    object-fit: cover;
  }

  .testimonials-stars-{{ section.id }} svg {
    background-color: {{ stars_bg_color }}; 
  }

  .testimonials-circle-icon-{{ section.id }} {
    width: {{ circle_size_mobile }}px;
    height: {{ circle_size_mobile }}px;
  }

  .testimonials-circle-icon-{{ section.id }} img,
  .testimonials-circle-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .testimonials-circle-icon-{{ section.id }} svg path {
    fill: {{ circle_color }};
  }

  .testimonials-circle-icon-{{ section.id }} svg circle {
    stroke: {{ circle_color }};
  }

  .testimonials-button-wrapper-{{ section.id }} {
    display: flex;
    width: 100%;
    justify-content: {{ button_horizontal_align_mobile }};
    padding: 0 {{ button_margin_horizontal_mobile }}px;
  }

  .testimonials-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    width: 100%;
    max-width: fit-content;
    margin: 0;
    margin-top: {{ button_mt_mobile }}px;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    background-color: transparent;
    border: 0px;
    cursor: pointer;
    font-weight: 700;
  }

  .testimonials-button-inner-{{ section.id }} {
    margin: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
  }

  .testimonials-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .testimonials-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .testimonials-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .testimonials-button-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }


  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .testimonials-heading-{{ section.id }} {
      text-align: {{ heading_align }};
      padding: 0 {{ heading_padding_horizontal }}px;
    }

    .testimonials-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .testimonials-scrolling-{{ section.id }} {
      margin-top: {{ scrolling_mt }}px;
    }

    .testimonials-wrapper-{{ section.id }} {
      gap: {{ scrolling_gap }}px;     
    }

    .testimonials-wrapper-{{ section.id }}:first-child .testimonials-list-{{ section.id }} {
      -webkit-animation: tickerUp{{ section.id | replace: '-', '' }} {{ scrolling_velocity | times: 3 }}s infinite linear;
      animation: tickerUp{{ section.id | replace: '-', '' }} {{ scrolling_velocity | times: 3 }}s infinite linear;
    }

    .testimonials-wrapper-{{ section.id }}:last-child .testimonials-list-{{ section.id }} {
      -webkit-animation: tickerDown{{ section.id | replace: '-', '' }} {{ scrolling_velocity | times: 3 }}s infinite linear;
      animation: tickerDown{{ section.id | replace: '-', '' }} {{ scrolling_velocity | times: 3 }}s infinite linear;
      margin-top: {{ scrolling_gap }}px;
    }    

    .testimonials-list-{{ section.id }} {
      gap: {{ scrolling_gap }}px;    
    }

    .testimonials-card-{{ section.id }} {
      width: {{ card_width }}px;
      padding: {{ card_padding_vertical }}px {{ card_padding_horizontal }}px;
    }

    .testimonials-author-{{ section.id }} {
      justify-content: {{ card_content_horizontal_align }};
      gap: {{ author_wrapper_gap }}px;
    }

    .testimonials-author-image-{{ section.id }} {
      width: {{ author_image_width }}px;    
    }

    .testimonials-author-text-{{ section.id }} {    
      font-size: {{ author_size }}px;
      text-align: {{ card_content_align }};
    }

    .testimonials-author-job-{{ section.id }} {
      margin-top: {{ author_job_mt }}px;
      font-size: {{ author_job_size }}px;
      text-align: {{ card_content_align }};
    }

    .testimonials-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ card_content_align }};
    }

    .testimonials-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .testimonials-bottom-{{ section.id }} {
      margin-top: {{ stars_mt }}px;
    }

    .testimonials-stars-{{ section.id }} svg,
    .testimonials-stars-{{ section.id }} img {
      width: {{ stars_size }}px;
      height: {{ stars_size }}px;
    }

    .testimonials-circle-icon-{{ section.id }} {
      width: {{ circle_size }}px;
      height: {{ circle_size }}px;
    }

    .testimonials-button-wrapper-{{ section.id }} {
      justify-content: {{ button_horizontal_align }};
      padding: 0 {{ button_margin_horizontal }}px;
    }

    .testimonials-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .testimonials-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if author_custom %}
  <style>
    .testimonials-author-text-{{ section.id }} {
      font-family: {{ author_font.family }}, {{ author_font.fallback_families }};
      font-weight: {{ author_font.weight }};
      font-style: {{ author_font.style }};
    }
  </style>
{% endif %}

{% if author_job_custom %}
  <style>
    .testimonials-author-job-{{ section.id }} {
      font-family: {{ author_job_font.family }}, {{ author_job_font.fallback_families }};
      font-weight: {{ author_job_font.weight }};
      font-style: {{ author_job_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .testimonials-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .testimonials-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if reverse_direction %}
  <style>
    @keyframes tickerUp{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(-100%);
      }
      100% {
        transform: translateX(0%);
      }
    }

    @keyframes tickerDown{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(0%);
      }
      100% {
        transform: translateX(-100%);
      }
    }
  </style>
{% else %}
  <style>
    @keyframes tickerUp{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(0%);
      }
      100% {
        transform: translateX(-100%);
      }
    }

    @keyframes tickerDown{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(-100%);
      }
      100% {
        transform: translateX(0%);
      }
    }
  </style>
{% endif %}

{% if card_content_align_mobile == 'right' %}
  <style>
    .testimonials-author-image-{{ section.id }} {
      order: 2;
    }
  </style>
{% endif %}

{% if card_content_align == 'right' %}
  <style>
    @media(min-width: 1024px) {
      .testimonials-author-image-{{ section.id }} {
        order: 2;
      }
    }   
  </style>
{% endif %}

{% if button_style == "non_outline" or button_style == "non_outline_arrow" %}
  <style>
    .testimonials-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
    }

    .testimonials-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
    }
  </style>
{% elsif button_style == "outline" or button_style == "outline_arrow" %}
  <style>
    .testimonials-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_color }};
    }

    .testimonials-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    }
  </style>
{% elsif button_style == "link" %}
  <style>
    .testimonials-button-{{ section.id }} {
      padding: 0px !important;
    }
