liquid_code:
{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign layout_gap = section.settings.layout_gap
  assign layout_gap_mobile = section.settings.layout_gap_mobile

  assign card_border_thickness = section.settings.card_border_thickness
  assign card_border_color = section.settings.card_border_color
  assign card_radius = section.settings.card_radius

  assign media_ratio_mobile = section.settings.media_ratio_mobile

  assign content_padding_horizontal = section.settings.content_padding_horizontal
  assign content_padding_horizontal_mobile = section.settings.content_padding_horizontal_mobile
  assign content_padding_vertical = section.settings.content_padding_vertical
  assign content_padding_vertical_mobile = section.settings.content_padding_vertical_mobile
  assign content_vertical_align = section.settings.content_vertical_align

  assign quote_size_mobile = section.settings.quote_size_mobile
  assign quote_size = section.settings.quote_size

  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_height = section.settings.text_height
  assign text_color = section.settings.text_color
  assign text_mt = section.settings.text_mt
  assign text_mt_mobile = section.settings.text_mt_mobile
  assign text_align = section.settings.text_align
  assign text_align_mobile = section.settings.text_align_mobile

  assign author_custom = section.settings.author_custom
  assign author_font = section.settings.author_font
  assign author_size = section.settings.author_size
  assign author_size_mobile = section.settings.author_size_mobile
  assign author_height = section.settings.author_height
  assign author_color = section.settings.author_color
  assign author_mt = section.settings.author_mt
  assign author_mt_mobile = section.settings.author_mt_mobile
  assign author_align = section.settings.author_align
  assign author_align_mobile = section.settings.author_align_mobile

-%}

{%- style -%}
  {{  text_font | font_face: font_display: 'swap' }}
  {{  author_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .testimonials-layout-{{ section.id }} {
    display: grid;
    gap: {{ layout_gap_mobile }}px;
    grid: auto-flow dense auto / repeat(2,minmax(0,1fr));
  }

  .testimonials-item-{{ section.id }} {
    overflow: hidden;
    border-radius: {{ card_radius }}px;
    border: {{ card_border_thickness }}px solid {{ card_border_color }};
  }

  .testimonials-item-{{ section.id }} img,
  .testimonials-item-{{ section.id }} svg,
  .testimonials-item-{{ section.id }} video,
  .testimonials-item-{{ section.id }} iframe {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    border: 0px;
    box-shadow: none;
  }

  .testimonials-item-{{ section.id }} > svg {
    background-color: #AFAFAF;
  }

  .testimonials-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    height: 100%;
    padding: {{ content_padding_vertical_mobile | times: 0.5 }}px {{ content_padding_horizontal_mobile }}px {{ content_padding_vertical_mobile }}px {{ content_padding_horizontal_mobile }}px;
  }

  .testimonials-quote-{{ section.id }} {
    margin-left: auto;
    width: {{ quote_size_mobile }}px;
    height: {{ quote_size_mobile }}px;
  }

  .testimonials-quote-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: contain;
  }

  .testimonials-text-{{ section.id }} {
    margin-top: {{ text_mt_mobile }}px;
    text-align: {{ text_align_mobile }};
  }

  .testimonials-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .testimonials-author-{{ section.id }} {
    margin: 0;
    margin-top: {{ author_mt_mobile }}px;
    text-align: {{ author_align_mobile }};   
    font-size: {{ author_size_mobile }}px;
    color: {{ author_color }};
    line-height: {{ author_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .testimonials-layout-{{ section.id }} {
      gap: {{ layout_gap }}px;
      grid: auto-flow dense auto / repeat(5,minmax(0,1fr));
    }

    .testimonials-content-{{ section.id }} {
      padding: {{ content_padding_vertical | times: 0.5 }}px {{ content_padding_horizontal }}px {{ content_padding_vertical }}px {{ content_padding_horizontal }}px;
      justify-content: {{ content_vertical_align }};
    }

    .testimonials-quote-{{ section.id }} {
      width: {{ quote_size }}px;
      height: {{ quote_size }}px;
    }

    .testimonials-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ text_align }};
    }

    .testimonials-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .testimonials-author-{{ section.id }} {
      margin-top: {{ author_mt }}px;
      text-align: {{ author_align }};
      font-size: {{ author_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .testimonials-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if author_custom %}
  <style>
    .testimonials-author-{{ section.id }} {
      font-family: {{ author_font.family }}, {{ author_font.fallback_families }};
      font-weight: {{ author_font.weight }};
      font-style: {{ author_font.style }};
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  <style>
    .testimonials-item-{{ block.id }} {
      grid-area: span 1 / span {{ block.settings.column_width_mobile }};
    }

    @media(min-width: 1024px) {
      .testimonials-item-{{ block.id }} {
         grid-area: span {{ block.settings.row_width }} / span {{ block.settings.column_width }};
      }
    }
  </style>

  {% if block.type == "text" %}
    <style>
      .testimonials-item-{{ block.id }} {
        background-color: {{ block.settings.bg_color }};
        border-color: {{ block.settings.border_color }};
      }

      .testimonials-item-{{ block.id }} .testimonials-quote-{{ section.id }} svg {
        color: {{ block.settings.quote_color }};
      }

      .testimonials-item-{{ block.id }} .testimonials-text-{{ section.id }} * {
        color: {{ block.settings.text_color }};
      }

      .testimonials-item-{{ block.id }} .testimonials-author-{{ section.id }} {
        color: {{ block.settings.author_color }};
      }
    </style>
  {% endif %}

  <style>
    @media (max-width: 1024px) {
      .testimonials-item-{{ section.id }} video,
      .testimonials-item-{{ section.id }} img,
      .testimonials-item-{{ section.id }} iframe {
        {% if media_ratio_mobile == "portrait" %}
          aspect-ratio: 9.6/13;
        {% elsif media_ratio_mobile == "landscape" %}
          aspect-ratio: 12/8;
        {% elsif media_ratio_mobile == "square" %}
          aspect-ratio: 12/12;
        {% else %}
          aspect-ratio: auto;
        {% endif %}
      }
    }
  </style> 
{% endfor %}

<div class="section-{{ section.id }} testimonials-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="testimonials-layout-{{ section.id }}">
        {% for block in section.blocks %}
          <div class="testimonials-item-{{ section.id }} testimonials-item-{{ block.id }}">
            {% if block.type == "image" %}
              {% if block.settings.image != blank %}
                <img src="{{ block.settings.image | img_url: "1000x1000" }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>                 
              {% else %}    
                {{ 'image' | placeholder_svg_tag }}
              {% endif %}
            {% elsif block.type == "video" %}
              {% assign video_id = block.settings.video_url.id %}
              {% assign loop = '&loop=1&playlist=' | append: video_id %}
              {%- if block.settings.video_url != null -%}
                {%- if block.settings.video_url.type == 'youtube' -%}
                  <iframe
                    data-src="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1&mute=1&autoplay=1&controls=0&showinfo=0{{ loop }}"                      
                    allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                    allowfullscreen
                    class="lazy"
                  ></iframe>
                  {%- else -%}
                  <iframe
                    data-src="https://player.vimeo.com/video/{{ video_id }}?enablejsapi=1&mute=1&muted=1&autoplay=1&controls=0&showinfo=0{{ loop }}"
                    allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                    allowfullscreen
                    class="lazy"
                  ></iframe>
                {% endif %}
              {% else %}
                {% assign placeholder_video = 'https://cdn.shopify.com/videos/c/o/v/fbb140fd46e3429eba459e1549dbbe38.mp4' %}
                <video data-src="{% if block.settings.video != blank %}{% if block.settings.video.sources[1].url != blank %}{{ block.settings.video.sources[1].url }}{% elsif block.settings.video.sources[0].url != blank %}{{ block.settings.video.sources[0].url }}{% endif %}{% else %}{{ placeholder_video }}{% endif %}" muted autoplay playsinline preload="none" preload="none" loop class="lazy"></video>
              {% endif %}
            {% elsif block.type == "text" %}
              <div class="testimonials-content-{{ section.id }}">
                <div class="testimonials-quote-{{ section.id }}">
                  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 73">
                    <path fill="currentColor" d="M76.19617,0.96 C88.9960786,0.96 99.996,9.75958 99.996,28.75998 C99.996,55.4597893 78.1961557,71.1596771 65.7962443,72.7596657 L63.1964414,65.35954 C70.39639,60.6595736 76.2961693,52.5596314 75.6963521,47.859665 C67.5965886,47.2597764 54.4965036,42.9595214 54.4965036,25.3598257 C54.3960579,12.0604564 61.8960936,0.96 76.1963486,0.96 L76.19617,0.96 Z M21.8965579,0.96017857 C34.6964664,0.96017857 45.6963879,9.76029428 45.6963879,28.75998 C45.6963879,55.4597893 23.8965436,71.1596771 11.4966321,72.7596657 L8.89682928,65.35954 C15.9964214,60.6602879 21.8962007,52.6601664 21.1958486,47.8603793 C13.1964414,47.2597943 -0.004,42.9602357 -0.004,25.36054 C-0.004,12.060635 7.59576714,0.96017857 21.8958436,0.96017857 L21.8965579,0.96017857 Z"></path>
                  </svg>
                </div>
                {% if block.settings.text != blank %}
                  <div class="testimonials-text-{{ section.id }}">
                    {{ block.settings.text }}
                  </div>
                {% endif %}
                {% if block.settings.author != blank %}
                  <p class="testimonials-author-{{ section.id }}">{{ block.settings.author }}</p>
                {% endif %}
              </div>
            {% endif %}
          </div>
        {% endfor %}
      </div>
    </div>
</div>

<script src="https://cdn.jsdelivr.net/npm/vanilla-lazyload@19.1.3/dist/lazyload.min.js"></script>

<script>
  function initTestimonials31() {
    let lazyLoadInstance = new LazyLoad();

    window.addEventListener(
      "LazyLoad::Initialized",
      function (event) {
        window.lazyLoadInstance = event.detail.instance;
      },
      false
    );
  }


  document.addEventListener('DOMContentLoaded', initTestimonials31);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initTestimonials31);
     document.addEventListener('shopify:section:load', initTestimonials31);
  }
</script>
