{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius
  assign background_style = section.settings.background_style

  assign wrapper_padding_vertical = section.settings.wrapper_padding_vertical
  assign wrapper_padding_vertical_mobile = section.settings.wrapper_padding_vertical_mobile

  assign scrolling_velocity = section.settings.scrolling_velocity
  assign scrolling_velocity_mobile = section.settings.scrolling_velocity_mobile
  assign scrolling_gap = section.settings.scrolling_gap
  assign scrolling_gap_mobile = section.settings.scrolling_gap_mobile
  assign reverse_direction = section.settings.reverse_direction
  assign pause_hover = section.settings.pause_hover

  assign image_width = section.settings.image_width
  assign image_width_mobile = section.settings.image_width_mobile
  assign image_ratio = section.settings.image_ratio
  assign image_ratio_mobile = section.settings.image_ratio_mobile
  assign image_radius = section.settings.image_radius
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color

  assign author_padding_vertical = section.settings.author_padding_vertical
  assign author_padding_vertical_mobile = section.settings.author_padding_vertical_mobile
  assign author_padding_horizontal = section.settings.author_padding_horizontal
  assign author_padding_horizontal_mobile = section.settings.author_padding_horizontal_mobile
  assign author_radius = section.settings.author_radius
  assign author_border_thickness = section.settings.author_border_thickness
  assign author_border_color = section.settings.author_border_color
  assign author_bg_color = section.settings.author_bg_color
  assign author_content_align = section.settings.author_content_align
  assign author_content_align_mobile = section.settings.author_content_align_mobile
  assign author_shadow = section.settings.author_shadow
  assign author_shadow_color = section.settings.author_shadow_color

  assign title_custom = section.settings.title_custom
  assign title_font = section.settings.title_font
  assign title_size = section.settings.title_size
  assign title_size_mobile = section.settings.title_size_mobile
  assign title_height = section.settings.title_height
  assign title_color = section.settings.title_color

  assign verified_size =section.settings.verified_size
  assign verified_size_mobile =section.settings.verified_size_mobile
  assign verified_icon_bg_color = section.settings.verified_icon_bg_color

  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_height = section.settings.text_height
  assign text_color = section.settings.text_color
  assign text_mt = section.settings.text_mt
  assign text_mt_mobile = section.settings.text_mt_mobile

  assign author_image_size = section.settings.author_image_size
  assign author_image_size_mobile = section.settings.author_image_size_mobile
  assign author_image_radius = section.settings.author_image_radius
  assign author_image_border_thickness = section.settings.author_image_border_thickness
  assign author_image_border_color = section.settings.author_image_border_color

  assign lazy_load_setting = 'lazy'
  unless lazy
    assign lazy_load_setting = 'eiger'
  endunless
-%}

{%- style -%}
  {{ title_font | font_face: font_display: 'swap' }}
  {{ text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .testimonials-wrapper-{{ section.id }} {
    position: relative;
    z-index: 1;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    scrollbar-width: none;
    -ms-flex-wrap: nowrap;
    flex-wrap: nowrap;
    padding: {{ wrapper_padding_vertical_mobile }}px 0px;
    background-attachment: scroll !important;
  }

  .testimonials-list-{{ section.id }} {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    white-space: nowrap;
    height: 100%;
    background-attachment: scroll !important;
    -webkit-animation: ticker{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
    animation: ticker{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
    flex-shrink: 0;
  }

  .testimonials-list-{{ section.id }}:first-child {
    z-index: 2;
  }

  .testimonials-card-{{ section.id }} {
    display: flex;
    flex-direction: column;
    margin-right: {{ scrolling_gap_mobile }}px;
    width: {{ image_width_mobile }}px;
    text-decoration: none;
    position: relative;
  }

  .testimonials-author-{{ section.id }} {
    position: absolute;
    display: flex;
    align-items: start;
    gap: 10px;
    z-index: 2;
    max-width: {{ image_width_mobile | times: 1.5 }}px;
    width: 100%;
    padding: {{ author_padding_vertical_mobile }}px {{ author_padding_horizontal_mobile }}px;
    border-radius: {{ author_radius }}px;
    border: {{ author_border_thickness }}px solid {{ author_border_color }};
    overflow: hidden;
    background-color: {{ author_bg_color }};
  }

  .testimonials-image-{{ section.id }} {
    overflow: hidden;
    background-attachment: scroll !important;
    border-radius: {{ image_radius }}px;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    flex-shrink: 0;
    width: 100%;
  }

  .testimonials-image-{{ section.id }} img,
  .testimonials-image-{{ section.id }} svg {
    display: inline-block;
    width: 100%;
    height: 100%;
    object-fit: cover
  }

  .testimonials-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .testimonials-title-{{ section.id }} {   
    margin: 0;
    display: flex;
    align-items: center;
    gap: 5px;
    text-align: {{ author_content_align_mobile }};
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
    font-weight: 400;
    text-wrap: wrap;
  }

  .testimonials-verified-{{ section.id }} {
    height: {{ verified_size_mobile }}px;
    width: {{ verified_size_mobile }}px;
  }

  .testimonials-verified-bg-{{ section.id }} {
    fill: {{ verified_icon_bg_color }};
  }

  .testimonials-text-{{ section.id }} {
    text-align: {{ author_content_align_mobile }};
    margin-top: {{ text_mt_mobile }}px;
  }

  .testimonials-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
    text-wrap: wrap;
  }

  .testimonials-author-image-{{ section.id }} {
    flex-shrink: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    border: {{ author_image_border_thickness }}px solid {{ author_image_border_color }};
    border-radius: {{ author_image_radius }}px;
    overflow: hidden;
    aspect-ratio: 1/1;
    width: {{ author_image_size_mobile }}px;
  }

  .testimonials-author-image-{{ section.id }} img,
  .testimonials-author-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .testimonials-author-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .testimonials-wrapper-{{ section.id }} {
      padding: {{ wrapper_padding_vertical }}px 0px;
    }

    .testimonials-list-{{ section.id }} {
      -webkit-animation: ticker{{ section.id | replace: '-', '' }} {{ scrolling_velocity }}s infinite linear;
      animation: ticker{{ section.id | replace: '-', '' }} {{ scrolling_velocity }}s infinite linear;
    }

    .testimonials-card-{{ section.id }} {
      margin-right: {{ scrolling_gap }}px;
      width: {{ image_width }}px;   
    }

    .testimonials-author-{{ section.id }} {
      max-width: {{ image_width }}px;
      width: 100%;
      padding: {{ author_padding_vertical }}px {{ author_padding_horizontal }}px;
    }

    .testimonials-author-image-{{ section.id }} {
      width: {{ author_image_size }}px;
    }

    .testimonials-title-{{ section.id }} {   
      text-align: {{ author_content_align }};
      font-size: {{ title_size }}px;     
    }

    .testimonials-verified-{{ section.id }} {
      height: {{ verified_size }}px;
      width: {{ verified_size }}px;
    }

    .testimonials-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ author_content_align }};
    }

    .testimonials-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .testimonials-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .testimonials-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if author_shadow %}
  <style>
    .testimonials-author-{{ section.id }} {
      box-shadow: {{ author_shadow_color }}33 0px 8px 24px;
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .testimonials-image-{{ section.id }} {
      aspect-ratio: 9.6/13;
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
  .testimonials-image-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .testimonials-image-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
      .testimonials-image-{{ section.id }} {
        aspect-ratio: 9.6/13;
      }
    }

  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .testimonials-image-{{ section.id }} {
        aspect-ratio: 12/8;
      }
    }  
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .testimonials-image-{{ section.id }} {
        aspect-ratio: 12/12;
      }
    }    
  </style>
{% endif %}

{% if reverse_direction %}
  <style>
    @keyframes ticker{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(-50%);
      }
      100% {
        transform: translateX(0%);
      }
    }
  </style>
{% else %}
  <style>
    @keyframes ticker{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(0%);
      }
      100% {
        transform: translateX(-50%);
      }
    }
  </style>
{% endif %}

{% if pause_hover %}
	<style>
      @media(min-width: 1024px) {
         .testimonials-wrapper-{{ section.id }}:hover .testimonials-list-{{ section.id }} {
          animation-play-state: paused !important;
        }
      }   
	</style>
{% endif %}

{% for block in section.blocks %}
  {% if block.settings.testimonial_position == "top" %}
    <style>
      .testimonials-author-{{ block.id }} {
        transform: translate(0%, -100%);
        left: {{ block.settings.horizontal_position_mobile }}px;
        top: {{ block.settings.vertical_position_mobile }}px;
      }

      @media(min-width: 1024px) {
        .testimonials-author-{{ block.id }} {
          transform: translate(0%, -100%);
          left: {{ block.settings.horizontal_position }}px;
          top: {{ block.settings.vertical_position }}px;
        }
      }
    </style>
  {% elsif block.settings.testimonial_position == "bottom" %}
    <style>
      .testimonials-author-{{ block.id }} {
        transform: translate(0px,
          {%- if section.settings.image_ratio_mobile == "landscape" -%}
            {{ section.settings.image_width_mobile | times: 8 | divided_by: 12 }}px
          {%- elsif section.settings.image_ratio_mobile == "portrait" -%}
            {{ section.settings.image_width_mobile | times: 13 | divided_by: 9.6 }}px
          {%- else -%}
            {{ section.settings.image_width_mobile }}px
          {%- endif -%}
        );
        left: {{ block.settings.horizontal_position_mobile }}px;
        top: {{ block.settings.vertical_position_mobile }}px;
      }

      @media(min-width: 1024px) {
        .testimonials-author-{{ block.id }} {
          transform: translate(0px,
            {%- if section.settings.image_ratio == "landscape" -%}
              {{ section.settings.image_width | times: 8 | divided_by: 12 }}px
            {%- elsif section.settings.image_ratio == "portrait" -%}
              {{ section.settings.image_width | times: 13 | divided_by: 9.6 }}px
            {%- else -%}
              {{ section.settings.image_width }}px
            {%- endif -%}
          );
          left: {{ block.settings.horizontal_position }}px;
          top: {{ block.settings.vertical_position }}px;
        }
      }
    </style>
  {% endif %}
{% endfor %}

{% capture list %}
  <div class="testimonials-list-{{ section.id }}">
    {% for item in (1..2) %}
      {% for block in section.blocks %}
        <div class="testimonials-card-{{ section.id }}">
          <div class="testimonials-image-{{ section.id }}">                 
            {% if block.settings.image != blank %}
              {%- capture sizes -%}(min-width: {{ content_width }}px) {{ content_width | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
                {{ block.settings.image | image_url: width: 750 | image_tag:
                  loading: lazy_load_setting,
                  width: block.settings.image.width,
                  height: block.settings.image.height,
                  sizes: sizes,
                  widths: '750, 1070'
                }}      
            {% else %}  
              {{ 'image' | placeholder_svg_tag }}
            {% endif %}
          </div>
          {% if block.settings.use_testimonial %}
            <div class="testimonials-author-{{ section.id }} testimonials-author-{{ block.id }}">
              <div class="testimonials-author-image-{{ section.id }}">
                {% if block.settings.author_image != blank %}
                  <img src="{{ block.settings.author_image | img_url: "360x360" }}" alt="{{ block.settings.author_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                {% elsif block.settings.author != blank %}
                  {{ block.settings.author | slice: 0, 1 }}
