{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius
  assign background_style = section.settings.background_style

  assign scrolling_velocity = section.settings.scrolling_velocity
  assign scrolling_velocity_mobile = section.settings.scrolling_velocity_mobile
  assign scrolling_gap = section.settings.scrolling_gap
  assign scrolling_gap_mobile = section.settings.scrolling_gap_mobile
  assign reverse_direction = section.settings.reverse_direction
  assign pause_hover = section.settings.pause_hover

  assign cards_mt = section.settings.cards_mt
  assign cards_mt_mobile = section.settings.cards_mt_mobile

  assign card_border_thickness = section.settings.card_border_thickness
  assign card_border_color = section.settings.card_border_color
  assign card_radius = section.settings.card_radius
  assign card_padding_horizontal = section.settings.card_padding_horizontal
  assign card_padding_horizontal_mobile = section.settings.card_padding_horizontal_mobile
  assign card_padding_vertical = section.settings.card_padding_vertical
  assign card_padding_vertical_mobile = section.settings.card_padding_vertical_mobile
  assign card_bg_color = section.settings.card_bg_color
  assign card_shadow = section.settings.card_shadow
  assign card_shadow_color = section.settings.card_shadow_color
  assign card_content_align = section.settings.card_content_align
  assign card_content_align_mobile = section.settings.card_content_align_mobile
  assign card_width = section.settings.card_width
  assign card_width_mobile = section.settings.card_width_mobile

  assign card_content_horizontal_align = ""
  if card_content_align == "center"
    assign card_content_horizontal_align = "center"
  elsif card_content_align == "right"
    assign card_content_horizontal_align = "end"
  else
    assign card_content_horizontal_align = "start"
  endif

  assign card_content_horizontal_align_mobile = ""
  if card_content_align_mobile == "center"
    assign card_content_horizontal_align_mobile = "center"
  elsif card_content_align_mobile == "right"
    assign card_content_horizontal_align_mobile = "end"
  else
    assign card_content_horizontal_align_mobile = "start"
  endif

  assign video_ratio = section.settings.video_ratio
  assign video_ratio_mobile = section.settings.video_ratio_mobile
  assign video_radius = section.settings.video_radius
  assign video_border_thickness = section.settings.video_border_thickness
  assign video_border_color = section.settings.video_border_color
  assign video_mt = section.settings.video_mt
  assign video_mt_mobile = section.settings.video_mt_mobile

  assign card_stars_icon = section.settings.card_stars_icon
  assign card_stars_color = section.settings.card_stars_color 
  assign card_stars_size = section.settings.card_stars_size
  assign card_stars_size_mobile = section.settings.card_stars_size_mobile
  assign card_stars_mt = section.settings.card_stars_mt
  assign card_stars_mt_mobile = section.settings.card_stars_mt_mobile
  assign card_stars_gap = section.settings.card_stars_gap

  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_height = section.settings.text_height
  assign text_color = section.settings.text_color
  assign text_mt = section.settings.text_mt
  assign text_mt_mobile = section.settings.text_mt_mobile

  assign author_custom = section.settings.author_custom
  assign author_font = section.settings.author_font
  assign author_size = section.settings.author_size
  assign author_size_mobile = section.settings.author_size_mobile
  assign author_height = section.settings.author_height
  assign author_color = section.settings.author_color

  assign author_image_size = section.settings.author_image_size
  assign author_image_size_mobile = section.settings.author_image_size_mobile
  assign author_image_radius = section.settings.author_image_radius
  assign author_image_border_thickness = section.settings.author_image_border_thickness
  assign author_image_border_color = section.settings.author_image_border_color
  assign author_image_bg_color = section.settings.author_image_bg_color
  assign author_image_color = section.settings.author_image_color

  assign play_icon_size_mobile = section.settings.play_icon_size_mobile
  assign play_border_thickness = section.settings.play_border_thickness
  assign play_border_color = section.settings.play_border_color
  assign play_padding_mobile = section.settings.play_padding_mobile
  assign play_radius =section.settings.play_radius
  assign play_bg_color = section.settings.play_bg_color
  assign play_color = section.settings.play_color
  assign show_play_icon_mobile = section.settings.show_play_icon_mobile
  assign play_position = section.settings.play_position
-%}

{%- style -%}
  {{ text_font | font_face: font_display: 'swap' }} 
  {{ author_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }
  
  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }
  
  .testimonials-wrapper-{{ section.id }} {
    position: relative;
    z-index: 1;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    scrollbar-width: none;
    -ms-flex-wrap: nowrap;
    flex-wrap: nowrap;
    margin-top: {{ cards_mt_mobile }}px;
    background-attachment: scroll !important;
  }

  .testimonials-list-{{ section.id }} {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    white-space: nowrap;
    height: 100%;
    background-attachment: scroll !important;
    -webkit-animation: ticker{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
    animation: ticker{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
    flex-shrink: 0;
  }

  .testimonials-card-{{ section.id }} {
    text-decoration: none;
    width: {{ card_width_mobile }}px;
    margin-right: {{ scrolling_gap_mobile }}px;
    display: flex !important;
    flex-direction: column !important;
    align-items: {{ card_content_horizontal_align_mobile }};
    justify-content: space-between !important;
    padding: {{ card_padding_vertical_mobile }}px {{ card_padding_horizontal_mobile }}px !important; 
    border: {{ card_border_thickness }}px solid {{ card_border_color }} !important;
    border-radius: {{ card_radius }}px !important;
    background-color: {{ card_bg_color }} !important;
    overflow: hidden !important;
  }

  .testimonials-card-bottom-wrapper-{{ section.id }} {
    flex: 1 1 0;
    display: flex !important;
    flex-direction: column !important;
    align-items: {{ card_content_horizontal_align_mobile }};
  }

  .testimonials-video-{{ section.id }} {
    overflow: hidden;
    margin-top: {{ video_mt_mobile }}px;
    background-attachment: scroll !important;
    border-radius: {{ video_radius }}px;
    border: {{ video_border_thickness }}px solid {{ video_border_color }};
    flex-shrink: 0;
    width: 100%;
  }

  .testimonials-video-{{ section.id }} video,
  .testimonials-video-{{ section.id }} iframe {
    display: inline-block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    border: 0px;
  }

  .testimonials-card-stars-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: {{ card_content_horizontal_align_mobile }};
    gap: {{ card_stars_gap }}px;
    margin-top: {{ card_stars_mt_mobile }}px;
  }

  .testimonials-card-stars-{{ section.id }} svg,
  .testimonials-card-stars-{{ section.id }} img {
    display: block;
    width: {{ card_stars_size_mobile }}px;
    height: {{ card_stars_size_mobile }}px;
    object-fit: cover;
  }

  .testimonials-card-text-{{ section.id }} {
    text-align: {{ card_content_align_mobile }};
    margin-top: {{ text_mt_mobile }}px;  
  }

  .testimonials-card-text-{{ section.id }} * {
    margin: 0;
    display: flex;
    flex-direction: column;
    gap: 10px;
    align-items: {{ card_content_horizontal_align_mobile }};
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
    text-wrap: wrap;
  }

  .testimonials-card-author-name-{{ section.id }} {
    margin: 0;
    font-size: {{ author_size_mobile }}px;
    color: {{ author_color }};
    line-height: {{ author_height }}%;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
    font-weight: 700;
    text-align: {{ card_content_align_mobile }};
  }

  .testimonials-author-image-{{ section.id }} {
    flex-shrink: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    color: {{ author_image_color }};
    background-color: {{ author_image_bg_color }};
    border: {{ author_image_border_thickness }}px solid {{ author_image_border_color }};
    border-radius: {{ author_image_radius }}px;
    overflow: hidden;
    aspect-ratio: 1/1;
    width: {{ author_image_size_mobile }}px;
  }

  .testimonials-author-image-{{ section.id }} img,
  .testimonials-author-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .testimonials-author-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .testimonials-card-top-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 8px;
    justify-content: {{ card_content_horizontal_align_mobile }};
    width: 100%;
  }

  .testimonials-play-{{ section.id }} {
    margin: 0;
    cursor: pointer;
    z-index: 2;
    max-width: fit-content;
    margin-left: auto;
    padding: {{ play_padding_mobile }}px;
    border: {{ play_border_thickness }}px solid {{ play_border_color }};
    border-radius: {{ play_radius }}px;
    background-color: {{ play_bg_color }};
    transition: all 0.25s ease 0s;
  }

  .testimonials-play-{{ section.id }} svg:first-child path {
    fill: {{ play_color }};
  }

  .testimonials-play-{{ section.id }} svg:last-child path {
    fill: {{ play_color }};
  }
  
  .testimonials-play-{{ section.id }} svg {
    display: block;
    width: {{ play_icon_size_mobile }}px;
    height: {{ play_icon_size_mobile }}px;
    object-fit: cover;
  }

  .testimonials-play-{{ section.id }} svg {
    background-color: transparent !important;
  }
  
  .testimonials-play-{{ section.id }} svg:nth-child(1) {
    display: none;
  }

  .testimonials-play-{{ section.id }}.active svg:nth-child(1) {
    display: block;
  }

  .testimonials-play-{{ section.id }}.active svg:nth-child(2) {
    display: none;
  }

  .testimonials-wrapper-{{ section.id }}.stop-animation .testimonials-list-{{ section.id }} {
    animation-play-state: paused;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }
    
    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .testimonials-wrapper-{{ section.id }} {
      margin-top: {{ cards_mt }}px;
    }

    .testimonials-list-{{ section.id }} {
      -webkit-animation: ticker{{ section.id | replace: '-', '' }} {{ scrolling_velocity }}s infinite linear;
      animation: ticker{{ section.id | replace: '-', '' }} {{ scrolling_velocity }}s infinite linear;
    }

    .testimonials-card-{{ section.id }} {
      width: {{ card_width }}px;
      margin-right: {{ scrolling_gap }}px;
      align-items: {{ card_content_horizontal_align }};
      padding: {{ card_padding_vertical }}px {{ card_padding_horizontal }}px !important; 
    }    

    .testimonials-card-bottom-wrapper-{{ section.id }} {
      align-items: {{ card_content_horizontal_align }};
    }

    .testimonials-card-top-{{ section.id }} {
      justify-content: {{ card_content_horizontal_align }};
    }

    .testimonials-card-stars-{{ section.id }} {
      justify-content: {{ card_content_horizontal_align }};
      margin-top: {{ card_stars_mt }}px;
    }

    .testimonials-card-stars-{{ section.id }} svg,
    .testimonials-card-stars-{{ section.id }} img {
      width: {{ card_stars_size }}px;
      height: {{ card_stars_size }}px;
    }

    .testimonials-video-{{ section.id }} {
      margin-top: {{ video_mt }}px;
    }

    .testimonials-card-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ card_content_align }};   
    }

    .testimonials-card-text-{{ section.id }} * {
      align-items: {{ card_content_horizontal_align }};
      font-size: {{ text_size }}px;
    }
      
    .testimonials-card-author-name-{{ section.id }} {
      text-align: {{ card_content_align }};
      font-size: {{ author_size }}px;
    }

    .testimonials-author-image-{{ section.id }} {
      width: {{ author_image_size }}px;
    }

    .testimonials-play-{{ section.id }} {
      display: none;
      padding: {{ play_padding }}px;
    }
  }
  
{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }
    
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .testimonials-card-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if author_custom %}
  <style>
    .testimonials-card-author-name-{{ section.id }} {
      font-family: {{ author_font.family }}, {{ author_font.fallback_families }};
      font-weight: {{ author_font.weight }};
      font-style: {{ author_font.style }};
    }
  </style>
{% endif %}

{% if card_shadow %}
  <style>
    .testimonials-card-{{ section.id }} {
      box-shadow: {{ card_shadow_color }}33 0px 8px 24px;
    }
  </style>
{% endif %}

{% if card_content_align_mobile == 'center' %}
  <style>
    .testimonials-card-top-{{ section.id }} {
      flex-direction: column;
      align-items: center;
    }
  </style>
{% elsif card_content_align_mobile == 'right' %}
  <style>
    .testimonials-card-top-{{ section.id }} {
      flex-direction: row-reverse;
    }
  </style>
{% else %}
  <style>
    .testimonials-card-top-{{ section.id }} {
      flex-direction: row;
    }
  </style>
{% endif %}

{% if card_content_align == 'center' %}
  <style>
    @media(min-width: 1024px) {
      .testimonials-card-top-{{ section.id }} {
        flex-direction: column;
        align-items: center;
      }
    }   
  </style>
{% elsif card_content_align == 'right' %}
  <style>
    @media(min-width: 1024px) {
      .testimonials-card-top-{{ section.id }} {
        flex-direction: row-reverse;
      }
    }   
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .testimonials-card-top-{{ section.id }} {
        flex-direction: row;
      }
    }    
  </style>
{% endif %}

{% if video_ratio_mobile == "portrait" %}
  <style>
    .testimonials-video-{{ section.id }} {
      aspect-ratio: 9.6/13;
    }
  </style>
{% elsif video_ratio_mobile == "landscape" %}
  <style>
  .testimonials-video-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif video_ratio_mobile == "square" %}
  <style>
    .testimonials-video-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .testimonials-video-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}

{% if video_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
      .testimonials-video-{{ section.id }} {
        aspect-ratio: 9.6/13;
      }
    }
    
  </style>
{% elsif video_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .testimonials-video-{{ section.id }} {
        aspect-ratio: 12/8;
      }
    }  
  </style>
{% elsif video_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .testimonials-video-{{ section.id }} {
        aspect-ratio: 12/12;
      }
    }    
  </style>
{% else %}
  <style>
     @media(min-width: 1024px) {
      .testimonials-video-{{ section.id }} {
        aspect-ratio: auto;
      }
    }
  </style>
{% endif %}


{% if reverse_direction %}
  <style>
    @keyframes ticker{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(-100%);
      }
      100% {
        transform: translateX(0%);
      }
    }
  </style>
{% else %}
  <style>
    @keyframes ticker{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(0%);
      }
      100% {
        transform: translateX(-100%);
      }
    }
  </style>
{% endif %}

{% if pause_hover %}
	<style>
    @media(min-width: 1024px) {
      .testimonials-wrapper-{{ section.id }}:hover .testimonials-list-{{ section.id }} {
        animation-play-state: paused;
      }
    } 
	</style>
{% endif %}

{% unless show_play_icon_mobile %}
  <style>
     .testimonials-play-{{ section.id }} {
        display: none !important;
      }
  </style>
{% endunless %}

{% for block in section.blocks %}
  {% case block.type %}      
    {% when 'heading' %}
      <style>
        {{ block.settings.heading_font | font_face: font_display: 'swap' }}
 
        .testimonials-heading-{{ block.id }} {
          max-width: 100%;
          text-align: {{ block.settings.content_align_mobile }};
        }

        .testimonials-heading-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.heading_size_mobile }}px;
          color: {{ block.settings.heading_color }};
          line-height: {{ block.settings.heading_height }}%;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
          font-weight: 700;
        }
        @media(min-width: 1024px) {
          .testimonials-heading-{{ block.id }} {
            max-width: {{ block.settings.heading_max_width }}%;
            text-align: {{ block.settings.content_align }};
          }
        
          .testimonials-heading-{{ block.id }} * {
            font-size: {{ block.settings.heading_size }}px;
          }
        }
      </style>
      {% if block.settings.heading_custom %}
        <style>
          .testimonials-heading-{{ block.id }} * {
            font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
            font-weight: {{ block.settings.heading_font.weight }};
            font-style: {{ block.settings.heading_font.style }};
          }
        </style>
      {% endif %}  

      
      {% if block.settings.content_align_mobile == "center" %}
        <style>
          .testimonials-heading-{{ block.id }} {
            margin-left: auto;
            margin-right: auto;
          } 
        </style>
      {% elsif block.settings.content_align_mobile == "right" %}
        <style>
          .testimonials-heading-{{ block.id }} {
            margin-left: auto;
          } 
        </style>
      {% endif %}

      {% if block.settings.content_align == "center" %}
        <style>
          @media(min-width: 1024px) {
            .testimonials-heading-{{ block.id }} {
              margin-left: auto;
              margin-right: auto;
            } 
          }
        </style>
      {% elsif block.settings.content_align == "right" %}
        <style>
          @media(min-width: 1024px) {
            .testimonials-heading-{{ block.id }} {
              margin-left: auto;
              margin-right: 0px;
            } 
          }
        </style>
      {% else %}
        <style>
          @media(min-width: 1024px) {
            .testimonials-heading-{{ block.id }} {
              margin-left: 0px;
              margin-right: 0px;
            } 
          }
        </style>
      {% endif %}
    
    {% else %}      
  {% endcase %}
{% endfor %}

{% capture list %}
  <div class="testimonials-list-{{ section.id }}">
    {% for block in section.blocks %}
      {% if block.type == 'testimonial' %}
        <{% if block.settings.url != blank %}a href="{{ block.settings.url }}"{% else %}div{% endif %} class="testimonials-card-{{ section.id }} swiper-card">    
          <div class="testimonials-card-top-{{ section.id }}">
            <div class="testimonials-author-image-{{ section.id }}">
              {% if block.settings.author_image != blank %}
                <img src="{{ block.settings.author_image | img_url: "360x360" }}" alt="{{ block.settings.author_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
              {% elsif block.settings.author != blank %}
                {{ block.settings.author | slice: 0, 1 }}
              {% else %}
                {{ 'image' | placeholder_svg_tag }}
              {% endif %}   
            </div>
            <div class="testimonials-author-wrapper-{{ section.id }}">                     
              {% if block.settings.author != blank %}
                <p class="testimonials-card-author-name-{{ section.id }}">{{ block.settings.author }}</p>
              {% endif %}                       
            </div>                    
          </div> 
          <div class="testimonials-card-bottom-wrapper-{{ section.id }}">
