<script>
  function initTestimonials43() {
    let lazyLoadInstance = new LazyLoad();

    window.addEventListener(
      "LazyLoad::Initialized",
      function (event) {
        window.lazyLoadInstance = event.detail.instance;
      },
      false
    );

    const controlButton = document.querySelector('.testimonials-play-{{ section.id }}');
    const testimonialsWrapper = document.querySelector('.testimonials-wrapper-{{ section.id }}');

    if (controlButton && testimonialsWrapper) {
      controlButton.addEventListener('click', function() {
        this.classList.toggle('active');
        testimonialsWrapper.classList.toggle('stop-animation');
      });
    }
  }

  document.addEventListener('DOMContentLoaded', initTestimonials43);

  if (Shopify.designMode) {
    document.addEventListener('shopify:section:unload', initTestimonials43);
    document.addEventListener('shopify:section:load', initTestimonials43);
  }
</script>
