{%- style -%}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  testimonial_text_font | font_face: font_display: 'swap' }}
  {{  testimonial_rating_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .testimonials-grid-{{ section.id }} {
    display: grid;
    gap: 20px;
    grid-template-columns: repeat(1,minmax(0,1fr));
    align-items: center;
  }

  .testimonials-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-align: {{ heading_align_mobile }};
    text-transform: unset;
  }

  .testimonials-heading-{{ section.id }} strong {
    color: {{ heading_other_color }};
  }

  .testimonials-items-{{ section.id }} {
    margin-top: {{ testimonials_mt | times: 0.75 | round: 0 }}px;
  }

  .testimonials-item-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: space-between;
    flex-wrap: wrap;
    gap: 12px;
    padding: {{ testimonials_item_padding_vertical | times: 0.75 | round: 0 }}px 0px;
    border-bottom: {{ testimonials_item_border_thickness }}px solid {{ testimonials_item_border_color }};
  }

  .testimonials-item-right-{{ section.id }} {
    margin-left: auto;
    display: flex;
    align-items: center;
    gap: 12px;
    flex-wrap: wrap;
  }

  .testimonials-text-{{ section.id }} {
    margin: 0;
    font-size: {{ testimonial_text_size_mobile }}px;
    color: {{ testimonial_text_color }};
    line-height: {{ testimonial_text_height }}%;
  }

  .testimonials-stars-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 5px;
  }

  .testimonials-stars-{{ section.id }} svg {
    display: block;
    width: {{ stars_size }}px;
    height: {{ stars_size }}px;
    object-fit: cover;
  }

  .testimonials-rating-{{ section.id }} {
    margin: 0;
    font-size: {{ testimonial_rating_size_mobile }}px;
    color: {{ testimonial_rating_color }};
    line-height: {{ testimonial_rating_height }}%;
  }

  .testimonials-btn-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 5px;
    max-width: fit-content;
    margin: 0;
    margin-top: {{ button_mt | times: 0.75 | round: 0 }}px;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical | times: 0.75 | round: 0 }}px {{ button_padding_horizontal | times: 0.75 | round: 0 }}px;
    border-radius: {{ button_radius }}px;
    border: {{ button_border_thickness }}px solid {{ button_border_color }};
    background-color: {{ button_bg_color }};
    background-image: {{ button_bg_gradient_color }};
    transition: all 0.25s ease 0s;
  }

  .testimonials-btn-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    background-color: {{ button_bg_hover_color }};
    background-image: {{ button_bg_gradient_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .testimonials-btn-{{ section.id }} svg,
  .testimonials-btn-{{ section.id }} img {
    display: block;
    object-fit: contain;
    width: 24px;
    height: 24px;
  }

  .testimonials-btn-{{ section.id }} svg path {
    fill: {{ button_color }};
  }

  .testimonials-btn-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
  }

  .testimonials-subheading-{{ section.id }} {
    margin: 0;
    margin-top: {{ text_mt | times: 0.75 | round: 0 }}px;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-align: {{ text_align_mobile }};
    text-transform: unset;
  }

  .testimonials-right-{{ section.id }} {
    display: grid;
    grid-template-columns: repeat({{ image_row_mobile }},minmax(0,1fr));
    gap: {{ image_gap_mobile }}px;
  }

  .testimonials-image-{{ section.id }} {
    padding: {{ image_padding | times: 0.75 | round: 0 }}px;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    border-radius: {{ image_radius }}px;
    background-color: {{ image_bg }};
    aspect-ratio: 1/1;
  }

  .testimonials-image-{{ section.id }} img,
  .testimonials-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: {{ image_radius }}px;
  }

  .testimonials-image-{{ section.id }}:nth-child(n+{{ image_show_mobile | plus: 1 }}) {
    display: none;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .testimonials-grid-{{ section.id }} {
      gap: 100px;
      /* margin-bottom: -100px; */
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .testimonials-grid-{{ section.id }} {
      grid-template-columns: repeat(2,minmax(0,1fr));
    }

    .testimonials-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
      text-align: {{ heading_align }};
    }

    .testimonials-items-{{ section.id }} {
      margin-top: {{ testimonials_mt }}px;
    }

    .testimonials-item-{{ section.id }} {
      padding: {{ testimonials_item_padding_vertical }}px 0px;
    }

    .testimonials-text-{{ section.id }} {
      font-size: {{ testimonial_text_size }}px;
    }

    .testimonials-rating-{{ section.id }} {
      font-size: {{ testimonial_rating_size }}px;
    }

    .testimonials-btn-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }

    .testimonials-subheading-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      font-size: {{ text_size }}px;
      text-align: {{ text_align }};
    }

    .testimonials-right-{{ section.id }} {
      grid-template-columns: repeat({{ image_row }},minmax(0,1fr));
      gap: {{ image_gap }}px;
    }

    .testimonials-image-{{ section.id }} {
      padding: {{ image_padding }}px;
    }

    .testimonials-image-{{ section.id }}:nth-child(n+{{ image_show_mobile | plus: 1 }}) {
      display: block;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .testimonials-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if testimonial_text_custom %}
  <style>
    .testimonials-text-{{ section.id }} {
      font-family: {{ testimonial_text_font.family }}, {{ testimonial_text_font.fallback_families }};
      font-weight: {{ testimonial_text_font.weight }};
      font-style: {{ testimonial_text_font.style }};
    }
  </style>
{% endif %}

{% if testimonial_rating_custom %}
  <style>
    .testimonials-rating-{{ section.id }} {
      font-family: {{ testimonial_rating_font.family }}, {{ testimonial_rating_font.fallback_families }};
      font-weight: {{ testimonial_rating_font.weight }};
      font-style: {{ testimonial_rating_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .testimonials-btn-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if button_align_mobile == "center" %}
  <style>
   .testimonials-btn-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    } 
  </style>
{% elsif button_align_mobile == "right" %}
  <style>
   .testimonials-btn-{{ section.id }} {
      margin-left: auto;
    } 
  </style>
{% endif %}

{% if button_align == "center" %}
  <style>
    @media(min-width: 1024px) {
     .testimonials-btn-{{ section.id }} {
        margin-left: auto;
        margin-right: auto;
      } 
    }
  </style>
{% elsif button_align == "right" %}
  <style>
    @media(min-width: 1024px) {
     .testimonials-btn-{{ section.id }} {
        margin-left: auto;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .testimonials-btn-{{ section.id }} {
        margin-left: 0px;
        margin-right: 0px;
      } 
    }
  </style>
{% endif %}

{% if button_use_shadow %}
  <style>
    .testimonials-btn-{{ section.id }} {
      box-shadow: 4px 4px #23252929;
    }
  </style>
{% endif %}

{% if button_use_hover %}
  <style>
    .testimonials-btn-{{ section.id }}:hover {
      box-shadow: none;
       transform: translate(6px,6px);
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .testimonials-subheading-{{ section.id }} {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if image_shadow %}
  <style>
    .testimonials-image-{{ section.id }} {
      box-shadow: 3px 3px {{ image_shadow_color | hex_to_rgba: 0.3 }}, 6px 6px {{ image_shadow_color | hex_to_rgba: 0.3 }};
    }
  </style>
{% endif %}

{% if image_rotate %}
  <style>
    .testimonials-image-{{ section.id }}:nth-child(odd) {
      transform: rotate(-1.5deg);
    }

    .testimonials-image-{{ section.id }}:nth-child(even) {
      transform: rotate(1.5deg);
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} testimonials-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings ">
      <div class="testimonials-grid-{{ section.id }}">
        <div class="testimonials-left-{{ section.id }}">
          {% if heading != blank %}
            <div class="testimonials-heading-{{ section.id }}">
              {{ heading }}
            </div>
          {% endif %}
          <div class="testimonials-items-{{ section.id }}">
            {% for block in section.blocks %}
              {% if block.type == "testimonial" %}
                <div class="testimonials-item-{{ section.id }}">
                  {% if block.settings.text != blank %}
                    <p class="testimonials-text-{{ section.id }}">{{ block.settings.text }}</p>
                  {% endif %}
                  <div class="testimonials-item-right-{{ section.id }}">
                    <div class="testimonials-stars-{{ section.id }}">
                      <svg xmlns="http://www.w3.org/2000/svg" width="20" height="19" viewBox="0 0 20 19" fill="none">
                        {% if block.settings.rating > 0.2 and block.settings.rating < 0.8  %}
                          <defs>
                            <linearGradient id="grad{{ section.id }}1">
                              <stop offset="0%" stop-color="{{ stars_color }}" />
                              <stop offset="50%" stop-color="{{ stars_color }}" />
                              <stop offset="50%" stop-color="white" />
                              <stop offset="100%" stop-color="white" />
                            </linearGradient>
                          </defs>
                        {% endif %}
                        <path {% if block.settings.rating < 0.8 %}stroke="{{ stars_color }}"{% endif %} d="M10 0L12.9389 5.95492L19.5106 6.90983L14.7553 11.5451L15.8779 18.0902L10 15L4.12215 18.0902L5.24472 11.5451L0.489435 6.90983L7.06107 5.95492L10 0Z" fill="{% if block.settings.rating > 0.2 and block.settings.rating < 0.8 %}url(#grad{{ section.id }}1){% elsif block.settings.rating > 0.7 %}{{ stars_color }}{% endif %}"></path>
                      </svg>
                      <svg xmlns="http://www.w3.org/2000/svg" width="20" height="19" viewBox="0 0 20 19" fill="none">
                        {% if block.settings.rating > 1.2 and block.settings.rating < 1.8  %}
                          <defs>
                            <linearGradient id="grad{{ section.id }}2">
                              <stop offset="0%" stop-color="{{ stars_color }}" />
                              <stop offset="50%" stop-color="{{ stars_color }}" />
                              <stop offset="50%" stop-color="white" />
                              <stop offset="100%" stop-color="white" />
                            </linearGradient>
                          </defs>
                        {% endif %}
                        <path {% if block.settings.rating < 1.8 %}stroke="{{ stars_color }}"{% endif %} d="M10 0L12.9389 5.95492L19.5106 6.90983L14.7553 11.5451L15.8779 18.0902L10 15L4.12215 18.0902L5.24472 11.5451L0.489435 6.90983L7.06107 5.95492L10 0Z" fill="{% if block.settings.rating > 1.2 and block.settings.rating < 1.8 %}url(#grad{{ section.id }}2){% elsif block.settings.rating > 1.7 %}{{ stars_color }}{% endif %}"></path>
                      </svg>
                      <svg xmlns="http://www.w3.org/2000/svg" width="20" height="19" viewBox="0 0 20 19" fill="none">
                        {% if block.settings.rating > 2.2 and block.settings.rating < 2.8  %}
                          <defs>
                            <linearGradient id="grad{{ section.id }}3">
                              <stop offset="0%" stop-color="{{ stars_color }}" />
                              <stop offset="50%" stop-color="{{ stars_color }}" />
                              <stop offset="50%" stop-color="white" />
                              <stop offset="100%" stop-color="white" />
                            </linearGradient>
                          </defs>
                        {% endif %}
                        <path {% if block.settings.rating < 2.8 %}stroke="{{ stars_color }}"{% endif %} d="M10 0L12.9389 5.95492L19.5106 6.90983L14.7553 11.5451L15.8779 18.0902L10 15L4.12215 18.0902L5.24472 11.5451L0.489435 6.90983L7.06107 5.95492L10 0Z" fill="{% if block.settings.rating > 2.2 and block.settings.rating < 2.8 %}url(#grad{{ section.id }}3){% elsif block.settings.rating > 2.7 %}{{ stars_color }}{% endif %}"></path>
                      </svg>
                      <svg xmlns="http://www.w3.org/2000/svg" width="20" height="19" viewBox="0 0 20 19" fill="none">
                        {% if block.settings.rating > 3.2 and block.settings.rating < 3.8  %}
                          <defs>
                            <linearGradient id="grad{{ section.id }}4">
                              <stop offset="0%" stop-color="{{ stars_color }}" />
                              <stop offset="50%" stop-color="{{ stars_color }}" />
                              <stop offset="50%" stop-color="white" />
                              <stop offset="100%" stop-color="white" />
                            </linearGradient>
                          </defs>
                        {% endif %}
                        <path {% if block.settings.rating < 3.8 %}stroke="{{ stars_color }}"{% endif %} d="M10 0L12.9389 5.95492L19.5106 6.90983L14.7553 11.5451L15.8779 18.0902L10 15L4.12215 18.0902L5.24472 11.5451L0.489435 6.90983L7.06107 5.95492L10 0Z" fill="{% if block.settings.rating > 3.2 and block.settings.rating < 3.8 %}url(#grad{{ section.id }}4){% elsif block.settings.rating > 3.7 %}{{ stars_color }}{% endif %}"></path>
                      </svg>
                      <svg xmlns="http://www.w3.org/2000/svg" width="20" height="19" viewBox="0 0 20 19" fill="none">
                        {% if block.settings.rating > 4.2 and block.settings.rating < 4.8  %}
                          <defs>
                            <linearGradient id="grad{{ section.id }}5">
                              <stop offset="0%" stop-color="{{ stars_color }}" />
                              <stop offset="50%" stop-color="{{ stars_color }}" />
                              <stop offset="50%" stop-color="white" />
                              <stop offset="100%" stop-color="white" />
                            </linearGradient>
                          </defs>
                        {% endif %}
                        <path {% if block.settings.rating < 4.8 %}stroke="{{ stars_color }}"{% endif %} d="M10 0L12.9389 5.95492L19.5106 6.90983L14.7553 11.5451L15.8779 18.0902L10 15L4.12215 18.0902L5.24472 11.5451L0.489435 6.90983L7.06107 5.95492L10 0Z" fill="{% if block.settings.rating > 4.2 and block.settings.rating < 4.8 %}url(#grad{{ section.id }}5){% elsif block.settings.rating > 4.7 %}{{ stars_color }}{% endif %}"></path>
                      </svg>
                    </div>
                    <p class="testimonials-rating-{{ section.id }}">{{ block.settings.rating }}</p>
                  </div>
                </div>
              {% endif %}
            {% endfor %}
          </div>
          {% if button != blank %}
            <a href="{{ button_url }}" class="testimonials-btn-{{ section.id }}">
              {% if button_use_icon %}
                {% if button_icon != blank %}
                  <img src="{{ button_icon | image_url }}" alt="{{ button_icon.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                {% else %}
                  <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M11.2033 14.6626H9.02026C6.77605 14.6626 5.65397 14.6626 5.18404 13.98C4.71411 13.2971 5.18552 12.3519 6.12835 10.4611L10.4804 1.7341C11.0767 0.538513 11.3746 -0.0592804 11.6874 0.00463788C12 0.068571 12 0.727325 12 2.04485V8.59772C12 8.94638 12 9.12078 12.1167 9.22906C12.2333 9.33735 12.4211 9.33735 12.7967 9.33735H14.9798C17.224 9.33735 18.3461 9.33735 18.816 10.02C19.2859 10.7028 18.8145 11.6481 17.8717 13.5387L13.5196 22.2658C12.9234 23.4615 12.6254 24.0593 12.3126 23.9954C12 23.9315 12 23.2726 12 21.9552V15.4023C12 15.0536 12 14.8792 11.8834 14.7709C11.7668 14.6626 11.5789 14.6626 11.2033 14.6626Z" fill="#232529"></path>
                  </svg>
                {% endif %}
              {% endif %}
              {{ button }}
            </a>
          {% endif %}
          {% if text != blank %}
            <p class="testimonials-subheading-{{ section.id }}">{{ text }}</p>
          {% endif %}
        </div>
        <div class="testimonials-right-{{ section.id }}">
          {% for block in section.blocks %}
            {% if block.type == "image" %}
              <div class="testimonials-image-{{ section.id }}">
                {% if block.settings.image != blank %}
                  <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                {% else %}
                  {{ "image" | placeholder_svg_tag }}
                {% endif %}
              </div>
            {% endif %}
          {% endfor %}
         </div>
      </div>
    </div>
</div>
