  assign item_icon_size = section.settings.item_icon_size
  assign item_icon_size_mobile = section.settings.item_icon_size_mobile
  assign item_icon_mt = section.settings.item_icon_mt
  assign item_icon_mt_mobile = section.settings.item_icon_mt_mobile
  assign item_icon_color = section.settings.item_icon_color
  assign item_icon_hover_color = section.settings.item_icon_hover_color

-%}

{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}
  {{  item_heading_font | font_face: font_display: 'swap' }}
  {{  item_text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    position: relative;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    position: relative;
  }

  .text-block-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: {{ content_horizontal_align_mobile }};
  }

  .text-block-heading-{{ section.id }} {
    width: 100%;
    text-align: {{ text_align_mobile }};
  }

  .text-block-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
    word-break: break-word;
  }

  .text-block-text-{{ section.id }} {
    width: 100%;
    margin-top: {{ text_mt_mobile }}px;
    text-align: {{ text_align_mobile }};
  }

  .text-block-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    word-break: break-word;
  }

  .text-block-text-{{ section.id }} a {
    display: inline-block;
    text-decoration: none;
    cursor: pointer;
    border-bottom: 3px solid {{ text_link_underline_color }};
  }

  .text-block-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    width: 100%;
    max-width: fit-content;
    margin: 0;
    margin-top: {{ button_mt_mobile }}px;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    position: relative;
    font-weight: 700;
    z-index: 2;
    box-sizing: border-box;
  }

  .text-block-button-{{ section.id }} svg {
    background: transparent;
    width: 14px;
    height: 14px;
  }

  .text-block-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .text-block-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .text-block-button-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .text-block-item-{{ section.id }} {
    position: absolute;
    pointer-events: none;
    opacity: 0;
    transform: translateY(30px);
    transition: all 0.25s ease 0s;
    padding: {{ item_padding_vertical_mobile }}px {{ item_padding_horizontal_mobile }}px;
    border-radius: {{ item_radius }}px;
    border: {{ item_border_thickness }}px solid {{ item_border_color }};
    background-color: {{ item_bg_color }};
    overflow: hidden;
    width: 100%;
    max-width: {{ item_width_mobile }}px;
    display: flex;
    flex-direction: column;
    align-items: {{ item_content_horizontal_align_mobile }};
  }

  .text-block-item-{{ section.id }}.active {
    pointer-events: all;
    opacity: 1;
    transform: translateY(0px);
    transition: all 0.25s ease 0s;
    z-index: 2;
  }

  .text-block-item-heading-{{ section.id }} {
    margin: 0;
    width: 100%;
    text-align: {{ item_text_align_mobile }};
    font-size: {{ item_heading_size_mobile }}px;
    color: {{ item_heading_color }};
    line-height: {{ item_heading_height }}%;
    text-transform: unset;
    word-break: break-word;
  }

  .text-block-item-text-{{ section.id }} {
    margin: 0;
    margin-top: {{ item_text_mt_mobile }}px;
    width: 100%;
    text-align: {{ item_text_align_mobile }};
    font-size: {{ item_text_size_mobile }}px;
    color: {{ item_text_color }};
    line-height: {{ item_text_height }}%;
    text-transform: unset;
    word-break: break-word;
  }

  .text-block-item-link-{{ section.id }} {
    display: block;
    width: 100%;
    max-width: {{ item_icon_size_mobile }}px;
    margin-top: {{ item_icon_mt_mobile }}px;
  }

  .text-block-item-link-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .text-block-item-link-{{ section.id }} svg path {
    fill: {{ item_icon_color }};
    transition: all 0.25s ease 0s;
  }

  .text-block-item-link-{{ section.id }}:hover svg path {
    fill: {{ item_icon_hover_color }};
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .text-block-content-{{ section.id }} {
      align-items: {{ content_horizontal_align }};
    }

    .text-block-heading-{{ section.id }} {
      text-align: {{ text_align }};
    }

    .text-block-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .text-block-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ text_align }};
    }

    .text-block-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .text-block-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }

    .text-block-item-{{ section.id }} {
      padding: {{ item_padding_vertical }}px {{ item_padding_horizontal }}px;
      max-width: {{ item_width }}px;
      align-items: {{ item_content_horizontal_align }};
    }

    .text-block-item-heading-{{ section.id }} {
      text-align: {{ item_text_align }};
      font-size: {{ item_heading_size }}px;
    }

    .text-block-item-text-{{ section.id }} {
      text-align: {{ item_text_align }};
      margin-top: {{ item_text_mt }}px;
      font-size: {{ item_text_size }}px;
    }

    .text-block-item-link-{{ section.id }} {
      margin-top: {{ item_icon_mt }}px;
      max-width: {{ item_icon_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .text-block-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .text-block-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .text-block-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if item_heading_custom %}
  <style>
    .text-block-item-heading-{{ section.id }} {
      font-family: {{ item_heading_font.family }}, {{ item_heading_font.fallback_families }};
      font-weight: {{ item_heading_font.weight }};
      font-style: {{ item_heading_font.style }};
    }
  </style>
{% endif %}

{% if item_text_custom %}
  <style>
    .text-block-item-text-{{ section.id }} {
      font-family: {{ item_text_font.family }}, {{ item_text_font.fallback_families }};
      font-weight: {{ item_text_font.weight }};
      font-style: {{ item_text_font.style }};
    }
  </style>
{% endif %}

{% if button_style == "non_outline" or button_style == "non_outline_arrow" %}
  <style>
    .text-block-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
    }

    .text-block-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
    }
  </style>
{% elsif button_style == "outline" or button_style == "outline_arrow" %}
  <style>
    .text-block-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_color }};
    }

    .text-block-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    }
  </style>
{% elsif button_style == "link" %}
  <style>
    .text-block-button-{{ section.id }} {
      padding: 0px !important;
      max-width: fit-content !important;
    }
  </style>
{% endif %}

{% if bg_image != blank %}
  <style>
    .text-block-{{ section.id }} {
      background-image: url('{{ bg_image | image_url }}') !important;
      background-size: cover;
      background-position: center;
      background-repeat: no-repeat;
    }
  </style>
{% endif %}

{% if overlay %}
  <style>
    .text-block-{{ section.id }}:before {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background-color: {{ background_color | hex_to_rgba: 0.6 }};
      z-index: 0;
      border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    }

    @media(min-width: 1024px) {
      .text-block-{{ section.id }}:before {
        border-radius: {{ section_radius }}px;
      }
    }
  </style>
{% endif %}

