      {% if heading != blank %}
        <div class="text-block-heading-{{ section.id }}">
          {{ heading }}
        </div>
      {% endif %}

      {% if text != blank %}
        <div class="text-block-text-{{ section.id }}">
          {{ text }}
        </div>
      {% endif %}

      {% if button != blank %}
        <a href="{{ button_url }}" class="text-block-button-{{ section.id }}">
