        </a>
      {% endif %}

        {% for block in section.blocks %}
          <div class="text-block-item-{{ section.id }}">
            {% if block.settings.heading != blank %}
              <p class="text-block-item-heading-{{ section.id }}">{{ block.settings.heading }}</p>
            {% endif %}

            {% if block.settings.text != blank %}
              <p class="text-block-item-text-{{ section.id }}">{{ block.settings.text }}</p>
            {% endif %}

            {% if block.settings.url != blank %}
              <a href="{{ block.settings.url }}" class="text-block-item-link-{{ section.id }}">
                <svg fill="none" aria-hidden="true" focusable="false" role="presentation" class="icon icon-arrow" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 28 16"><g><path d="M27.888 7.693 20.662.138a.468.468 0 0 0-.645-.02.464.464 0 0 0-.021.634l6.505 6.802H.452a.456.456 0 0 0-.307.137.441.441 0 0 0 0 .616c.08.083.19.132.307.137h26.049l-6.507 6.805a.46.46 0 0 0 .021.635.464.464 0 0 0 .645-.02l7.226-7.556a.472.472 0 0 0 0-.61l.002-.005Z" fill="currentColor"></path></g></svg>
              </a>
            {% endif %}
          </div>
        {% endfor %}

      </div>
    </div>
</div>

<script>
  function initTextBlock2() {
    const textBlockItems = document.querySelectorAll('.text-block-item-{{ section.id }}');
    const textBlockLinks = document.querySelectorAll('.text-block-text-{{ section.id }} a');

    if(textBlockLinks) {
      textBlockLinks.forEach(link => {
        link.removeAttribute('href');
        link.removeAttribute('title');
      })
    }

    textBlockItems.forEach((item, index) => {
      const link = textBlockLinks[index];
      item.style.position = 'absolute';
      item.style.top = link.offsetTop + link.offsetHeight - 5 +  'px';
      if(window.innerWidth < 1024) {
        item.style.left = 50 + '%';
        item.style.transform = 'translateX(-50%)';
      } else {
        item.style.left = link.offsetLeft + link.offsetWidth - (item.offsetWidth / 2) - (link.offsetWidth / 2) + 'px';
      }

      link.addEventListener('mouseenter', () => {
        item.classList.add('active');
      });

      item.addEventListener('mouseenter', () => {
        item.classList.add('active');
      });

      link.addEventListener('mouseleave', () => {
        item.classList.remove('active');
      });

      item.addEventListener('mouseleave', () => {
        item.classList.remove('active');
      });
    });

    if (window.innerWidth < 1024) {
      if(textBlockLinks) {
        textBlockLinks.forEach((link,index) => {
          let item = textBlockItems[index];
          link.addEventListener('click', ()=> {
            if(textBlockItems) {
              textBlockItems.forEach(item => {
                item.classList.remove('active');
              }) 
            }
            item.classList.add('active');
          })
        })
      }
    }
  }

  document.addEventListener('DOMContentLoaded', initTextBlock2);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initTextBlock2);
     document.addEventListener('shopify:section:load', initTextBlock2);
  }
</script>
