{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign section_radius = section.settings.section_radius

  assign wave_type = section.settings.wave_type

  assign animation_speed = section.settings.animation_speed
  assign animation_speed_mobile = section.settings.animation_speed_mobile
  assign reverse_direction = section.settings.reverse_direction
  assign line_color = section.settings.line_color

  assign body_padding_vertical = section.settings.body_padding_vertical
  assign body_padding_vertical_mobile = section.settings.body_padding_vertical_mobile
  assign body_align = section.settings.body_align
  assign body_align_mobile = section.settings.body_align_mobile

  assign body_horizontal_align = ""
  if body_align == "center"
    assign body_horizontal_align = "center"
  elsif body_align == "right"
    assign body_horizontal_align = "end"
  else
    assign body_horizontal_align = "start"
  endif

  assign body_horizontal_align_mobile = ""
  if body_align_mobile == "center"
    assign body_horizontal_align_mobile = "center"
  elsif body_align_mobile == "right"
    assign body_horizontal_align_mobile = "end"
  else
    assign body_horizontal_align_mobile = "start"
  endif
-%}

{%- style -%}
  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    overflow: hidden;
  }

  .text-block-animation-line-{{ section.id }}:first-child {
    display: flex;
    align-items: center;
    width: 200vw;
    position: relative; 
    left: -100vw;
    -webkit-animation: ticker_up{{ section.id | replace: '-', '' }} {{ animation_speed_mobile }}s infinite linear;
    animation-duration: {{ animation_speed_mobile }}s;
  }

  .text-block-animation-line-{{ section.id }}:last-child {
    display: flex;
    align-items: center;
    width: 200vw;
    left: -100vw;
    position: relative; 
    -webkit-animation: ticker_down{{ section.id | replace: '-', '' }} {{ animation_speed_mobile }}s infinite linear;
    animation-duration: {{ animation_speed_mobile }}s;
  }

  .text-block-line-{{ section.id }} {
    flex-shrink: 0;
    height: 14px;
    overflow: hidden;
  }

  .text-block-line-{{ section.id }}.text-block-line-two-{{ section.id }} {
    width: 959px;
  }

  .text-block-line-{{ section.id }}.text-block-line-one-{{ section.id }} path,
  .text-block-line-{{ section.id }}.text-block-line-two-{{ section.id }} path {    
    stroke: {{ line_color }};
  }

  .text-block-line-{{ section.id }}.text-block-line-zig-zag-{{ section.id }} path {
    fill: {{ line_color }};
  }

  .text-block-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    justify-content: {{ body_horizontal_align_mobile }};
    padding: {{ body_padding_vertical_mobile }}px 0;
  }

  .text-block-button-wrapper-{{ section.id }} {
    display: flex;
    justify-content: {{ body_horizontal_align_mobile }};
    width: 100%;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .text-block-animation-line-{{ section.id }}:first-child {
    display: flex;
    align-items: center;
    width: 200vw;
    position: relative; 
    left: -100vw;
    -webkit-animation: ticker_up{{ section.id | replace: '-', '' }} {{ animation_speed }}s infinite linear;
    animation-duration: {{ animation_speed }}s;
  }

  .text-block-animation-line-{{ section.id }}:last-child {
    display: flex;
    align-items: center;
    width: 200vw;
    position: relative; 
    left: -100vw;
    -webkit-animation: ticker_down{{ section.id | replace: '-', '' }} {{ animation_speed }}s infinite linear;
    animation-duration: {{ animation_speed }}s;
  }

    .text-block-content-{{ section.id }} {
      justify-content: {{ body_horizontal_align }};
      padding: {{ body_padding_vertical }}px 0;
    }

    .text-block-button-wrapper-{{ section.id }} {
      justify-content: {{ body_horizontal_align }};
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if reverse_direction %}
	<style>
    @keyframes ticker_up{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(-100%);
      }
      100% {
        transform: translateX(0%);
      }
    }
    @keyframes ticker_down{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(0%);
      }
      100% {
        transform: translateX(-100%);
      }
    }
	</style>
{% else %}
	<style>
    @keyframes ticker_up{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(0%);
      }
      100% {
        transform: translateX(-100%);
      }
    }
    @keyframes ticker_down{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(-100%);
      }
      100% {
        transform: translateX(0%);
      }
    }
	</style>
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}
    {% when 'subtext' %}
      <style>
        {{ block.settings.sub_text_font | font_face: font_display: 'swap' }}

        .text-block-subtext-{{ block.id }} {
          margin-top: {{ block.settings.sub_text_mt_mobile }}px;
        }

        .text-block-subtext-{{ block.id }} * {
          margin: 0px;
          text-align: {{ body_align_mobile }};
          font-size: {{ block.settings.sub_text_size_mobile }}px;
          line-height: {{ block.settings.sub_text_height }}%;
          color: {{ block.settings.sub_text_color }};
          text-transform: unset;
          word-break: break-word;
        }
        @media (min-width: 1024px) {
          .text-block-subtext-{{ block.id }} {
            margin-top: {{ block.settings.sub_text_mt }}px;
          }

          .text-block-subtext-{{ block.id }} * {
            text-align: {{ body_align }};
            font-size: {{ block.settings.sub_text_size }}px;
          }
        }
      </style>
      {% if block.settings.sub_text_custom %}
        <style>
          .text-block-subtext-{{ block.id }} * {
            font-family: {{ block.settings.sub_text_font.family }}, {{ block.settings.sub_text_font.fallback_families }};
            font-weight: {{ block.settings.sub_text_font.weight }};
            font-style: {{ block.settings.sub_text_font.style }};
          }
        </style>
      {% endif %}

    {% when 'text' %}
      <style>        
        {{ block.settings.text_font | font_face: font_display: 'swap' }}

        .text-block-text-{{ block.id }} {
          margin-top: {{ block.settings.text_mt_mobile }}px;
        }

        .text-block-text-{{ block.id }} * {
          margin: 0px;
          text-align: {{ body_align_mobile }};
          font-size: {{ block.settings.text_size_mobile }}px;
          line-height: {{ block.settings.text_height }}%;
          color: {{ block.settings.text_color }};
          text-transform: unset;
          word-break: break-word;
          font-weight: 700;
        }

        @media (min-width: 1024px) {          
          .text-block-text-{{ block.id }} {
            margin-top: {{ block.settings.text_mt }}px;
          }

          .text-block-text-{{ block.id }} * {
            text-align: {{ body_align }};
            font-size: {{ block.settings.text_size }}px;
          }
        }
      </style>
      {% if block.settings.text_custom %}
        <style>
          .text-block-text-{{ block.id }} * {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>
      {% endif %}

    {% when 'button' %}
      <style>
        {{ block.settings.button_font | font_face: font_display: 'swap' }}

        .text-block-button-{{ block.id }} {
          display: flex;
          align-items: center;
          justify-content: center;
          max-width: fit-content;
          gap: 10px;
          width: 100%;
          margin: 0;
          margin-top: {{ block.settings.button_mt_mobile }}px;
          font-size: {{ block.settings.button_size_mobile }}px;
          color: {{ block.settings.button_color }};
          line-height: {{ block.settings.button_height }}%;
          text-align: center;
          text-transform: unset;
          text-decoration: none;
          padding: {{ block.settings.button_padding_vertical_mobile }}px {{ block.settings.button_padding_horizontal_mobile }}px;
          border-radius: {{ block.settings.button_radius }}px;
          transition: all 0.25s ease 0s;
          background-color: transparent;
          border: 0px;
          cursor: pointer;
          font-weight: 700;
        } 

        .text-block-button-inner-{{ block.id }} {
          margin: 0px;
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 10px;
        }

        .text-block-button-{{ block.id }}:hover {
          color: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }

        .text-block-button-{{ block.id }} svg {
          width: 14px;
          height: 14px;
        }

        .text-block-button-{{ block.id }} svg path {
          fill: {{ block.settings.button_color }};
          transition: all 0.25s ease 0s;
        }

        .text-block-button-{{ block.id }}:hover svg path {
          fill: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }
        @media (min-width: 1024px) {
          .text-block-button-{{ block.id }} {
            margin-top: {{ block.settings.button_mt }}px;
            padding: {{ block.settings.button_padding_vertical }}px {{ block.settings.button_padding_horizontal }}px;
            font-size: {{ block.settings.button_size }}px;
          }
        }
      </style>
      {% if block.settings.button_style == "non_outline" or block.settings.button_style == "non_outline_arrow" %}
        <style>
          .text-block-button-{{ block.id }} {
            background-color: {{ block.settings.button_bg_color }};
          }

          .text-block-button-{{ block.id }}:hover {
            transition: all 0.25s ease 0s;
            background-color: {{ block.settings.button_bg_hover_color }};
          }

          .text-block-button-{{ block.id }}:hover svg {
            transition: all 0.25s ease 0s;
          }
        </style>
      {% elsif block.settings.button_style == "outline" or block.settings.button_style == "outline_arrow" %}
        <style>
          .text-block-button-{{ block.id }} {
            background-color: {{ block.settings.button_bg_color }};
            border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_color }};
          }

          .text-block-button-{{ block.id }}:hover {
            transition: all 0.25s ease 0s;
            background-color: {{ block.settings.button_bg_hover_color }};
            border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_hover_color }};
          }

          .text-block-button-{{ block.id }}:hover svg {
            transition: all 0.25s ease 0s;
          }
        </style>
      {% elsif block.settings.button_style == "link" %}
        <style>
          .text-block-button-{{ block.id }} {
            padding: 0px !important;
          }
        </style>
      {% endif %}

      {% if block.settings.button_custom %}
        <style>
          .text-block-button-{{ block.id }} {
            font-family: {{ block.settings.button_font.family }}, {{ block.settings.button_font.fallback_families }};
            font-weight: {{ block.settings.button_font.weight }};
            font-style: {{ block.settings.button_font.style }};
          }
        </style>
      {% endif %}
  {% endcase %}
{% endfor %}


