{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign heading = section.settings.heading
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_color = section.settings.heading_color
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_height = section.settings.heading_height
  assign heading_align = section.settings.heading_align
  assign heading_align_mobile = section.settings.heading_align_mobile

  assign items_mt_mobile = section.settings.items_mt_mobile
  assign items_mt = section.settings.items_mt
  assign items_space_mobile = section.settings.items_space_mobile
  assign items_space = section.settings.items_space

  assign line_color = section.settings.line_color

  assign circle_border_thickness = section.settings.circle_border_thickness
  assign circle_border_color = section.settings.circle_border_color
  assign circle_size = section.settings.circle_size
  assign circle_size_mobile = section.settings.circle_size_mobile
  assign circle_bg_color = section.settings.circle_bg_color

  assign content_padding_horizontal_mobile = section.settings.content_padding_horizontal_mobile
  assign content_padding_horizontal = section.settings.content_padding_horizontal

  assign image_radius = section.settings.image_radius
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color
  assign image_ratio = section.settings.image_ratio
  assign image_ratio_mobile = section.settings.image_ratio_mobile
  assign image_width = section.settings.image_width
  assign image_width_mobile = section.settings.image_width_mobile
  assign image_align = section.settings.image_align
  assign image_align_mobile = section.settings.image_align_mobile

  assign item_heading_size = section.settings.item_heading_size
  assign item_heading_size_mobile = section.settings.item_heading_size_mobile
  assign item_heading_color = section.settings.item_heading_color
  assign item_heading_custom = section.settings.item_heading_custom
  assign item_heading_font = section.settings.item_heading_font
  assign item_heading_height = section.settings.item_heading_height
  assign item_heading_align = section.settings.item_heading_align
  assign item_heading_align_mobile = section.settings.item_heading_align_mobile
  assign item_heading_mt = section.settings.item_heading_mt
  assign item_heading_mt_mobile = section.settings.item_heading_mt_mobile

  assign item_text_size = section.settings.item_text_size
  assign item_text_size_mobile = section.settings.item_text_size_mobile
  assign item_text_color = section.settings.item_text_color
  assign item_text_custom = section.settings.item_text_custom
  assign item_text_font = section.settings.item_text_font
  assign item_text_height = section.settings.item_text_height
  assign item_text_align = section.settings.item_text_align
  assign item_text_align_mobile = section.settings.item_text_align_mobile
  assign item_text_mt = section.settings.item_text_mt
  assign item_text_mt_mobile = section.settings.item_text_mt_mobile

  assign item_heading_align_reverse = ""
  if item_heading_align == "left"
    assign item_heading_align_reverse = "right"
  elsif item_heading_align == "right"
     assign item_heading_align_reverse = "left"
  endif

  assign item_text_align_reverse = ""
  if item_text_align == "left"
    assign item_text_align_reverse = "right"
  elsif item_text_align == "right"
     assign item_text_align_reverse = "left"
  endif

-%}

{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  item_heading_font | font_face: font_display: 'swap' }}
  {{  item_text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .timeline-heading-{{ section.id }} {
    width: 100%;
    text-align: {{ heading_align_mobile }};
  }

  .timeline-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .timeline-items-{{ section.id }} {
    width: 100%;
    max-width: 75%;
    margin: 0px auto;
    margin-top: {{ items_mt_mobile }}px;
  }

  .timeline-item-{{ section.id }} {
    display: flex;
    position: relative;
  }

  /* .timeline-item-{{ section.id }}:before {
    content: '';
    height: 100%;
    width: 0;
    border-left: 1.5px dashed {{ line_color }};
    position: absolute;
    top: 0;
    left: 0;
  } */

  .timeline-item-{{ section.id }}:nth-child(odd) {
    justify-content: flex-end;
  }

  .timeline-item-{{ section.id }}:first-child:before {
    display: none;
  }

  .timeline-content-inner-{{ section.id }} {
    position: relative;
  }

  .timeline-item-{{ section.id }}:nth-child(even) .timeline-content-inner-{{ section.id }}:before {
    left: -{{ content_padding_horizontal_mobile }}px;
    content: '';
    height: 100%;
    width: 0;
    border-left: 1px dashed {{ line_color }};
    position: absolute;
    top: 0;
  }

  .timeline-item-{{ section.id }}:nth-child(odd) .timeline-content-inner-{{ section.id }}:before {
    left: -{{ content_padding_horizontal_mobile }}px;
    content: '';
    height: 100%;
    width: 0;
    border-left: 1px dashed {{ line_color }};
    position: absolute;
    top: 0;
  }

  .timeline-item-{{ section.id }}:last-child:before {
    display: none;
  }

  .timeline-top-{{ section.id }} {
    display: block !important;
  }

  .timeline-item-{{ section.id }}:not(:first-child) .timeline-top-{{ section.id }} {
    padding-top: {{ items_space_mobile }}px;
    position: relative;
  }

  .timeline-item-{{ section.id }}:not(:first-child) .timeline-top-{{ section.id }}:before {
    left: -{{ content_padding_horizontal_mobile }}px;
    content: '';
    height: 100%;
    width: 0;
    border-left: 1px dashed {{ line_color }};
    position: absolute;
    top: 0;
  }

  .timeline-item-{{ section.id }}:not(:first-child) .timeline-top-{{ section.id }}:before {
    left: -{{ content_padding_horizontal_mobile }}px;
    content: '';
    height: 100%;
    width: 0;
    border-left: 1px dashed {{ line_color }};
    position: absolute;
    top: 0;
  }

  .timeline-content-{{ section.id }} {
    padding-left: {{ content_padding_horizontal_mobile }}px;
  }

  .timeline-item-heading-{{ section.id }} {
    position: relative;
  }

  .timeline-item-heading-{{ section.id }}:before {
    content: '';
    width: {{ circle_size_mobile }}px;
    height: {{ circle_size_mobile }}px;
    background-color: {{ circle_bg_color }};
    border: {{ circle_border_thickness }}px solid {{ circle_border_color }};
    border-radius: 50%;
    position: absolute;
    top: -{{ circle_size_mobile | times: 0.25 | round: 0 }}px;
    left: calc(({{ content_padding_horizontal_mobile }}px + {{ circle_size_mobile | times: 0.5 | round: 0 }}px) * -1);
    z-index: 2;
  }

  .timeline-image-{{ section.id }} {
    width: 100%;
    max-width: {{ image_width_mobile }}%;
    border-radius: {{ image_radius }}px;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    overflow: hidden;
  }

  .timeline-image-{{ section.id }} img,
  .timeline-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .timeline-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .timeline-top-{{ section.id }} {
    padding-bottom: {{ item_heading_mt_mobile }}px;
  }

  .timeline-item-heading-{{ section.id }} {
    width: 100%;
    text-align: {{ item_heading_align_mobile }};
  }

  .timeline-item-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ item_heading_size_mobile }}px;
    color: {{ item_heading_color }};
    line-height: {{ item_heading_height }}%;
    text-transform: unset;
  }

  .timeline-item-text-{{ section.id }} {
    width: 100%;
    margin-top: {{ item_text_mt_mobile }}px;
    text-align: {{ item_text_align_mobile }};
  }

  .timeline-item-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ item_text_size_mobile }}px;
    color: {{ item_text_color }};
    line-height: {{ item_text_height }}%;
    text-transform: unset;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .timeline-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .timeline-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .timeline-items-{{ section.id }} {
      max-width: 100%;
      margin-top: {{ items_mt }}px;
    }

    .timeline-item-{{ section.id }}:not(:first-child) .timeline-top-{{ section.id }} {
      padding-top: {{ items_space }}px;
    }

    /* .timeline-item-{{ section.id }}:before {
      left: 50%;
      -webkit-transform: translateX(-50%);
      transform: translateX(-50%);
    } */

    .timeline-item-{{ section.id }}:nth-child(odd) .timeline-content-inner-{{ section.id }}:before {
      left: -{{ content_padding_horizontal | plus: 1 }}px;
    }

    .timeline-item-{{ section.id }}:nth-child(even) .timeline-content-inner-{{ section.id }}:before {
      right: -{{ content_padding_horizontal | plus: 1 }}px;
      left: unset;
    }

    .timeline-content-{{ section.id }} {
      width: 50%;
      padding-left: 0px;
      padding-right: {{ content_padding_horizontal }}px;
    }

    .timeline-item-{{ section.id }}:nth-child(odd) .timeline-content-{{ section.id }} {
      padding-left: {{ content_padding_horizontal }}px;
    }

    .timeline-item-heading-{{ section.id }}:before {
      width: {{ circle_size }}px;
      height: {{ circle_size }}px;
      top: -{{ circle_size | times: 0.25 | round: 0 }}px;
      left: calc(({{ content_padding_horizontal }}px + {{ circle_size | times: 0.5 | round: 0 }}px) * -1);
    }

    .timeline-item-{{ section.id }}:nth-child(even) .timeline-item-heading-{{ section.id }}:before {
      left: unset;
      right: calc(({{ content_padding_horizontal }}px + {{ circle_size | times: 0.5 | round: 0 }}px) * -1);
    }

    .timeline-image-{{ section.id }} {
      max-width: {{ image_width }}%;
    }

    .timeline-top-{{ section.id }} {
      padding-bottom: {{ item_heading_mt }}px;
    }

    .timeline-item-heading-{{ section.id }} {
      text-align: {{ item_heading_align }};
    }

    .timeline-item-heading-{{ section.id }} * {
      font-size: {{ item_heading_size }}px;
    }

    .timeline-item-{{ section.id }}:nth-child(even) .timeline-item-heading-{{ section.id }} {
     text-align: {{ item_heading_align_reverse }};
    }

    .timeline-item-text-{{ section.id }} {
      margin-top: {{ item_text_mt }}px;
      text-align: {{ item_text_align }};
    }

    .timeline-item-text-{{ section.id }} * {
      font-size: {{ item_text_size }}px;
    }

    .timeline-item-{{ section.id }}:nth-child(even) .timeline-item-text-{{ section.id }} {
      text-align: {{ item_text_align_reverse }};
    }

    .timeline-item-{{ section.id }}:nth-child(odd):not(:first-child) .timeline-top-{{ section.id }}:before {
      left: -{{ content_padding_horizontal }}px;
    }

    .timeline-item-{{ section.id }}:nth-child(even):not(:first-child) .timeline-top-{{ section.id }}:before {
      left: unset;
      right: -{{ content_padding_horizontal }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .timeline-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if item_heading_custom %}
  <style>
    .timeline-item-heading-{{ section.id }} * {
      font-family: {{ item_heading_font.family }}, {{ item_heading_font.fallback_families }};
      font-weight: {{ item_heading_font.weight }};
      font-style: {{ item_heading_font.style }};
    }
  </style>
{% endif %}

{% if item_text_custom %}
  <style>
    .timeline-item-text-{{ section.id }} * {
      font-family: {{ item_text_font.family }}, {{ item_text_font.fallback_families }};
      font-weight: {{ item_text_font.weight }};
      font-style: {{ item_text_font.style }};
    }
  </style>
{% endif %}

{% if image_align_mobile == "center" %}
  <style>
   .timeline-image-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    } 
  </style>
{% elsif image_align_mobile == "right" %}
  <style>
   .timeline-image-{{ section.id }} {
      margin-left: auto;
    } 
  </style>
{% endif %}

{% if image_align == "center" %}
  <style>
    @media(min-width: 1024px) {
     .timeline-image-{{ section.id }} {
        margin-left: auto;
        margin-right: auto;
      } 
    }
  </style>
{% elsif image_align == "right" %}
  <style>
    @media(min-width: 1024px) {
     .timeline-image-{{ section.id }} {
        margin-left: auto;
        margin-right: 0px;
      }

      .timeline-item-{{ section.id }}:nth-child(even) .timeline-image-{{ section.id }} {
        margin-left: 0px;
        margin-right: auto;
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .timeline-image-{{ section.id }} {
        margin-left: 0px;
        margin-right: 0px;
      }

      .timeline-item-{{ section.id }}:nth-child(even) .timeline-image-{{ section.id }} {
        margin-left: auto;
        margin-right: 0px;
      }
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .timeline-image-{{ section.id }} {
     aspect-ratio: 9.6/12; 
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
    .timeline-image-{{ section.id }}{
     aspect-ratio: 12/10; 
    }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .timeline-image-{{ section.id }} {
     aspect-ratio: 12/12; 
    }
  </style>
{% else %}
  <style>
    .timeline-image-{{ section.id }} {
     aspect-ratio: auto; 
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .timeline-image-{{ section.id }} {
       aspect-ratio: 9.6/12; 
      } 
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .timeline-image-{{ section.id }}{
       aspect-ratio: 12/10; 
      }
    }
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .timeline-image-{{ section.id }} {
       aspect-ratio: 12/12; 
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .timeline-image-{{ section.id }} {
       aspect-ratio: auto; 
      }
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} timeline-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      {% if heading != blank %}
        <div class="timeline-heading-{{ section.id }}">
          {{ heading }}
        </div>
      {% endif %}

      <div class="timeline-items-{{ section.id }}">
        {% for block in section.blocks %}
          <div class="timeline-item-{{ section.id }}">
            <div class="timeline-content-{{ section.id }}">
                <div class="timeline-top-{{ section.id }}">
                  {% unless block.settings.hide_image %}
                    <div class="timeline-image-{{ section.id }}">
                      {% if block.settings.image != blank %}
                        <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                      {% else %}
                        {{ 'image' | placeholder_svg_tag }}
                      {% endif %}
                    </div>
                  {% endunless %}
                </div>
              <div class="timeline-content-inner-{{ section.id }}">
                <div class="timeline-item-heading-{{ section.id }}">
                  {{ block.settings.heading }}
                </div>
                {% if block.settings.text != blank %}
                  <div class="timeline-item-text-{{ section.id }}">
                    {{ block.settings.text }}
                  </div>
                {% endif %}
              </div>
            </div>
          </div>
        {% endfor %}
      </div>
    </div>
</div>
