{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy

  assign heading = section.settings.heading
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_color = section.settings.heading_color
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_height = section.settings.heading_height
  assign heading_align = section.settings.heading_align
  assign heading_align_mobile = section.settings.heading_align_mobile

  assign items_width = section.settings.items_width
  assign items_width_mobile = section.settings.items_width_mobile
  assign items_align = section.settings.items_align
  assign items_align_mobile = section.settings.items_align_mobile
  assign items_mt = section.settings.items_mt
  assign items_gap_mobile = section.settings.items_gap_mobile
  assign items_gap = section.settings.items_gap

  assign line_color = section.settings.line_color
  assign line_thickness = section.settings.line_thickness

  assign item_width_mobile = section.settings.item_width_mobile
  assign item_width = section.settings.item_width
  assign item_gap_mobile = section.settings.item_gap_mobile
  assign item_gap = section.settings.item_gap
  assign item_align_mobile = section.settings.item_align_mobile
  assign item_align = section.settings.item_align

  assign circle_size = section.settings.circle_size
  assign circle_color = section.settings.circle_color
  assign circle_border_thickness = section.settings.circle_border_thickness
  assign circle_border_color = section.settings.circle_border_color

  assign title_size = section.settings.title_size
  assign title_size_mobile = section.settings.title_size_mobile
  assign title_color = section.settings.title_color
  assign title_custom = section.settings.title_custom
  assign title_font = section.settings.title_font
  assign title_height = section.settings.title_height

  assign date_size = section.settings.date_size
  assign date_size_mobile = section.settings.date_size_mobile
  assign date_color = section.settings.date_color
  assign date_custom = section.settings.date_custom
  assign date_font = section.settings.date_font
  assign date_height = section.settings.date_height
  assign date_mt = section.settings.date_mt

  assign card_align_mobile = section.settings.card_align_mobile
  assign card_align = section.settings.card_align_mobile
  assign card_padding_vertical = section.settings.card_padding_vertical
  assign card_padding_horizontal = section.settings.card_padding_horizontal
  assign card_radius = section.settings.card_radius
  assign card_border_thickness = section.settings.card_border_thickness
  assign card_border_color = section.settings.card_border_color
  assign card_bg = section.settings.card_bg
  assign card_shadow = section.settings.card_shadow
  assign card_shadow_color = section.settings.card_shadow_color

  assign image_ratio_mobile = section.settings.image_ratio_mobile
  assign image_ratio = section.settings.image_ratio
  assign image_radius = section.settings.image_radius
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color

  assign card_title_size = section.settings.card_title_size
  assign card_title_size_mobile = section.settings.card_title_size_mobile
  assign card_title_color = section.settings.card_title_color
  assign card_title_custom = section.settings.card_title_custom
  assign card_title_font = section.settings.card_title_font
  assign card_title_height = section.settings.card_title_height
  assign card_title_mt = section.settings.card_title_mt

  assign card_text_size = section.settings.card_text_size
  assign card_text_size_mobile = section.settings.card_text_size_mobile
  assign card_text_color = section.settings.card_text_color
  assign card_text_custom = section.settings.card_text_custom
  assign card_text_font = section.settings.card_text_font
  assign card_text_height = section.settings.card_text_height
  assign card_text_mt = section.settings.card_text_mt

-%}

{%- style -%}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  date_font | font_face: font_display: 'swap' }}
  {{  card_title_font | font_face: font_display: 'swap' }}
  {{  card_text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    overflow-x: auto;
  }

  .timeline-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
    text-align: {{ heading_align_mobile }};
  }

  .timeline-heading-{{ section.id }} {
    margin-bottom: {{ items_mt | times: 0.75 | round: 0 }}px;
  }

  .timeline-items-{{ section.id }} {
    width: 100%;
    max-width: {{ items_width_mobile }}%;
    position: relative;
    display: flex;
    align-items: stretch;
    gap: {{ items_gap_mobile }}px;
  }

  .timeline-item-line-{{ section.id }} .timeline-item-{{ section.id }} {
    position: relative;
  }

  .timeline-item-line-{{ section.id }} .timeline-item-{{ section.id }}:before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%);
    width: 115%;
    height: {{ line_thickness }}px;
    background-color: {{ line_color }};
  }

  .timeline-item-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: space-between;
    gap: {{ item_gap_mobile }}px;
    word-break: break-word;
    flex-direction: column;
    flex: 0 0 {{ item_width_mobile }}px;
  }

  .timeline-item-reverse-{{ section.id }} .timeline-right-{{ section.id }} {
    flex-direction: column-reverse;
  }

  .timeline-left-{{ section.id }} {
    text-align: center;
  }

  .timeline-circle-{{ section.id }} {
    display: block !important;
    flex: 0 0 {{ circle_size }}px;
    width: {{ circle_size }}px;
    border-radius: 50%;
    border: {{ circle_border_thickness }}px solid {{ circle_border_color }};
    background-color: {{ circle_color }};
    position: relative;
    z-index: 2;
  }

  .timeline-title-{{ section.id }} {
    margin: 0;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .timeline-date-{{ section.id }} {
    margin: 0;
    margin-top: {{ date_mt | times: 0.75 | round: 0 }}px;
    font-size: {{ date_size_mobile }}px;
    color: {{ date_color }};
    line-height: {{ date_height }}%;
    text-transform: unset;
  }

  .timeline-right-{{ section.id }} {
    display: flex;
    align-items: center;
    flex-direction: column;
  }

  .timeline-right-{{ section.id }} > svg {
    margin-bottom: -12px;
    display: block;
    width: 20px;
    height: 40px;
    transform: rotate(270deg);
  }

  .timeline-item-reverse-{{ section.id }} .timeline-right-{{ section.id }} > svg {
    transform: rotate(90deg);
    margin-bottom: 0px;
    margin-top: -12px;
  }

  .timeline-card-{{ section.id }} {
    padding: {{ card_padding_vertical | times: 0.75 | round: 0 }}px {{ card_padding_horizontal | times: 0.75 | round: 0 }}px;
    border-radius: {{ card_radius }}px;
    border: {{ card_border_thickness }}px solid {{ card_border_color }};
    background-color: {{ card_bg }};
    text-align: {{ card_align_mobile }};
    position: relative;
    z-index: 2;
  }

  .timeline-card-title-{{ section.id }} {
    margin: 0;
    margin-top: {{ card_title_mt | times: 0.75 | round: 0 }}px;
    font-size: {{ card_title_size_mobile }}px;
    color: {{ card_title_color }};
    line-height: {{ card_title_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .timeline-card-text-{{ section.id }} {
    margin: 0;
    margin-top: {{ card_text_mt | times: 0.75 | round: 0 }}px;
    font-size: {{ card_text_size_mobile }}px;
    color: {{ card_text_color }};
    line-height: {{ card_text_height }}%;
    text-transform: unset;
  }

  .timeline-image-{{ section.id }} {
    border-radius: {{ image_radius }}px;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
  }

  .timeline-image-{{ section.id }} img,
  .timeline-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: {{ image_radius }}px;
  }

  .timeline-image-{{ section.id }} svg {
    background-color: #DDDDDD;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .timeline-heading-{{ section.id }} {
      margin-bottom: {{ items_mt }}px;
    }

    .timeline-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
      text-align: {{ heading_align }};
    }

    .timeline-items-{{ section.id }} {
      max-width: {{ items_width }}%;
      gap: {{ items_gap }}px;
    }

    .timeline-item-{{ section.id }} {
      gap: {{ item_gap }}px;
      flex: 0 0 {{ item_width }}px;
    }

    .timeline-title-{{ section.id }} {
      font-size: {{ title_size }}px;
    }

    .timeline-date-{{ section.id }} {
      margin-top: {{ date_mt }}px;
      font-size: {{ date_size }}px;
    }

    .timeline-card-{{ section.id }} {
      padding: {{ card_padding_vertical }}px {{ card_padding_horizontal }}px;
      text-align: {{ card_align }};
    }

    .timeline-card-title-{{ section.id }} {
      margin-top: {{ card_title_mt }}px;
      font-size: {{ card_title_size }}px;
    }

    .timeline-card-text-{{ section.id }} {
      margin-top: {{ card_text_mt }}px;
      font-size: {{ card_text_size }}px;
    }

    .timeline-right-{{ section.id }} > svg {
      width: 20px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .timeline-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .timeline-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if date_custom %}
  <style>
    .timeline-date-{{ section.id }} {
      font-family: {{ date_font.family }}, {{ date_font.fallback_families }};
      font-weight: {{ date_font.weight }};
      font-style: {{ date_font.style }};
    }
  </style>
{% endif %}

{% if card_title_custom %}
  <style>
    .timeline-card-title-{{ section.id }} {
      font-family: {{ card_title_font.family }}, {{ card_title_font.fallback_families }};
      font-weight: {{ card_title_font.weight }};
      font-style: {{ card_title_font.style }};
    }
  </style>
{% endif %}

{% if card_text_custom %}
  <style>
    .timeline-card-text-{{ section.id }} {
      font-family: {{ card_text_font.family }}, {{ card_text_font.fallback_families }};
      font-weight: {{ card_text_font.weight }};
      font-style: {{ card_text_font.style }};
    }
  </style>
{% endif %}

{% if items_align_mobile == "center" %}
  <style>
   .timeline-items-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    } 
  </style>
{% elsif items_align_mobile == "right" %}
  <style>
   .timeline-items-{{ section.id }} {
      margin-left: auto;
    } 
  </style>
{% endif %}

{% if items_align == "center" %}
  <style>
    @media(min-width: 1024px) {
     .timeline-items-{{ section.id }} {
        margin-left: auto;
        margin-right: auto;
       justify-content: center;
      } 
    }
  </style>
{% elsif items_align == "right" %}
  <style>
    @media(min-width: 1024px) {
     .timeline-items-{{ section.id }} {
        margin-left: auto;
        margin-right: 0px;
       justify-content: end;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .timeline-items-{{ section.id }} {
        margin-left: 0px;
        margin-right: 0px;
       justify-content: start;
      } 
    }
  </style>
{% endif %}

{% if item_align_mobile == "center" %}
  <style>
   .timeline-item-text-{{ section.id }} {
      justify-content: center;
    }
  </style>
{% elsif item_align_mobile == "bottom" %}
  <style>
   .timeline-item-text-{{ section.id }} {
      padding-bottom: 20px;
      justify-content: flex-end;
    }
    .timeline-item-reverse-{{ section.id }}.timeline-item-text-{{ section.id }} {
      padding-bottom: 0px;
      padding-top: 20px;
      justify-content: flex-start;
    }
  </style>
{% else %}
  <style>
   .timeline-item-text-{{ section.id }} {
      justify-content: flex-start;
    }
    .timeline-item-reverse-{{ section.id }}.timeline-item-text-{{ section.id }} {
      justify-content: flex-end;
    }
  </style>
{% endif %}

{% if item_align == "center" %}
  <style>
    @media(min-width: 1024px) {
     .timeline-item-text-{{ section.id }} {
        padding: 0px;
        justify-content: center;
      }
      .timeline-item-reverse-{{ section.id }}.timeline-item-text-{{ section.id }} {
        padding: 0px;
        justify-content: center;
      }
    }
  </style>
{% elsif item_align == "bottom" %}
  <style>
    @media(min-width: 1024px) {
      .timeline-item-text-{{ section.id }} {
        padding-bottom: 20px;
        justify-content: flex-end;
      }
      .timeline-item-reverse-{{ section.id }}.timeline-item-text-{{ section.id }} {
        padding-top: 20px;
        padding-bottom: 0px;
        justify-content: flex-start;
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .timeline-item-text-{{ section.id }} {
       justify-content: flex-start;
       padding: 0px;
      }
      .timeline-item-reverse-{{ section.id }}.timeline-item-text-{{ section.id }} {
        justify-content: flex-end;
        padding: 0px;
      }
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .timeline-image-{{ section.id }} {
     aspect-ratio: 9.6/12; 
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
    .timeline-image-{{ section.id }} {
     aspect-ratio: 12/8; 
    }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .timeline-image-{{ section.id }} {
     aspect-ratio: 12/12; 
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .timeline-image-{{ section.id }} {
       aspect-ratio: 9.6/12; 
      }
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .timeline-image-{{ section.id }} {
       aspect-ratio: 12/8; 
      }
    }
  </style>
{% elsif image_ratio == "square" %}
  <style>
     @media(min-width: 1024px) {
      .timeline-image-{{ section.id }} {
       aspect-ratio: 12/12; 
      } 
     }
  </style>
{% else %}
  <style>
     @media(min-width: 1024px) {
      .timeline-image-{{ section.id }} {
       aspect-ratio: auto; 
      }
     }
  </style>
{% endif %}

{% if card_shadow %}
  <style>
    .timeline-card-{{ section.id }} {
      box-shadow: 0px 0px 10px 0px {{ card_shadow_color | hex_to_rgba: 0.2 }};
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} timeline-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      {% if heading != blank %}
        <div class="timeline-heading-{{ section.id }}">{{ heading }}</div>
      {% endif %}
      <div class="timeline-items-{{ section.id }}">
        {% for block in section.blocks %}
          {% if block.settings.card_reverse %}
            <div class="timeline-item-{{ section.id }} {% if block.settings.card_reverse %}timeline-item-reverse-{{ section.id }}{% endif %}">
              <div class="timeline-right-{{ section.id }}">
                <svg class="triangle" width="18" height="42" viewBox="0 0 22 48" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M20.4645 27.5355L0 48L-2.09815e-06 0L20.4645 20.4645C22.4171 22.4171 22.4171 25.5829 20.4645 27.5355Z" fill="{{ card_bg }}"></path></svg>
                <div class="timeline-card-{{ section.id }}">
                  {% unless block.settings.hide_image %}
                    <div class="timeline-image-{{ section.id }}">
                      {% if block.settings.image != blank %}
                        <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}lazy="loading"{% endif %}>
                      {% else %}
                        {{ 'image' | placeholder_svg_tag }}
                      {% endif %}
                    </div>
                  {% endunless %}
                  {% if block.settings.card_title != blank %}
                    <p class="timeline-card-title-{{ section.id }}" {% if block.settings.hide_image %}style="margin: 0px;"{% endif %}>{{ block.settings.card_title }}</p>
                  {% endif %}
                  {% if block.settings.card_text != blank %}
                    <p class="timeline-card-text-{{ section.id }}">{{ block.settings.card_text }}</p>
                  {% endif %}
                </div>
              </div>
            </div>
          {% else %}
            <div class="timeline-item-{{ section.id }} timeline-item-text-{{ section.id }} {% if block.settings.card_reverse %}timeline-item-reverse-{{ section.id }}{% endif %}">
              <div class="timeline-left-{{ section.id }}">
                {% if block.settings.title != blank %}
                  <p class="timeline-title-{{ section.id }}">{{ block.settings.title }}</p>
                {% endif %}
                {% if block.settings.date != blank %}
                  <p class="timeline-date-{{ section.id }}">{{ block.settings.date }}</p>
                {% endif %}
              </div>
            </div>
          {% endif %}
        {% endfor %}
      </div>
      <div class="timeline-items-{{ section.id }} timeline-item-line-{{ section.id }}">
        {% for block in section.blocks %}
          <div class="timeline-item-{{ section.id }}">
            <div class="timeline-circle-{{ section.id }}"></div>
          </div>
        {% endfor %}
      </div>
      <div class="timeline-items-{{ section.id }}">
        {% for block in section.blocks %}
          {% if block.settings.card_reverse %}
            <div class="timeline-item-{{ section.id }} timeline-item-text-{{ section.id }} {% if block.settings.card_reverse %}timeline-item-reverse-{{ section.id }}{% endif %}">
              <div class="timeline-left-{{ section.id }}">
                {% if block.settings.title != blank %}
                  <p class="timeline-title-{{ section.id }}">{{ block.settings.title }}</p>
                {% endif %}
                {% if block.settings.date != blank %}
                  <p class="timeline-date-{{ section.id }}">{{ block.settings.date }}</p>
                {% endif %}
              </div>
            </div>
          {% else %}
            <div class="timeline-item-{{ section.id }} {% if block.settings.card_reverse %}timeline-item-reverse-{{ section.id }}{% endif %}">
              <div class="timeline-right-{{ section.id }}">
                <svg class="triangle" width="18" height="42" viewBox="0 0 22 48" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M20.4645 27.5355L0 48L-2.09815e-06 0L20.4645 20.4645C22.4171 22.4171 22.4171 25.5829 20.4645 27.5355Z" fill="{{ card_bg }}"></path></svg>
                <div class="timeline-card-{{ section.id }}">
                  {% unless block.settings.hide_image %}
                    <div class="timeline-image-{{ section.id }}">
                      {% if block.settings.image != blank %}
                        <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}lazy="loading"{% endif %}>
                      {% else %}
                        {{ 'image' | placeholder_svg_tag }}
                      {% endif %}
                    </div>
                  {% endunless %}
                  {% if block.settings.card_title != blank %}
                    <p class="timeline-card-title-{{ section.id }}" {% if block.settings.hide_image %}style="margin: 0px;"{% endif %}>{{ block.settings.card_title }}</p>
                  {% endif %}
                  {% if block.settings.card_text != blank %}
                    <p class="timeline-card-text-{{ section.id }}">{{ block.settings.card_text }}</p>
                  {% endif %}
                </div>
              </div>
            </div>
          {% endif %}
        {% endfor %}
      </div>
    </div>
</div>
